INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3B325A4DB8E65489E22810F5B1392F5', X'77B4B62D19261448A36F422188064982', X'77B4B62D19261448A36F422188064982', 8, NULL, NULL, 2, NULL, 0, 'Drizzle olive oil over each ball, and place in preheated oven at 350F for 25-30 minutes, until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2585CC2E7609C4AB28BE25B2BB9CD7D', X'C6BBCFF5FC6295479AC1556CED49FB23', X'C6BBCFF5FC6295479AC1556CED49FB23', 0, NULL, NULL, 2, NULL, 0, 'In bowl, combine all ingredients (not including chicken), mash well with fork.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD59D1F41750F94F999AC2BB4380CD62', X'C6BBCFF5FC6295479AC1556CED49FB23', X'C6BBCFF5FC6295479AC1556CED49FB23', 1, NULL, NULL, 2, NULL, 0, 'Cut slit in top corner of chicken, and stick your finger in to create deep pocket. Go slow as to not break through.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4D7F7D35D5ECE468AB3DA075352A291', X'C6BBCFF5FC6295479AC1556CED49FB23', X'C6BBCFF5FC6295479AC1556CED49FB23', 2, NULL, NULL, 2, NULL, 0, 'Place half of the stuffing mixture inside each chicken, reserving a small amount to rub on outside of each piece.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A2335C8AE76B345ADF492A06359FAF0', X'C6BBCFF5FC6295479AC1556CED49FB23', X'C6BBCFF5FC6295479AC1556CED49FB23', 3, NULL, NULL, 2, NULL, 0, 'Put in ovensafe dish.*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84C284D1DC1AEE48BC633BE241FA1063', X'C6BBCFF5FC6295479AC1556CED49FB23', X'C6BBCFF5FC6295479AC1556CED49FB23', 4, NULL, NULL, 2, NULL, 0, 'Bake in preheated oven at 350F for 40-45 minutes, or until cooked');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2904C7165ED1854C808DF428508C5DB9', X'C6BBCFF5FC6295479AC1556CED49FB23', X'C6BBCFF5FC6295479AC1556CED49FB23', 5, NULL, NULL, 2, NULL, 0, 'Remove and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D34A2E525C8C84FA3240E6842AC150B', X'84B849C6B30D3F41A89E20496F04E967', X'84B849C6B30D3F41A89E20496F04E967', 0, NULL, NULL, 2, NULL, 0, 'Bring lightly salted water to boil (we add a little olive oil to the water as well).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD5CEBB186547A449A83A7EE58CF9BB9', X'84B849C6B30D3F41A89E20496F04E967', X'84B849C6B30D3F41A89E20496F04E967', 1, NULL, NULL, 2, NULL, 0, 'Add orzo and cook as directed, normally 9 to 10 minutes for al dente.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9328DADDD6569B42827D4626F83CEDBF', X'84B849C6B30D3F41A89E20496F04E967', X'84B849C6B30D3F41A89E20496F04E967', 2, NULL, NULL, 2, NULL, 0, 'While boiling roast pine nuts in pan until golden brown and fragrant. Remove and set aside.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC89DD2BDAA7A9468AD7D7354798015A', X'84B849C6B30D3F41A89E20496F04E967', X'84B849C6B30D3F41A89E20496F04E967', 3, NULL, NULL, 2, NULL, 0, 'Once orzo is done, drain and place back in pot.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF8747E04E22944D9A21DF960F39E465', X'84B849C6B30D3F41A89E20496F04E967', X'84B849C6B30D3F41A89E20496F04E967', 4, NULL, NULL, 2, NULL, 0, 'Add all other ingredients in with orzo, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7F5C1CEAEBF0E43A8BEAEBDDC9C1848', X'84B849C6B30D3F41A89E20496F04E967', X'84B849C6B30D3F41A89E20496F04E967', 5, NULL, NULL, 2, NULL, 0, 'Scoop out desired amount per plate, serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24E9C90D0358EE44AADF93EF490269B9', X'961B825C920B914C9054974441EE2DC8', X'961B825C920B914C9054974441EE2DC8', 0, NULL, NULL, 2, NULL, 0, 'In large bowl, combine ALL dry ingredients: flour, sugar, baking powder, cinnamon, and walnuts, mix.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC55564553767A48AAB9DE801B1343C2', X'961B825C920B914C9054974441EE2DC8', X'961B825C920B914C9054974441EE2DC8', 1, NULL, NULL, 2, NULL, 0, 'Add in remaining wet ingredients: olive oil, vanilla, orange juice, brandy, orange zest, mix well until batter is formed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F27E1C23C0A46A4A9CFF5F481484E74A', X'961B825C920B914C9054974441EE2DC8', X'961B825C920B914C9054974441EE2DC8', 2, NULL, NULL, 2, NULL, 0, 'Pour into cake pan (we used a bundt pan) greased with some olive oil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8B769C61AD1F14B816777D8FF22458A', X'961B825C920B914C9054974441EE2DC8', X'961B825C920B914C9054974441EE2DC8', 3, NULL, NULL, 2, NULL, 0, 'Place in preheated oven at 350F for 40-45 minutes, until toothpick comes out clean and nice color is achieved.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'442D775BDAC13245B9FED80789C5B909', X'961B825C920B914C9054974441EE2DC8', X'961B825C920B914C9054974441EE2DC8', 4, NULL, NULL, 2, NULL, 0, 'Remove, top with powdered sugar, let cool for about 10 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'085135243A686E4A83B79F54390DEC7D', X'961B825C920B914C9054974441EE2DC8', X'961B825C920B914C9054974441EE2DC8', 5, NULL, NULL, 2, NULL, 0, 'Slice and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A47C879A758C904195C6CFFDCE0DA1FF', X'709CEDF04A4A42428EA1311BC65730C5', X'709CEDF04A4A42428EA1311BC65730C5', 0, NULL, NULL, 2, NULL, 0, 'Dice up peppers and olives.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67B046311E916D409CBBB4B3F6F7B993', X'709CEDF04A4A42428EA1311BC65730C5', X'709CEDF04A4A42428EA1311BC65730C5', 1, NULL, NULL, 2, NULL, 0, 'In an oiled pan over medium heat add peppers and olives and sauté for a few minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A02134475E0ADA4299F7D1FF3959B516', X'709CEDF04A4A42428EA1311BC65730C5', X'709CEDF04A4A42428EA1311BC65730C5', 2, NULL, NULL, 2, NULL, 0, 'Next, add turkey and cook until it is done.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2D548FA9032174D92226EDFC831FE79', X'709CEDF04A4A42428EA1311BC65730C5', X'709CEDF04A4A42428EA1311BC65730C5', 3, NULL, NULL, 2, NULL, 0, 'While doing this, add egg whites to bowl and whisk with a fork.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C1E8690EDBEEB4486FB900E04EFACC2', X'709CEDF04A4A42428EA1311BC65730C5', X'709CEDF04A4A42428EA1311BC65730C5', 4, NULL, NULL, 2, NULL, 0, 'When turkey is cooked, add egg whites and stir occasionally.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B24EEBC1EC6BC04E937672CAAB04376F', X'709CEDF04A4A42428EA1311BC65730C5', X'709CEDF04A4A42428EA1311BC65730C5', 5, NULL, NULL, 2, NULL, 0, 'Cook until eggs are done, about 2-4 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAE388A930B9AC449D55D9AF5D692100', X'709CEDF04A4A42428EA1311BC65730C5', X'709CEDF04A4A42428EA1311BC65730C5', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and place half of mixture (or desired amount) on warm tortilla. Sprinkle with feta, wrap, cut in half and put on plate.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BC845BB919B304094375E9282D8EEB3', X'709CEDF04A4A42428EA1311BC65730C5', X'709CEDF04A4A42428EA1311BC65730C5', 7, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CFB6486A30B0D4499857A86365EFEF7', X'A0C781B48541B74B81472446E33B3F54', X'A0C781B48541B74B81472446E33B3F54', 0, NULL, NULL, 2, NULL, 0, 'In large mixing bowl, cream butter.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30AEBF391884D44D969B618FF070BB69', X'A0C781B48541B74B81472446E33B3F54', X'A0C781B48541B74B81472446E33B3F54', 1, NULL, NULL, 2, NULL, 0, 'Once butter is creamed, slowly mix in sugar.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E532C66BF22AF4EBA71765ED35E2DCE', X'A0C781B48541B74B81472446E33B3F54', X'A0C781B48541B74B81472446E33B3F54', 2, NULL, NULL, 2, NULL, 0, 'Continue beating, add in egg yolks (one at a time), cognac, and vanilla.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEFD85C06B978C479297A90511248749', X'A0C781B48541B74B81472446E33B3F54', X'A0C781B48541B74B81472446E33B3F54', 3, NULL, NULL, 2, NULL, 0, 'Mix baking soda and flour together in separate bowl.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E93E5DA5EE014145B8BBB9D7D1ECA1D4', X'A0C781B48541B74B81472446E33B3F54', X'A0C781B48541B74B81472446E33B3F54', 4, NULL, NULL, 2, NULL, 0, 'While mixing, slowly add flour to butter mixture. Continue mixing until soft dough is formed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D690C0A524706B48B840BF16C3E7556A', X'A0C781B48541B74B81472446E33B3F54', X'A0C781B48541B74B81472446E33B3F54', 5, NULL, NULL, 2, NULL, 0, 'Take about 2 inch pieces and roll it out using your hands.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08CEF9346B7BF54AAE9D9B785A74F435', X'A0C781B48541B74B81472446E33B3F54', X'A0C781B48541B74B81472446E33B3F54', 6, NULL, NULL, 2, NULL, 0, 'Form an upside down V, and braid pieces together. **');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C5657F0BCB43B4EBCC0F0F9EBF72A4A', X'A0C781B48541B74B81472446E33B3F54', X'A0C781B48541B74B81472446E33B3F54', 7, NULL, NULL, 2, NULL, 0, 'Place on baking sheet lined with parchment paper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AD95BD041A5A0428E43F0F37C7F5E60', X'A0C781B48541B74B81472446E33B3F54', X'A0C781B48541B74B81472446E33B3F54', 8, NULL, NULL, 2, NULL, 0, 'Repeat until all dough is used.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82096408E8EE374089D67C4BF5C7AF16', X'A0C781B48541B74B81472446E33B3F54', X'A0C781B48541B74B81472446E33B3F54', 9, NULL, NULL, 2, NULL, 0, 'Create egg wash (egg and splash of water, mixed), and brush over each koulourakia.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'082E2C1484AFE94A91C60D1B18EF873D', X'A0C781B48541B74B81472446E33B3F54', X'A0C781B48541B74B81472446E33B3F54', 10, NULL, NULL, 2, NULL, 0, 'Sprinkle with sesame seeds.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC087A69425CB14E8DCFC6FC2601CC88', X'A0C781B48541B74B81472446E33B3F54', X'A0C781B48541B74B81472446E33B3F54', 11, NULL, NULL, 2, NULL, 0, 'Place in preheated oven at 350F for 20 to 25 minutes, or until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DF2708BB0B6344B83F4BBC9BF316C78', X'A0C781B48541B74B81472446E33B3F54', X'A0C781B48541B74B81472446E33B3F54', 12, NULL, NULL, 2, NULL, 0, 'Remove, cool, and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C5F56392A421E47969443F091557CEC', X'30C5881EC1CCC144A281B2C2BB924851', X'30C5881EC1CCC144A281B2C2BB924851', 0, NULL, NULL, 2, NULL, 0, 'Cut a rectangular piece out of the top of the zucchini. Don''t go from end to end, keep at least 1inch gap.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CC16AEE2A702E4B8DB77C59E25BC219', X'30C5881EC1CCC144A281B2C2BB924851', X'30C5881EC1CCC144A281B2C2BB924851', 1, NULL, NULL, 2, NULL, 0, 'Scoop out inside using a spoon. Discard all except 1 cups worth.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBEAF436BE04824084FFFE2FEE0E821C', X'30C5881EC1CCC144A281B2C2BB924851', X'30C5881EC1CCC144A281B2C2BB924851', 2, NULL, NULL, 2, NULL, 0, 'Heat some oil in a pan and add copped onions. Cook for 5 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A12A67F61CB4540B424E3FA11DA5A44', X'30C5881EC1CCC144A281B2C2BB924851', X'30C5881EC1CCC144A281B2C2BB924851', 3, NULL, NULL, 2, NULL, 0, 'Add red wine, garlic, cinnamon, tomato paste, and 1 cup zucchini meat. mix');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EEB3D22658DA34580A6BB18D91B97B6', X'30C5881EC1CCC144A281B2C2BB924851', X'30C5881EC1CCC144A281B2C2BB924851', 4, NULL, NULL, 2, NULL, 0, 'Add lamb, salt and pepper, and cook lamb to desired preference.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61E433E4C9F41F40AF2F4CCA71CAE0DD', X'30C5881EC1CCC144A281B2C2BB924851', X'30C5881EC1CCC144A281B2C2BB924851', 5, NULL, NULL, 2, NULL, 0, 'Remove and stuff meat mixture inside each zucchini.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'095B3B8559BF544AAD90CBF469ED1AEE', X'30C5881EC1CCC144A281B2C2BB924851', X'30C5881EC1CCC144A281B2C2BB924851', 6, NULL, NULL, 2, NULL, 0, 'Place in ovensafe dish.');
