INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF3B0C21C5128944A98B94D70EDD060D', X'8B97A719400BC649BD9C5B1389AC6CBF', X'8B97A719400BC649BD9C5B1389AC6CBF', 5, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4CF15E45B95F444940A60DE05B2FE07', X'D904E387CCF8474F988DF49C12E617E0', X'D904E387CCF8474F988DF49C12E617E0', 0, NULL, NULL, 2, NULL, 0, 'Heat oil in pan.');
