INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CACBE2073387CD44A92F709D7BB64165', X'D904E387CCF8474F988DF49C12E617E0', X'D904E387CCF8474F988DF49C12E617E0', 5, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA8141040E207E439C7087AAC428AB68', X'DFBBAA82EE81E54B9AE339F2B879C080', X'DFBBAA82EE81E54B9AE339F2B879C080', 0, NULL, NULL, 2, NULL, 0, 'In large bowl, mix flour and baking soda.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55BBD0C130277C41A4334E34181F4311', X'DFBBAA82EE81E54B9AE339F2B879C080', X'DFBBAA82EE81E54B9AE339F2B879C080', 1, NULL, NULL, 2, NULL, 0, 'Add melted butter and milk- mix.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F965D388C525AB4A908762FC0AC3F1FE', X'DFBBAA82EE81E54B9AE339F2B879C080', X'DFBBAA82EE81E54B9AE339F2B879C080', 2, NULL, NULL, 2, NULL, 0, 'Next, add sugar, brandy, vanilla, and lemon juice - mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC29ED66C385A243826E07ECB236250B', X'DFBBAA82EE81E54B9AE339F2B879C080', X'DFBBAA82EE81E54B9AE339F2B879C080', 3, NULL, NULL, 2, NULL, 0, 'Add eggs, one at a time - mixing well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9208B0412A72634A9AFFC069F5D8C292', X'DFBBAA82EE81E54B9AE339F2B879C080', X'DFBBAA82EE81E54B9AE339F2B879C080', 4, NULL, NULL, 2, NULL, 0, 'Pour into greased 10 inch round pan.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6242E3736E19C148A99150DEAB993945', X'DFBBAA82EE81E54B9AE339F2B879C080', X'DFBBAA82EE81E54B9AE339F2B879C080', 5, NULL, NULL, 2, NULL, 0, 'Take coin and hide somewhere in bread mix');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C91BE4B3FE6D88439D690A362096105D', X'DFBBAA82EE81E54B9AE339F2B879C080', X'DFBBAA82EE81E54B9AE339F2B879C080', 6, NULL, NULL, 2, NULL, 0, 'Place in preheated oven at 350F for 25 min.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3870147978485346B5DB283A620FFA7F', X'DFBBAA82EE81E54B9AE339F2B879C080', X'DFBBAA82EE81E54B9AE339F2B879C080', 7, NULL, NULL, 2, NULL, 0, 'While baking, prepare topping');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E65D375A62A8340ADFBEB83BE20E5E0', X'DFBBAA82EE81E54B9AE339F2B879C080', X'DFBBAA82EE81E54B9AE339F2B879C080', 8, NULL, NULL, 2, NULL, 0, 'Combined in bowl chopped pistachios and sugar, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED5CDBADAF34FF4F9940311B8BBB789A', X'DFBBAA82EE81E54B9AE339F2B879C080', X'DFBBAA82EE81E54B9AE339F2B879C080', 9, NULL, NULL, 2, NULL, 0, 'After 25 min, remove bread from oven and sprinkle with pistachio and sugar mixture.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C9FDCFE0085174483252539950B3B96', X'DFBBAA82EE81E54B9AE339F2B879C080', X'DFBBAA82EE81E54B9AE339F2B879C080', 10, NULL, NULL, 2, NULL, 0, 'Place back in oven for additional 20 min or until golden brown and cooked.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D28C29DD4791248B9B946415F08D137', X'DFBBAA82EE81E54B9AE339F2B879C080', X'DFBBAA82EE81E54B9AE339F2B879C080', 11, NULL, NULL, 2, NULL, 0, 'Remove and let cool.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'282B1844AB927F448FF53DE47C62B0BE', X'DFBBAA82EE81E54B9AE339F2B879C080', X'DFBBAA82EE81E54B9AE339F2B879C080', 12, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11D0EAAE4C64C44A9B92659AF16D4104', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl add: yeast, water, sugar, Sea salt, oil, brandy, milk, vanilla, 2 eggs, butter, lemon zest, and orange zest - mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57848B0A75D09441BF5929650410C3CC', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 1, NULL, NULL, 2, NULL, 0, 'Next add 1 cup flour and mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D411618931D6774DBA2D48A1D6929DA5', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 2, NULL, NULL, 2, NULL, 0, 'Continue to add flour one cup at a time, mixing well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E305480811B79449D2B14A343409DCE', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 3, NULL, NULL, 2, NULL, 0, 'After all flour is added, mix in almonds and raisins.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F52140C032B9C34A85783030A892FF19', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 4, NULL, NULL, 2, NULL, 0, 'Knead dough for about 10 minutes on well-floured surface. Dough should be elastic and not sticky - add more flour as needed. If too dry add some water.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C62D81844445EA49A21DC6C5CE4ABED7', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 5, NULL, NULL, 2, NULL, 0, 'Place dough in oiled bowl and cover. Allow to rise double in size, about 1 hour.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD0CE3B0E5B8234CB7BD320E6189AB59', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 6, NULL, NULL, 2, NULL, 0, 'Remove dough from bowl and knead for 30 seconds on floured surface.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4AF7EE9160FC24681D59CE76D5F7745', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 7, NULL, NULL, 2, NULL, 0, 'Break into 2 equal parts and place on baking sheet lined with parchment paper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A1DBE5CD5517749978A7974B8D43A9E', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 8, NULL, NULL, 2, NULL, 0, 'Shape each dough piece into a circle.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B900A4E97759C4F8D4AE30C0C258D25', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 9, NULL, NULL, 2, NULL, 0, 'Break off a small piece of dough from each piece to create your design(s) if desired.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'975AF6216748AC4A89B12CE1679B5D0E', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 10, NULL, NULL, 2, NULL, 0, 'Sit and let rise for another 30 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D8F434435BD354C8DCCF8FCFF42FF74', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 11, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350F.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7519BA7DD9D12743AA00C04FE6BCFD21', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 12, NULL, NULL, 2, NULL, 0, 'Use remaining egg and create egg wash (break egg in bowl, add splash of water, and mix well).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2500F267AB6C649A3560115229D8693', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 13, NULL, NULL, 2, NULL, 0, 'Brush egg wash over bread.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC187518DE211A478C775B5CB92ECF69', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 14, NULL, NULL, 2, NULL, 0, 'Place in oven and bake for 35-40 minutes or until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D5141AB15570B4EB07540CB2635A4C0', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 15, NULL, NULL, 2, NULL, 0, 'Remove and let cool.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0B74FABD7ABB5409E100D9CEAD093BB', X'72074721121614498A8D9CA6DB09ED50', X'72074721121614498A8D9CA6DB09ED50', 16, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B82C83B708A5F4E8C021B0923B12A8D', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 0, NULL, NULL, 2, NULL, 0, 'In large bowl, add flour, baking soda, and baking powder, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86B9C82B0EAFEE4F85D1E6CBFCA7D13A', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 1, NULL, NULL, 2, NULL, 0, 'Add olive oil, butter, sugar, brandy, orange juice, orange zest, lemon juice, and lemon zest - mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8535616755001F49A1ECB0BFFD376ACA', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 2, NULL, NULL, 2, NULL, 0, 'Form a soft dough ball, light kneading may be needed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26D3272D07F5274A942E8452EDF0AD51', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 3, NULL, NULL, 2, NULL, 0, 'Line baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A996FA55C60BA740B565443FA080642C', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 4, NULL, NULL, 2, NULL, 0, 'Break off small pieces of dough and roll into ball, place on baking sheet.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6A483DC19B431468406B83742C0C8C2', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 5, NULL, NULL, 2, NULL, 0, 'Lightly press down on center to flatten dough ball out a bit');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BEC17A914922A4DB8DCB44EB72AA588', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 6, NULL, NULL, 2, NULL, 0, 'Repeat until all dough is used.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C878A2C7D4A775498A1781EFC2287326', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 7, NULL, NULL, 2, NULL, 0, 'Place in preheated oven at 350F for 30min, or until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21C1A5A8D8516A42B17AD2B2E7C96DE1', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 8, NULL, NULL, 2, NULL, 0, 'While baking, prepare syrup.*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99C266DB319F4344903BBDD66C6AC22E', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 9, NULL, NULL, 2, NULL, 0, 'Add water, sugar, and honey in pot and bring to boil.');
