INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70EF0A0307AC33449653EFFA633F386E', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 11, NULL, NULL, 2, NULL, 0, 'Remove cookies from oven and pour syrup over cookies immediately.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAF2FAFABA7F6749B8E62575C41F68C8', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 12, NULL, NULL, 2, NULL, 0, 'Move cookies around pan after 10mins to make sure they absorb as much honey syrup as possible.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA776BAB56020F41A8CCF6C95633F294', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 13, NULL, NULL, 2, NULL, 0, 'Let cool.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6376AE85C94C9540B48FCA16363B939E', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 14, NULL, NULL, 2, NULL, 0, 'Combined walnuts, cinnamon and ground cloves in bowl and mix.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CD04E9AA693C34F906567F5AE866021', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 15, NULL, NULL, 2, NULL, 0, 'Once melomakaronas have cooled, sprinkle walnut mixture over, covering the tops.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8297CECCE5F4D4D857C299CD34ED990', X'1023524F1502F24FAA971016E063D071', X'1023524F1502F24FAA971016E063D071', 16, NULL, NULL, 2, NULL, 0, 'Place on dish and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D57BAC2E9FB2B34BBD9C4DB9E006AC79', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350F.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'725F227119875744839D4D19201D579B', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 1, NULL, NULL, 2, NULL, 0, 'Place butter in bowl and whip until light and fluffy, about 10-15mins.*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B47BE150515EC54AA32C2CAE2702EA7C', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 2, NULL, NULL, 2, NULL, 0, 'Add egg yolk and blend with whipped butter.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2FE7442A572814195EED6DC94FA2F70', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 3, NULL, NULL, 2, NULL, 0, 'Add in ¼ cup powdered sugar, vanilla, and brandy. Mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5F86E6EB4D83341B672879ED878FBAF', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 4, NULL, NULL, 2, NULL, 0, 'Add flour and mix until soft dough is formed. Use more flour as needed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA3F1DD8E9770B4A9CA6367B25949EBC', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 5, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAF5351E46B0FE45A37C0FE62983FD6D', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 6, NULL, NULL, 2, NULL, 0, 'Break off small pieces of dough, form into a small ball (or whatever size you like).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE565DD9E6AE194A8E3FFBCEC4B18374', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 7, NULL, NULL, 2, NULL, 0, 'Pinch the center of the dough ball to flatten a bit and place on baking sheet.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'984F180AA862E246AC00AF926D4E0A89', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 8, NULL, NULL, 2, NULL, 0, 'Repeat until all dough it used.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DDD1F2602B00F4680C8E16870EBFF51', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 9, NULL, NULL, 2, NULL, 0, 'Bake in the oven for 20-25mins, or until bottoms of cookies are golden brown**');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6D0947CCCBC3045A32D7484664C1EF8', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 10, NULL, NULL, 2, NULL, 0, 'While the kourambiethes are baking, cover serving plates with powdered sugar.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA6AE7A246A51B4FBFB83A2EAC34AC18', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 11, NULL, NULL, 2, NULL, 0, 'Remove kourambiethes from oven and place on cooling rack for 10-15mins.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD1F83A1BE9BCA48B945F09FFF26FB9E', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 12, NULL, NULL, 2, NULL, 0, 'Place kourambiethes on serving plates.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9F402D0AB6E74429F6879681F6A2F63', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 13, NULL, NULL, 2, NULL, 0, 'Sprinkle remaining powdered sugar over them - the more the better!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AEA28BB002FF64D8B958766135F9D82', X'24BA6506F52C36409875A75E205C56B8', X'24BA6506F52C36409875A75E205C56B8', 14, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15AD4B5CE9E43E4C815A94D2AC7492E7', X'6804FF1A0666974B93B8C6EE8473D57C', X'6804FF1A0666974B93B8C6EE8473D57C', 0, NULL, NULL, 2, NULL, 0, 'Combine oil, salt, garlic, and onion into a medium sauce pan over medium heat. Cook for 2-3 minutes or until onion becomes translucent.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'252ACC8026DE9C47AB8BD871340B1813', X'6804FF1A0666974B93B8C6EE8473D57C', X'6804FF1A0666974B93B8C6EE8473D57C', 1, NULL, NULL, 2, NULL, 0, 'Add broth to saucepan; bring to a boil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6A14B82C4A49C42B12DBD737FB3888F', X'6804FF1A0666974B93B8C6EE8473D57C', X'6804FF1A0666974B93B8C6EE8473D57C', 2, NULL, NULL, 2, NULL, 0, 'Turn off heat and stir in couscous, figs, spices, and spinach. Transfer pan to a different burner.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5045284ADD29E4C9608215B63B90621', X'6804FF1A0666974B93B8C6EE8473D57C', X'6804FF1A0666974B93B8C6EE8473D57C', 3, NULL, NULL, 2, NULL, 0, 'Cover; let stand until water is absorbed (5-10 minutes).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6747B6E9F2FDC24080EC69C1878C4927', X'6804FF1A0666974B93B8C6EE8473D57C', X'6804FF1A0666974B93B8C6EE8473D57C', 4, NULL, NULL, 2, NULL, 0, 'Stir in nuts and basil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFDD2A3908A8644CB015505068FA4859', X'6804FF1A0666974B93B8C6EE8473D57C', X'6804FF1A0666974B93B8C6EE8473D57C', 5, NULL, NULL, 2, NULL, 0, 'Serve with feta sprinkled on top.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DCFB2EB56A48346BA4372468080F67C', X'D069E768EA3A0E4E8DCAFE4BDE48789A', X'D069E768EA3A0E4E8DCAFE4BDE48789A', 0, NULL, NULL, 2, NULL, 0, 'In a large pot, heat olive oil over medium heat. Add the onion, celery, and garlic and cook until veggies are tender, about 3-5 minutes. Stir in the bay leaves.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91A921C244CF324192D51EC3375EA09F', X'D069E768EA3A0E4E8DCAFE4BDE48789A', X'D069E768EA3A0E4E8DCAFE4BDE48789A', 1, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and chicken broth, stir. Stir in the Greek honey and fresh basil (everything should be very fragrant by now). Season with red pepper flakes, salt, pepper, to taste. Simmer mixture on low for about 10-15 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B71EC5E56C2E94BA0F22B8DE5E1CCBC', X'D069E768EA3A0E4E8DCAFE4BDE48789A', X'D069E768EA3A0E4E8DCAFE4BDE48789A', 2, NULL, NULL, 2, NULL, 0, 'Remove the bay leaves and use a hand blender to mix everything together (you can also pour the soup into a regular blender and get the creamy texture that way).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26F9D7D031AE664DB18C0C2A65D2A0D7', X'D069E768EA3A0E4E8DCAFE4BDE48789A', X'D069E768EA3A0E4E8DCAFE4BDE48789A', 3, NULL, NULL, 2, NULL, 0, 'Stir in the Greek yogurt until well mixed. Stir in your COOKED orzo pasta.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C51C68EC89F925409AE2A3842094C0C6', X'D069E768EA3A0E4E8DCAFE4BDE48789A', X'D069E768EA3A0E4E8DCAFE4BDE48789A', 4, NULL, NULL, 2, NULL, 0, 'Serve warm with homemade bread and top with parmesan (optional)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95FF35E75827494B983B2A3E0AED0683', X'6FB889E6DA329B47AD941C70D1E3A109', X'6FB889E6DA329B47AD941C70D1E3A109', 0, NULL, NULL, 2, NULL, 0, 'Place the chicken in a large pan');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C9EB4823BC32F43A21F3C4E5F9E9409', X'6FB889E6DA329B47AD941C70D1E3A109', X'6FB889E6DA329B47AD941C70D1E3A109', 1, NULL, NULL, 2, NULL, 0, 'Grate the rind from lemon and then cut the lemon in half.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FEB127CF01F5543887A780E975A6A6F', X'6FB889E6DA329B47AD941C70D1E3A109', X'6FB889E6DA329B47AD941C70D1E3A109', 2, NULL, NULL, 2, NULL, 0, 'Add the lemon rind, 3 tbsp of oregano, juice from ½ a lemon, and garlic inside the cavity.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7F66ED6448A9848AF098C3154BF400D', X'6FB889E6DA329B47AD941C70D1E3A109', X'6FB889E6DA329B47AD941C70D1E3A109', 3, NULL, NULL, 2, NULL, 0, 'Rub butter, the juice from the remaining lemon half, and olive oil over the chicken.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CB7D27A88C28D4DA1EC2BB959EC53AB', X'6FB889E6DA329B47AD941C70D1E3A109', X'6FB889E6DA329B47AD941C70D1E3A109', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle with any remaining oregano, salt and pepper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFD313A475173D499DFD4D7D8B0AF2A2', X'6FB889E6DA329B47AD941C70D1E3A109', X'6FB889E6DA329B47AD941C70D1E3A109', 5, NULL, NULL, 2, NULL, 0, 'Place both lemon halves inside of the chicken cavity.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74025E998D9F5746BA7E0B3AFEABC6DD', X'6FB889E6DA329B47AD941C70D1E3A109', X'6FB889E6DA329B47AD941C70D1E3A109', 6, NULL, NULL, 2, NULL, 0, 'Roast the chicken in the oven at 375F for the cooking time that you have calculated.*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'314547560730414F87EF489D8998EB75', X'6FB889E6DA329B47AD941C70D1E3A109', X'6FB889E6DA329B47AD941C70D1E3A109', 7, NULL, NULL, 2, NULL, 0, 'Bast the chicken periodically until golden brown and juices run clear when chicken is pierced.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65F0DC0BFB77FE40A859BDDC11442375', X'6FB889E6DA329B47AD941C70D1E3A109', X'6FB889E6DA329B47AD941C70D1E3A109', 8, NULL, NULL, 2, NULL, 0, 'With 15 minutes left, cover chicken loosely with aluminum foil to prevent skin from getting burnt (it is best to do this when desired color of chicken is reached).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FE363170E518C4AA287D8AE55AB4F19', X'6FB889E6DA329B47AD941C70D1E3A109', X'6FB889E6DA329B47AD941C70D1E3A109', 9, NULL, NULL, 2, NULL, 0, 'Carve chicken and spoon any liquids from the bottom of the pan on each serving.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65B7CE5E640DEF4299C51A29EDF6920D', X'6FB889E6DA329B47AD941C70D1E3A109', X'6FB889E6DA329B47AD941C70D1E3A109', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BD4F98D8E97DB48A7A9DECA29E0667A', X'1B9DE9436FB6894B80EF546D074813B9', X'1B9DE9436FB6894B80EF546D074813B9', 0, NULL, NULL, 2, NULL, 0, 'In a bowl, mix together flour, sugar, baking soda, and salt.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A60096E5BDCCE4597EF092382D2431F', X'1B9DE9436FB6894B80EF546D074813B9', X'1B9DE9436FB6894B80EF546D074813B9', 1, NULL, NULL, 2, NULL, 0, 'Next, add Greek yogurt, egg, cottage cheese, vanilla, and lemon. Mix until a batter is formed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9244EEA4757D3E4F9A6942593D9C888C', X'1B9DE9436FB6894B80EF546D074813B9', X'1B9DE9436FB6894B80EF546D074813B9', 2, NULL, NULL, 2, NULL, 0, 'Heat pan on med-high heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'409F0950DF80B34AB6D2E8025AF127C5', X'1B9DE9436FB6894B80EF546D074813B9', X'1B9DE9436FB6894B80EF546D074813B9', 3, NULL, NULL, 2, NULL, 0, 'Using a shot glass (or whatever you prefer), fill with batter and pour on hot pan.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22983CF434DE074DBAF7FE57891CDE29', X'1B9DE9436FB6894B80EF546D074813B9', X'1B9DE9436FB6894B80EF546D074813B9', 4, NULL, NULL, 2, NULL, 0, 'Cook until bubbles begin to form on top and edges become slightly solid, about 3-4 min.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF9D4864C3C5744BA40C44A512A012F2', X'1B9DE9436FB6894B80EF546D074813B9', X'1B9DE9436FB6894B80EF546D074813B9', 5, NULL, NULL, 2, NULL, 0, 'Turn over and cook for another 2-3 min, or until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBA82B5C4F54A0428D3800ED5276DB52', X'1B9DE9436FB6894B80EF546D074813B9', X'1B9DE9436FB6894B80EF546D074813B9', 6, NULL, NULL, 2, NULL, 0, 'Repeat until all batter is used.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A13A23BD887C14DA137E2AC1FF9C263', X'1B9DE9436FB6894B80EF546D074813B9', X'1B9DE9436FB6894B80EF546D074813B9', 7, NULL, NULL, 2, NULL, 0, 'Serve warm with your choice of sliced fruit and syrup/butter.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0D181328761B14F9009398D74C30A06', X'4222257154F35948834FBE39A2ACCC4F', X'4222257154F35948834FBE39A2ACCC4F', 0, NULL, NULL, 2, NULL, 0, 'Wash and halve potatoes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37EED4AEC1D9244184875D819A04EBC8', X'4222257154F35948834FBE39A2ACCC4F', X'4222257154F35948834FBE39A2ACCC4F', 1, NULL, NULL, 2, NULL, 0, 'Boil 4-6 cups of salted water over high heat. Add potatoes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B6D571D4528554FB3B54A8DA7422386', X'4222257154F35948834FBE39A2ACCC4F', X'4222257154F35948834FBE39A2ACCC4F', 2, NULL, NULL, 2, NULL, 0, 'Bring heat to medium and cook potatoes for 20 minutes or until soft.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E98DC24F50E6934D95588794FF234496', X'4222257154F35948834FBE39A2ACCC4F', X'4222257154F35948834FBE39A2ACCC4F', 3, NULL, NULL, 2, NULL, 0, 'Strain potatoes and place back in pot.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B69E54B53C32F84E90C0EFFD7299CDC5', X'4222257154F35948834FBE39A2ACCC4F', X'4222257154F35948834FBE39A2ACCC4F', 4, NULL, NULL, 2, NULL, 0, 'Add butter, dill, oregano, milk, and salt and pepper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFFC6005CAE9744EABC3CE4A1F5DA43F', X'4222257154F35948834FBE39A2ACCC4F', X'4222257154F35948834FBE39A2ACCC4F', 5, NULL, NULL, 2, NULL, 0, 'Mash until well mixed. Stir in feta.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A47029B8D07C74F94CB7909B5F9D4F6', X'4222257154F35948834FBE39A2ACCC4F', X'4222257154F35948834FBE39A2ACCC4F', 6, NULL, NULL, 2, NULL, 0, 'Add to plate and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B951D8E1394354449AFB6080DBBF4E9B', X'8C842538B59D934F8BDE240BEC8BFCB7', X'8C842538B59D934F8BDE240BEC8BFCB7', 0, NULL, NULL, 2, NULL, 0, 'Once beans have soaked for at least 12hrs, drain and add to soup pot.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'085BDE866D6A584B9D43B36840DA1674', X'8C842538B59D934F8BDE240BEC8BFCB7', X'8C842538B59D934F8BDE240BEC8BFCB7', 1, NULL, NULL, 2, NULL, 0, 'Fill pot with enough water to cover beans.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B39008C4CF557243BD84E845AFF57D2B', X'8C842538B59D934F8BDE240BEC8BFCB7', X'8C842538B59D934F8BDE240BEC8BFCB7', 2, NULL, NULL, 2, NULL, 0, 'Turn on to med-high heat and bring to boil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA2BCAF3D2F11344B777CEF28EDD9559', X'8C842538B59D934F8BDE240BEC8BFCB7', X'8C842538B59D934F8BDE240BEC8BFCB7', 3, NULL, NULL, 2, NULL, 0, 'Simmer for 30min, until tender (some foaming may occur).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A0C7FDD45E9D6408CE330B48D1A85E6', X'8C842538B59D934F8BDE240BEC8BFCB7', X'8C842538B59D934F8BDE240BEC8BFCB7', 4, NULL, NULL, 2, NULL, 0, 'Remove from heat, drain beans, and set aside.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0F42CC383A6F74482E458B865463043', X'8C842538B59D934F8BDE240BEC8BFCB7', X'8C842538B59D934F8BDE240BEC8BFCB7', 5, NULL, NULL, 2, NULL, 0, 'In soup pot, add a little olive oil and chopped onions, cook until translucent, 5-7min.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23E0AEE14EDCC64C8E81A105F357A00E', X'8C842538B59D934F8BDE240BEC8BFCB7', X'8C842538B59D934F8BDE240BEC8BFCB7', 6, NULL, NULL, 2, NULL, 0, 'Add tomato puree, carrots, celery, and 5 cups of water to onion mix.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C54322E92E985045995746A8175E80C3', X'8C842538B59D934F8BDE240BEC8BFCB7', X'8C842538B59D934F8BDE240BEC8BFCB7', 7, NULL, NULL, 2, NULL, 0, 'Add beans - add more water to cover beans if needed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59B9AB3B40137940A82C5782A6429543', X'8C842538B59D934F8BDE240BEC8BFCB7', X'8C842538B59D934F8BDE240BEC8BFCB7', 8, NULL, NULL, 2, NULL, 0, 'Add thyme, parsley, Sea salt, and pepper and bay leaf.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AD77320BD41EF418756C8A23256D0D2', X'8C842538B59D934F8BDE240BEC8BFCB7', X'8C842538B59D934F8BDE240BEC8BFCB7', 9, NULL, NULL, 2, NULL, 0, 'Cover and bring to boil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0FEFA235AEF7E4E96DAC9303382FBF5', X'8C842538B59D934F8BDE240BEC8BFCB7', X'8C842538B59D934F8BDE240BEC8BFCB7', 10, NULL, NULL, 2, NULL, 0, 'Once boiling, reduce heat and simmer for 1-2hrs, until thick and beans are soft.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A79C13D0B3FEC04CBA74D329304CE7F3', X'8C842538B59D934F8BDE240BEC8BFCB7', X'8C842538B59D934F8BDE240BEC8BFCB7', 11, NULL, NULL, 2, NULL, 0, 'Enjoy this delicious Greek bean soup !');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10A3071524CC074F9D8459103CBF4560', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 0, NULL, NULL, 2, NULL, 0, 'Boil the chicken breasts for 30 minutes or until cooked.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A2ADEC11C0FF8409A41E8B0AC43971E', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 1, NULL, NULL, 2, NULL, 0, 'Once done, remove chicken from water and let cool. Shred chicken by hand and set aside for later.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACE9AEF70CB5E94F9FF03EB5541D853C', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 2, NULL, NULL, 2, NULL, 0, 'Melt butter in pan over medium heat, then add flour. Stir constantly until smooth.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26A516AA18914D4D9A1792248EB253CF', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 3, NULL, NULL, 2, NULL, 0, 'Add warmed chicken broth.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2E2AFCBB168B246BC46CB3806D8563F', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 4, NULL, NULL, 2, NULL, 0, 'Continue to cook until mixture begins to boil. Turn off heat and let cool.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'083C54AA986D0E4A9C43E3B167A7EF94', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 5, NULL, NULL, 2, NULL, 0, 'In separate pan, add olive oil, carrot, celery, onion, and peas. Cook for 5 min or until veggies are soft (or desired texture).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'655D9809AB6AD346BD90718C670340BC', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 6, NULL, NULL, 2, NULL, 0, 'In pan with flour and broth, add egg, salt, pepper, thyme, cinnamon, cheese, chicken, vegetables and mix together.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA7E8604ABBCE849BD5ABCDFA1E5F525', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 7, NULL, NULL, 2, NULL, 0, 'Melt butter and grease each muffin mold.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37F1879A29E35D46B92EE41D33492042', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 8, NULL, NULL, 2, NULL, 0, 'Add one piece of phyllo, to each muffin mold.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33CA9A2D6D937A449C5929B0B30E4B15', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 9, NULL, NULL, 2, NULL, 0, 'Brush each phyllo piece with butter, and repeat. You should end up with each muffin mold containing 6 pieces of phyllo.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B705976C6F32424EB2FBD6F42D8291E4', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 10, NULL, NULL, 2, NULL, 0, 'Add a scoop of chicken mixture to each muffin mold. Repeat until each mold has equal amount of chicken filling.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93B7334D705DCC43873BB19580143E01', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 11, NULL, NULL, 2, NULL, 0, 'Fold phyllo tops over to enclose filling.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C657716D090FBF419D95465D91579A07', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 12, NULL, NULL, 2, NULL, 0, 'Add a small piece of phyllo to top if needed to seal.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEAB5B84343FCD42BDCB7C74B39AC261', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 13, NULL, NULL, 2, NULL, 0, 'Bake in the oven at 350F for about 30 mins or until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95922600E9C3D94482361590BF95DB67', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 14, NULL, NULL, 2, NULL, 0, 'Remove from oven, let sit for about 10 minutes');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E86388237CC9424297FD0331B38F2F56', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', X'54B2D8B087F0FF4FB4F2D7C90C33FD55', 15, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15F1DBC05EF2BE45B79997E4B2B8C875', X'9E4FD795120BED41948C7D8A98DF3DEE', X'9E4FD795120BED41948C7D8A98DF3DEE', 0, NULL, NULL, 2, NULL, 0, 'Add frozen spinach to hot pan (coated with oil) and cook until wilted.*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB9F21F8A30CF145A129701C12BA10C3', X'9E4FD795120BED41948C7D8A98DF3DEE', X'9E4FD795120BED41948C7D8A98DF3DEE', 1, NULL, NULL, 2, NULL, 0, 'Whisk egg whites until thoroughly mixed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29EFAF5EDB3A8146AF09B52FAA5F0DA3', X'9E4FD795120BED41948C7D8A98DF3DEE', X'9E4FD795120BED41948C7D8A98DF3DEE', 2, NULL, NULL, 2, NULL, 0, 'Add egg whites to pan and mix gently with spinach.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6B551AA4891934E894C7AD2DDE6B439', X'9E4FD795120BED41948C7D8A98DF3DEE', X'9E4FD795120BED41948C7D8A98DF3DEE', 3, NULL, NULL, 2, NULL, 0, 'Add the feta and mix again.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE4CA08110C2DD4B8CDCAA6F7E476386', X'9E4FD795120BED41948C7D8A98DF3DEE', X'9E4FD795120BED41948C7D8A98DF3DEE', 4, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D229B1AE9ADF547872E3339F38F459A', X'9E4FD795120BED41948C7D8A98DF3DEE', X'9E4FD795120BED41948C7D8A98DF3DEE', 5, NULL, NULL, 2, NULL, 0, 'Serve on plate with sliced tomato and topped with feta!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39A8812755E24245A814AA8EE0E92757', X'D99A3BEEA228EC49AB673EA27AE13CAB', X'D99A3BEEA228EC49AB673EA27AE13CAB', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine: pork, olive oil, lemon juice, oregano, thyme, garlic, salt, and pepper - mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC6C5CC30A31D74B8A93B2FD749A0A4A', X'D99A3BEEA228EC49AB673EA27AE13CAB', X'D99A3BEEA228EC49AB673EA27AE13CAB', 1, NULL, NULL, 2, NULL, 0, 'Cover and place in refrigerator for at least 1hr to marinate.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AB5C6A28AB87F468BE2CD47E8132D01', X'D99A3BEEA228EC49AB673EA27AE13CAB', X'D99A3BEEA228EC49AB673EA27AE13CAB', 2, NULL, NULL, 2, NULL, 0, 'Remove, and skew pork, onions, and bell peppers.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'806EB11583034E42ABF8AD7D0C06C2E4', X'D99A3BEEA228EC49AB673EA27AE13CAB', X'D99A3BEEA228EC49AB673EA27AE13CAB', 3, NULL, NULL, 2, NULL, 0, 'Pour any remaining marinade over entire skewer.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA3A17F327B2BC4EB5101B45279074BA', X'D99A3BEEA228EC49AB673EA27AE13CAB', X'D99A3BEEA228EC49AB673EA27AE13CAB', 4, NULL, NULL, 2, NULL, 0, 'Place on grill for 10-15, until thoroughly cooked. Turning twice.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF0AE4BFDA259C4ABA146E96D4075E72', X'D99A3BEEA228EC49AB673EA27AE13CAB', X'D99A3BEEA228EC49AB673EA27AE13CAB', 5, NULL, NULL, 2, NULL, 0, 'Remove, and serve with pita and tzatziki!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED36889BC39B084C905FD6FECAB71D3B', X'4200D8465351E847A3617A460C4643EC', X'4200D8465351E847A3617A460C4643EC', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375F degrees.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34183B9A4D335349909EF09746819423', X'4200D8465351E847A3617A460C4643EC', X'4200D8465351E847A3617A460C4643EC', 1, NULL, NULL, 2, NULL, 0, 'Butter each cup in your cupcake tin.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'941539A5DA87834380D6E85FBC5F7E47', X'4200D8465351E847A3617A460C4643EC', X'4200D8465351E847A3617A460C4643EC', 2, NULL, NULL, 2, NULL, 0, 'Lay out the phyllo dough and cut into 3x3 or 4x4 squares depending on which size tin you are using.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E37014911E83AB4CB5C012B067EEE7B0', X'4200D8465351E847A3617A460C4643EC', X'4200D8465351E847A3617A460C4643EC', 3, NULL, NULL, 2, NULL, 0, 'Make sure to cover the phyllo that you aren''t using as it will dry out quickly.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03399BE6FFBE7346BB87F04D7AAEBEB1', X'4200D8465351E847A3617A460C4643EC', X'4200D8465351E847A3617A460C4643EC', 4, NULL, NULL, 2, NULL, 0, 'Dip a baking brush in your melted butter and lightly coat each sheet before placing it in the cupcake tin.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2576140D61FC634297FBD6F5C8F30A88', X'4200D8465351E847A3617A460C4643EC', X'4200D8465351E847A3617A460C4643EC', 5, NULL, NULL, 2, NULL, 0, 'Layer each one slightly more clockwise than the last.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D037C843132A240BD91A419380BD5C7', X'4200D8465351E847A3617A460C4643EC', X'4200D8465351E847A3617A460C4643EC', 6, NULL, NULL, 2, NULL, 0, 'After your last sheet is in the tin, sprinkle each with a pinch of sugar and place in oven for 8-10 minutes or until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B3F049C7070274B883855E268710ADD', X'4200D8465351E847A3617A460C4643EC', X'4200D8465351E847A3617A460C4643EC', 7, NULL, NULL, 2, NULL, 0, 'When done, take out of oven and let cool outside of mold by gently removing shell from tin.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A300533B3B944E4D8B513BF4A9E5542C', X'4200D8465351E847A3617A460C4643EC', X'4200D8465351E847A3617A460C4643EC', 8, NULL, NULL, 2, NULL, 0, 'Serve with a scoop of your favorite ice cream.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B20084D1447F53408C8169B43010F043', X'F579DBE5FEE8394F9A048177508A8DD1', X'F579DBE5FEE8394F9A048177508A8DD1', 0, NULL, NULL, 2, NULL, 0, 'Place 1 cup butter in microwave safe dish for 20 seconds to soften.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DC4EFEB81DB774BB24CB76A20227EE8', X'F579DBE5FEE8394F9A048177508A8DD1', X'F579DBE5FEE8394F9A048177508A8DD1', 1, NULL, NULL, 2, NULL, 0, 'Combine butter, flour, sugar, vanilla, milk, and eggs - mix until well blended and a batter is formed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'381110628807734B980A05DB89BCB206', X'F579DBE5FEE8394F9A048177508A8DD1', X'F579DBE5FEE8394F9A048177508A8DD1', 2, NULL, NULL, 2, NULL, 0, 'Place hazelnuts in food processor and pulsate to ground, remove and add to flour mixture - mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AAFB95D84976F4688FFA01A825363D5', X'F579DBE5FEE8394F9A048177508A8DD1', X'F579DBE5FEE8394F9A048177508A8DD1', 3, NULL, NULL, 2, NULL, 0, 'Pour mixture into greased bundt cake pan.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07E24EB8E7DDF0448FA2D7ED67A54BBC', X'F579DBE5FEE8394F9A048177508A8DD1', X'F579DBE5FEE8394F9A048177508A8DD1', 4, NULL, NULL, 2, NULL, 0, 'Bake for 45 min in preheated oven at 350F.*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A72EEF116609BC4B8094C37A81A607CC', X'F579DBE5FEE8394F9A048177508A8DD1', X'F579DBE5FEE8394F9A048177508A8DD1', 5, NULL, NULL, 2, NULL, 0, 'Remove and let sit for 10 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FCAC8DB28F34D4FA849692C0E46B60B', X'F579DBE5FEE8394F9A048177508A8DD1', X'F579DBE5FEE8394F9A048177508A8DD1', 6, NULL, NULL, 2, NULL, 0, 'Flip cake out of bundt pan and onto plate.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DBFE444F12BCA43984F7F8478619095', X'F579DBE5FEE8394F9A048177508A8DD1', X'F579DBE5FEE8394F9A048177508A8DD1', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle powdered sugar over cake.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95D69FBAAA2AAF4AA15EF1DF0613067E', X'F579DBE5FEE8394F9A048177508A8DD1', X'F579DBE5FEE8394F9A048177508A8DD1', 8, NULL, NULL, 2, NULL, 0, 'Melt ½ cup chocolate and drizzle over cake.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37F726B2E0FBC14390F93D42B68D6DA9', X'F579DBE5FEE8394F9A048177508A8DD1', X'F579DBE5FEE8394F9A048177508A8DD1', 9, NULL, NULL, 2, NULL, 0, 'Slice and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AD36F2885490F468A80AD3082FC1619', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, mix together regular and bread flour, salt, and yeast.*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86B3DA5A21840543A795DAF32AB0D404', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 1, NULL, NULL, 2, NULL, 0, 'Add, honey, oil, milk, egg white, and water.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E8C29B9C4F40A4EBAFEED7289097D40', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 2, NULL, NULL, 2, NULL, 0, 'Mix until a dough ball in formed (add more water if too dry).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EB7B123835A074B9C62050182536736', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle flour on countertop, and knead dough for about 10 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'431A39E37A3A634D828042305B15C0C6', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 4, NULL, NULL, 2, NULL, 0, 'Brush large bowl with olive oil, and place kneaded dough in and brush top with more oil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04746601E808BC43A7ADD22520CDED25', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 5, NULL, NULL, 2, NULL, 0, 'Cover and let rise for at least 1 hour (should almost double in size).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7FCA61571BC8640A3652AB820394A38', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 6, NULL, NULL, 2, NULL, 0, 'Remove dough and separate into 4 equal parts.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'837B4D0F5324FC4399547EAEAC36BB12', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 7, NULL, NULL, 2, NULL, 0, 'Roll out on floured countertop. Each piece should be about 2-2½ inches wide, and about .5 inch thick.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57A79D149E539043A1ACFEC789AA7446', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle cheese in the center of each strip, moving lengthwise.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E184869AB890204686B4E2386FCFEA13', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 9, NULL, NULL, 2, NULL, 0, 'Pinch the two ends together, enclosing the cheese in the dough.**');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B74909DA26134E4293443CA0B4BD4CF6', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 10, NULL, NULL, 2, NULL, 0, 'With the seam facing right, roll from bottom to top, creating a pinwheel.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4272EFFA2949A948A4DDC39D6FE7ABEE', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 11, NULL, NULL, 2, NULL, 0, 'Place on greased baking sheet.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6A30EB6DA0EDB448E064F0FAFEE35BF', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 12, NULL, NULL, 2, NULL, 0, 'Repeat for remaining strips.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9C42D3D317EA949906F33F16E0412AA', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 13, NULL, NULL, 2, NULL, 0, 'Brush tops of each pinwheel with melted butter, and cover and let rise for 1 hour (double in size).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FC91314AAE0A34B8F33C58A187557F2', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 14, NULL, NULL, 2, NULL, 0, 'Make an egg wash (egg yoke and splash of water) and brush each bread piece.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFC0EBEB49184249A794D75562FF2D2F', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 15, NULL, NULL, 2, NULL, 0, 'Sprinkle with sesame seeds.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77E104591A725749ACCBB0CF4A29320F', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 16, NULL, NULL, 2, NULL, 0, 'Place in 400F oven for 30-40min, until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF38C63C3F3D79418154C47ABA598C14', X'A5B40D27C699DF4089590ED6D496439F', X'A5B40D27C699DF4089590ED6D496439F', 17, NULL, NULL, 2, NULL, 0, 'Serve warm and enjoy!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCFB03449D9E0B499D71C9BD5FD3F4F8', X'E521BC1D27142C4DAE9AF5FCD1E5728E', X'E521BC1D27142C4DAE9AF5FCD1E5728E', 0, NULL, NULL, 2, NULL, 0, 'Pre-heat oven to 350F.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B830F094D58272419D20546D50FBAA59', X'E521BC1D27142C4DAE9AF5FCD1E5728E', X'E521BC1D27142C4DAE9AF5FCD1E5728E', 1, NULL, NULL, 2, NULL, 0, 'Heat an oiled pan.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97592C861CB4BB42BFC4E28B8E79E7DF', X'E521BC1D27142C4DAE9AF5FCD1E5728E', X'E521BC1D27142C4DAE9AF5FCD1E5728E', 2, NULL, NULL, 2, NULL, 0, 'Add ground beef and brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CB93404248F8A4B9FE77C45B4FB8C3F', X'E521BC1D27142C4DAE9AF5FCD1E5728E', X'E521BC1D27142C4DAE9AF5FCD1E5728E', 3, NULL, NULL, 2, NULL, 0, 'Add onion, orzo, oregano, thyme, broth, pine nuts, raisins, and salt and pepper to preference.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'066B7023D5E6B946AF52B6639247D4AE', X'E521BC1D27142C4DAE9AF5FCD1E5728E', X'E521BC1D27142C4DAE9AF5FCD1E5728E', 4, NULL, NULL, 2, NULL, 0, 'Cover and simmer for 20 minutes, or until broth is absorbed, orzo is soft, and meat is cooked.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CBE51CE30438947BD721BDD16843969', X'E521BC1D27142C4DAE9AF5FCD1E5728E', X'E521BC1D27142C4DAE9AF5FCD1E5728E', 5, NULL, NULL, 2, NULL, 0, 'While simmering, coat inside of each pepper with olive oil and sprinkle of salt and pepper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B0D6A255CBFFB49A48254565299FEDF', X'E521BC1D27142C4DAE9AF5FCD1E5728E', X'E521BC1D27142C4DAE9AF5FCD1E5728E', 6, NULL, NULL, 2, NULL, 0, 'Place in oven, tops facing up, and bake for 15mins, or until desired texture is reached (softer peppers will require more time).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFB216440CFEFE4B87C2314409910EF3', X'E521BC1D27142C4DAE9AF5FCD1E5728E', X'E521BC1D27142C4DAE9AF5FCD1E5728E', 7, NULL, NULL, 2, NULL, 0, 'Remove from oven.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D979760FBDF88E4F99382759BBF03688', X'E521BC1D27142C4DAE9AF5FCD1E5728E', X'E521BC1D27142C4DAE9AF5FCD1E5728E', 8, NULL, NULL, 2, NULL, 0, 'Stuff each pepper with meat mixture.');
