INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C26F129330886C48B1E253D54053786E', X'5EF6054DB4741A4CB88BF2355150AFC1', X'5EF6054DB4741A4CB88BF2355150AFC1', 6, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B9FA8DAA75609419DA1756303C1E3E2', X'5EF6054DB4741A4CB88BF2355150AFC1', X'5EF6054DB4741A4CB88BF2355150AFC1', 7, NULL, NULL, 2, NULL, 0, 'Remove pan from heat mixture and stir in pine nuts and raisins.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'451D9ECCE7EC38489AC22D36B4C17683', X'5EF6054DB4741A4CB88BF2355150AFC1', X'5EF6054DB4741A4CB88BF2355150AFC1', 8, NULL, NULL, 2, NULL, 0, 'Fill each apple with meat mixture and place them inside an oven proof dish.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC82B86369102E4EA68D7521F42A398A', X'5EF6054DB4741A4CB88BF2355150AFC1', X'5EF6054DB4741A4CB88BF2355150AFC1', 9, NULL, NULL, 2, NULL, 0, 'Top each apple with bechamel sauce.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C9C9013271B9247AB85FF142293CC6D', X'5EF6054DB4741A4CB88BF2355150AFC1', X'5EF6054DB4741A4CB88BF2355150AFC1', 10, NULL, NULL, 2, NULL, 0, 'Sauce:');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E5632AE0699EB4BB1E24B586DA5E9A5', X'5EF6054DB4741A4CB88BF2355150AFC1', X'5EF6054DB4741A4CB88BF2355150AFC1', 11, NULL, NULL, 2, NULL, 0, 'Melt butter in a deep pan on medium-high heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C60734B91477A64B8AA47080BC1EE1C8', X'5EF6054DB4741A4CB88BF2355150AFC1', X'5EF6054DB4741A4CB88BF2355150AFC1', 12, NULL, NULL, 2, NULL, 0, 'Add milk to pan and stir; add salt to taste.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C675F40D0314840838CF9DF31C74605', X'5EF6054DB4741A4CB88BF2355150AFC1', X'5EF6054DB4741A4CB88BF2355150AFC1', 13, NULL, NULL, 2, NULL, 0, 'Right before milk starts to boil, slowly add in semolina flour; stir frequently.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0A4219BD0E68E4DB5B5A3DE8B8B0AAD', X'5EF6054DB4741A4CB88BF2355150AFC1', X'5EF6054DB4741A4CB88BF2355150AFC1', 14, NULL, NULL, 2, NULL, 0, 'When sauce becomes thick, turn off heat and add over apples.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52DE9A0577BC2B4DA36A8A79FA56276A', X'5EF6054DB4741A4CB88BF2355150AFC1', X'5EF6054DB4741A4CB88BF2355150AFC1', 15, NULL, NULL, 2, NULL, 0, 'Lightly sprinkle top of each with cinnamon.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C52B0F34CAAECB4FB6D59B038A5948EC', X'5EF6054DB4741A4CB88BF2355150AFC1', X'5EF6054DB4741A4CB88BF2355150AFC1', 16, NULL, NULL, 2, NULL, 0, 'Use remaining stocks to cook any left over bits from your meat filling and then pour mixture at the base of the apples. (photo)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D46D53E387CBC498ACB8ACF4CB68E90', X'5EF6054DB4741A4CB88BF2355150AFC1', X'5EF6054DB4741A4CB88BF2355150AFC1', 17, NULL, NULL, 2, NULL, 0, 'Bake for 30-45 mins, until apples are soft.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82CDF1BC5BA90D4C8D20B7D123E3AFB0', X'5EF6054DB4741A4CB88BF2355150AFC1', X'5EF6054DB4741A4CB88BF2355150AFC1', 18, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F564EB5322C8724FB72C56F850CD3F23', X'29BD6884096D9E419E6209370B7184AA', X'29BD6884096D9E419E6209370B7184AA', 0, NULL, NULL, 2, NULL, 0, 'Mix the yeast, 2 tablespoons sugar, salt, 2 tablespoons flour, and milk together and let sit for 10 mins. This is to activate yeast.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D07483577AD1244814C9066C85063DD', X'29BD6884096D9E419E6209370B7184AA', X'29BD6884096D9E419E6209370B7184AA', 1, NULL, NULL, 2, NULL, 0, 'Mix butter, eggs, remaining sugar, orange/lemon zest, and vanilla in with the yeast mixture, blend well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B3CEF8E5DF1C74D89714E2518CD4DEB', X'29BD6884096D9E419E6209370B7184AA', X'29BD6884096D9E419E6209370B7184AA', 2, NULL, NULL, 2, NULL, 0, 'Add remaining flour and mix until it becomes a dough (add more flour if you notice the dough is too sticky).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F2E2A111CC8A54795920267079096BC', X'29BD6884096D9E419E6209370B7184AA', X'29BD6884096D9E419E6209370B7184AA', 3, NULL, NULL, 2, NULL, 0, 'Knead dough for 5 to 7 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BA65ABE07470946B1536C6A34558602', X'29BD6884096D9E419E6209370B7184AA', X'29BD6884096D9E419E6209370B7184AA', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle a little flour in a bowl and place dough in bowl, cover and let rise until it becomes double the size (roughly 2 hours) (photo)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1516C0BE5BD7C24984B1678BE6ECCCBF', X'29BD6884096D9E419E6209370B7184AA', X'29BD6884096D9E419E6209370B7184AA', 5, NULL, NULL, 2, NULL, 0, 'Divide dough into 6 parts (3 strands per loaf of bread), roll out length wise, pinch 3 strands together, and braid. (photo)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2904CA0EF70684EA3EB118AB9C8FCC9', X'29BD6884096D9E419E6209370B7184AA', X'29BD6884096D9E419E6209370B7184AA', 6, NULL, NULL, 2, NULL, 0, 'Place each braided dough loaf onto a baking sheet lined with parchment paper and let rise (roughly 1 hour) (photo)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FF3289154260F49A5267E5DD6CFAE57', X'29BD6884096D9E419E6209370B7184AA', X'29BD6884096D9E419E6209370B7184AA', 7, NULL, NULL, 2, NULL, 0, 'Brush top with egg wash and sprinkle with almond slices (or topping of your choice)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAA0A65468F479429813C1EA52658338', X'29BD6884096D9E419E6209370B7184AA', X'29BD6884096D9E419E6209370B7184AA', 8, NULL, NULL, 2, NULL, 0, 'Press one hardboiled egg into mixture or you can also place egg onto dough before you begin braiding (optional)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D88E33FE87D72408AA70365F1B0285E', X'29BD6884096D9E419E6209370B7184AA', X'29BD6884096D9E419E6209370B7184AA', 9, NULL, NULL, 2, NULL, 0, 'Bake in preheated oven at 350F degrees for 30-40 minutes or until center is cooked through.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E28678D55EADE4886C6D224FC1F56E2', X'29BD6884096D9E419E6209370B7184AA', X'29BD6884096D9E419E6209370B7184AA', 10, NULL, NULL, 2, NULL, 0, 'Remove and let cool and rack.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03AAF80AF07CCC4986D7D9E1F6F1EB85', X'29BD6884096D9E419E6209370B7184AA', X'29BD6884096D9E419E6209370B7184AA', 11, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'678BA3F9BDD0AE49A9C9D57DC32083F6', X'EB4BE91AD648E347ADD4641F86236CCB', X'EB4BE91AD648E347ADD4641F86236CCB', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F and line an oven safe dish with parchment paper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AEC93C164E232428D935D155FB5A87F', X'EB4BE91AD648E347ADD4641F86236CCB', X'EB4BE91AD648E347ADD4641F86236CCB', 1, NULL, NULL, 2, NULL, 0, 'Heat about 4-5 tablespoons of oil in a pan over medium-high heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21E43C8D32222248834E5E46C1FAA7D4', X'EB4BE91AD648E347ADD4641F86236CCB', X'EB4BE91AD648E347ADD4641F86236CCB', 2, NULL, NULL, 2, NULL, 0, 'Chop tentacles and add to pan. Cook for 4-5 minutes or until golden.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17D09CA431A37648B7C94D93FCFE000E', X'EB4BE91AD648E347ADD4641F86236CCB', X'EB4BE91AD648E347ADD4641F86236CCB', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and reserve for later use.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39679EBF0BA8E24A89599779CE08A0B4', X'EB4BE91AD648E347ADD4641F86236CCB', X'EB4BE91AD648E347ADD4641F86236CCB', 4, NULL, NULL, 2, NULL, 0, 'Heat remaining oil in skillet on medium heat and cook onion, shallot, and garlic for approx. 4-5 mins.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EAFD8FFBB27654B8D4BBC9C59AFAD07', X'EB4BE91AD648E347ADD4641F86236CCB', X'EB4BE91AD648E347ADD4641F86236CCB', 5, NULL, NULL, 2, NULL, 0, 'Mix in cilantro, ricotta cheese, lemon juice, chopped tentacles, and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFC4330F22494444BEC6B0103CA50203', X'EB4BE91AD648E347ADD4641F86236CCB', X'EB4BE91AD648E347ADD4641F86236CCB', 6, NULL, NULL, 2, NULL, 0, 'Stir for 1-2 minutes and remove from heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3396B496683A6348AB929495F218B57E', X'EB4BE91AD648E347ADD4641F86236CCB', X'EB4BE91AD648E347ADD4641F86236CCB', 7, NULL, NULL, 2, NULL, 0, 'Allow to cool long enough so you can touch the mixture with your hands.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6D3277577A6B548802347ED6BC8176C', X'EB4BE91AD648E347ADD4641F86236CCB', X'EB4BE91AD648E347ADD4641F86236CCB', 8, NULL, NULL, 2, NULL, 0, 'Fill squid sacs with mixture being careful not to overstuff. Three quarters of the way is perfect.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8120422762C7546AA8325F11D362950', X'EB4BE91AD648E347ADD4641F86236CCB', X'EB4BE91AD648E347ADD4641F86236CCB', 9, NULL, NULL, 2, NULL, 0, 'Secure openings with toothpicks or skew them all on one skewer.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3199A593E9A3974DA30B606CD16D5D71', X'EB4BE91AD648E347ADD4641F86236CCB', X'EB4BE91AD648E347ADD4641F86236CCB', 10, NULL, NULL, 2, NULL, 0, 'Line up squid on baking sheet and drizzle olive oil on top and add salt and pepper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4487F2BF3AC9C41A2564103B61820C1', X'EB4BE91AD648E347ADD4641F86236CCB', X'EB4BE91AD648E347ADD4641F86236CCB', 11, NULL, NULL, 2, NULL, 0, 'Cook for about 16-20 mins (depending on size of squid)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'185AA8B810F0974889563AF227480FBE', X'EB4BE91AD648E347ADD4641F86236CCB', X'EB4BE91AD648E347ADD4641F86236CCB', 12, NULL, NULL, 2, NULL, 0, 'Serve with a side of sliced lemon.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3306E8340563FB44BE35BF9534D6C9B1', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 0, NULL, NULL, 2, NULL, 0, '-Syrup-');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DF0458BC285664ABAB731E2C7ED41AA', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 1, NULL, NULL, 2, NULL, 0, 'Combine sugar, water, and lemon juice in sauce pan. Bring to boil over medium heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'578B4E27983903439B1AF34D6F18A01D', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 2, NULL, NULL, 2, NULL, 0, 'Add lemon and orange peel - simmer for 10 min (see photo).Turn off and remove from heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A3493402A3F8249B9355EDA92D7AF52', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 3, NULL, NULL, 2, NULL, 0, '-Custard-');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71A60E3F86B0B5419722847507CA4F9B', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 4, NULL, NULL, 2, NULL, 0, 'Add milk to large pot and bring to slight boil and reduce heat to medium.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09F5FA5C04737547B9AFAD8D5A854457', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 5, NULL, NULL, 2, NULL, 0, 'While milk is heating, beat eggs and add sugar and vanilla and mix thoroughly.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'298D4D43C6BC854F948AF1E5501B9E17', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 6, NULL, NULL, 2, NULL, 0, 'When milk is almost boiling, take a cups worth and slowly add it to egg/sugar mixture--stirring entire time.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F18A146ED3B7D647A2088C73E83BC371', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 7, NULL, NULL, 2, NULL, 0, 'Now, slowly add semolina to the milk and mix. Next add egg/sugar and stir until it begins to thicken.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18D9884D1C581447A83D83FBAAEDE59F', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 8, NULL, NULL, 2, NULL, 0, 'Once thick (see photo) remove from heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B017DA520FF931429B76CCE36DDA1C4F', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 9, NULL, NULL, 2, NULL, 0, '--Making Rolls--');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'468153574BDB3D429D5600EEBD5FE849', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 10, NULL, NULL, 2, NULL, 0, 'Unwrap phyllo and unroll. Take one sheet and lay it flat and cover rest with damp towel.');
