INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41B70410F456754494DC351A39DE5544', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 12, NULL, NULL, 2, NULL, 0, 'Place 1 tablespoon of custard at right edge of phyllo sheet.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E71E48D0211A164C8AB10F6F1B56FF7C', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 13, NULL, NULL, 2, NULL, 0, 'Fold it over once (see photo) Fold bottom up and top down. (see photo)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E37B2782FC131C409D96912CEDAE97A6', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 14, NULL, NULL, 2, NULL, 0, 'Now, roll all the way to opposite end and place on baking tray.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FEF1D78C34197419938E91CDE07DC0F', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 15, NULL, NULL, 2, NULL, 0, 'Repeat until finished. (see photo)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04A3843970517242A1EFCE800DDDE2AE', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 16, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350F and bake the Galaktoboureko Rolls for 30-40 min or until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7DDF468B8B8D543B4935C5C5D4CFB87', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', X'B6CCA1BBC26BF048A12FEC43EE29ABA3', 17, NULL, NULL, 2, NULL, 0, 'Once cooled, drizzle syrup over the Galaktoboureko Rolls and serve!!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD93251D5F0C1C41A776C77FF079172B', X'A42F0C9A8FF9E447BF14527AB288C0F1', X'A42F0C9A8FF9E447BF14527AB288C0F1', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400F.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C063604B8ABE9540B31684AA0DA1BADF', X'A42F0C9A8FF9E447BF14527AB288C0F1', X'A42F0C9A8FF9E447BF14527AB288C0F1', 1, NULL, NULL, 2, NULL, 0, 'Heat olive oil, lemon zest, minced garlic, red pepper flakes, and oregano in a pan over low heat until garlic becomes golden and oil becomes fragrant; remove from heat. (see photo)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1168B22BA068AF409300AECFD51BEC07', X'A42F0C9A8FF9E447BF14527AB288C0F1', X'A42F0C9A8FF9E447BF14527AB288C0F1', 2, NULL, NULL, 2, NULL, 0, 'Cut ends off of asparagus and discard.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'224B73A2677D6D48B6CF334E46EA42FC', X'A42F0C9A8FF9E447BF14527AB288C0F1', X'A42F0C9A8FF9E447BF14527AB288C0F1', 3, NULL, NULL, 2, NULL, 0, 'Toss asparagus pieces with olive oil mixture (see photo) and arrange in single layer on a baking sheet.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C180C0D158789F4EAB26D9D8E16CFDE8', X'A42F0C9A8FF9E447BF14527AB288C0F1', X'A42F0C9A8FF9E447BF14527AB288C0F1', 4, NULL, NULL, 2, NULL, 0, 'Season asparagus with salt and pepper and crumble feta cheese on top.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CACF4D7D0E0E148B1203CD7F9BF7DE7', X'A42F0C9A8FF9E447BF14527AB288C0F1', X'A42F0C9A8FF9E447BF14527AB288C0F1', 5, NULL, NULL, 2, NULL, 0, 'Bake at 400F for 10 mins or until cooked through. (see photo)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18CE165F9BFCD64ABBE63DF11140729A', X'A42F0C9A8FF9E447BF14527AB288C0F1', X'A42F0C9A8FF9E447BF14527AB288C0F1', 6, NULL, NULL, 2, NULL, 0, 'Squeeze lemon juice over the asparagus.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D00C15D8FB03D84CBA82FF5D941710A1', X'A42F0C9A8FF9E447BF14527AB288C0F1', X'A42F0C9A8FF9E447BF14527AB288C0F1', 7, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE45958B2AF14A468404DF50B8AA6A44', X'0DD9454450608A409D54D0A660F7F942', X'0DD9454450608A409D54D0A660F7F942', 0, NULL, NULL, 2, NULL, 0, 'In a large pan, heat some olive oil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3D75A630E3BDD4096DEF28496C28861', X'0DD9454450608A409D54D0A660F7F942', X'0DD9454450608A409D54D0A660F7F942', 1, NULL, NULL, 2, NULL, 0, 'Add chicken and cook until lightly browned (8-10min).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07C5EFD7E5D25341827960858EBFDD52', X'0DD9454450608A409D54D0A660F7F942', X'0DD9454450608A409D54D0A660F7F942', 2, NULL, NULL, 2, NULL, 0, 'Squeeze lemon over chicken and add oregano and rosemary.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6F4587CF71C474C87BD58F499725B72', X'0DD9454450608A409D54D0A660F7F942', X'0DD9454450608A409D54D0A660F7F942', 3, NULL, NULL, 2, NULL, 0, 'Add red potatoes around chicken.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FBA25AB19BD714D8C5A64A6E40BD1E5', X'0DD9454450608A409D54D0A660F7F942', X'0DD9454450608A409D54D0A660F7F942', 4, NULL, NULL, 2, NULL, 0, 'Add enough chicken broth to cover potatoes about half way (be sure to turn them half way through cooking).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'021E34ADA5390A45A5C5A82768FDFD01', X'0DD9454450608A409D54D0A660F7F942', X'0DD9454450608A409D54D0A660F7F942', 5, NULL, NULL, 2, NULL, 0, 'Cover and simmer and reduce for about 60min or until potatoes are soft.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D9F70A7E565934483A0E4F5BCAA74A1', X'0DD9454450608A409D54D0A660F7F942', X'0DD9454450608A409D54D0A660F7F942', 6, NULL, NULL, 2, NULL, 0, 'Plate and squeeze lemon and add a pinch of oregano over chicken and potatoes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81481F999C7B6340A0EEB4DF65757287', X'0DD9454450608A409D54D0A660F7F942', X'0DD9454450608A409D54D0A660F7F942', 7, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13E1923D278A8142923D6C90A8B37A84', X'E660F21461E0B9498465E7D0AECBA331', X'E660F21461E0B9498465E7D0AECBA331', 0, NULL, NULL, 2, NULL, 0, 'Cut the ends off the zucchini.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7478F1C1F2AC54D9F10F3A8530FA11D', X'E660F21461E0B9498465E7D0AECBA331', X'E660F21461E0B9498465E7D0AECBA331', 1, NULL, NULL, 2, NULL, 0, 'Cut a slit down the middle of each zucchini (don''t go all the way to the end nor all the way through) (see photo)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A0DB749BFA67C4AAB26EFFAC32C4539', X'E660F21461E0B9498465E7D0AECBA331', X'E660F21461E0B9498465E7D0AECBA331', 2, NULL, NULL, 2, NULL, 0, 'Remove slit from each zucchini (where you''ll stuff garlic and feta)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01EAD7C345CED941957DCA9B2C2E30F4', X'E660F21461E0B9498465E7D0AECBA331', X'E660F21461E0B9498465E7D0AECBA331', 3, NULL, NULL, 2, NULL, 0, 'In a bowl, mix together: garlic, oregano, feta, lemon juice, salt pepper, and olive oil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'481C5BF7C28347428369A0597F83D499', X'E660F21461E0B9498465E7D0AECBA331', X'E660F21461E0B9498465E7D0AECBA331', 4, NULL, NULL, 2, NULL, 0, 'Add mixture to each slit on each zucchini.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEA1600096174749B3D27B85B83916CC', X'E660F21461E0B9498465E7D0AECBA331', X'E660F21461E0B9498465E7D0AECBA331', 5, NULL, NULL, 2, NULL, 0, 'Place each zucchini in oven safe dish.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B061B58776AA4A4CB139EEE4114DB13D', X'E660F21461E0B9498465E7D0AECBA331', X'E660F21461E0B9498465E7D0AECBA331', 6, NULL, NULL, 2, NULL, 0, 'Drizzle with olive oil and add ¼ water to pan');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5B4A5C8B7FCFB45B859200517BB978D', X'E660F21461E0B9498465E7D0AECBA331', X'E660F21461E0B9498465E7D0AECBA331', 7, NULL, NULL, 2, NULL, 0, 'Cover with aluminum foil and bake at 350F for 1hr to 1.5hrs or until soft.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B6948E9D3AF6840B1811EB350F548AA', X'E660F21461E0B9498465E7D0AECBA331', X'E660F21461E0B9498465E7D0AECBA331', 8, NULL, NULL, 2, NULL, 0, 'Serve');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82CDF8FCB0B78644AFD8F823FC5B0C70', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 0, NULL, NULL, 2, NULL, 0, 'In a bowl, combine: eggs, egg yoke, milk, cheese, garlic powder, pepper, and mix well. Should be thick. If too runny, add more cheese.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7B6C8B8F777314DAC77485485F4D2AD', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 1, NULL, NULL, 2, NULL, 0, 'Combine melted butter and olive oil in bowl (we’ll use this to brush each phyllo strip).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B62071E0B5A6F64181B23C75E649FD4E', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 2, NULL, NULL, 2, NULL, 0, 'Take out the phyllo dough roll--don''t unroll it!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1B140B87D76084B9312C1BA311F4BD4', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 3, NULL, NULL, 2, NULL, 0, 'Now, cut the phyllo dough into thirds.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91B52D13C5AD5543B74C22AC1B802BD1', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 4, NULL, NULL, 2, NULL, 0, 'Take one-third and unroll it, this will be what you work with.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'928CF5007659F54BB9897DCFE5335431', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 5, NULL, NULL, 2, NULL, 0, 'Cover phyllo that you''re not using with a damp paper towel to keep moist.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F46D8B65C6F7C40A49A54585A9327D0', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 6, NULL, NULL, 2, NULL, 0, 'Take olive oil/butter mixture and lightly brush the first strip.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38BFD2541D69B74892292A8A5EDFDCEE', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 7, NULL, NULL, 2, NULL, 0, 'Take 1 spoonful of cheese mixture and place it on right corner (right or left, whichever is easier for you to roll).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'245C1094827CE3458A1CF6BABD7BD871', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 8, NULL, NULL, 2, NULL, 0, 'Now, fold the cheese over on the phyllo dough making a triangle. Continue rolling it over and over until you''ve reached the end.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2FA9BC2612C754AB01844FF26FD183F', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 9, NULL, NULL, 2, NULL, 0, 'Place on baking sheet (preferably lined with parchment paper).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B0C550BDA4CAA4A980232607832985B', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 10, NULL, NULL, 2, NULL, 0, 'Repeat until all cheese/phyllo is used or desired amount.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'352F20614164A14FA05735F1F26EEA5C', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 11, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400F.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61F0862AC695FC4480C540AD394B49CA', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 12, NULL, NULL, 2, NULL, 0, 'Once all tiropitakia''s are on the baking sheet, take your egg wash mixture and brush the top of each cheese pie.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED61AF1F3370E74B8B2274861AF883E3', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 13, NULL, NULL, 2, NULL, 0, 'Sprinkle sesame seeds and place in oven.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F3C8CE7CED7AC4FB5A094CE9123D78F', X'0D02CE2DE2C01043B61710A3AF055C71', X'0D02CE2DE2C01043B61710A3AF055C71', 14, NULL, NULL, 2, NULL, 0, 'Bake for 30 min or until tiropitakia''s are golden brown.');
