INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E0C9C7366B52D43948278AD2423275F', X'A4B56F9D9E20D94AA3580619D7D8391C', X'A4B56F9D9E20D94AA3580619D7D8391C', 0, NULL, NULL, 2, NULL, 0, 'Put a pot that is large enough to hold the grape leaves over high heat. Add enough water to cover the leaves and bring to a boil. Add grape leaves and blanch for 5 minutes, drain and set aside.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AD0D6C62918ED479CD47BBF67CBA920', X'A4B56F9D9E20D94AA3580619D7D8391C', X'A4B56F9D9E20D94AA3580619D7D8391C', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine: ground beef, rice, sea salt, pepper, onion, olive oil, and dill, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27038A767136CA448ADB43D39DAAD034', X'A4B56F9D9E20D94AA3580619D7D8391C', X'A4B56F9D9E20D94AA3580619D7D8391C', 2, NULL, NULL, 2, NULL, 0, 'Take a knife and cut off the stem of each grape leaf.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7302D21101462141A8F1CC15E0F5C840', X'A4B56F9D9E20D94AA3580619D7D8391C', X'A4B56F9D9E20D94AA3580619D7D8391C', 3, NULL, NULL, 2, NULL, 0, 'Place about 1 tablespoon of the meat mixture at the bottom of each leaf (ribs up) and fold over the sides of the leaf to cover the mixture. Then roll upwards. Place into a deep pot in layers (should have about 2 layers depending on the size of your pot).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2A6333055DB394AAAD94272BDE63A20', X'A4B56F9D9E20D94AA3580619D7D8391C', X'A4B56F9D9E20D94AA3580619D7D8391C', 4, NULL, NULL, 2, NULL, 0, 'Once all dolmathes are done, cover the tops of them with any broken grape leaves (if you have them).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E37DA82D7C410342B046C58E616C5FD7', X'A4B56F9D9E20D94AA3580619D7D8391C', X'A4B56F9D9E20D94AA3580619D7D8391C', 5, NULL, NULL, 2, NULL, 0, 'Add the chicken broth, cover dolmathes with a small plate or lid to keep them down and together. Turn heat to medium.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80B224AB8E595640859960897ED32CCC', X'A4B56F9D9E20D94AA3580619D7D8391C', X'A4B56F9D9E20D94AA3580619D7D8391C', 6, NULL, NULL, 2, NULL, 0, 'Bring to simmer, cook on low for about 15-20 minutes, or until the rice is cooked.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'113A754D19DB1941812AC4088C6AA446', X'A4B56F9D9E20D94AA3580619D7D8391C', X'A4B56F9D9E20D94AA3580619D7D8391C', 7, NULL, NULL, 2, NULL, 0, 'Once cooked, prepare the avgolemono sauce.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B7C8688C18BAE49BB2098CF7A139B23', X'A4B56F9D9E20D94AA3580619D7D8391C', X'A4B56F9D9E20D94AA3580619D7D8391C', 8, NULL, NULL, 2, NULL, 0, 'Beat eggs in a bowl for 5 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0498A7B7E496CB449BAF23A78E86C3F3', X'A4B56F9D9E20D94AA3580619D7D8391C', X'A4B56F9D9E20D94AA3580619D7D8391C', 9, NULL, NULL, 2, NULL, 0, 'Add lemon juice to the egg mixture and while still beating slowly pour in 1 cup of hot broth from the dolmathes pot into bowl with the eggs – this tempers them so they won’t curdle.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7059990C278614A98E379392C5B0FB6', X'A4B56F9D9E20D94AA3580619D7D8391C', X'A4B56F9D9E20D94AA3580619D7D8391C', 10, NULL, NULL, 2, NULL, 0, 'Now, pour egg mixture into pot with dolmathes and slowly mix everything together. Add salt and pepper if desired.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A39AA090EB2E8499FB0B5F0C2DCC660', X'A4B56F9D9E20D94AA3580619D7D8391C', X'A4B56F9D9E20D94AA3580619D7D8391C', 11, NULL, NULL, 2, NULL, 0, 'Bring to simmer, cover, and remove from heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F31197F022130948AD6CC136257C3A38', X'A4B56F9D9E20D94AA3580619D7D8391C', X'A4B56F9D9E20D94AA3580619D7D8391C', 12, NULL, NULL, 2, NULL, 0, 'Place a few dolmathes on a plate, pour some avgolemono sauce over them, and serve with lemon wedges!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEDA5709C2384E44AAF7E1F05C7D91A1', X'39C3AB102670FB4E9AD6C394E59BC782', X'39C3AB102670FB4E9AD6C394E59BC782', 0, NULL, NULL, 2, NULL, 0, 'Break bread into small pieces and place in mixing bowl with milk.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3371C40130C366419783EFDE3654EFC8', X'39C3AB102670FB4E9AD6C394E59BC782', X'39C3AB102670FB4E9AD6C394E59BC782', 1, NULL, NULL, 2, NULL, 0, 'Place garlic, onion, oregano, Sea salt, and pepper in blender and mix until blended and onion is finely chopped.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35A7954132EB6C419AA744E124BA9FDD', X'39C3AB102670FB4E9AD6C394E59BC782', X'39C3AB102670FB4E9AD6C394E59BC782', 2, NULL, NULL, 2, NULL, 0, 'Add onion mixture to bowl with milk and bread.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E7B339E6B22E343BC22DF9DCFD4A68C', X'39C3AB102670FB4E9AD6C394E59BC782', X'39C3AB102670FB4E9AD6C394E59BC782', 3, NULL, NULL, 2, NULL, 0, 'Add ground lamb to bowl.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14804346743B5949AC204BF0F604179F', X'39C3AB102670FB4E9AD6C394E59BC782', X'39C3AB102670FB4E9AD6C394E59BC782', 4, NULL, NULL, 2, NULL, 0, 'Add eggs to bowl and mix everything together (yep, get your hands dirty!).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77458C0BF7DFB147AFA806D19F4282A3', X'39C3AB102670FB4E9AD6C394E59BC782', X'39C3AB102670FB4E9AD6C394E59BC782', 5, NULL, NULL, 2, NULL, 0, 'Place flour on to a plate (big enough to roll the meatballs).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0CCFD1BDA929543B0D55F86AA03BFAF', X'39C3AB102670FB4E9AD6C394E59BC782', X'39C3AB102670FB4E9AD6C394E59BC782', 6, NULL, NULL, 2, NULL, 0, 'Roll meat mixture into tiny balls.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9EEB409197489488DD057067EFFE9FC', X'39C3AB102670FB4E9AD6C394E59BC782', X'39C3AB102670FB4E9AD6C394E59BC782', 7, NULL, NULL, 2, NULL, 0, 'Roll each one in flour, coating the entire meatball--shake off excess.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B23DE2C659D8BE48BCE0C456059D554A', X'39C3AB102670FB4E9AD6C394E59BC782', X'39C3AB102670FB4E9AD6C394E59BC782', 8, NULL, NULL, 2, NULL, 0, 'Flatten a bit to form a meat patty.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72BEC01A9364BE4D9BCFF0EBB802776C', X'39C3AB102670FB4E9AD6C394E59BC782', X'39C3AB102670FB4E9AD6C394E59BC782', 9, NULL, NULL, 2, NULL, 0, 'Heat pan with olive oil over med-high heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'721565F96CD0A6478A852E5720812A11', X'39C3AB102670FB4E9AD6C394E59BC782', X'39C3AB102670FB4E9AD6C394E59BC782', 10, NULL, NULL, 2, NULL, 0, 'Add the Greek meatballs and cook for around 7 minutes, turning every few minutes. Cook until they reach a nice brown color (no pink in middle – 160F internal temperature).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'759B4CD8F735B142B51E3730A9E0F6B9', X'39C3AB102670FB4E9AD6C394E59BC782', X'39C3AB102670FB4E9AD6C394E59BC782', 11, NULL, NULL, 2, NULL, 0, 'Remove from oil and let sit on paper towel. Pat dry.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CACC7F7066D6C749AED547E80B1D007F', X'39C3AB102670FB4E9AD6C394E59BC782', X'39C3AB102670FB4E9AD6C394E59BC782', 12, NULL, NULL, 2, NULL, 0, 'Serve these Greek meatballs along with some tzatziki and bread!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E5A9AD7348C31418BDAB14DA63F8832', X'6ECD955B85D79C46A559CDAEB1FE6643', X'6ECD955B85D79C46A559CDAEB1FE6643', 0, NULL, NULL, 2, NULL, 0, 'Combine everything in a food processor and blend until smooth. Add more olive oil if too thick.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7859CBE631E7543BD58E7EAE71BD440', X'6ECD955B85D79C46A559CDAEB1FE6643', X'6ECD955B85D79C46A559CDAEB1FE6643', 1, NULL, NULL, 2, NULL, 0, 'Transfer to bowl and top with a little extra pepper and a little olive oil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'426E39F3A317374EBB1744339E571EBA', X'6ECD955B85D79C46A559CDAEB1FE6643', X'6ECD955B85D79C46A559CDAEB1FE6643', 2, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4656D6990A553F4899CF9453E26EB67D', X'BE91EA56428BDF49BCB4D097855AE35A', X'BE91EA56428BDF49BCB4D097855AE35A', 0, NULL, NULL, 2, NULL, 0, 'Drain garbanzo beans leaving a little juice to use.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F2286EC9D920942BD6ADCF3523FD25B', X'BE91EA56428BDF49BCB4D097855AE35A', X'BE91EA56428BDF49BCB4D097855AE35A', 1, NULL, NULL, 2, NULL, 0, 'In a mixer, combined all ingredients - except peppers.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2B3C9B50176B44BB3AEE1E81E4954C2', X'BE91EA56428BDF49BCB4D097855AE35A', X'BE91EA56428BDF49BCB4D097855AE35A', 2, NULL, NULL, 2, NULL, 0, 'Mix until smooth paste,');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A664CFA4DFC39346ACD64246194AC780', X'BE91EA56428BDF49BCB4D097855AE35A', X'BE91EA56428BDF49BCB4D097855AE35A', 3, NULL, NULL, 2, NULL, 0, 'Add red peppers and pulsate for a few seconds.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47B5819087CA114D8055AA0780538D21', X'BE91EA56428BDF49BCB4D097855AE35A', X'BE91EA56428BDF49BCB4D097855AE35A', 4, NULL, NULL, 2, NULL, 0, 'Transfer to bowl - Sea salt to taste.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'532DB34E99CD834C9FCC1196FFA45123', X'BE91EA56428BDF49BCB4D097855AE35A', X'BE91EA56428BDF49BCB4D097855AE35A', 5, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAE213481B223A4B8B6C2AED68769508', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350F');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A19B73631CF1C49B8C02FDBA875DE5F', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', 1, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a large saucepan over medium heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DCF5348BEB0DB4F820F53B9239210E8', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', 2, NULL, NULL, 2, NULL, 0, 'Add onion and garlic, cook until onions are soft.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E1CCE2573543D45BC303191D7FA54A0', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', 3, NULL, NULL, 2, NULL, 0, 'Add spinach, dill, oregano, lemon, and lemon zest, mix well.*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A356DBE9BB23F43BCBAF8A0F89BEA26', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', 4, NULL, NULL, 2, NULL, 0, 'Cook for 10 minutes');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C97172D90FFA5046AFD1702B266516DC', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', 5, NULL, NULL, 2, NULL, 0, 'While cooking, add eggs to separate bowl, and beat with fork until well mixed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'206E73E162027847973411CAF975F23F', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and add feta cheese, eggs, pepper, mix until well incorporated.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'423BBF44DA10A540B1524EF17AC92B22', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', 7, NULL, NULL, 2, NULL, 0, 'Take one sheet of phyllo and lay it down flat in pan and brush with a light coating of melted butter');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5ED78188183BED42BAB0A5A8E15DB352', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', 8, NULL, NULL, 2, NULL, 0, 'Repeat about 9 more times to have 10 pieces of phyllo total.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F122FDA8F4E39B4CB493D608419DD080', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', 9, NULL, NULL, 2, NULL, 0, 'Pour Spinach cheese mixture over phyllo, spread evenly.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E592ECE77A5F04AAF53F2936075681F', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', X'FD8A7DAD0B85CC4D8A9F7184A950B4C8', 10, NULL, NULL, 2, NULL, 0, 'Repeat #8 until you have 10 more sheets of phyllo over spinach mixture (sheet, butter, sheet)');
