INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E26AD53CE36B7A428D724E47B16E377B', X'7BB023BBD6A79449827300258B00FC14', X'7BB023BBD6A79449827300258B00FC14', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B1939463B8A3A4C8ED64477CC6F0355', X'7BB023BBD6A79449827300258B00FC14', X'7BB023BBD6A79449827300258B00FC14', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine masa harina and salt. Stir in 1 1/2 cups of the warm water. Knead until mixture forms a smooth ball without lumps. If it feels a bit dry, add some more water to achieved the desired consistency.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7FC86B78B030946BCCE808653575C9F', X'7BB023BBD6A79449827300258B00FC14', X'7BB023BBD6A79449827300258B00FC14', 2, NULL, NULL, 2, NULL, 0, 'Divide the dough into 12 portions. Shape into 2-inch diameter balls and cover with plastic wrap or a wet kitchen towel to keep the dough moist and pliable. Keep a small bowl of water next to your working area. If the dough begins to stick to your hands as you are rolling and shaping the balls, occasionally dip your fingertips into the water to lightly moisten the dough.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06E4B0C578EDD541B3B9638DD02C97F6', X'7BB023BBD6A79449827300258B00FC14', X'7BB023BBD6A79449827300258B00FC14', 3, NULL, NULL, 2, NULL, 0, 'Working one at a time while keeping the other balls covered, place one of the balls of dough between two sheets of plastic wrap.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85C8B25B1E0E1747B6A7C0B511BE474C', X'7BB023BBD6A79449827300258B00FC14', X'7BB023BBD6A79449827300258B00FC14', 4, NULL, NULL, 2, NULL, 0, 'If you have a tortilla press, gently press down to make a flat 1/4-inch thick disk, about the thickness of a pancake. It will be much thicker than a tortilla, so do not flatten all the way down. If you do not have a tortilla press, use a glass pie pan to evenly press down the dough into 3-or 4-inch circles. Repeat with all 12 balls of dough.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'718E7C34E4A68C4CB76EB9BEE0628DE0', X'7BB023BBD6A79449827300258B00FC14', X'7BB023BBD6A79449827300258B00FC14', 5, NULL, NULL, 2, NULL, 0, 'On an ungreased griddle or large saut pan, cook the dough circles over medium-low heat for 2 minutes on each side until the dough is just set and can be turned without sticking. Work in batches of 3 or 4 circles at a time.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71EE0C843C8367499A2F6F27C92B6159', X'7BB023BBD6A79449827300258B00FC14', X'7BB023BBD6A79449827300258B00FC14', 6, NULL, NULL, 2, NULL, 0, 'Remove the circles from the heat and quickly pinch the edge of circles to form a 1/2-in. rim all the way around. Set aside until all the circles have been gently cooked and pinched. Be careful to protect your fingers as you are making a rim around each sope. If they are very warm, use a thin kitchen towel or paper towel as you form and shape the sopes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD7798DF32B1DD47A29F726F972CD522', X'7BB023BBD6A79449827300258B00FC14', X'7BB023BBD6A79449827300258B00FC14', 7, NULL, NULL, 2, NULL, 0, 'Add a small amount of oil to the pan and return the shaped sopes to the pan. Cook in batches until lightly browned and cooked through, about three minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AAF690F9096E940ADA7747D14BCAB34', X'7BB023BBD6A79449827300258B00FC14', X'7BB023BBD6A79449827300258B00FC14', 8, NULL, NULL, 2, NULL, 0, 'Place the sopes on a paper towel lined plate as you finish the whole batch.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7B7EE09BC5D4A43A025B1D0A5F3A389', X'7BB023BBD6A79449827300258B00FC14', X'7BB023BBD6A79449827300258B00FC14', 9, NULL, NULL, 2, NULL, 0, 'In a small saucepan, combine the shredded cooked chicken breast and 1/2 cup of the salsa. Cook over medium-low heat until heated through, stirring occasionally.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A580EA0992BDE945AB45EA68DFFFDFEE', X'7BB023BBD6A79449827300258B00FC14', X'7BB023BBD6A79449827300258B00FC14', 10, NULL, NULL, 2, NULL, 0, 'To assemble, layer each sope with some warmed refried beans, and the warmed chicken mixture. Sprinkle each sope with shredded lettuce and queso fresco. Top with remaining salsa.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08F417F8520496448288EA69D5933D0A', X'7BB023BBD6A79449827300258B00FC14', X'7BB023BBD6A79449827300258B00FC14', 11, NULL, NULL, 2, NULL, 0, 'Serve immediately and enjoy! If eaten the same day, they may be kept wrapped in a clean kitchen towel. Cooked sopes can also be stored in an airtight container in the refrigerator for up to 3 days and then reheated. Sopes can also be frozen and then thawed and reheated later.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7A95575470C9C4A97F1C90CCBA5BC3B', X'1BB6DBBE1D31F24DB15BE3E30870CB47', X'1BB6DBBE1D31F24DB15BE3E30870CB47', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'497578E23CA372458947673FA61245D3', X'1BB6DBBE1D31F24DB15BE3E30870CB47', X'1BB6DBBE1D31F24DB15BE3E30870CB47', 1, NULL, NULL, 2, NULL, 0, 'If you choose to brine your chicken, combine the salt, sugar, and water in a plastic bowl and stir until the salt and sugar are completely dissolved.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A51D00DB9717FF46881ABE2411D8A460', X'1BB6DBBE1D31F24DB15BE3E30870CB47', X'1BB6DBBE1D31F24DB15BE3E30870CB47', 2, NULL, NULL, 2, NULL, 0, 'Apply a spice rub under the skin of the breast and in the chicken cavity. The rub can be almost any combination of herbs and spices, but if you have brined your chicken, it already has all the salt it needs, so avoid a rub with extra salt. If you have skipped the brining step, use a rub that contains salt.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E26504A7CA5ED149B49E790056536B7D', X'1BB6DBBE1D31F24DB15BE3E30870CB47', X'1BB6DBBE1D31F24DB15BE3E30870CB47', 3, NULL, NULL, 2, NULL, 0, 'Set up your full-size charcoal grill for smoking. The trick is to separate the fire from the food. To do this, light enough charcoal to cover half the coal grate with two layers of burning coals. It is best to light the charcoal with a charcoal chimney; this allows you to light more charcoal outside the grill if necessary.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DC6C7D05F591D458D7E9A955B1C6250', X'1BB6DBBE1D31F24DB15BE3E30870CB47', X'1BB6DBBE1D31F24DB15BE3E30870CB47', 4, NULL, NULL, 2, NULL, 0, 'Place one of the aluminum pans on the coal grate next to the burning coals, but not on the coals. This is the drip pan that helps to keep the grill clean and prevents any dripping fat from reaching the fire which can cause flare-ups.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A72893575FCFF4FBD39E5BC588F9869', X'1BB6DBBE1D31F24DB15BE3E30870CB47', X'1BB6DBBE1D31F24DB15BE3E30870CB47', 5, NULL, NULL, 2, NULL, 0, 'Heat the grill to 250 F to 275 F/120 C to 135 C and then adjust the vents on the grill to hold that temperature. While the grill is heating up, take the chicken out of the refrigerator to come to room temperature. Once the grill is ready, place the chicken, breast-side up, on the grill grates directly over the drip pan on the coals and next to the pan of water on the grate.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CF344D02A24574AB7566E539B34A8EC', X'1BB6DBBE1D31F24DB15BE3E30870CB47', X'1BB6DBBE1D31F24DB15BE3E30870CB47', 6, NULL, NULL, 2, NULL, 0, 'Replace the grill lid and let the chicken cook for about 1 hour to 90 minutes. It is important to keep an eye on the grill to ensure that the fire is still burning and that the temperature is being maintained.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF27DDC0BB53EF46BA1E2E00CE1398A8', X'1BB6DBBE1D31F24DB15BE3E30870CB47', X'1BB6DBBE1D31F24DB15BE3E30870CB47', 7, NULL, NULL, 2, NULL, 0, 'After about 90 minutes, it is time to flip and turn the chicken. Since our makeshift smoker won''t cook evenly, it is important to do this halfway through the cooking time. Flip the chicken upside down, placing the side that was away from the fire close to the fire. This should make for an evenly cooked chicken.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D8CA6D3A21ED246AE31C1F9FFAB66EC', X'1BB6DBBE1D31F24DB15BE3E30870CB47', X'1BB6DBBE1D31F24DB15BE3E30870CB47', 8, NULL, NULL, 2, NULL, 0, 'The total cooking time is about 3 hours depending on the size of the chicken. As the chicken gets close to being done, it is time to start checking the temperature. Make sure to use a reliable meat thermometer; a high-quality digital thermometer is recommended. Check the center of each breast as well as the thickest part of each thigh. If the part away from the fire is lower in temperature, you might want to rotate the chicken again. Look for a temperature around 185 F/85 C. Chicken is cooked at 165 F/75 C, but it is best to overcook the chicken to make it easier to shred. Generally, any type of barbecue is overcooked to maximize tenderness.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'207654F1DBE3ED41BEA91F8A65AE96FE', X'1BB6DBBE1D31F24DB15BE3E30870CB47', X'1BB6DBBE1D31F24DB15BE3E30870CB47', 9, NULL, NULL, 2, NULL, 0, 'After the chicken has rested for 15 minutes, it''s time to start shredding the meat. This is best done with a pair of insulated food gloves so the hot chicken can be easily handled. It is something of a messy job, so a large, clean workspace is needed.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E19DB1E59E1F74DB878CC95F2E33003', X'1BB6DBBE1D31F24DB15BE3E30870CB47', X'1BB6DBBE1D31F24DB15BE3E30870CB47', 10, NULL, NULL, 2, NULL, 0, 'There is no trick to shredding the meat, so you may need to try out a few techniques before you find what works for you. Start by pulling off the skin and breaking down the larger pieces of meat, removing bones and fat as you go. Once you have the pile of meat, taking a portion at a time, use two forks to shred. Either pull each fork away from each other or use one to hold the meat and the other to pull away, shredding the chicken meat.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85A5EB8EBDF5224A8111AFF4EBB16EE7', X'1BB6DBBE1D31F24DB15BE3E30870CB47', X'1BB6DBBE1D31F24DB15BE3E30870CB47', 11, NULL, NULL, 2, NULL, 0, 'To keep the meat warm, place it in a large pot over a low flame, tossing occasionally, or if you are going to be eating later, a slow cooker on the warm setting works well. Once the shredded chicken is warmed, add your desired barbecue sauce, tossing to evenly coat. Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0582842625DC964C959525A48742F051', X'1E3B5FDB8837DE47A0E29D5F589806E5', X'1E3B5FDB8837DE47A0E29D5F589806E5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B76B09004FCEF42A2D12B71FAF58DF1', X'1E3B5FDB8837DE47A0E29D5F589806E5', X'1E3B5FDB8837DE47A0E29D5F589806E5', 1, NULL, NULL, 2, NULL, 0, 'Select the Instant Pot saut setting and add the olive oil to the pot. When the display reads `hot,` add the chicken breasts. Cook for about 3 minutes on each side (the chicken will be partially cooked and lightly browned on the outside). Remove the chicken to a plate and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DA6C7B06B97FA48B29320ECBFD5C352', X'1E3B5FDB8837DE47A0E29D5F589806E5', X'1E3B5FDB8837DE47A0E29D5F589806E5', 2, NULL, NULL, 2, NULL, 0, 'Add the onion, garlic, celery, and carrots to the pot along with the thyme, rosemary, kosher salt, and pepper. Cook, stirring, for 1 minute longer.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5472828D3D8A87409237A8A348715B0B', X'1E3B5FDB8837DE47A0E29D5F589806E5', X'1E3B5FDB8837DE47A0E29D5F589806E5', 3, NULL, NULL, 2, NULL, 0, 'Add the chicken broth and rice and stir, scraping the bottom of the pot to loosen any browned bits.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86D70CF6E1379241A6617F417C0105C5', X'1E3B5FDB8837DE47A0E29D5F589806E5', X'1E3B5FDB8837DE47A0E29D5F589806E5', 4, NULL, NULL, 2, NULL, 0, 'Add the partially cooked chicken breasts.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'072E769672348F47B7E0B67430B4C6FE', X'1E3B5FDB8837DE47A0E29D5F589806E5', X'1E3B5FDB8837DE47A0E29D5F589806E5', 5, NULL, NULL, 2, NULL, 0, 'Secure the lid on the Instant Pot and make sure the valve is in the sealing position. Select the pressure cook or manual button, high pressure, and set the timer for 3 minutes (it will take roughly 13 minutes for the pot to come to full pressure and begin cooking). When the time is up, let the pressure come down naturally for 10 minutes. Move the steam release valve to the venting position to release the remaining pressure.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E242F0ABD0939947B29FA9C462AAA6E7', X'1E3B5FDB8837DE47A0E29D5F589806E5', X'1E3B5FDB8837DE47A0E29D5F589806E5', 6, NULL, NULL, 2, NULL, 0, 'Remove the chicken breasts to a plate and shred it with 2 forks.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'504144507F926E4E860F6DE643B9E703', X'1E3B5FDB8837DE47A0E29D5F589806E5', X'1E3B5FDB8837DE47A0E29D5F589806E5', 7, NULL, NULL, 2, NULL, 0, 'Return the shredded chicken to the Instant Pot and add the chopped parsley. Taste the broth and adjust the seasonings, as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C492662507184449CFD867176E94CCC', X'1E3B5FDB8837DE47A0E29D5F589806E5', X'1E3B5FDB8837DE47A0E29D5F589806E5', 8, NULL, NULL, 2, NULL, 0, 'Ladle the soup into bowls and garnish with extra parsley, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B300C4A3B0C5FB418B1C52ABEAB4B48E', X'84DE0A6B193C2D4FB047B7C3BDA4E075', X'84DE0A6B193C2D4FB047B7C3BDA4E075', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4A5623623F9FF46AD5942F362F3F90D', X'84DE0A6B193C2D4FB047B7C3BDA4E075', X'84DE0A6B193C2D4FB047B7C3BDA4E075', 1, NULL, NULL, 2, NULL, 0, 'Spray the inside of the air fryer basket with cooking oil spray.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD62D02834256249B285F6F99D7735B1', X'84DE0A6B193C2D4FB047B7C3BDA4E075', X'84DE0A6B193C2D4FB047B7C3BDA4E075', 2, NULL, NULL, 2, NULL, 0, 'Dry the chicken breasts thoroughly with paper towels and then sprinkle them lightly with kosher salt and black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8426DBDB42800147972E79BD71B3F05D', X'84DE0A6B193C2D4FB047B7C3BDA4E075', X'84DE0A6B193C2D4FB047B7C3BDA4E075', 3, NULL, NULL, 2, NULL, 0, 'Set out three shallow bowls. Add 1/2 cup of the flour to the first bowl, the beaten eggs to the second bowl, and combine the remaining 1 cup of flour and Cajun seasoning in the third bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98C50FD0A6E44848BF191B9667D457C7', X'84DE0A6B193C2D4FB047B7C3BDA4E075', X'84DE0A6B193C2D4FB047B7C3BDA4E075', 4, NULL, NULL, 2, NULL, 0, 'Dip a chicken breast in the first bowl of flour, flipping it to coat it thoroughly. Place the coated chicken breast in the beaten egg and turn it over to coat both sides. Shake off excess egg mixture and dip it into the seasoned flour, coating both sides. Shake off the excess flour and place the chicken breast on a plate. Repeat with the remaining chicken pieces.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0225DC27CB81946B0457A7D2AA979D6', X'84DE0A6B193C2D4FB047B7C3BDA4E075', X'84DE0A6B193C2D4FB047B7C3BDA4E075', 5, NULL, NULL, 2, NULL, 0, 'Arrange the chicken breasts in the air fryer basket and spray them lightly with cooking oil spray.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'352F2C580B9F82479505E2CB597AC8CC', X'84DE0A6B193C2D4FB047B7C3BDA4E075', X'84DE0A6B193C2D4FB047B7C3BDA4E075', 6, NULL, NULL, 2, NULL, 0, 'Set the air fryer temperature to 390 F and set the timer for 16 minutes. Halfway through the cooking time, flip the chicken breasts and spray them lightly with cooking oil spray. When the time is up, use an instant-read thermometer to check the temperature. The minimum safe temperature for chicken is 165 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33AE93EB567469468E25DCE237123BBD', X'84DE0A6B193C2D4FB047B7C3BDA4E075', X'84DE0A6B193C2D4FB047B7C3BDA4E075', 7, NULL, NULL, 2, NULL, 0, 'Arrange the chicken pieces on a serving tray and garnish with parsley if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5133A0B2D11B8249B12F6B2D13F8A292', X'E3336EBC1D899E42BD99D620B3CD58A0', X'E3336EBC1D899E42BD99D620B3CD58A0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C205470CECCEB4FBF231105BDC4216A', X'E3336EBC1D899E42BD99D620B3CD58A0', X'E3336EBC1D899E42BD99D620B3CD58A0', 1, NULL, NULL, 2, NULL, 0, 'Cut the chicken breast in half horizontally to create two thinner pieces. Lay the chicken on kitchen paper and keep to one side.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F2A3E12B471E24E8E4D9B0CFC4B2907', X'E3336EBC1D899E42BD99D620B3CD58A0', X'E3336EBC1D899E42BD99D620B3CD58A0', 2, NULL, NULL, 2, NULL, 0, 'Place the flour into a shallow bowl large enough to hold the chicken breast. In another shallow bowl as above, beat the eggs together with a generous pinch of salt and a few grinds of black pepper and 2 tablespoons of lemon juice.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E0E90E34A40054DAC417549385B187D', X'E3336EBC1D899E42BD99D620B3CD58A0', X'E3336EBC1D899E42BD99D620B3CD58A0', 3, NULL, NULL, 2, NULL, 0, 'Heat the oil and butter together in a non-stick frying pan over medium-high heat.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D9C19FFE72EDD478B29641492345BEA', X'E3336EBC1D899E42BD99D620B3CD58A0', X'E3336EBC1D899E42BD99D620B3CD58A0', 4, NULL, NULL, 2, NULL, 0, 'While pan is pre-heating, gently press both sides of the chicken fillets into the flour and shake off any excess. Then dip them into the egg one at a time, making sure the fillets are thoroughly covered, lift to drain off any excess, then quickly transfer to the frying pan.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34F873E16DA5304FBD4C1EA56D2509A3', X'E3336EBC1D899E42BD99D620B3CD58A0', X'E3336EBC1D899E42BD99D620B3CD58A0', 5, NULL, NULL, 2, NULL, 0, 'Cook the fillets for 4 minutes on each side until golden brown and lightly crispy. Lift from the pan carefully using a spatula, lay onto a plate and keep warm while you make the sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FF344961E1D7A40A1721D28D915CB62', X'E3336EBC1D899E42BD99D620B3CD58A0', X'E3336EBC1D899E42BD99D620B3CD58A0', 6, NULL, NULL, 2, NULL, 0, 'Keep the frying pan on medium heat, add the shallots and stir well. Cook for 3 minutes to soften the shallot.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE3CAAE5100A464AAC081F87E3F17229', X'E3336EBC1D899E42BD99D620B3CD58A0', X'E3336EBC1D899E42BD99D620B3CD58A0', 7, NULL, NULL, 2, NULL, 0, 'Increase the heat to medium-hot, add the white wine and stir, then let the wine reduce by half.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'616EF4FED86FF444BEF114047993424B', X'E3336EBC1D899E42BD99D620B3CD58A0', X'E3336EBC1D899E42BD99D620B3CD58A0', 8, NULL, NULL, 2, NULL, 0, 'While the sauce is reducing, heat a small skillet to hot, add the lemon wedges, and cook for 2 minutes on each side to slightly caramelize the edges. Keep to one side.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76FC98408F51F44EB1B4F62D517D77AD', X'E3336EBC1D899E42BD99D620B3CD58A0', X'E3336EBC1D899E42BD99D620B3CD58A0', 9, NULL, NULL, 2, NULL, 0, 'Add the chicken broth and remaining lemon juice to the sauce and stir well. Cook for a further 3 minutes, then stir in the cream and parsley. Let this bubble for a couple of minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48E8BF923B08334D9D482D01994412C7', X'E3336EBC1D899E42BD99D620B3CD58A0', X'E3336EBC1D899E42BD99D620B3CD58A0', 10, NULL, NULL, 2, NULL, 0, 'Serve the chicken cutlets with two or three generous spoonfuls of the sauce, lemon wedges if using and green vegetables, potatoes, or rice on the side.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32EF3CE6B0070042BF48619ABC853260', X'D4A2FA7AE35BC246BED2EC5EE5788820', X'D4A2FA7AE35BC246BED2EC5EE5788820', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8BA51071D6CA64BBE5A13B5FA357D74', X'D4A2FA7AE35BC246BED2EC5EE5788820', X'D4A2FA7AE35BC246BED2EC5EE5788820', 1, NULL, NULL, 2, NULL, 0, 'In a shallow dish or large bowl, combine flour, salt, pepper and 1 tablespoon of the Italian seasoning. Add heavy cream to a separate bowl and salt and pepper to season.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C3C6598B896974D81809E99CDF49F10', X'D4A2FA7AE35BC246BED2EC5EE5788820', X'D4A2FA7AE35BC246BED2EC5EE5788820', 2, NULL, NULL, 2, NULL, 0, 'Add heavy cream to a separate bowl and salt and pepper to season.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21A4B14FEBD844448919305509131246', X'D4A2FA7AE35BC246BED2EC5EE5788820', X'D4A2FA7AE35BC246BED2EC5EE5788820', 3, NULL, NULL, 2, NULL, 0, 'Dredge chicken in flour, then milk and again into the flour, coating generously.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'342E85B456FDF046891DD1044B604F75', X'D4A2FA7AE35BC246BED2EC5EE5788820', X'D4A2FA7AE35BC246BED2EC5EE5788820', 4, NULL, NULL, 2, NULL, 0, 'Heat 2 tablespoon olive oil in a large skillet over high heat. Once hot, saute chicken about 2 minutes on each side, or until golden brown.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34E802133A4CFD42BBBDB6CFCA4F631F', X'D4A2FA7AE35BC246BED2EC5EE5788820', X'D4A2FA7AE35BC246BED2EC5EE5788820', 5, NULL, NULL, 2, NULL, 0, 'Remove chicken from skillet and slice.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B79E65330DF7D9499D66A4BB5554CAE6', X'D4A2FA7AE35BC246BED2EC5EE5788820', X'D4A2FA7AE35BC246BED2EC5EE5788820', 6, NULL, NULL, 2, NULL, 0, 'Add 2 more tablespoons of olive oil to the skillet and lower temperature to medium-high. Add garlic, peppers and red onion and cook for 2-3 minutes, or until peppers and onions begin to soften slightly.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8655FB39B929FF40B9AA1255845C84F6', X'D4A2FA7AE35BC246BED2EC5EE5788820', X'D4A2FA7AE35BC246BED2EC5EE5788820', 7, NULL, NULL, 2, NULL, 0, 'Add butter and cook another minute, stirring as needed. Add the wine and broth to the vegetables and lower the temperature to medium-low. Cook for 8-10 minutes, or until most of the liquid has evaporated.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'445705AC5B7F594980DFCDA643E1808C', X'D4A2FA7AE35BC246BED2EC5EE5788820', X'D4A2FA7AE35BC246BED2EC5EE5788820', 8, NULL, NULL, 2, NULL, 0, 'Add whole milk, remaining 1/2 tablespoon Italian seasoning, dried parsley, and salt and pepper to taste, cook another minute.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'422DD37ED93C674CAF6EE84A41EB7F57', X'D4A2FA7AE35BC246BED2EC5EE5788820', X'D4A2FA7AE35BC246BED2EC5EE5788820', 9, NULL, NULL, 2, NULL, 0, 'Add the sliced chicken and pasta to the vegetable mixture then add the lemon juice. Raise the temperature to medium-high and cook for 1 to 2 minutes or until all ingredients are heated through.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6050C1D315C1CD4682F9A25778CADFB1', X'D4A2FA7AE35BC246BED2EC5EE5788820', X'D4A2FA7AE35BC246BED2EC5EE5788820', 10, NULL, NULL, 2, NULL, 0, 'Top with a finish of salt, if desired, and fresh parsley. Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DBA1340BDC6C440BBFEF35B5B4F5663', X'53DB83E0423A3C40A80420558B84BFE2', X'53DB83E0423A3C40A80420558B84BFE2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D9CD8E6CA558142BA6DF18677C8F6E8', X'53DB83E0423A3C40A80420558B84BFE2', X'53DB83E0423A3C40A80420558B84BFE2', 1, NULL, NULL, 2, NULL, 0, 'Pat the chicken thighs with paper towels to dry.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92E2FDEB274D4F45BCDC9EA267CCA760', X'53DB83E0423A3C40A80420558B84BFE2', X'53DB83E0423A3C40A80420558B84BFE2', 2, NULL, NULL, 2, NULL, 0, 'Spray the air fryer basket with cooking oil spray.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AD1FE8813FC5E44BD36DFF75DF7ED28', X'53DB83E0423A3C40A80420558B84BFE2', X'53DB83E0423A3C40A80420558B84BFE2', 3, NULL, NULL, 2, NULL, 0, 'Place the chicken thighs on a plate, skin-side up, and sprinkle evenly with the Parmesan, garlic, salt, Italian seasoning, pepper, and pepper flakes, if using.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66FAE12DEAD66F4E923E9BA770EE00A1', X'53DB83E0423A3C40A80420558B84BFE2', X'53DB83E0423A3C40A80420558B84BFE2', 4, NULL, NULL, 2, NULL, 0, 'Place the seasoned chicken thighs in the air fryer basket, skin-side up, and drizzle with the olive oil or spray with olive oil spray.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D36108BB456CD64D94651C0D9831E9AC', X'53DB83E0423A3C40A80420558B84BFE2', X'53DB83E0423A3C40A80420558B84BFE2', 5, NULL, NULL, 2, NULL, 0, 'Set the air fryer temperature to 380 F and set the timer for 18 minutes. Remove the basket after about 12 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'589D6BEBB05F39438AD30C7A16EE5780', X'53DB83E0423A3C40A80420558B84BFE2', X'53DB83E0423A3C40A80420558B84BFE2', 6, NULL, NULL, 2, NULL, 0, 'Turn the chicken thighs over and replace the basket; continue to cook for the remaining 6 minutes. To ensure the chicken is thoroughly cooked, check the largest thighs with an instant-read thermometer. The minimum safe temperature for chicken is 165 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5654BC61280300478ACDFFF94386C378', X'53DB83E0423A3C40A80420558B84BFE2', X'53DB83E0423A3C40A80420558B84BFE2', 7, NULL, NULL, 2, NULL, 0, 'Serve the chicken thighs with extra grated Parmesan cheese and a garnish of parsley, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'347E589053AD4A43BF39CC8FF40F14B3', X'4ABA18735D61164EACFEDC6BF441319D', X'4ABA18735D61164EACFEDC6BF441319D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34EFC84A05DACE479345DAE623A745E6', X'4ABA18735D61164EACFEDC6BF441319D', X'4ABA18735D61164EACFEDC6BF441319D', 1, NULL, NULL, 2, NULL, 0, 'Place a skillet, saut pan, or saucier over medium-low heat and add the butter. Cook, constantly stirring, until the butter has melted and begins to get foamy. This might take 1 to 2 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38C17F29BE083A4E826C772F52A5DFED', X'4ABA18735D61164EACFEDC6BF441319D', X'4ABA18735D61164EACFEDC6BF441319D', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle the flour over the butter and stir or whisk until smooth and well blended, approximately 2 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14E1C353DC51DB48A3A59C2850F46FD2', X'4ABA18735D61164EACFEDC6BF441319D', X'4ABA18735D61164EACFEDC6BF441319D', 3, NULL, NULL, 2, NULL, 0, 'Continue cooking the flour and butter, while stirring or whisking, for 2 to 3 minutes. This step is essential and requires care because the roux could easily burn; keep an eye on the mixture for the whole time.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'775234C27D744C46AD7F98F76B3B0792', X'4ABA18735D61164EACFEDC6BF441319D', X'4ABA18735D61164EACFEDC6BF441319D', 4, NULL, NULL, 2, NULL, 0, 'Gradually whisk in the stock, stirring until well blended. Cook, constantly stirring, until the gravy begins to bubble and thicken. If your gravy seems too thin, mix in a bowl a few tablespoons of flour with the same amount of cold water until a smooth paste has formed. Slowly stir the water-flour paste into the boiling gravy until it is thickened as desired. On the contrary, if the gravy seems too thick, thin it with more stock, broth, or water. Cook, stirring, for 8 to 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4C8113C54A4264C8F5230CF4F5DFC06', X'4ABA18735D61164EACFEDC6BF441319D', X'4ABA18735D61164EACFEDC6BF441319D', 5, NULL, NULL, 2, NULL, 0, 'If using, add browning sauce, a few drops at a time, until you have a nice rich color. Don''t pour directly from the bottle, because your gravy could end up darker in color than you''d like. Taste the gravy and add the optional kosher salt and freshly ground black pepper, to taste. Cook for an extra 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA0729EC27BD8D46A39773BFCC89A40C', X'4ABA18735D61164EACFEDC6BF441319D', X'4ABA18735D61164EACFEDC6BF441319D', 6, NULL, NULL, 2, NULL, 0, 'Pour the gravy through a fine-mesh sieve into a serving bowl with a ladle or into a gravy boat.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8553D14ED624E344B4D802E3C7191EF8', X'4ABA18735D61164EACFEDC6BF441319D', X'4ABA18735D61164EACFEDC6BF441319D', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C803D5AFE172FC439A1069CFE8F9FEA1', X'3097A1966B485E4BAB55D16BEBC40F9D', X'3097A1966B485E4BAB55D16BEBC40F9D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41532CC9F7CDFA4F9EB65892FBEE825C', X'3097A1966B485E4BAB55D16BEBC40F9D', X'3097A1966B485E4BAB55D16BEBC40F9D', 1, NULL, NULL, 2, NULL, 0, 'Select the Instant Pot saut button and add the oil. When the display reads hot, add the chopped onion, bell pepper, and garlic. Saut the vegetables in the hot oil for 3 to 4 minutes or until the onion is softened.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87AE480779F00A4BB1ED7B5903AD01F9', X'3097A1966B485E4BAB55D16BEBC40F9D', X'3097A1966B485E4BAB55D16BEBC40F9D', 2, NULL, NULL, 2, NULL, 0, 'Add the chili powder, oregano, cumin, cayenne pepper, bay leaf, chicken stock, tomatoes, beans, and corn. Stir and then top with the chicken breasts.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87702F5537D5814291281F3D3F1C05E7', X'3097A1966B485E4BAB55D16BEBC40F9D', X'3097A1966B485E4BAB55D16BEBC40F9D', 3, NULL, NULL, 2, NULL, 0, 'Secure the lid and make sure the valve is in the sealing position. Choose pressure cook or manual (high pressure), and set the timer for 8 minutes. When the time is up, let the pressure come down naturally for 10 minutes, and then move the valve to the venting position to release the remaining pressure.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D19D1216C5D81E44B711B28395B6D55E', X'3097A1966B485E4BAB55D16BEBC40F9D', X'3097A1966B485E4BAB55D16BEBC40F9D', 4, NULL, NULL, 2, NULL, 0, 'Remove the chicken to a plate and shred it with two forks.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18B4A7F830D63245A0E7157F6CA0B095', X'3097A1966B485E4BAB55D16BEBC40F9D', X'3097A1966B485E4BAB55D16BEBC40F9D', 5, NULL, NULL, 2, NULL, 0, 'Return the shredded chicken to the pot along with the lime juice and chopped cilantro. Taste the soup and season with salt and pepper, as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53F90D980FA0A84F9C2EE64681AA544C', X'3097A1966B485E4BAB55D16BEBC40F9D', X'3097A1966B485E4BAB55D16BEBC40F9D', 6, NULL, NULL, 2, NULL, 0, 'Garnish each serving with tortilla strips or chips and serve along with lime wedges, shredded cheese, diced avocados, extra cilantro, or sour cream.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05AAC52B51284041BFB35325B72B39CA', X'FCE51739C2C3F841A5E8876497B39EF8', X'FCE51739C2C3F841A5E8876497B39EF8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6496BA4A59F5914495ED93EC75F1C256', X'FCE51739C2C3F841A5E8876497B39EF8', X'FCE51739C2C3F841A5E8876497B39EF8', 1, NULL, NULL, 2, NULL, 0, 'Using a sharp knife, divide the chicken breasts in half (or thirds if the breasts are especially large). You want large chunks, not strips, so slice the breasts crosswise, perpendicular to the grain of the muscle.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BF59DA98774844197C01986BCD29625', X'FCE51739C2C3F841A5E8876497B39EF8', X'FCE51739C2C3F841A5E8876497B39EF8', 2, NULL, NULL, 2, NULL, 0, 'Put one of the chicken chunks into a 1-gallon plastic freezer bag, but dont seal the closure. (If you dont have such a bag, you can also sandwich the chicken between two layers of plastic wrap or parchment paper.) Using a rolling pin, mallet, or similar blunt object, lightly pound the meat to flatten it to an even thickness of about 1/2 inch. Repeat with remaining chicken breasts. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1488336C90E8D9478A56699F9F104B24', X'FCE51739C2C3F841A5E8876497B39EF8', X'FCE51739C2C3F841A5E8876497B39EF8', 3, NULL, NULL, 2, NULL, 0, 'Crack the eggs into a bowl or dish. Youll be dipping the chicken pieces into this so choose a dish that''s wide enough. Add the milk and scramble with a whisk.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B02103E3D60C34BB9CB2D24D7C1CA12', X'FCE51739C2C3F841A5E8876497B39EF8', X'FCE51739C2C3F841A5E8876497B39EF8', 4, NULL, NULL, 2, NULL, 0, 'Add the flour, salt, and pepper to another dish of similar size. Combine well.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D375A57A1783749BE43372B4CF8098C', X'FCE51739C2C3F841A5E8876497B39EF8', X'FCE51739C2C3F841A5E8876497B39EF8', 5, NULL, NULL, 2, NULL, 0, 'Set a large skillet over medium heat. Add the butter and olive oil to the skillet.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74B5EBFA3887414592116351E97D0A05', X'FCE51739C2C3F841A5E8876497B39EF8', X'FCE51739C2C3F841A5E8876497B39EF8', 6, NULL, NULL, 2, NULL, 0, 'When the butter has finished foaming, dredge one of the chicken pieces in the flour mixture. Dip the dredged piece into the egg, then back once more in the seasoned flour. Place the breaded chicken in the pan. Repeat until the pan is full, but not crowded, maybe 2 to 4 pieces depending on size. Fry chicken until cooked through and golden brown on both sides, around 8 to 10 minutes. Repeat with remaining chicken, adding extra butter and oil for frying as needed. Remove cooked chicken to a sheet pan or large platter and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79DAF7A7388C94429F7A0473F8FBA21C', X'FCE51739C2C3F841A5E8876497B39EF8', X'FCE51739C2C3F841A5E8876497B39EF8', 7, NULL, NULL, 2, NULL, 0, 'Prepare the sauce. With caution, wipe the grease and debris from the pan with paper towels or use another large pan if you dont mind the extra cleaning. Set over medium heat and add half the butter. When the foam subsides, add the lemon slices. Saut until the rind browns slightly, 3 to 4 minutes. Remove the lemon.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F9594B06CB9C44A9C79FB4E50CC65A0', X'FCE51739C2C3F841A5E8876497B39EF8', X'FCE51739C2C3F841A5E8876497B39EF8', 8, NULL, NULL, 2, NULL, 0, 'Add the minced onion and saut until softened and translucent, about 4 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB56DFD53651E9428A3EA643E07117DC', X'FCE51739C2C3F841A5E8876497B39EF8', X'FCE51739C2C3F841A5E8876497B39EF8', 9, NULL, NULL, 2, NULL, 0, 'Add the flour and cook, stirring frequently, for 2 or 3 minutes more.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99B70215B6F30D4FB5A7CF0BB3A8E39B', X'FCE51739C2C3F841A5E8876497B39EF8', X'FCE51739C2C3F841A5E8876497B39EF8', 10, NULL, NULL, 2, NULL, 0, 'Add the wine, lemon juice, and chicken stock. Whisking periodically, bringing the sauce to a low boil. Reduce the sauce until it is a little more than half its original volume and somewhat thickened like gravy, about 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'541965A9436880458115A11FCF5A878E', X'FCE51739C2C3F841A5E8876497B39EF8', X'FCE51739C2C3F841A5E8876497B39EF8', 11, NULL, NULL, 2, NULL, 0, 'When the sauce has reduced, turn the heat to low, add the remaining butter and whisk to incorporate as it melts. Season to taste with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D751EB94F3A3064E906E218761282BE6', X'FCE51739C2C3F841A5E8876497B39EF8', X'FCE51739C2C3F841A5E8876497B39EF8', 12, NULL, NULL, 2, NULL, 0, 'Put the cooked chicken cutlets into the pan with the sauce. Turn to coat. When the chicken is warmed through, remove from heat and garnish with the sauted lemon slices and chopped parsley. Serve immediately.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6888535221BCA1458F1161535CAE7C57', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients for the chicken marinade.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E887DB8D5C7A9E4F9FDA91F8C4C08F79', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 1, NULL, NULL, 2, NULL, 0, 'Pound the chicken breasts with a mallet between two pieces of parchment paper until they are just under 1 inch in thickness.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCB8A59E42620F49AA62014E1FA28EA9', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 2, NULL, NULL, 2, NULL, 0, 'Cut the chicken breasts in half to fit the size of the bun. Poke the chicken breasts with the sharp end a knife and make 4 to 5 punctures per side.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDB6D2114ABF7449A7743596669597C1', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 3, NULL, NULL, 2, NULL, 0, 'Add the buttermilk, hot sauce, and spices to a large bowl and whisk until completely combined.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F96BF5BCC098594784F285D197CA86FC', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 4, NULL, NULL, 2, NULL, 0, 'Add the chicken and cover with plastic wrap, place it in the fridge for at least one hour or up to overnight.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC0E29A4F1AD5C47A05CC2E31700EEEE', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 5, NULL, NULL, 2, NULL, 0, 'Gather the breading ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E4F699DA270F34183ED2EDA516853F0', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 6, NULL, NULL, 2, NULL, 0, 'Whisk together the breading ingredients in a shallow baking dish. Add a 1/4 cup of the marinade to the flour and whisk to combine.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0406CF19CC20E04A8DAE74C184F85591', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 7, NULL, NULL, 2, NULL, 0, 'Remove a piece of chicken from the marinade and dip into the flour mixture. Dredge both sides to coat completely. Then dip back into the buttermilk mixture and then back into the flour mixture, pressing the flour to help it coat the chicken.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4BB85F83181F44E9035A3FC06A8F8C2', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 8, NULL, NULL, 2, NULL, 0, 'While it is refrigerating, add the oil to a cast-iron skillet or other heavy bottomed pan. Heat the oil to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C27724B95C8944B9C46BD5D9F4FB781', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 9, NULL, NULL, 2, NULL, 0, 'Add the chicken to the pan and fry 3 to 5 minutes per side. The outside should be crispy and golden brown and the internal temperature should be 165F. Remove from the oil and allow to drain on a cooling rack over a baking sheet or on a paper-towel lined plate.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68934EFADFA59D41B7255B57A2ABFAAF', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 10, NULL, NULL, 2, NULL, 0, 'While the chicken is cooking, gather the spicy mayo ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'460CF59E70509047AA5A900A44252962', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 11, NULL, NULL, 2, NULL, 0, 'Whisk together the ingredients for the mayo in a small bowl. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7D0107C1AAF2C40B8AEFF18907585E1', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 12, NULL, NULL, 2, NULL, 0, 'Gather the rest of the sandwich components.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BE9E9B5D417664386D8BA98E735ED13', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 13, NULL, NULL, 2, NULL, 0, 'Add butter to a large skillet and heat on medium high heat Add the buns to the buttered pan and toast cut-side down until golden brown.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32CA7E512F70FF439F62F32142EA065A', X'74C730DD2AF2974CB5DFA3583422C282', X'74C730DD2AF2974CB5DFA3583422C282', 14, NULL, NULL, 2, NULL, 0, 'Spread a tablespoon of the mayo onto both sides of the buns. Add 3 or so pickles to the bottom of the bun, top with the chicken and then top bun and enjoy immediately!');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8C708486E1AB444A31BD5B2C40DC0E5', X'99ADABE0D9650044821BD15E4CEF98BA', X'99ADABE0D9650044821BD15E4CEF98BA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2ADA08953E4D74382DFDA387DCEC1C1', X'99ADABE0D9650044821BD15E4CEF98BA', X'99ADABE0D9650044821BD15E4CEF98BA', 1, NULL, NULL, 2, NULL, 0, 'Butterfly the chicken breasts into two separate cutlets. Pound each breast to a 1/2-inch thickness. Add the cutlets to a shallow bowl and top with the soy sauce and sherry (or rice wine). Set aside, covered for at least 15 minutes or up to 4 hours. If marinating for longer than 15 minutes, place in the refrigerator.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC99D5E7EDD63745AE66A64104DFCB43', X'99ADABE0D9650044821BD15E4CEF98BA', X'99ADABE0D9650044821BD15E4CEF98BA', 2, NULL, NULL, 2, NULL, 0, 'Add a teaspoon of salt and a 1/2 teaspoon of pepper to the flour in a shallow bowl and stir to combine. Remove the chicken from the marinade and set the marinade aside. You will use it again. Dip each chicken breast into the flour and flip to coat both sides completely.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C71E6EF8C2B0B54BBADEBFFE30A622EC', X'99ADABE0D9650044821BD15E4CEF98BA', X'99ADABE0D9650044821BD15E4CEF98BA', 3, NULL, NULL, 2, NULL, 0, 'Add a teaspoon of salt and a 1/2 teaspoon of pepper to the bowl of soy sauce and sherry you set aside. Beat the eggs in that mixture until smooth. Dip each of the flour coated chicken cutlets into the egg mixture and flip to coat both sides.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'043EA12083DE6A4AB760392D0990924C', X'99ADABE0D9650044821BD15E4CEF98BA', X'99ADABE0D9650044821BD15E4CEF98BA', 4, NULL, NULL, 2, NULL, 0, 'Add the panko breadcrumbs to a shallow bowl and add the rest of the salt and pepper to the bowl. Stir to combine and then add the egg coated chicken cutlets to the panko breadcrumb mixture. If you want the coating extra thick you can dip them back into the egg mixture and then back into the breadcrumb mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F80AA09F1890A4FA763915350282E57', X'99ADABE0D9650044821BD15E4CEF98BA', X'99ADABE0D9650044821BD15E4CEF98BA', 5, NULL, NULL, 2, NULL, 0, 'Heat the oil on medium high heat. Check that the oil is hot enough by adding a few breadcrumbs to the oil. If they sizzle, its ready. Add two of the cutlets to the hot oil and cook for 2 to 3 minutes per side. Check the edges of the cutlet as it cooks to watch for burning. Turn down or adjust the heat as needed. You are not deep frying the cutlets but you are frying them in a good amount of oil.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B5273B95421E947B8AED22401C9DA54', X'99ADABE0D9650044821BD15E4CEF98BA', X'99ADABE0D9650044821BD15E4CEF98BA', 6, NULL, NULL, 2, NULL, 0, 'Drain the cutlets onto a paper towel lined plate or place on a cooling rack over a baking sheet. You can keep them warm in a 200 F oven while you fry the other two cutlets.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF9FAEB0B7C5E84A839809DC145AC782', X'99ADABE0D9650044821BD15E4CEF98BA', X'99ADABE0D9650044821BD15E4CEF98BA', 7, NULL, NULL, 2, NULL, 0, 'Fry the other two cutlets and then serve with tonkatsu sauce and rice. You can also top with chopped scallions.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E44FCAAA1888C49986354356CF8AF2D', X'8A0E3B1267207345B27A6016215CC140', X'8A0E3B1267207345B27A6016215CC140', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0494C11F10444E4FB08E35E731E5ADB0', X'8A0E3B1267207345B27A6016215CC140', X'8A0E3B1267207345B27A6016215CC140', 1, NULL, NULL, 2, NULL, 0, 'Select the saut button on the Instant Pot and add the olive oil and butter to the inner pot. When the pot is hot, add the onion and saut for 4 minutes, stirring frequently.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D8203A8F27B6548B028A5F0A7CB5A63', X'8A0E3B1267207345B27A6016215CC140', X'8A0E3B1267207345B27A6016215CC140', 2, NULL, NULL, 2, NULL, 0, 'To the onion, add the carrots, celery, and garlic and cook for 2 minutes longer.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACB184F8BA58564EB78546DFBC1BB9A3', X'8A0E3B1267207345B27A6016215CC140', X'8A0E3B1267207345B27A6016215CC140', 3, NULL, NULL, 2, NULL, 0, 'Add the bell pepper, Italian seasoning, salt, pepper, and chicken stock to the pot. Stir and scrape the bottom of the pan to loosen any browned bits from the onion.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2951CA37CE24354FB5F3C018560F497E', X'8A0E3B1267207345B27A6016215CC140', X'8A0E3B1267207345B27A6016215CC140', 4, NULL, NULL, 2, NULL, 0, 'Stir the rice into the vegetable and stock mixture and place the chicken pieces on top.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'484EBC0C35D30F42AA76111342B4F44C', X'8A0E3B1267207345B27A6016215CC140', X'8A0E3B1267207345B27A6016215CC140', 5, NULL, NULL, 2, NULL, 0, 'Secure the lid and make sure the steam release knob or switch is in the sealing position. Choose the pressure cook button (or manual on some pots), high pressure, and set the timer for 6 minutes. The pressure will build, and cooking will begin in approximately 6 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FBDDC93E36A8E41B98C58BF2935F6B7', X'8A0E3B1267207345B27A6016215CC140', X'8A0E3B1267207345B27A6016215CC140', 6, NULL, NULL, 2, NULL, 0, 'When the time is up, let the pressure come down naturally for 10 minutes, and then release the remaining pressure with a quick release.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D23014DB3D23E488033D00C74C0C8DC', X'8A0E3B1267207345B27A6016215CC140', X'8A0E3B1267207345B27A6016215CC140', 7, NULL, NULL, 2, NULL, 0, 'Remove the chicken breasts to a plate and then chop or shred with two forks.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EB08285C909BF46B1A937FF318C163C', X'8A0E3B1267207345B27A6016215CC140', X'8A0E3B1267207345B27A6016215CC140', 8, NULL, NULL, 2, NULL, 0, 'Add the peas and about half of the parsley to the rice mixture; stir. The residual heat from the rice mixture will heat the thawed peas.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AF2BCA4B921764B95F70B88CBE19F84', X'8A0E3B1267207345B27A6016215CC140', X'8A0E3B1267207345B27A6016215CC140', 9, NULL, NULL, 2, NULL, 0, 'Add the chopped or shredded chicken back to the pot and stir to blend. Taste and adjust the seasonings.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C067E873A0D31049BF6D54D62957E697', X'8A0E3B1267207345B27A6016215CC140', X'8A0E3B1267207345B27A6016215CC140', 10, NULL, NULL, 2, NULL, 0, 'Spoon the chicken and rice into a serving bowl and garnish with the remaining parsley. Enjoy the dish with crusty bread or biscuits and a green salad.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A2A30002B214948AACEC9F242129CA3', X'5E939C0A17970A40857ABDB4BEF4D5FE', X'5E939C0A17970A40857ABDB4BEF4D5FE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AB63995DE8BE5498B8EA500B6F2EB0B', X'5E939C0A17970A40857ABDB4BEF4D5FE', X'5E939C0A17970A40857ABDB4BEF4D5FE', 1, NULL, NULL, 2, NULL, 0, 'Toss the chicken with 1 teaspoon of the salt and pepper and the pickle juice. Allow to marinate for at least 3 hours covered in the refrigerator. You can marinate for up to 1 day.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A58D4957385A2C49AA157CDBC6F2AEBA', X'5E939C0A17970A40857ABDB4BEF4D5FE', X'5E939C0A17970A40857ABDB4BEF4D5FE', 2, NULL, NULL, 2, NULL, 0, 'Whisk the eggs and buttermilk and another teaspoon of the salt and pepper in a bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C21E38A15CABD4986697BF44205F12A', X'5E939C0A17970A40857ABDB4BEF4D5FE', X'5E939C0A17970A40857ABDB4BEF4D5FE', 3, NULL, NULL, 2, NULL, 0, 'Place the flour and paprika in a separate bowl and add another teaspoon of salt and pepper. Whisk it to combine.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41386FF35718CB418A3CD782E24C1AE1', X'5E939C0A17970A40857ABDB4BEF4D5FE', X'5E939C0A17970A40857ABDB4BEF4D5FE', 4, NULL, NULL, 2, NULL, 0, 'Place the chicken into the flour mixture and toss to coat completely.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3B9EDDC6C2E4748B7EF62D4553EFE57', X'5E939C0A17970A40857ABDB4BEF4D5FE', X'5E939C0A17970A40857ABDB4BEF4D5FE', 5, NULL, NULL, 2, NULL, 0, 'Then dip the chicken into the buttermilk mixture, tossing to coat. Then place the chicken back into the flour mixture and cover with the flour mixture. Shake off the excess.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97B4819BAEF7E7439467B1BBC4513441', X'5E939C0A17970A40857ABDB4BEF4D5FE', X'5E939C0A17970A40857ABDB4BEF4D5FE', 6, NULL, NULL, 2, NULL, 0, 'Place the breaded chicken pieces onto a baking sheet in a single layer.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D1BB11EFDBCFB46913E88D0ADB4CE49', X'5E939C0A17970A40857ABDB4BEF4D5FE', X'5E939C0A17970A40857ABDB4BEF4D5FE', 7, NULL, NULL, 2, NULL, 0, 'Set your air fryer to 380 F. Place your chicken in the air fryer in a single layer. Add the olive oil to the bottom of the inner drum of the air fryer. Cook for 18 to 20 minutes or until the internal temperature is 165 F and the outside is crispy and golden brown.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C811A143DE284E49BC381F18AB4157F4', X'5E939C0A17970A40857ABDB4BEF4D5FE', X'5E939C0A17970A40857ABDB4BEF4D5FE', 8, NULL, NULL, 2, NULL, 0, 'Cook the other chicken pieces until you are finished. You can keep the finished pieces in a warm 200 F oven while you wait for all of the pieces to finish cooking in the air fryer.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1BC0E581E0FFA4A93FD15754CF3F91F', X'5E939C0A17970A40857ABDB4BEF4D5FE', X'5E939C0A17970A40857ABDB4BEF4D5FE', 9, NULL, NULL, 2, NULL, 0, 'Serve immediately with your favorite sides like macaroni and cheese or collard greens.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3189B355B5CAC44DAD55A3AA410DA2EE', X'7E947C2644506449888DA2E6B22D846D', X'7E947C2644506449888DA2E6B22D846D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D9F65B77022394D8CB38B125C1DF03A', X'7E947C2644506449888DA2E6B22D846D', X'7E947C2644506449888DA2E6B22D846D', 1, NULL, NULL, 2, NULL, 0, 'Place a steaming basket or trivet in the inner pot of the Instant Pot and add 1 cup of water.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6F4B9AD8AE9FD4290A4B4865BF9397E', X'7E947C2644506449888DA2E6B22D846D', X'7E947C2644506449888DA2E6B22D846D', 2, NULL, NULL, 2, NULL, 0, 'Pat the chicken wings with paper towels to dry. Toss them with the garlic powder, 1 teaspoon of the kosher salt, and the black pepper. Arrange the wings in the pot.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C9AABCC0FA0E84D9FB353BA99461230', X'7E947C2644506449888DA2E6B22D846D', X'7E947C2644506449888DA2E6B22D846D', 3, NULL, NULL, 2, NULL, 0, 'Secure the lid on the pot and turn the steam release knob to the sealing position. Select the pressure cook or manual button, high pressure. Set the timer for 7 minutes. Meanwhile, preheat the broiler.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'169235BFC4212C4D90A37AC09BC51919', X'7E947C2644506449888DA2E6B22D846D', X'7E947C2644506449888DA2E6B22D846D', 4, NULL, NULL, 2, NULL, 0, 'When the time is up, let the pressure come down naturally for 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B511DCE22D8CD4F90D0FDF1CF192868', X'7E947C2644506449888DA2E6B22D846D', X'7E947C2644506449888DA2E6B22D846D', 5, NULL, NULL, 2, NULL, 0, 'Arrange the wings on lightly greased rimmed baking sheets. Combine the pressed garlic, butter, and the remaining 1/2 teaspoon of salt. Toss the wings with the butter mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'617687D23F4D7B448DFC3159F63B85E9', X'7E947C2644506449888DA2E6B22D846D', X'7E947C2644506449888DA2E6B22D846D', 6, NULL, NULL, 2, NULL, 0, 'Broil the wings for about 10 minutes, or until browned, turning after about 10 minutes to brown both sides.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C2EB9A9C716434BBC9C5BFC16A48C28', X'7E947C2644506449888DA2E6B22D846D', X'7E947C2644506449888DA2E6B22D846D', 7, NULL, NULL, 2, NULL, 0, 'Transfer the wings to a serving bowl and toss them with the grated Parmesan cheese and chopped parsley. Serve the wings with a marinara sauce or creamy garlic or ranch-style dressing. Serve extra Parmesan along with the wings, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D661C50EC769774BA28043346A97E2B8', X'01019F39B9828040836D5EB9B456EB20', X'01019F39B9828040836D5EB9B456EB20', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E69BEF826BA0B44881CFC8DB6207AD6', X'01019F39B9828040836D5EB9B456EB20', X'01019F39B9828040836D5EB9B456EB20', 1, NULL, NULL, 2, NULL, 0, 'Carefully dry the chicken pieces with paper towels. On a chopping board, sprinkle a light layer of salt and several grinds of black pepper. Press the chicken pieces into the salt and pepper, turning the meat around to make sure all the meat is seasoned.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3F576919A7D4C4FA90AFC5DB3F2E7DB', X'01019F39B9828040836D5EB9B456EB20', X'01019F39B9828040836D5EB9B456EB20', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium-high heat, add the oil and melt the butter until hot, but not burning. Sear the chicken pieces, turning frequently; the breasts will need about 5 minutes and the legs 8 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AFDCED0A4666F4A91B9D01FDDBF650E', X'01019F39B9828040836D5EB9B456EB20', X'01019F39B9828040836D5EB9B456EB20', 3, NULL, NULL, 2, NULL, 0, 'Into an ovenproof dish or Dutch oven, place the two shallots halves, garlic, and rosemary, then lay the chicken pieces on top. Pour any oil and butter from the pan onto the chicken, but do not scrape the pan. Keep as many of the cooking bits and pieces as you can.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BCEE199F32AD24CA52DB428539D27B0', X'01019F39B9828040836D5EB9B456EB20', X'01019F39B9828040836D5EB9B456EB20', 4, NULL, NULL, 2, NULL, 0, 'Cook in the center of the preheated oven for 25 minutes or until the internal temperature reaches 165 F which may be longer or shorter depending on the size of the chicken.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D689F84D99A6743A2C729A6614B005E', X'01019F39B9828040836D5EB9B456EB20', X'01019F39B9828040836D5EB9B456EB20', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, put the frying pan back on medium-high heat. Once hot, add the dry white wine and stir to scrape all the bits on the bottom, cook for 1 minute and keep to one side.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'455E3A0187C3FC438FBC7F6FE3E85456', X'01019F39B9828040836D5EB9B456EB20', X'01019F39B9828040836D5EB9B456EB20', 6, NULL, NULL, 2, NULL, 0, 'Once the chicken has cooked, move it from the pan using a slotted spoon or tongs onto a warm plate and cover with foil. Leave the chicken to rest for five minutes in a warm, but not hot place.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C9151480817C34CBA403CC89BE0C02D', X'01019F39B9828040836D5EB9B456EB20', X'01019F39B9828040836D5EB9B456EB20', 7, NULL, NULL, 2, NULL, 0, 'Put the dish or Dutch oven used for cooking the chicken on a burner over medium-high heat. Add the reduced wine and stir well, then add the stock. Boil until reduced by half. Check if there are any juices from the resting chicken. If so, add these to the sauce and continue to simmer for a few minutes more. Whisk in the cream and then strain through a fine sieve into a warmed gravy boat or other vessel to serve with the chicken.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75A93D5C0C917341B259D4C412E3C679', X'01019F39B9828040836D5EB9B456EB20', X'01019F39B9828040836D5EB9B456EB20', 8, NULL, NULL, 2, NULL, 0, 'Serve the roasted chicken with fresh vegetables and the sauce on the side.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1DF4300B49BF44886C4DBAA1A90526C', X'FA9E7E45EEF4094E95C6E5809622E8B7', X'FA9E7E45EEF4094E95C6E5809622E8B7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D17BA4690A72A24CB97268DDB9CAEF6A', X'FA9E7E45EEF4094E95C6E5809622E8B7', X'FA9E7E45EEF4094E95C6E5809622E8B7', 1, NULL, NULL, 2, NULL, 0, 'Heat the olive oil and butter over medium-high heat in a large heavy-bottomed pot like a Dutch oven. Add the celery, onions, carrots, and garlic to the oil and then add the salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'891E8AE8E9F55B4AB2C9AAE521B956D0', X'FA9E7E45EEF4094E95C6E5809622E8B7', X'FA9E7E45EEF4094E95C6E5809622E8B7', 2, NULL, NULL, 2, NULL, 0, 'Saut for 2 to 3 minutes or until the vegetables have slightly softened and the onions are translucent. Add the flour and saut for 1 to 2 minutes so the flour taste cooks off a bit.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF9F536A866D9641A99E35931FA58F66', X'FA9E7E45EEF4094E95C6E5809622E8B7', X'FA9E7E45EEF4094E95C6E5809622E8B7', 3, NULL, NULL, 2, NULL, 0, 'Add the chicken broth and thyme to the pot. Allow it to come to a boil.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE18466E816DFF409BAA9B51A06B7953', X'FA9E7E45EEF4094E95C6E5809622E8B7', X'FA9E7E45EEF4094E95C6E5809622E8B7', 4, NULL, NULL, 2, NULL, 0, 'Add the shredded chicken and gnocchi and cook for about 3 minutes or until the chicken is warmed through and the gnocchi is cooked.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'308B421CD4BADC4FA5A5636AE71F421E', X'FA9E7E45EEF4094E95C6E5809622E8B7', X'FA9E7E45EEF4094E95C6E5809622E8B7', 5, NULL, NULL, 2, NULL, 0, 'Turn the heat down to medium-low. Stir in the heavy cream and frozen chopped spinach into the soup and cook until the spinach is warmed and the soup has thickened. Add more salt and pepper to taste.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48A8AC816BD27348892600D7BC455B77', X'FA9E7E45EEF4094E95C6E5809622E8B7', X'FA9E7E45EEF4094E95C6E5809622E8B7', 6, NULL, NULL, 2, NULL, 0, 'Serve immediately with crusty bread, freshly grated Parmesan cheese, and a grind of black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B82848C64F9F0940A4E2FAEEB1F7DEB1', X'26E164BD5FFE934D91C3636F22B995DE', X'26E164BD5FFE934D91C3636F22B995DE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. If you are using frozen chicken livers, defrost before using.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69A4E443F6DDFF40A7A99042A91ADF25', X'26E164BD5FFE934D91C3636F22B995DE', X'26E164BD5FFE934D91C3636F22B995DE', 1, NULL, NULL, 2, NULL, 0, 'Clean the livers by soaking the livers in the milk for 20 minutes. Drain and pat dry with kitchen paper and remove any connective tissue with a sharp knife, then chop into 1/2-inch chunks.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17DFD1FAF6C9AA47888A889A5121829F', X'26E164BD5FFE934D91C3636F22B995DE', X'26E164BD5FFE934D91C3636F22B995DE', 2, NULL, NULL, 2, NULL, 0, 'Heat 1 ounce of the butter in a large frying pan over medium heat until the butter has melted and is foaming. Add the minced shallots and cook until they are softened and translucent. Turn the heat up and add the chopped livers. Cook for 3 minutes, turning with a spatula; do not worry of the centers are a little pink, thats fine.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D9D6CA3DC05E447AF49B37100F4156C', X'26E164BD5FFE934D91C3636F22B995DE', X'26E164BD5FFE934D91C3636F22B995DE', 3, NULL, NULL, 2, NULL, 0, 'Put the livers to one side to cool slightly. As they are cooling, reheat the pan they were cooked into hot, add the sherry or wine. Stir furiously to scrape up any bits of liver on the pan (also know as deglaze) and let this bubble until reduced to a bare tablespoon and add to the livers. Tip everything to a food processor.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAD656E8A9CF6F468BBD94972521C73C', X'26E164BD5FFE934D91C3636F22B995DE', X'26E164BD5FFE934D91C3636F22B995DE', 4, NULL, NULL, 2, NULL, 0, 'Add the thyme, salt, black pepper, mace and ginger and blitz to combine. Add the cream, 3 ounces of the butte, and blitz until super-smooth.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81B9013EAFDAB741B1242DFF22821CCC', X'26E164BD5FFE934D91C3636F22B995DE', X'26E164BD5FFE934D91C3636F22B995DE', 5, NULL, NULL, 2, NULL, 0, 'Using a fine sieve, push the liver mousse through the remove any lumps or remaining bits of connective tissue into a bowl. Using a wooden spoon whip the mousse once again and now it will be light and airy and exceedingly smooth.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BA0B2473C58184B8AFAAECB0AA55AB6', X'26E164BD5FFE934D91C3636F22B995DE', X'26E164BD5FFE934D91C3636F22B995DE', 6, NULL, NULL, 2, NULL, 0, 'Decant the mousse into either a serving dish or individual jars. Try to avoid any holes, so gently push the mousse into the dish using the back of a spoon. Put into the refrigerator for an hour to set. Individual jars will only need 30 minutes. While the mousse is setting, melt the butter in a small saucepan over a low heat then leave to cool.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7288188F102B84192C92B24C012A8E2', X'26E164BD5FFE934D91C3636F22B995DE', X'26E164BD5FFE934D91C3636F22B995DE', 7, NULL, NULL, 2, NULL, 0, 'Once cooled, pour a layer of butter over the mousse, return to the refrigerator. Serve with toast or thinly sliced bread, or on crackers.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A88B1B7AA5E3AE43A29D579C56AF20D6', X'806FEBA0BC36AF419B6DEF99964E3C5A', X'806FEBA0BC36AF419B6DEF99964E3C5A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FE1D769E1984843B2E0E68DF2CA6484', X'806FEBA0BC36AF419B6DEF99964E3C5A', X'806FEBA0BC36AF419B6DEF99964E3C5A', 1, NULL, NULL, 2, NULL, 0, 'Pat the wings dry with paper towels.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDAE0FB0AB6FDD44A5EE2B39CAB0B5AE', X'806FEBA0BC36AF419B6DEF99964E3C5A', X'806FEBA0BC36AF419B6DEF99964E3C5A', 2, NULL, NULL, 2, NULL, 0, 'Spread them out in a single layer and sprinkle with the salt, pepper, garlic powder, and cayenne pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2340E53E5030704E9AE2E228EFF3541C', X'806FEBA0BC36AF419B6DEF99964E3C5A', X'806FEBA0BC36AF419B6DEF99964E3C5A', 3, NULL, NULL, 2, NULL, 0, 'Place all of the wings in the air fryer and set for 380 F. Cook for 25 minutes. Pause the air fryer and toss the wings every five minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'374B52EAA9D8DF4EA20F834918BCA63B', X'806FEBA0BC36AF419B6DEF99964E3C5A', X'806FEBA0BC36AF419B6DEF99964E3C5A', 4, NULL, NULL, 2, NULL, 0, 'Turn the heat up on the air fryer to 400 F and cook for another 5 to 10 minutes until crispy and browned. You can cook the wings in a single layer in two batches for even more crispiness.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9ED71807FD0CA14BAC32DE77957C54BA', X'806FEBA0BC36AF419B6DEF99964E3C5A', X'806FEBA0BC36AF419B6DEF99964E3C5A', 5, NULL, NULL, 2, NULL, 0, 'To make the sauce, melt the butter in the microwave for one minute or until melted. Whisk in the garlic powder and hot sauce until completely combined. Place it back in the microwave for one to two minutes to heat thoroughly. Make sure the sauce is at least warm before you toss it with the wings.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70239F9CC4BFEA439EF6D49569F19D64', X'806FEBA0BC36AF419B6DEF99964E3C5A', X'806FEBA0BC36AF419B6DEF99964E3C5A', 6, NULL, NULL, 2, NULL, 0, 'Add the wings to a large bowl. Drizzle the sauce over the top and toss to coat. Serve the wings immediately topped with chopped chives and alongside your favorite dipping sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87572D1B661E0C4488A38FEF05903373', X'56CC6DA39FDF634AA348D1949D4A5FE6', X'56CC6DA39FDF634AA348D1949D4A5FE6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'455884A4F83F0641A3D679EBF9329D62', X'56CC6DA39FDF634AA348D1949D4A5FE6', X'56CC6DA39FDF634AA348D1949D4A5FE6', 1, NULL, NULL, 2, NULL, 0, 'Slice the chicken thighs into strips that are 1 to 2 inches wide and several inches long. For smaller thighs, just slice them in half lengthwise.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCA98AFD23DDB94C89AE8429A5AC2003', X'56CC6DA39FDF634AA348D1949D4A5FE6', X'56CC6DA39FDF634AA348D1949D4A5FE6', 2, NULL, NULL, 2, NULL, 0, 'Combine the other ingredients in a large bowl and mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CF91A74E4A23D45A2EF7BCCB991B6D0', X'56CC6DA39FDF634AA348D1949D4A5FE6', X'56CC6DA39FDF634AA348D1949D4A5FE6', 3, NULL, NULL, 2, NULL, 0, 'Add the chicken strips to the marinade and stir to coat them thoroughly. Cover and refrigerate for several hours or overnight.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'420CAD9128EC3040BDCDA24C230B4D34', X'56CC6DA39FDF634AA348D1949D4A5FE6', X'56CC6DA39FDF634AA348D1949D4A5FE6', 4, NULL, NULL, 2, NULL, 0, 'Set up a grill for searing.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A587800CA794474CBE1D0CC2DA9E4627', X'56CC6DA39FDF634AA348D1949D4A5FE6', X'56CC6DA39FDF634AA348D1949D4A5FE6', 5, NULL, NULL, 2, NULL, 0, 'Grill the marinated chicken strips for two to three minutes per side, depending on their thickness.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9A0897224C8F447AF8B97704E395C1A', X'56CC6DA39FDF634AA348D1949D4A5FE6', X'56CC6DA39FDF634AA348D1949D4A5FE6', 6, NULL, NULL, 2, NULL, 0, 'When fully cooked, remove to a platter and top with the sliced green onion tops.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A833EFEAEBA9944FAB0BC7019F91C32F', X'56CC6DA39FDF634AA348D1949D4A5FE6', X'56CC6DA39FDF634AA348D1949D4A5FE6', 7, NULL, NULL, 2, NULL, 0, 'Serve immediately with desired accompaniments.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6A6CE3F38C9C5418D82D70E927D6D91', X'621C28437D309746A11E791C2384B4BF', X'621C28437D309746A11E791C2384B4BF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2290D11F10F19C488F13CAB988E9FB6A', X'621C28437D309746A11E791C2384B4BF', X'621C28437D309746A11E791C2384B4BF', 1, NULL, NULL, 2, NULL, 0, 'Add the chicken tender pieces to a bowl or plastic bag. Pour the dill pickle juice over the top. Cover and refrigerate for at least 30 minutes or up to 3 hours. Do not marinate overnight.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F5306C674738D4596346C5731682028', X'621C28437D309746A11E791C2384B4BF', X'621C28437D309746A11E791C2384B4BF', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the egg, milk, and 1 teaspoon of the salt in a shallow bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3ECC62036EB2E140B480EA9E205C0EDF', X'621C28437D309746A11E791C2384B4BF', X'621C28437D309746A11E791C2384B4BF', 3, NULL, NULL, 2, NULL, 0, 'Whisk together the flour, powdered sugar, black pepper, onion powder, paprika, and remaining 1 teaspoon of salt in a shallow bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B3448B083FFC748B3BEC79BA944F7A1', X'621C28437D309746A11E791C2384B4BF', X'621C28437D309746A11E791C2384B4BF', 4, NULL, NULL, 2, NULL, 0, 'Drain the chicken tenders and dip them into the dry mixture. Shake off the excess flour.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E508C47532B294896702379C1117A96', X'621C28437D309746A11E791C2384B4BF', X'621C28437D309746A11E791C2384B4BF', 5, NULL, NULL, 2, NULL, 0, 'Dip them into the egg mixture. Coat each tender completely.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A47AE22B847DAA46B489F27444CFC8E4', X'621C28437D309746A11E791C2384B4BF', X'621C28437D309746A11E791C2384B4BF', 6, NULL, NULL, 2, NULL, 0, 'Dip them into the dry mixture again. Shake off the excess flour. Make sure to completely coat with flour. You don''t want any wet spots. This will cause the tenders to stick to the air fryer basket.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82317C865F1EF54286E6EE5B75E3A24C', X'621C28437D309746A11E791C2384B4BF', X'621C28437D309746A11E791C2384B4BF', 7, NULL, NULL, 2, NULL, 0, 'Preheat the air fryer to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8639E83DE832834B969266C7FFE9B158', X'621C28437D309746A11E791C2384B4BF', X'621C28437D309746A11E791C2384B4BF', 8, NULL, NULL, 2, NULL, 0, 'Grease the basket of the air fryer with olive oil generously to prevent sticking. Add a single layer of the tenders and spray them with olive oil spray.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C61A85847A5C0A48BD784790F7EC31BA', X'621C28437D309746A11E791C2384B4BF', X'621C28437D309746A11E791C2384B4BF', 9, NULL, NULL, 2, NULL, 0, 'Fry for 15 minutes. Pause half way through and flip the tenders. Spray them again with the olive oil spray and finish cooking.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B978E0932B3EF4459D026C49B24C902E', X'621C28437D309746A11E791C2384B4BF', X'621C28437D309746A11E791C2384B4BF', 10, NULL, NULL, 2, NULL, 0, 'Repeat with remaining chicken tenders. Serve with your favorite dipping sauces.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E010C272F907394CAD5E1EF9B899698A', X'AA880DB45060CD4881F0A9D882FE4D7B', X'AA880DB45060CD4881F0A9D882FE4D7B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E78F1C7D3092D34C884A5CAD26BB687D', X'AA880DB45060CD4881F0A9D882FE4D7B', X'AA880DB45060CD4881F0A9D882FE4D7B', 1, NULL, NULL, 2, NULL, 0, 'Place a trivet in the Instant Pot inner pot and add 3/4 cup of water or stock.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17E2DB57DF06EC4E9387D6210B457C4F', X'AA880DB45060CD4881F0A9D882FE4D7B', X'AA880DB45060CD4881F0A9D882FE4D7B', 2, NULL, NULL, 2, NULL, 0, 'If the chicken wings are whole, cut them at the joint and cut off the wing tips. Discard the wing tips or freeze them to use in stock. Sprinkle the chicken wings with kosher salt and arrange them on the trivet.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8752952C5FEFB14D98A1972915046227', X'AA880DB45060CD4881F0A9D882FE4D7B', X'AA880DB45060CD4881F0A9D882FE4D7B', 3, NULL, NULL, 2, NULL, 0, 'Lock the lid in place and turn the steam release knob to the sealing position. Choose the manual or pressure cook button (high pressure) and set the timer for 10 minutes. When the time is up, let the pressure come down for 5 minutes and then carefully turn the knob to the venting position to release the remaining pressure.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14B054C99DD4EA4B994BB2C96A7E84F2', X'AA880DB45060CD4881F0A9D882FE4D7B', X'AA880DB45060CD4881F0A9D882FE4D7B', 4, NULL, NULL, 2, NULL, 0, 'In a saucepan, combine the hot sauce, butter, vinegar, black pepper, garlic powder, and cayenne pepper (if using). Bring to a simmer and cook for 1 minute.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78B241FA0199264F8E73B8FBF3827431', X'AA880DB45060CD4881F0A9D882FE4D7B', X'AA880DB45060CD4881F0A9D882FE4D7B', 5, NULL, NULL, 2, NULL, 0, 'Drain the wings and transfer them to a bowl. Add about half of the sauce mixture and toss them gently.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37F8A3E9FC2F23459766D3331CE74D1B', X'AA880DB45060CD4881F0A9D882FE4D7B', X'AA880DB45060CD4881F0A9D882FE4D7B', 6, NULL, NULL, 2, NULL, 0, 'Arrange the wings in a single layer on the prepared baking sheets. Broil about 4 to 6 inches from the heat source for 5 minutes. Turn the wings and continue to broil the other side for 4 to 5 minutes longer, or until golden brown.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D658B8675CA45047A142069BC324A38D', X'AA880DB45060CD4881F0A9D882FE4D7B', X'AA880DB45060CD4881F0A9D882FE4D7B', 7, NULL, NULL, 2, NULL, 0, 'Remove the wings to a bowl and drizzle with the remaining sauce mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74F93ACD3B89A347839C3AA100EE0FE1', X'AA880DB45060CD4881F0A9D882FE4D7B', X'AA880DB45060CD4881F0A9D882FE4D7B', 8, NULL, NULL, 2, NULL, 0, 'Serve Buffalo wings with celery and carrot sticks along with blue cheese or ranch dressing for dipping.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B94E6E550F2B554EA2A04DB8FAB0B6BA', X'1CA77E182CF22145A0374E8DC06C9253', X'1CA77E182CF22145A0374E8DC06C9253', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat the oven to 375F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E95171E5ABEB6E499B956011A14DE8EC', X'1CA77E182CF22145A0374E8DC06C9253', X'1CA77E182CF22145A0374E8DC06C9253', 1, NULL, NULL, 2, NULL, 0, 'Lay each skin down on a non-stick baking sheet, and spread them out so they take up as much space as possible without overlapping. Sprinkle with onion powder, then lay a sprig of fresh herbs (or an individual leaf of fresh sage) on top of each one, if using.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D09A1B082394F04EA80D25ED3C53DD28', X'1CA77E182CF22145A0374E8DC06C9253', X'1CA77E182CF22145A0374E8DC06C9253', 2, NULL, NULL, 2, NULL, 0, 'Lay a second non-stick baking sheet on top of the first, tightly sandwiching the skins.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C5D659A4A5D0B41847A2FC17936F242', X'1CA77E182CF22145A0374E8DC06C9253', X'1CA77E182CF22145A0374E8DC06C9253', 3, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes. You should be able to hear sizzling and popping as the skin renders.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0EAC3E6F70542449617D250F94C1793', X'1CA77E182CF22145A0374E8DC06C9253', X'1CA77E182CF22145A0374E8DC06C9253', 4, NULL, NULL, 2, NULL, 0, 'Remove the pans and let skins cool for several minutes before lifting off the top pan. If you''re not using a non-stick pan, allow the skins to sit for 15 minutes after cooking. As they cool, they''ll release from the surface naturally (or with minimal help from a spatula).');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04EF08F46936F245A46C7DE87BC77BC0', X'1CA77E182CF22145A0374E8DC06C9253', X'1CA77E182CF22145A0374E8DC06C9253', 5, NULL, NULL, 2, NULL, 0, 'Carefully remove the skins from the sheet  they''ll be fragile  transfer to a paper towel-lined plate, and season with Kosher salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A82C2DBF0498FE49B2FCF656535CE153', X'1CA77E182CF22145A0374E8DC06C9253', X'1CA77E182CF22145A0374E8DC06C9253', 6, NULL, NULL, 2, NULL, 0, 'Keep skins whole for use as chips, crackers, or sandwich filling, or crumble for use as a garnish. Keep any leftovers in a tightly sealed container in a cool, dry place for up to three days.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F4CF2DD9C0C284CA9C118E034D4A1FC', X'C9118A66F69B5F41BD76F53273ABB5FC', X'C9118A66F69B5F41BD76F53273ABB5FC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2863827660E807429168F2563C7C33E6', X'C9118A66F69B5F41BD76F53273ABB5FC', X'C9118A66F69B5F41BD76F53273ABB5FC', 1, NULL, NULL, 2, NULL, 0, 'Dry the chicken breasts with paper towels to dry. Sprinkle the chicken breasts with salt and pepper and arrange them in the Instant Pot. Add the chicken stock, garlic, bay leaf, and herbs, if using.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCCDFDA36598DA44B90685BF2AB91AE6', X'C9118A66F69B5F41BD76F53273ABB5FC', X'C9118A66F69B5F41BD76F53273ABB5FC', 2, NULL, NULL, 2, NULL, 0, 'Secure the Instant Pot lid and turn the pressure release knob to the sealing position. Choose pressure cook or manual, high pressure, and set the timer for 7 minutes. It will take approximately 10 minutes for pressure to build and the cooking to start.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCA2FF2D529D994AAAFD311F5CE990FA', X'C9118A66F69B5F41BD76F53273ABB5FC', X'C9118A66F69B5F41BD76F53273ABB5FC', 3, NULL, NULL, 2, NULL, 0, 'When the time is up, allow a natural release for 5 minutes and then carefully turn the knob to the venting position to release the remaining pressure.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55FFCE37E4BB1741987F5A43E3863613', X'C9118A66F69B5F41BD76F53273ABB5FC', X'C9118A66F69B5F41BD76F53273ABB5FC', 4, NULL, NULL, 2, NULL, 0, 'Remove the chicken breasts to a platter or cutting board and let them rest for 5 minutes. Slice or shred the chicken and then strain the broth to use for a sauce or save it for another recipe.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9817B60E1B0FB439F499B3BFFFAA44A', X'C9118A66F69B5F41BD76F53273ABB5FC', X'C9118A66F69B5F41BD76F53273ABB5FC', 5, NULL, NULL, 2, NULL, 0, 'To serve the chicken as an entre, plate with rice or noodles and some of the broth along with a side vegetable or salad. Add a little color to the chicken with a sprinkling of paprika or drizzle with soy sauce or liquid aminos.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFF295F5BF8F9C4BA617718E188FE892', X'2E30BF40A30FD444A317955EBE1F07D3', X'2E30BF40A30FD444A317955EBE1F07D3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8A9FBE502E8644DACB2935A9A363911', X'2E30BF40A30FD444A317955EBE1F07D3', X'2E30BF40A30FD444A317955EBE1F07D3', 1, NULL, NULL, 2, NULL, 0, 'Add the chicken, vegetables, garlic, thyme, parsley, bay leaves, and peppercorns. If you are making a salted broth, add the salt (you can always add the salt later).');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63571DD9416C2F4E9CC2003AB4D555FC', X'2E30BF40A30FD444A317955EBE1F07D3', X'2E30BF40A30FD444A317955EBE1F07D3', 2, NULL, NULL, 2, NULL, 0, 'Add about 6 cups of water to the pot. Make sure the ingredients are not above the max fill line, which is 1 3/4 inches below the rim of the pot.Older cookers have a higher max fill line, but the cooker should never be filled more than the 2/3 mark (approximately 1 3/4 inches from the rim). If too full, the liquids will splatter as the pressure is released. To avoid spatter, continue the natural release until the float valve drops.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C72589AFB7711044887779BC62A4FD08', X'2E30BF40A30FD444A317955EBE1F07D3', X'2E30BF40A30FD444A317955EBE1F07D3', 3, NULL, NULL, 2, NULL, 0, 'Secure the lid and ensure the steam release vent is in the sealing position. Choose manual/pressure cook, high pressure, and set the timer for 60 minutes. When the time is up, let the pressure come down naturally for 30 minutes or longer. Carefully release the remaining pressure.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9152301CBE76304AAEA4FEE335695BB9', X'2E30BF40A30FD444A317955EBE1F07D3', X'2E30BF40A30FD444A317955EBE1F07D3', 4, NULL, NULL, 2, NULL, 0, 'Strain the stock and discard the solids; transfer the liquids to a fat separator, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0C46CDF0E5C7444A451263081422BB3', X'2E30BF40A30FD444A317955EBE1F07D3', X'2E30BF40A30FD444A317955EBE1F07D3', 5, NULL, NULL, 2, NULL, 0, 'For a clearer stock, strain once again in a mesh strainer lined with several thicknesses of cheesecloth.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D202B26BCE73A448894F4796551A6FBA', X'2E30BF40A30FD444A317955EBE1F07D3', X'2E30BF40A30FD444A317955EBE1F07D3', 6, NULL, NULL, 2, NULL, 0, 'Use the stock for soups, stews, braises, or as a cooking liquid for rice and grains.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4AC7E2DB45D324994A6E3A526164B4B', X'A336C7FC13AB4045876CDE9CE8350ED9', X'A336C7FC13AB4045876CDE9CE8350ED9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Set the oven to broil.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'640D15F01F5C2E428033D4F6E5BB39D8', X'A336C7FC13AB4045876CDE9CE8350ED9', X'A336C7FC13AB4045876CDE9CE8350ED9', 1, NULL, NULL, 2, NULL, 0, 'Place the sliced onions in a cast-iron skillet and put the whole Roma tomatoes on top. Place under the broiler close to the flame. Turn them periodically so that the tomatoes have a light char on all sides.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8454DF0852468040B4AD8A0E03098744', X'A336C7FC13AB4045876CDE9CE8350ED9', X'A336C7FC13AB4045876CDE9CE8350ED9', 2, NULL, NULL, 2, NULL, 0, 'Remove from the oven and place the tomatoes and onions into a large bowl. Add the chipotles, chopped garlic, and bay leaves. Toss to combine.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20721106B0658340AC81A60DFB574DCC', X'A336C7FC13AB4045876CDE9CE8350ED9', X'A336C7FC13AB4045876CDE9CE8350ED9', 3, NULL, NULL, 2, NULL, 0, 'Put the skillet you used for the tomatoes on the stove over a high flame. Add the oil. When hot but not smoking, add the chicken thighs. If necessary, cook the chicken in batches to avoid overcrowding the pan. To brown well, keep the meat in a single layer and leave undisturbed for a couple of minutes before turning.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A4781E3B0E1534B8844ACE3D092B669', X'A336C7FC13AB4045876CDE9CE8350ED9', X'A336C7FC13AB4045876CDE9CE8350ED9', 4, NULL, NULL, 2, NULL, 0, 'Add the browned (but not cooked through) chicken to a slow cooker. Scrape all the pan juices into the pot as well. Add the tomato, onion, garlic, chipotles, and bay leaves on top.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1F6123AC469004DA46815FCD51AFD9D', X'A336C7FC13AB4045876CDE9CE8350ED9', X'A336C7FC13AB4045876CDE9CE8350ED9', 5, NULL, NULL, 2, NULL, 0, 'Put the lid on the slow cooker and cook on the low setting for about six hours.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A15A05AB8838C4E91C9AD7B572A329F', X'A336C7FC13AB4045876CDE9CE8350ED9', X'A336C7FC13AB4045876CDE9CE8350ED9', 6, NULL, NULL, 2, NULL, 0, 'Stir the contents of the crockpot and remove the bay leaves. All the ingredients will be quite soft. As you stir, the chicken will shred. You may have to exert a little effort breaking up the tomatoes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BE1934495C5904E8AC0B215C22BCE8E', X'A336C7FC13AB4045876CDE9CE8350ED9', X'A336C7FC13AB4045876CDE9CE8350ED9', 7, NULL, NULL, 2, NULL, 0, 'Season the stew with a splash of vinegar and salt to taste. Serve immediately.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6DC2FA165D0AE4991638A87FEB0C504', X'3FC6897C3AF31845BB6F667443DD9F36', X'3FC6897C3AF31845BB6F667443DD9F36', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'808268EB8DB5BE4DB7685B76808EB62C', X'3FC6897C3AF31845BB6F667443DD9F36', X'3FC6897C3AF31845BB6F667443DD9F36', 1, NULL, NULL, 2, NULL, 0, 'Turn on the saut function on the Instant Pot (medium heat). Once hot, add the butter to the inner pot and melt. Add the onion, celery, and carrots and saut for a few minutes, or until the onions are turning translucent.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98327FBD05508E4FA727AC0039414822', X'3FC6897C3AF31845BB6F667443DD9F36', X'3FC6897C3AF31845BB6F667443DD9F36', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic and oregano and stir; turn off the saut function. Add the bay leaf and the chicken thighs in a single layer. Add the broth and water and season with salt and pepper. Secure the lid.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AE239D183AF1E4E9A27F827582BAB26', X'3FC6897C3AF31845BB6F667443DD9F36', X'3FC6897C3AF31845BB6F667443DD9F36', 3, NULL, NULL, 2, NULL, 0, 'Cook at high pressure for 6 minutes and use a natural release. Depending on your model of Instant Pot, don''t forget to close the steam release valve before pressure cooking.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAFF9F57B9153D47BB5034F7728A2BD9', X'3FC6897C3AF31845BB6F667443DD9F36', X'3FC6897C3AF31845BB6F667443DD9F36', 4, NULL, NULL, 2, NULL, 0, 'Once the pressure has released naturally, remove the lid. Using a slotted spoon or tongs, remove the chicken to a plate. Remove the bay leaf. Shred the chicken and return to the pot. Turn on the saut function (medium heat).');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE95EA6125F1814991D57629F43E5982', X'3FC6897C3AF31845BB6F667443DD9F36', X'3FC6897C3AF31845BB6F667443DD9F36', 5, NULL, NULL, 2, NULL, 0, 'Once simmering, add the egg noodles and stir to submerge. Cook for about 5 minutes, uncovered, or until the noodles are cooked through. Carefully remove the inner pot from the Instant Pot and add the parsley and lemon juice. Taste for seasoning and add salt and pepper as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFF9D0ED1E4998429593173889A92405', X'3FC6897C3AF31845BB6F667443DD9F36', X'3FC6897C3AF31845BB6F667443DD9F36', 6, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'392A318E3565AE4192682D114B2011F7', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A84000E1E6A9574E820D76506C11B313', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the orange zest, lime zest, orange juice, lime juice, olive oil, cilantro, garlic cloves, jalapeno (if using), oregano, cumin, salt, and black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C3F437AB3DA48479399F4FDE12CAC7B', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', 2, NULL, NULL, 2, NULL, 0, 'Place the chicken into the marinade, cover with plastic wrap, and refrigerate for at least 4 hours or overnight. You can also do this in a large zip-top bag, but be sure to place the bag into a large bowl to safeguard against drips.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'271849294D0FDD4A8AB2BEA86FB16B17', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', 3, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3643BE87F8898043B1B686CE7516991F', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', 4, NULL, NULL, 2, NULL, 0, 'Remove the chicken from the marinade and place on a large roasting pan. If you like, tie the legs together with kitchen string and tuck the wing tips under the body to help prevent them from darkening too much.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D576ECE4F845CA48AFE687A9951EF697', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', 5, NULL, NULL, 2, NULL, 0, 'Roast in the oven for approximately 1 1/2 hours, or until the thickest part of the thigh reaches 165 F on a meat thermometer. If the breast starts to get too dark while roasting, cover loosely with a sheet of aluminum foil.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'511A77B324ABD743B438EF5A442205DC', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', 6, NULL, NULL, 2, NULL, 0, 'Remove from the oven, cover loosely with aluminum foil, and allow to rest for about 10 minutes before carving.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5898B0C43E756741AF0BD595EBBEF761', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', X'21EA120BD3B6FA4DACBA4FD0C02EE4AF', 7, NULL, NULL, 2, NULL, 0, 'Garnish with orange and lime slices (if using) and serve with rice and black beans.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D57546B48061F488FC9E9C6CF0A4CD2', X'37FF34FDF0C0374697DC4CD58F8EFB2C', X'37FF34FDF0C0374697DC4CD58F8EFB2C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B148D33454695C409FD944B6380BEDE3', X'37FF34FDF0C0374697DC4CD58F8EFB2C', X'37FF34FDF0C0374697DC4CD58F8EFB2C', 1, NULL, NULL, 2, NULL, 0, 'Chop the onion into a small dice. Add the chicken breasts and thighs to the pot of the Instant Pot. Add the onion, garlic, olive oil, spices, and chipotle peppers to the pot. Toss all of the ingredients with the chicken to coat completely.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34AE72425E5F284EBCC73236D3B0825F', X'37FF34FDF0C0374697DC4CD58F8EFB2C', X'37FF34FDF0C0374697DC4CD58F8EFB2C', 2, NULL, NULL, 2, NULL, 0, 'Cook the chicken on the meat setting for 30 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E93C2ABD06AD6B43B84A5F59EDAEE327', X'37FF34FDF0C0374697DC4CD58F8EFB2C', X'37FF34FDF0C0374697DC4CD58F8EFB2C', 3, NULL, NULL, 2, NULL, 0, 'Top warmed corn tortillas with the pulled chicken, chopped cilantro, and chopped onion. Add taco sauce or a shredded cheese if you wish!');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7573EE4F1704984685AE9F3273D3CE1B', X'A09FD950B82C1A42BB16B4EF0C289972', X'A09FD950B82C1A42BB16B4EF0C289972', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEF35F7CF8F83B4288BB9164FDB7F77B', X'A09FD950B82C1A42BB16B4EF0C289972', X'A09FD950B82C1A42BB16B4EF0C289972', 1, NULL, NULL, 2, NULL, 0, 'Add the chicken in a single layer to a deep skillet. Add the onion, garlic, and broth. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C600B0BDAC75146904C22592DCCEB63', X'A09FD950B82C1A42BB16B4EF0C289972', X'A09FD950B82C1A42BB16B4EF0C289972', 2, NULL, NULL, 2, NULL, 0, 'Over medium heat, barely bring to a simmer and reduce the heat to low. Partially cover. Skim any foam that forms off the top and flip the chicken over if needed. Cook the chicken breasts for 10 to 15 minutes, or until just cooked through. Do not overcook. Cook thighs at a low simmer for 30 minutes, or until tender and cooked through. Drain and reserve the broth, discarding the onion and garlic.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BFC2EE7473CB74AA439DF1805A24778', X'A09FD950B82C1A42BB16B4EF0C289972', X'A09FD950B82C1A42BB16B4EF0C289972', 3, NULL, NULL, 2, NULL, 0, 'Shred the meat with two forks or let cool enough to shred with your hands. Add 1/4 cup of the reserved broth (save the rest for soup), chili powder, cumin, and paprika to the pan over medium heat. Stir and cook until the broth is simmering. Add the shredded chicken and stir well. Turn off the heat.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BAE3332A9D693439AAA1A3BC4E083A5', X'A09FD950B82C1A42BB16B4EF0C289972', X'A09FD950B82C1A42BB16B4EF0C289972', 4, NULL, NULL, 2, NULL, 0, 'Add the lime juice, stir, and taste for seasoning. Add salt and pepper as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EB10BC09000F2458C3FDAE4A64A8466', X'A09FD950B82C1A42BB16B4EF0C289972', X'A09FD950B82C1A42BB16B4EF0C289972', 5, NULL, NULL, 2, NULL, 0, 'Serve on warm tortillas with your choice of toppings.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35556E3AD50CA24AA88BEBD1A9FBD935', X'D0C5F52197FF9946A6465A6F1CE500BB', X'D0C5F52197FF9946A6465A6F1CE500BB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA610A059B475C4FBBFA2CD49D1B19B8', X'D0C5F52197FF9946A6465A6F1CE500BB', X'D0C5F52197FF9946A6465A6F1CE500BB', 1, NULL, NULL, 2, NULL, 0, 'Start by making the rub. Place all the rub ingredients in a food processor and blitz to create a dark, lump-free powder. Pour into a small bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC7171727AFF364D84BE291B6EFE60EE', X'D0C5F52197FF9946A6465A6F1CE500BB', X'D0C5F52197FF9946A6465A6F1CE500BB', 2, NULL, NULL, 2, NULL, 0, 'Dry the chicken breasts with kitchen paper. Place in a large dish, pour enough spice rub over the breasts to coat both sides of the chicken breasts. Cover the dish with a kitchen towel and set aside for 30 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BF6C955AE0C2B4D8B918232E0B565AE', X'D0C5F52197FF9946A6465A6F1CE500BB', X'D0C5F52197FF9946A6465A6F1CE500BB', 3, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D143D74A344FB0438272323EFC6FB7A0', X'D0C5F52197FF9946A6465A6F1CE500BB', X'D0C5F52197FF9946A6465A6F1CE500BB', 4, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a non-stick ovenproof pan to hot, but not smoking. Carefully remove the chicken breasts one at a time from the dish. Shake gently to remove any loose rub, then place in the pan to sear. Cook for 3 minutes on each side, then place the pan in the center of the oven.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEE5102B664ADC49ACA77321A15D2684', X'D0C5F52197FF9946A6465A6F1CE500BB', X'D0C5F52197FF9946A6465A6F1CE500BB', 5, NULL, NULL, 2, NULL, 0, 'Bake the chicken for 15 minutes or until cooked by checking using a meat thermometer reading 165 F or press the breast firmly and it should feel solid to the touch.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'647257480415DF46977CFAB0A80E6774', X'D0C5F52197FF9946A6465A6F1CE500BB', X'D0C5F52197FF9946A6465A6F1CE500BB', 6, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from the oven, cover with a tea cloth and leave to rest for 10 minutes, which will soften the chicken and make it more tender. Slice the chicken into even slices and serve over hot white rice (or pasta if you prefer) sprinkled with chili flakes and a little lime zest.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA4E641911404B46BCB1E5EB69D0BA87', X'D0C5F52197FF9946A6465A6F1CE500BB', X'D0C5F52197FF9946A6465A6F1CE500BB', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4C2EF5FCCF7484C9F19FFC61AED627B', X'668CED1154C7744E92D7382504C048BE', X'668CED1154C7744E92D7382504C048BE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D0F536F767E8A408E3E26B37A377C6A', X'668CED1154C7744E92D7382504C048BE', X'668CED1154C7744E92D7382504C048BE', 1, NULL, NULL, 2, NULL, 0, 'Split the chicken breasts in half, so that you have two thinner cutlets. Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'471CD7394D3D0C43A649ED26BF451A44', X'668CED1154C7744E92D7382504C048BE', X'668CED1154C7744E92D7382504C048BE', 2, NULL, NULL, 2, NULL, 0, 'Pound each breast until they are around a quarter-inch thick.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4017E2307C88044C91597DD2E028D406', X'668CED1154C7744E92D7382504C048BE', X'668CED1154C7744E92D7382504C048BE', 3, NULL, NULL, 2, NULL, 0, 'Place the potato chips in a plastic bag and press out all of the air. Use a rolling pin to crush the potato chips. Place them in a shallow bowl until you are ready to dip them.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'525C5911F346D9459C48B44908495444', X'668CED1154C7744E92D7382504C048BE', X'668CED1154C7744E92D7382504C048BE', 4, NULL, NULL, 2, NULL, 0, 'Season the flour with the garlic powder, salt, and pepper. Whisk to evenly distribute the spices. Dip the chicken into the flour mixture and coat both sides.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5780F1235E2FA499328348A79F1ADB4', X'668CED1154C7744E92D7382504C048BE', X'668CED1154C7744E92D7382504C048BE', 5, NULL, NULL, 2, NULL, 0, 'Whisk the eggs until very well beaten. Dip the chicken into the egg mixture and coat on both sides.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFB48C7F25C7FC47BD6574B9B00DC25D', X'668CED1154C7744E92D7382504C048BE', X'668CED1154C7744E92D7382504C048BE', 6, NULL, NULL, 2, NULL, 0, 'Place the chicken into the potato chips and press the chips all over the chicken on both sides.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C4E47C295387F44965CD5FA7BA4C05A', X'668CED1154C7744E92D7382504C048BE', X'668CED1154C7744E92D7382504C048BE', 7, NULL, NULL, 2, NULL, 0, 'Put a cooling rack on top of a baking sheet. Place the chicken onto the rack. You may need two baking sheets to fit all of the chicken.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AE773932237F74C8A3070CDF81CFC7E', X'668CED1154C7744E92D7382504C048BE', X'668CED1154C7744E92D7382504C048BE', 8, NULL, NULL, 2, NULL, 0, 'Place the chicken in the oven and bake for about twenty minutes or until crunchy and cooked through. In the summer, serve with a fresh salad and dipping sauce. Or serve with rice or mashed potatoes and a steamed vegetable in colder weather.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54E3FB08F01D5840845B0B7B0FF56400', X'D2D7681CD0432342B9137B99FDC085C4', X'D2D7681CD0432342B9137B99FDC085C4', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'659260C1EB35A947A77A73BE2B875DD8', X'D2D7681CD0432342B9137B99FDC085C4', X'D2D7681CD0432342B9137B99FDC085C4', 1, NULL, NULL, 2, NULL, 0, 'Place the chicken pieces in a large kettle or Dutch oven, then add the chicken stock, water, chopped onion, carrots, celery, garlic, salt, pepper, thyme, parsley, and rosemary.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A874DF046E8724DBD357080DA0F2507', X'D2D7681CD0432342B9137B99FDC085C4', X'D2D7681CD0432342B9137B99FDC085C4', 2, NULL, NULL, 2, NULL, 0, 'Place the pan over medium-high heat and bring to a boil. Reduce the heat to low and cover the pan. Simmer the chicken for 35 to 45 minutes, or until the chicken is cooked. Remove the chicken to a bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F28A068894F6E469A6B3A7DFEBFCF30', X'D2D7681CD0432342B9137B99FDC085C4', X'D2D7681CD0432342B9137B99FDC085C4', 3, NULL, NULL, 2, NULL, 0, 'Strain the broth; discard the solids and return the liquids to the pan. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E30CD0EF1DBCB44A3BCB5CE2CD0FB47', X'D2D7681CD0432342B9137B99FDC085C4', X'D2D7681CD0432342B9137B99FDC085C4', 4, NULL, NULL, 2, NULL, 0, 'When the chicken is cool enough to handle, remove the meat from the bones. Discard the skin and return the bones to the broth.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74C14CF8CC662342ACBBB687B9041271', X'D2D7681CD0432342B9137B99FDC085C4', X'D2D7681CD0432342B9137B99FDC085C4', 5, NULL, NULL, 2, NULL, 0, 'Bring the broth with the bones to a simmer; reduce the heat to low and simmer for 10 to 15 minutes, or until reduced; the liquids should measure about 3 cups. Taste the broth and adjust the seasoning, as needed. Remove and discard the bones. If desired, remove some of the fat from the broth with a fat separator.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E70BA9360BBA4048BD181D40634327E8', X'D2D7681CD0432342B9137B99FDC085C4', X'D2D7681CD0432342B9137B99FDC085C4', 6, NULL, NULL, 2, NULL, 0, 'Add the chicken back to the broth along with the rice, sliced sausage, and green onion. Continue to cook for 20 to 25 minutes, or until the rice is tender.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90B125FFD46A9441AB027E152285370B', X'D2D7681CD0432342B9137B99FDC085C4', X'D2D7681CD0432342B9137B99FDC085C4', 7, NULL, NULL, 2, NULL, 0, 'Stir to fluff the rice and serve immediately. Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'081E8234A3FDEC48A9FB7DA82500CBA1', X'297EC092E8F06F48BF69B3E1E87D37FE', X'297EC092E8F06F48BF69B3E1E87D37FE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Prepare a grill or preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E41C336E76DD8842AA48260BFF306E20', X'297EC092E8F06F48BF69B3E1E87D37FE', X'297EC092E8F06F48BF69B3E1E87D37FE', 1, NULL, NULL, 2, NULL, 0, 'Place the chicken, with its backbone removed, on a plastic cutting board skin-side down. Using your hands, press down hard to make the chicken as flat as possible, focusing on the breastbone to flatten it.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF8551465BC1DD4A8CF130111510FF72', X'297EC092E8F06F48BF69B3E1E87D37FE', X'297EC092E8F06F48BF69B3E1E87D37FE', 2, NULL, NULL, 2, NULL, 0, 'Combine the olive oil, fresh thyme, dried rosemary, garlic, salt and pepper and rub generously all over the chicken. Tuck the wings slightly under breast.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04AE4917D32DC84DA589576C7CA6786B', X'297EC092E8F06F48BF69B3E1E87D37FE', X'297EC092E8F06F48BF69B3E1E87D37FE', 3, NULL, NULL, 2, NULL, 0, 'Place the chicken, skin-side down, on the grill or, if using an oven, on a baking sheet or in a large cast iron pan.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDF6AAFDF70AF74D8BC65B8DA73EA4B5', X'297EC092E8F06F48BF69B3E1E87D37FE', X'297EC092E8F06F48BF69B3E1E87D37FE', 4, NULL, NULL, 2, NULL, 0, 'Wrap the bricks with aluminum foil to prevent brick dust from contaminating the food. Set one brick on top of each side of the chicken. The bricks will help to further flatten the chicken with their weight and allow it to cook evenly and quickly.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5318EC3096CC2448B1C87FF4F37EA1B', X'297EC092E8F06F48BF69B3E1E87D37FE', X'297EC092E8F06F48BF69B3E1E87D37FE', 5, NULL, NULL, 2, NULL, 0, 'Roast in the oven for about 20 minutes. Remove the bricks, flip the chicken so that the breast and skin are now up, and continue cooking for another 10 to 15 minutes, or until the skin is golden brown and crisp. Use an instant-read thermometer, inserted into the thickest part of the thigh, to check that the bird has reached the safe temperature 165 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F88164F48D2D3C4BB99F947597D15C8D', X'297EC092E8F06F48BF69B3E1E87D37FE', X'297EC092E8F06F48BF69B3E1E87D37FE', 6, NULL, NULL, 2, NULL, 0, 'Carve and enjoy.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'120581CB27CADA45AEB536B979A02A67', X'4A1562706FE8954F90BF819397EDBADD', X'4A1562706FE8954F90BF819397EDBADD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA7DDFD7FAD0D9488651E20C7DA11437', X'4A1562706FE8954F90BF819397EDBADD', X'4A1562706FE8954F90BF819397EDBADD', 1, NULL, NULL, 2, NULL, 0, 'Season the chicken breast with dried oregano, salt, and pepper. Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5927414B9BB3CE469EFE1B00B961A45B', X'4A1562706FE8954F90BF819397EDBADD', X'4A1562706FE8954F90BF819397EDBADD', 2, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large cast-iron skillet or saut pan on medium-high heat. Add the chicken to the pan and brown it on both sides for three to five minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C790E888CFFF44A9E6F80384D878015', X'4A1562706FE8954F90BF819397EDBADD', X'4A1562706FE8954F90BF819397EDBADD', 3, NULL, NULL, 2, NULL, 0, 'Remove the chicken from the pan and place it on a plate. Turn down the heat, add the white wine to the pan, and deglaze it by scraping at the browned bits on the bottom. Add the butter, garlic and lemon juice as well. Cook for two to three minutes, or until the butter is completely melted and the garlic is fragrant.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8B8B360BA059540B707E59FA292F75C', X'4A1562706FE8954F90BF819397EDBADD', X'4A1562706FE8954F90BF819397EDBADD', 4, NULL, NULL, 2, NULL, 0, 'Add the chicken back to the pan and top each piece with an equal amount of the fresh mozzarella. Place it in the preheated oven until the top of the cheese is melted and slightly browned.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB60AC3F5D094A4FBDB416FBB58B897D', X'4A1562706FE8954F90BF819397EDBADD', X'4A1562706FE8954F90BF819397EDBADD', 5, NULL, NULL, 2, NULL, 0, 'Top each chicken breast with an equal amount of the pesto, cherry tomatoes, and Parmesan cheese. Serve immediately with a side salad, pasta, or bread to make it a complete meal.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38A9391CE41B3841B7234DF2157112FB', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A51F9EF671FA64DBC29EDC50FABE836', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F. Split the chicken breasts with a chefs knife. Be careful not to cut all the way through.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28370ECCBDA2F644A1EC751A4AA76C21', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', 2, NULL, NULL, 2, NULL, 0, 'Stuff the center with a 1/4 cup of the shredded mozzarella.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84176406EE59C6469B48FA99DE5AF7B4', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', 3, NULL, NULL, 2, NULL, 0, 'Fold the top half of the chicken over the top of the mozzarella stuffing. If it is too full and the edges dont seal, take a little of the mozzarella cheese out.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'258D0ED2AC809A4CBAA148B2C89B1FE7', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', 4, NULL, NULL, 2, NULL, 0, 'Prepare your breading station. Toss some of the salt and pepper with the flour in a shallow bowl. Beat the eggs with a little salt and pepper in another shallow bowl. Toss the breadcrumbs with salt and pepper and Parmesan cheese in a third shallow bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0969C0D1EBE88419CAF68DEE2F67378', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', 5, NULL, NULL, 2, NULL, 0, 'Dip the stuffed chicken breast into the flour, then flip to coat both sides in the flour mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E475FFD532E6F74CA6C2B837FF45785E', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', 6, NULL, NULL, 2, NULL, 0, 'Dip the flour coated chicken into the egg mixture. Flip to coat both sides.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80D53D8D06E8004284F718542E181B38', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', 7, NULL, NULL, 2, NULL, 0, 'Dip the egg coated chicken into the breadcrumbs and cover both sides thickly.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85BCB12FB9BCB64396A1EB11500E6ABD', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', 8, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large skillet. Add the chicken cutlets so that they fit snugly. but arent overlapping. If you need to do it in two pans, you can.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'552B644099635D4D96736685CBCD7A68', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', 9, NULL, NULL, 2, NULL, 0, 'Add the marinara sauce around the outside of the cutlets and a little on the top of each cutlet.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF0033BC7B47D84A91906C7C3CC4A55D', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', 10, NULL, NULL, 2, NULL, 0, 'Top the cutlets with a few slices of fresh mozzarella.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDD52C5D2DBBB34FA4BDABAD1F13F653', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', 11, NULL, NULL, 2, NULL, 0, 'Place in the preheated oven and bake for about 20 minutes or until the center reads 165 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA375CF212120E42951F251E21C4C89F', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', X'B3FA4E4F2E406E43915DCD07B8C1EBFB', 12, NULL, NULL, 2, NULL, 0, 'Serve the hot chicken with chopped parsley and Parmesan cheese on top of pasta with extra marinara sauce if you wish.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1E975F95353584C88A72DED5748AD42', X'5BB433BC89D40A4CBC760B9B07CDD26F', X'5BB433BC89D40A4CBC760B9B07CDD26F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'078B31D6701BB841AFFABF4DFAD1C0F9', X'5BB433BC89D40A4CBC760B9B07CDD26F', X'5BB433BC89D40A4CBC760B9B07CDD26F', 1, NULL, NULL, 2, NULL, 0, 'Rub the chicken breasts with the olive oil and place them on a sheet pan. Sprinkle with salt and pepper. Roast them in the oven for 35 to 40 minutes, until the chicken is cooked through. Set aside to cool.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F72026BC5CB5848AF8B901BF7D31213', X'5BB433BC89D40A4CBC760B9B07CDD26F', X'5BB433BC89D40A4CBC760B9B07CDD26F', 2, NULL, NULL, 2, NULL, 0, 'Once the chicken has cooled, dice or shred it with two forks.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76ECD801D849DC4694F82FAF06060FDE', X'5BB433BC89D40A4CBC760B9B07CDD26F', X'5BB433BC89D40A4CBC760B9B07CDD26F', 3, NULL, NULL, 2, NULL, 0, 'Make the dressing by whisking together the mayonnaise, Greek yogurt, fresh lemon juice, Dijon mustard and fresh chopped tarragon. Stir in the diced celery, diced onion and chicken. Taste and season with additional salt and pepper, if needed.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B4830EF5FBA6A4E80AE06B47254FCF4', X'5BB433BC89D40A4CBC760B9B07CDD26F', X'5BB433BC89D40A4CBC760B9B07CDD26F', 4, NULL, NULL, 2, NULL, 0, 'Serve the chicken salad on a bed of the mixed salad greens alongside crackers or toasted bread and fresh seedless grapes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C94E5323086113429B0E3A742399504D', X'867F2B1EB41A8A488243987B88655764', X'867F2B1EB41A8A488243987B88655764', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'278A5B01502ADA409CF56B7B49092417', X'867F2B1EB41A8A488243987B88655764', X'867F2B1EB41A8A488243987B88655764', 1, NULL, NULL, 2, NULL, 0, 'Add the oil to a saute pan or wok. Heat on high heat and add the chicken and half of the garlic. Do not disturb the chicken and let it cook on high heat for about 3 minutes, or until browned.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A384990C25822E40BD73BDBC5CB9D5FA', X'867F2B1EB41A8A488243987B88655764', X'867F2B1EB41A8A488243987B88655764', 2, NULL, NULL, 2, NULL, 0, 'Stir the chicken, rotating it to the other side to brown. Add in the diced onions. Cook them together stirring occasionally until the onions are softened and the chicken is cooked through.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C51C6C888891747A17CA4F436E1FC04', X'867F2B1EB41A8A488243987B88655764', X'867F2B1EB41A8A488243987B88655764', 3, NULL, NULL, 2, NULL, 0, 'Put the cornstarch, ginger, red pepper flakes, and remaining half of the garlic into a small bowl. Gradually whisk in the soy, broth, vinegar and brown sugar.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EF775AA18A99449AC639FFA0CA1CB2C', X'867F2B1EB41A8A488243987B88655764', X'867F2B1EB41A8A488243987B88655764', 4, NULL, NULL, 2, NULL, 0, 'Add the sauce to the pan and cook for two minutes, or until the sauce is thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7477EF10370AB44193C8A8DAF1B76287', X'867F2B1EB41A8A488243987B88655764', X'867F2B1EB41A8A488243987B88655764', 5, NULL, NULL, 2, NULL, 0, 'Toss the diced pineapple and chopped bell pepper into the chicken and sauce. Stir it until they are coated with sauce. Top it with scallions. Stir and cook for another minute or two. If you want the bell peppers softer you can toss them in with the chicken while it is cooking or cook the entire mixture for longer.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EBC9FA4774EB14CA832F610629AB933', X'867F2B1EB41A8A488243987B88655764', X'867F2B1EB41A8A488243987B88655764', 6, NULL, NULL, 2, NULL, 0, 'Serve with hot rice, more scallions, and crushed peanuts or sesame seeds if you wish.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6AF96DDE34D5345A4A3E911A659F929', X'5FF39CBB6B832745B1D9C647E99FA5A0', X'5FF39CBB6B832745B1D9C647E99FA5A0', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92D13AFC64547947B6D9661AB4D470AC', X'5FF39CBB6B832745B1D9C647E99FA5A0', X'5FF39CBB6B832745B1D9C647E99FA5A0', 1, NULL, NULL, 2, NULL, 0, 'Cook your pasta according to package directions. Reserve 1 1/2 cups of the cooking water just before you drain the pasta.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5AF7334B5075F4CB691CCEFA0E4E88D', X'5FF39CBB6B832745B1D9C647E99FA5A0', X'5FF39CBB6B832745B1D9C647E99FA5A0', 2, NULL, NULL, 2, NULL, 0, 'Season the chicken with about a teaspoon each of salt and pepper. Heat your olive oil in a saut pan on medium high heat and add the chicken. Spread out the chicken so that the pieces are not overlapping. You may need to do this step in two batches, depending on the size of your pan.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DA3A30C5A6A3E429663EF076D592790', X'5FF39CBB6B832745B1D9C647E99FA5A0', X'5FF39CBB6B832745B1D9C647E99FA5A0', 3, NULL, NULL, 2, NULL, 0, 'Use the same pan to cook the asparagus. Add the minced garlic and asparagus. Saut for about 2 minutes over medium-high heat, or until the asparagus is bright green but still firm and snappy. Remove the asparagus from the pan and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9775B36C43322147A4186467FE8C5534', X'5FF39CBB6B832745B1D9C647E99FA5A0', X'5FF39CBB6B832745B1D9C647E99FA5A0', 4, NULL, NULL, 2, NULL, 0, 'Add the reserved pasta water, chicken bouillon, lemon juice, and lemon zest to the pan. Bring to a simmer, then whisk in the mozzarella, cream cheese, and Parmesan cheese until the cheeses are melted and incorporated into the sauce. If you are adding heavy cream, add it now.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C95E63051118E4083207A56D2AEDBFC', X'5FF39CBB6B832745B1D9C647E99FA5A0', X'5FF39CBB6B832745B1D9C647E99FA5A0', 5, NULL, NULL, 2, NULL, 0, 'Add the cooked chicken, asparagus, and cooked pasta to the pan along with the chopped parsley, and toss with the sauce. Add lemon slices and more parsley just before serving.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D62AAE8322C8364FBDB0809A0831568F', X'5FF39CBB6B832745B1D9C647E99FA5A0', X'5FF39CBB6B832745B1D9C647E99FA5A0', 6, NULL, NULL, 2, NULL, 0, 'Garnish with extra Parmesan cheese and dig in!');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'588D118064A9594BB0C9CFFFB09C3AA5', X'A2B6CBEE30BC9C429F8D5A35706BDE47', X'A2B6CBEE30BC9C429F8D5A35706BDE47', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15E6D6A34BBF194B918541851A812D26', X'A2B6CBEE30BC9C429F8D5A35706BDE47', X'A2B6CBEE30BC9C429F8D5A35706BDE47', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the half cup of olive oil, a quarter cup of lemon juice, dried oregano, salt, and ground black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'496C28ED7CE3824C9D2C1AFB53B49A6A', X'A2B6CBEE30BC9C429F8D5A35706BDE47', X'A2B6CBEE30BC9C429F8D5A35706BDE47', 2, NULL, NULL, 2, NULL, 0, 'Reserve half in a separate bowl for the dressing. Add the chicken breasts in the remaining half of the marinade and make sure they are fully coated. Cover with plastic wrap and refrigerate for about an hour, but no more than 4 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C346E4D07E63E848B81BD6000463D5A7', X'A2B6CBEE30BC9C429F8D5A35706BDE47', X'A2B6CBEE30BC9C429F8D5A35706BDE47', 3, NULL, NULL, 2, NULL, 0, 'Heat an outdoor grill or indoor grill pan to approximately 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DF95EDEEC07D845B56C0DA281B2A9E2', X'A2B6CBEE30BC9C429F8D5A35706BDE47', X'A2B6CBEE30BC9C429F8D5A35706BDE47', 4, NULL, NULL, 2, NULL, 0, 'Grill the chicken on one side for about 5 minutes, flip, and grill for an additional 5 minutes or until it is cooked through and no longer pink.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4435FEAB3F0EC94686E3C4BDDA0F9AD2', X'A2B6CBEE30BC9C429F8D5A35706BDE47', X'A2B6CBEE30BC9C429F8D5A35706BDE47', 5, NULL, NULL, 2, NULL, 0, 'Slice the chicken lengthwise.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EF3E5F40425744D8A08EFA7D24176D7', X'A2B6CBEE30BC9C429F8D5A35706BDE47', X'A2B6CBEE30BC9C429F8D5A35706BDE47', 6, NULL, NULL, 2, NULL, 0, 'In a separate bowl, toss together the baby spinach or other salad greens, cherry tomatoes, sliced red onion, olives, and feta cheese. Pour on the dressing and toss together.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABD83EECD7099049BDBB441E3DBD6CA4', X'A2B6CBEE30BC9C429F8D5A35706BDE47', X'A2B6CBEE30BC9C429F8D5A35706BDE47', 7, NULL, NULL, 2, NULL, 0, 'Assemble the salad on a platter and top with the sliced, grilled chicken, and toasted pita bread.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F699806EE53E4D4BBFCAAED487565550', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73A1505BDD2CEB4EBB69196349F950E0', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 1, NULL, NULL, 2, NULL, 0, 'Warm the oil over very low heat in a heat-proof shallow dish or skillet. You want it very warm but not burning hot.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29EBDBEE819BC94FA1CF4C3DFD979B0B', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 2, NULL, NULL, 2, NULL, 0, 'Keep another heat-proof dish or skillet over low heat to keep the tortillas warm.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'075DAE2AE898B74C9055B98CF362E6E9', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 3, NULL, NULL, 2, NULL, 0, 'Dip each tortilla in the warm oil for 5 to 8 seconds until the tortilla is warmed through and pliable.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'845FA0770CF4E940AADCE669D45AD069', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 4, NULL, NULL, 2, NULL, 0, 'Set aside in the warming pan so they stay warm until you''re ready to use them.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2CBA445A7E65C4DB37F5B970B43FB75', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 5, NULL, NULL, 2, NULL, 0, 'Put the homemade or purchased enchilada sauce in a shallow dish or bowl so that you can easily dip the tortillas in it.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20C7F7980BDEAD40BEFCC5D9BEFACF9C', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 6, NULL, NULL, 2, NULL, 0, 'Take 1 tortilla and carefully dip it into the sauce until it is thoroughly coated');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35A4E2D53469F44A88C317658CE40198', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 7, NULL, NULL, 2, NULL, 0, 'Do the same with all the tortillas.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B624DEE46483E42BC8C5A291266CBB6', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 8, NULL, NULL, 2, NULL, 0, 'Lay the sauce-coated tortilla in the bottom of a 9- by 13-inch baking dish. Place 2 to 3 tablespoons of cheese or your filling of choice down the middle. If you''ve used a chicken or beef filling, top that with shredded cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'707BA0B87A312B46A483275B79923179', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 9, NULL, NULL, 2, NULL, 0, 'Fold one side over, then the other side.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B8FE240ECF7CB4A96AD885286BFB3E2', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 10, NULL, NULL, 2, NULL, 0, 'Turn the enchilada over, seam side down, so the tortilla stays closed, and place it in the baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B44D56B135DB75479076450F0A632B9F', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 11, NULL, NULL, 2, NULL, 0, 'Repeat with each tortilla until you have filled the dish. Depending on the amount of filling of used and the size of the tortillas, you will use between 10 and 14 tortillas.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3530A5817ABC64CAED7D652A89EAC5D', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 12, NULL, NULL, 2, NULL, 0, 'Pour the remaining sauce evenly over all of the enchiladas in the baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A41910791118AD4E9732DEEE09E9453D', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 13, NULL, NULL, 2, NULL, 0, 'Sprinkle any remaining cheese over the top of the enchiladas in the baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0BFF62B689EBD40A4932A04A3E71F79', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 14, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F. Bake for 20 to 30 minutes or until the cheese is melted and bubbly.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABB93A5F9D3BFC409152A504461594C6', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 15, NULL, NULL, 2, NULL, 0, 'If you can, let the enchiladas sit for 10 minutes to cool down before serving. Accompany them with a lettuce and tomato salad with crumbled Cotija cheese (the Mexican version of Parmesan cheese), guacamole, Mexican yellow rice, and refried beans.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F0D18354345F6428618E537AD9AEA4B', X'1903858AF3A1174DB9255581CAD6FFDF', X'1903858AF3A1174DB9255581CAD6FFDF', 16, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1E3445DAE2B3F459A5AF0E3E5DA71B4', X'D3858D72DC278A4090AB7708E0B8B2DA', X'D3858D72DC278A4090AB7708E0B8B2DA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 450 F. Generously brush an 11-inch by 17-inch baking sheet with olive oil.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4429D7D59AACF14196438B91258F0234', X'D3858D72DC278A4090AB7708E0B8B2DA', X'D3858D72DC278A4090AB7708E0B8B2DA', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, beat the egg, butter and hot sauce together.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'014169505262EA4A913EF9EEA8F9AA7B', X'D3858D72DC278A4090AB7708E0B8B2DA', X'D3858D72DC278A4090AB7708E0B8B2DA', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the chicken, garlic, pepper, salt, parsley, panko, and the egg mixture. Stir until well mixed using a rubber spatula or fork.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'654C205A40E5F44DA1187D77BFB41345', X'D3858D72DC278A4090AB7708E0B8B2DA', X'D3858D72DC278A4090AB7708E0B8B2DA', 3, NULL, NULL, 2, NULL, 0, 'Form the mixture into 20 equal sized balls. The mixture will be very wet and sticky; dabbing your fingertips in a bit of water helps form the balls.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75DFBA579EDD364BB2F7BE26B5231C01', X'D3858D72DC278A4090AB7708E0B8B2DA', X'D3858D72DC278A4090AB7708E0B8B2DA', 4, NULL, NULL, 2, NULL, 0, 'Arrange them in a single layer on the prepared baking sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7326D760CAA86545A65BE4D23346CD20', X'D3858D72DC278A4090AB7708E0B8B2DA', X'D3858D72DC278A4090AB7708E0B8B2DA', 5, NULL, NULL, 2, NULL, 0, 'Bake 15 to 20 minutes, turning once, until golden all over and chicken is cooked through (165 F on an instant read thermometer).');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79A4226F27DDF148B995C13416338B5B', X'D3858D72DC278A4090AB7708E0B8B2DA', X'D3858D72DC278A4090AB7708E0B8B2DA', 6, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk the yogurt, sour cream, chives, and lemon juice together.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E0BC805DD268640BF2200ADD9A1B55E', X'D3858D72DC278A4090AB7708E0B8B2DA', X'D3858D72DC278A4090AB7708E0B8B2DA', 7, NULL, NULL, 2, NULL, 0, 'Serve the meatballs with the ranch dipping sauce on the side.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39B8D76F177FA640BA916BA9BFBED4A8', X'4354A3E86031D74FA4ABB9EA30114B1D', X'4354A3E86031D74FA4ABB9EA30114B1D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'029879AF4D44B04ABFE92945CD99E8F5', X'4354A3E86031D74FA4ABB9EA30114B1D', X'4354A3E86031D74FA4ABB9EA30114B1D', 1, NULL, NULL, 2, NULL, 0, 'Put 3 tablespoons of the ghee or butter in the Instant Pot and choose the saut setting. When the fat is hot, add the onions. Cook, stirring frequently until the onion is softened. Add the garlic and ginger and cook for 1 minute longer. Add the garam masala, turmeric, paprika, kosher salt, coriander, cumin, and cayenne pepper; cook for 1 minute longer. Cancel the saut setting.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40A157AE062A544FB7B50C2874175703', X'4354A3E86031D74FA4ABB9EA30114B1D', X'4354A3E86031D74FA4ABB9EA30114B1D', 2, NULL, NULL, 2, NULL, 0, 'Add the chicken stock, then the tomatoes. Layer the boneless chicken thighs on the tomato layer; do not stir.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32BA722C8ABB9D448EE21D7A93CAFD13', X'4354A3E86031D74FA4ABB9EA30114B1D', X'4354A3E86031D74FA4ABB9EA30114B1D', 3, NULL, NULL, 2, NULL, 0, 'Lock the lid in place and turn the venting knob to the sealing position. Choose the manual setting, high pressure, and set it for 10 minutes. When the time is up, let the pressure release naturally for 15 minutes. Carefully release the remaining pressure and remove the Instant Pot lid.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CF5AF6E5EB91A46893A7FB09E05487C', X'4354A3E86031D74FA4ABB9EA30114B1D', X'4354A3E86031D74FA4ABB9EA30114B1D', 4, NULL, NULL, 2, NULL, 0, 'Remove the chicken thighs to a cutting board and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1632060A52E65D4CAC60498C3F9132F7', X'4354A3E86031D74FA4ABB9EA30114B1D', X'4354A3E86031D74FA4ABB9EA30114B1D', 5, NULL, NULL, 2, NULL, 0, 'Pure the sauce with an immersion blender. Add the tomato paste and set the pot on the low saut setting. Cook for about 3 to 5 minutes, or until the sauce is reduced slightly and thickened as desired. Stir in the remaining 3 tablespoons of butter and cream.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6418D37D6484A7488D2992DF4720EACC', X'4354A3E86031D74FA4ABB9EA30114B1D', X'4354A3E86031D74FA4ABB9EA30114B1D', 6, NULL, NULL, 2, NULL, 0, 'Cut the chicken thighs into bite-size pieces and return the pieces to the pot. Cook until hot. Stir in half of the chopped cilantro.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7FD6CCD503DB240B275379C51B83CDC', X'4354A3E86031D74FA4ABB9EA30114B1D', X'4354A3E86031D74FA4ABB9EA30114B1D', 7, NULL, NULL, 2, NULL, 0, 'Serve the butter chicken with hot cooked rice and garnish with the remaining cilantro.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C641E3F4EA1FEB45A2977A66CF46CE3C', X'4354A3E86031D74FA4ABB9EA30114B1D', X'4354A3E86031D74FA4ABB9EA30114B1D', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6B4C4A6E34656499B2A19D4211B24EB', X'91DF60AA1245D74D897655D71E94946B', X'91DF60AA1245D74D897655D71E94946B', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients. Place the spinach in a large pan, add 4 tablespoons cold water. Cover with a lid and place on medium heat for 5 minutes, shaking the pan from time to time. You only want the spinach to wilt in the pan, not steam or cook down totally. Once wilted, remove the lid, transfer the spinach to a sieve over a bowl and leave to drain and cool.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81379C60B7310C4C83B1720FCA5210E0', X'91DF60AA1245D74D897655D71E94946B', X'91DF60AA1245D74D897655D71E94946B', 1, NULL, NULL, 2, NULL, 0, 'Dry the chicken breasts with paper towels. Place them on a clean cutting board, lay a piece of plastic wrap over them, and flatten slightly with a rolling pin to create an even thickness across the breast. Carefully, with a sharp knife, cut a pocket into the side of each chicken breast about  of the way in and across, taking care not to cut right through. Place the chicken breasts on a plate and chill in the fridge while you make the filling.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81ADE11A17DF6F49BEE837AA9073EC3A', X'91DF60AA1245D74D897655D71E94946B', X'91DF60AA1245D74D897655D71E94946B', 2, NULL, NULL, 2, NULL, 0, 'Press the spinach in the sieve with the back of a spoon to squeeze away as much water as possible. Place the spinach, artichoke hearts, and mascarpone into a food processor and blitz for a few seconds to create a thick, creamy, green sauce. Add the lemon zest, juice and a tiny pinch of salt and pepper and stir through.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10E22A9AC5C34D4086316E63F0F1023E', X'91DF60AA1245D74D897655D71E94946B', X'91DF60AA1245D74D897655D71E94946B', 3, NULL, NULL, 2, NULL, 0, 'Take the chicken breasts from the fridge. Sprinkle a clean cutting board with one tablespoon of salt and one of pepper. Place the chicken breasts on the seasoning and press lightly, turn the breasts over and press again. Then, holding the breast open with one hand, carefully tuck one tablespoon of the filling deep into the pocket. Take care to not to get filling on the outside of the breast  if you do, wipe away with a paper towel. Secure the opening with a couple of toothpicks to hold in the filling.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'049271BFE98AEC4DA3E314B2572740A2', X'91DF60AA1245D74D897655D71E94946B', X'91DF60AA1245D74D897655D71E94946B', 4, NULL, NULL, 2, NULL, 0, 'Heat the vegetable oil in a large pan over high heat, and place the breasts in the pan. Lower the heat to medium  you do not want to cook the outside of the chicken too quickly, or it will burn before the center cooks. Cook for 6 minutes, turn the breasts over and cook for another 6 minutes. Remove chicken from the pan, lay on a warmed serving plate and cover to keep warm.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'508E6FC76D29E74BB93D1CE65CFFFE92', X'91DF60AA1245D74D897655D71E94946B', X'91DF60AA1245D74D897655D71E94946B', 5, NULL, NULL, 2, NULL, 0, 'Make the Sauce: To deglaze the pan for the sauce, put the pan with the juices from the chicken back on the stove top and heat until bubbling. Add the lemon juice and the mascarpone, and stir well, scraping up any bits of sauce and chicken. Lower the heat, add the cream, mix thoroughly and let the sauce simmer for a few minutes. Strain into a warm jug.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AD1C31284F7EA49A59E1C348BB7D702', X'91DF60AA1245D74D897655D71E94946B', X'91DF60AA1245D74D897655D71E94946B', 6, NULL, NULL, 2, NULL, 0, 'To serve, first, remove the toothpicks from the chicken, cut each breast in two and serve on warm plates with the sauce on the side and a green vegetable.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D561154916BDBD408386F6C0D01F21A5', X'0C6225C899A5B945B8E356819F53CBC9', X'0C6225C899A5B945B8E356819F53CBC9', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71FE71EB366AB149A70D987A1959A0ED', X'0C6225C899A5B945B8E356819F53CBC9', X'0C6225C899A5B945B8E356819F53CBC9', 1, NULL, NULL, 2, NULL, 0, 'Cover the chicken breasts with the honey mustard in a large bowl. Cover with plastic wrap and allow to marinate in the refrigerator for at least 2 hours, and up to over night.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DC77814C3A2CF49B443349B4CAD8A49', X'0C6225C899A5B945B8E356819F53CBC9', X'0C6225C899A5B945B8E356819F53CBC9', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F. Heat the butter in a saut pan over medium heat. Add the mushrooms and a pinch of salt, and allow to brown and soften for a few minutes. Stir every minute or so to make sure the mushrooms brown evenly.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C50C29D954F2046B4E5794D7B73FCED', X'0C6225C899A5B945B8E356819F53CBC9', X'0C6225C899A5B945B8E356819F53CBC9', 3, NULL, NULL, 2, NULL, 0, 'Remove the mushrooms from the saut pan and set them aside. Add a tablespoon of olive oil to the pan and bring back up to medium heat. Add the chicken to the pan, making sure to allow the excess marinade to drip off first. Cook for five minutes per side. Do not try to move or turn the chicken before the five minutes is up, to allow for thorough browning. Flip the chicken and brown on the other side.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8209CE12D6E1C244812B158C642B0B93', X'0C6225C899A5B945B8E356819F53CBC9', X'0C6225C899A5B945B8E356819F53CBC9', 4, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat. The chicken will continue cooking in the oven, so don''t worry about the internal temperature yet. Place the mushrooms and chopped bacon over the top of the chicken, then top each with shredded cheese. Cover the pan with an oven-safe lid, transfer to the preheated oven and bake for 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6AF04B449E4A545B8BB3E1FDB7CFD0A', X'0C6225C899A5B945B8E356819F53CBC9', X'0C6225C899A5B945B8E356819F53CBC9', 5, NULL, NULL, 2, NULL, 0, 'Remove the chicken from the oven and test it with a meat thermometer to make sure it has reached an internal temperature of 165 F. Garnish with chopped parsley and serve immediately.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B408E445BB03B840B221A6270E65D8E4', X'F09CFC52175F7B42969501465322641F', X'F09CFC52175F7B42969501465322641F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDD75A67B318324FA244DA66CEEBDB2F', X'F09CFC52175F7B42969501465322641F', X'F09CFC52175F7B42969501465322641F', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle the Kosher salt and a few good grinds of black pepper onto your chopping board and spread. Press the chicken thighs, skin-side surface down into the salt and pepper then roll each thigh tight and place in a bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8D5A585445A6A4CBBE438611F80239D', X'F09CFC52175F7B42969501465322641F', X'F09CFC52175F7B42969501465322641F', 2, NULL, NULL, 2, NULL, 0, 'In another small bowl mix the garlic, vinegar, brine, wine and sugar and lightly whisk. Add the olives, prunes and capers, stir, then pour everything over the chicken thighs. Cover the dish tightly with plastic or wax wrap and place into the fridge to marinate overnight.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B491985426E3BB40B3083CF66B3157C7', X'F09CFC52175F7B42969501465322641F', X'F09CFC52175F7B42969501465322641F', 3, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F. When ready to cook, lift the thighs from the dish and pat dry with kitchen paper. Heat the oil in a frying pan until hot but not smoking.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDD9FE8E82FDCC47A6C7D696A91C770F', X'F09CFC52175F7B42969501465322641F', X'F09CFC52175F7B42969501465322641F', 4, NULL, NULL, 2, NULL, 0, 'Add the thighs and sear all over until the thighs are golden brown and slightly crisp on the skin-side surface.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07E6990AAEC262488B671D3083A1C57C', X'F09CFC52175F7B42969501465322641F', X'F09CFC52175F7B42969501465322641F', 5, NULL, NULL, 2, NULL, 0, 'Pack the browned thighs into shallow ovenproof baking then pour over the marinade, olives, prunes and capers. Bake in the center of the preheated oven for 45 minutes basting the chicken with the marinade from time to time. The marinade will reduce, and the chicken will turn dark golden and slightly caramelized.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70C118CA7B254846B4AAF9BC40221E3E', X'F09CFC52175F7B42969501465322641F', X'F09CFC52175F7B42969501465322641F', 6, NULL, NULL, 2, NULL, 0, 'Serve while piping hot with extra olives and capers on the side and rice, or a mixture of rice and quinoa, or pasta. The Chicken Marbella also works well with creamy mashed potatoes.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41E544CED1F6A94DA74AF7BAEF34DB3A', X'09C7087518AA734BAC497367BDA687A5', X'09C7087518AA734BAC497367BDA687A5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1497B486569553429AC98103DB38DC36', X'09C7087518AA734BAC497367BDA687A5', X'09C7087518AA734BAC497367BDA687A5', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the brown sugar, salt, paprika, chili powder, garlic powder, onion powder, black pepper, and cayenne pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A81F7C8050BB7143A837A4C99F792F17', X'09C7087518AA734BAC497367BDA687A5', X'09C7087518AA734BAC497367BDA687A5', 2, NULL, NULL, 2, NULL, 0, 'Pat the chicken with paper towels to dry. Rub the chicken pieces all over with the seasoning mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2BEB80D7B5E574B98D6E1340EF5C269', X'09C7087518AA734BAC497367BDA687A5', X'09C7087518AA734BAC497367BDA687A5', 3, NULL, NULL, 2, NULL, 0, 'Place the trivet in the instant pot and add 1 cup of water. Place the seasoned chicken pieces on the trivet.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A75ED6ECCD6540458F8954945E84EC6B', X'09C7087518AA734BAC497367BDA687A5', X'09C7087518AA734BAC497367BDA687A5', 4, NULL, NULL, 2, NULL, 0, 'Pour about 1/4 cup of barbecue sauce into a small bowl and brush it over the chicken pieces.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57FDE1F33DD7C54FB9083B8FFFD7E7DA', X'09C7087518AA734BAC497367BDA687A5', X'09C7087518AA734BAC497367BDA687A5', 5, NULL, NULL, 2, NULL, 0, 'Secure the Instant Pot lid and turn the steam release valve to the sealing position. Choose the pressure cook function (or manual on early models), high pressure, and set the time for 18 minutes. When the time is up, let the steam release naturally for 10 minutes. Carefully turn the steam release valve to the venting position to release any remaining steam.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'923A33955371934383F2C46E2B4C3CD3', X'09C7087518AA734BAC497367BDA687A5', X'09C7087518AA734BAC497367BDA687A5', 6, NULL, NULL, 2, NULL, 0, 'Preheat the oven broiler. Arrange the chicken on a foil-lined baking pan and brush the pieces with more barbecue sauce. Broil the chicken for about 2 to 4 minutes, or until slightly charred. Broiling gives the chicken extra color, but feel free to skip this step.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AB69BB881ED794C9BD554134F84C52F', X'09C7087518AA734BAC497367BDA687A5', X'09C7087518AA734BAC497367BDA687A5', 7, NULL, NULL, 2, NULL, 0, 'Serve the chicken with potatoes or rice and offer extra barbecue sauce at the table.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B2E91578654F048970C9167421C176C', X'09C7087518AA734BAC497367BDA687A5', X'09C7087518AA734BAC497367BDA687A5', 8, NULL, NULL, 2, NULL, 0, 'Enjoy your meal!');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A18EA737102994691E0ED33739CF475', X'401861A28C058C459F73ADDF980D3550', X'401861A28C058C459F73ADDF980D3550', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC7B37B27898DE42932BC20D825BB1F2', X'401861A28C058C459F73ADDF980D3550', X'401861A28C058C459F73ADDF980D3550', 1, NULL, NULL, 2, NULL, 0, 'Dry the chicken pieces with a paper towel and arrange them in pan or baking dish. Rub the chicken with 1 tablespoon of kosher salt and 1 teaspoon of black pepper. Cover the pan with plastic wrap and refrigerate for 4 hours or up to 24 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B62A1C96C2F30B4B863AF5F91A12DD0C', X'401861A28C058C459F73ADDF980D3550', X'401861A28C058C459F73ADDF980D3550', 2, NULL, NULL, 2, NULL, 0, 'In a deep, heavy skillet or saucepan, heat about 2 inches of oil to 325 F. Place a cooling rack in a rimmed baking pan. Preheat the oven to 200 F.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E5F32306D53684D903397E16571FF04', X'401861A28C058C459F73ADDF980D3550', X'401861A28C058C459F73ADDF980D3550', 3, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the milk, eggs, and hot sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F29ABF4D3CC69043AF4AB5C43CB3DF68', X'401861A28C058C459F73ADDF980D3550', X'401861A28C058C459F73ADDF980D3550', 4, NULL, NULL, 2, NULL, 0, 'Pour the milk and egg mixture into a wide, shallow bowl. Put the flour in a separate wide, shallow bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F3261A751A24443A17172633BFBB08E', X'401861A28C058C459F73ADDF980D3550', X'401861A28C058C459F73ADDF980D3550', 5, NULL, NULL, 2, NULL, 0, 'Dip a chicken piece in the flour and turn until it is thoroughly coated.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F346F8E29E92A48BC7D5D9A1B3F35BF', X'401861A28C058C459F73ADDF980D3550', X'401861A28C058C459F73ADDF980D3550', 6, NULL, NULL, 2, NULL, 0, 'Dip the flour-coated chicken in the milk and egg mixture and turn to coat.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1D53BDA4A9A1F4A8513DBA65AAC6338', X'401861A28C058C459F73ADDF980D3550', X'401861A28C058C459F73ADDF980D3550', 7, NULL, NULL, 2, NULL, 0, 'Dip the chicken piece in the flour once again, turning to coat.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF6CAD2852EA424DA3218F6D93C488F7', X'401861A28C058C459F73ADDF980D3550', X'401861A28C058C459F73ADDF980D3550', 8, NULL, NULL, 2, NULL, 0, 'Repeat with the remaining chicken pieces.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9CEB07A3A43D249A1A1B910D2F18F06', X'401861A28C058C459F73ADDF980D3550', X'401861A28C058C459F73ADDF980D3550', 9, NULL, NULL, 2, NULL, 0, 'Fry the chicken in small batches in the hot oil for 16 to 19 minutes, or until the pieces reach about 165 F at their centers on an instant-read thermometer. Chicken breast pieces will take 15 to 17 minutes. Remove the chicken pieces to the prepared pan with the cooling rack and place the pan in the oven. Repeat with the remaining chicken pieces. Set the cooking oil aside to cool slightly.');
INSERT INTO `VWR INTERNAL MeatRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56E8B2C9F103194CBE0B4290F57EBC77', X'401861A28C058C459F73ADDF980D3550', X'401861A28C058C459F73ADDF980D3550', 10, NULL, NULL, 2, NULL, 0, 'In a heatproof bowl, combine the cayenne pepper, brown sugar, paprika, 1 teaspoon of kosher salt, 1/2 teaspoon of black pepper, and the garlic powder. Add about 1/2 cup of the hot cooking oil to the spices and stir to blend.');
