INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'952E2C01E5711D49B86618FDEC8229EE', X'E5F1C308239B8841AFE4997941493EA4', X'E5F1C308239B8841AFE4997941493EA4', 1, NULL, NULL, 2, NULL, 0, 'Slice the tops off the bell peppers. Remove the seeds and carefully scrape out the white veins. Chop some of the tops for the filling.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46F040A72602284E8125785D7F84C127', X'E5F1C308239B8841AFE4997941493EA4', X'E5F1C308239B8841AFE4997941493EA4', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the ground beef with the cooked rice, chopped pepper tops, onion, minced garlic, diced tomato, Parmesan cheese, tomato paste, 1/3 cup of the tomato sauce, the beaten egg, breadcrumbs, salt, basil, oregano, Worcestershire sauce, and 2 tablespoons of chopped parsley. Mix the ingredients until well blended. Stuff the pepper shells with the mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85F346B2DA0CC94DAF7760605664DB98', X'E5F1C308239B8841AFE4997941493EA4', X'E5F1C308239B8841AFE4997941493EA4', 3, NULL, NULL, 2, NULL, 0, 'Add 2/3 cup of water to the Instant Pot and place a trivet (with handles) or baking sling in the pot. Arrange the stuffed peppers on the trivet and pour the remaining tomato sauce over them.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98C18B4F63E1334295496568EB2F23FE', X'E5F1C308239B8841AFE4997941493EA4', X'E5F1C308239B8841AFE4997941493EA4', 4, NULL, NULL, 2, NULL, 0, 'Lock the lid in place and turn the steam release valve to the sealing position. Select the pressure cook or manual function (high pressure) and set the time to 8 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37279F95BBBEE04DB2844CF6438C3654', X'E5F1C308239B8841AFE4997941493EA4', X'E5F1C308239B8841AFE4997941493EA4', 5, NULL, NULL, 2, NULL, 0, 'When the time is up, wait for 5 minutes and then turn the steam release valve to the venting position to release any remaining pressure.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38244DDDF29CC742B90F9E7D1B8008B0', X'E5F1C308239B8841AFE4997941493EA4', X'E5F1C308239B8841AFE4997941493EA4', 6, NULL, NULL, 2, NULL, 0, 'Top each of the peppers with a generous amount of mozzarella cheese and place the lid back on for a minute or two, or just long enough to melt the cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EF3E717A514A24CAD31A9CB18570E69', X'E5F1C308239B8841AFE4997941493EA4', X'E5F1C308239B8841AFE4997941493EA4', 7, NULL, NULL, 2, NULL, 0, 'Remove the peppers and trivet and set aside. Choose the saut function and simmer the liquids for about 5 minutes to reduce slightly. Taste and adjust the seasonings. Serve the peppers with the reduced sauce and garnish with thinly sliced basil leaves or chopped fresh parsley.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3256FE9EBFDC25478CA44E32785AF21C', X'E5F1C308239B8841AFE4997941493EA4', X'E5F1C308239B8841AFE4997941493EA4', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03C6493890622F46BA8D186C4E25265F', X'952BD3A5E51F6D41BD6C1E91136E0B7A', X'952BD3A5E51F6D41BD6C1E91136E0B7A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D71E7F4E725444CB3F9F9D849D7F812', X'952BD3A5E51F6D41BD6C1E91136E0B7A', X'952BD3A5E51F6D41BD6C1E91136E0B7A', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BDEC975FA44284CAD00AD1BF3B855B3', X'952BD3A5E51F6D41BD6C1E91136E0B7A', X'952BD3A5E51F6D41BD6C1E91136E0B7A', 2, NULL, NULL, 2, NULL, 0, 'Add the ground beef to the pan and continue heating on medium-high heat, breaking up the meat as it cooks until it is browned. Drain off some of the fat if needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D87172966D1045468E83EA07A0CF6720', X'952BD3A5E51F6D41BD6C1E91136E0B7A', X'952BD3A5E51F6D41BD6C1E91136E0B7A', 3, NULL, NULL, 2, NULL, 0, 'Add chopped onion to the ground beef and continue cooking until the onions are slightly softened.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22183165C9360A4AB36EC0C9B975E058', X'952BD3A5E51F6D41BD6C1E91136E0B7A', X'952BD3A5E51F6D41BD6C1E91136E0B7A', 4, NULL, NULL, 2, NULL, 0, 'Add the cream cheese and half of the cheddar cheese and half of the Mexican blend cheese to the ground beef mixture. Mix until well blended.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68D5B12ECF32DF4CB0DEFDE15AF554F6', X'952BD3A5E51F6D41BD6C1E91136E0B7A', X'952BD3A5E51F6D41BD6C1E91136E0B7A', 5, NULL, NULL, 2, NULL, 0, 'Pour the dip into a small casserole dish or, if the pan you''re using is oven-safe, leave the mixture in the pan. Then top with the rest of the cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C5887A0BF539C4BBC08F1B5AE4D18FF', X'952BD3A5E51F6D41BD6C1E91136E0B7A', X'952BD3A5E51F6D41BD6C1E91136E0B7A', 6, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven until the dip is bubbly and the cheese has melted and is slightly browned, about 20 minutes. If you dont want a crispy cheese on top, then add the cheese at the end and allow it to melt.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37C79E93F420384CB1DDB1580F31EFDE', X'952BD3A5E51F6D41BD6C1E91136E0B7A', X'952BD3A5E51F6D41BD6C1E91136E0B7A', 7, NULL, NULL, 2, NULL, 0, 'Top with scallions and serve with thick potato chips. If you are feeling adventurous, drizzle on a little more ketchup or some chopped pickles.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CA2CF13B84A5E4DB9A82A0AFB5107BC', X'FABA76758F13264AA61AF7FBC4417142', X'FABA76758F13264AA61AF7FBC4417142', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3DE9E7C4E2603419CC3CCD683A5BABF', X'FABA76758F13264AA61AF7FBC4417142', X'FABA76758F13264AA61AF7FBC4417142', 1, NULL, NULL, 2, NULL, 0, 'Lightly butter the cut side of the buns and toast on a griddle, skillet, or grill, or under the broiler. Set the buns aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1200AFC5C57F824C899CE389B3D5C6BC', X'FABA76758F13264AA61AF7FBC4417142', X'FABA76758F13264AA61AF7FBC4417142', 2, NULL, NULL, 2, NULL, 0, 'Place a large skillet over medium heat and add 2 tablespoons of butter. When the butter stops sizzling, add the onions. Cook, stirring frequently until the onions are soft and just lightly browned, or about 15 minutes. Remove them to a bowl and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'886B32DD5B35D94CB9A73A7AFF69FC65', X'FABA76758F13264AA61AF7FBC4417142', X'FABA76758F13264AA61AF7FBC4417142', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, shape ground beef into four burgers. Make an indentation in the center of each patty.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'377264A0EEA24D4FA6C04526A01D9660', X'FABA76758F13264AA61AF7FBC4417142', X'FABA76758F13264AA61AF7FBC4417142', 4, NULL, NULL, 2, NULL, 0, 'Place the same skillet back on medium heat. Arrange the burger patties in the skillet. Season the burgers with kosher salt and black pepper and cook them for about 6 to 8 minutes on each side, or until they reach at least 160 F, the minimum safe temperature (USDA) for ground meat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D03ABA6E7D82244B423ECD98684BEA5', X'FABA76758F13264AA61AF7FBC4417142', X'FABA76758F13264AA61AF7FBC4417142', 5, NULL, NULL, 2, NULL, 0, 'Place a square of cheese on each burger and continue to cook until the cheese is melted. Meanwhile, reheat the chopped onions on the stovetop or microwave so they will be hot when you assemble the burgers.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0F1D160E3BB8A44A21BDF02B1CC26C0', X'FABA76758F13264AA61AF7FBC4417142', X'FABA76758F13264AA61AF7FBC4417142', 6, NULL, NULL, 2, NULL, 0, 'Assemble the burgers. Slather each top bun with about 2 tablespoons of butter. If desired, place lettuce leaves on the bottom bun. Top the bottom bun with a cheeseburger. Top with a heaping tablespoon of the cooked chopped onion. Place the buttered top bun on the onion layer and serve immediately. Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'694A23151DAC4F468D872C003085E9F9', X'5F101E272294C74291F308F7B7923C8E', X'5F101E272294C74291F308F7B7923C8E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DE0161E4B87EC46A03F0CE266CDD483', X'5F101E272294C74291F308F7B7923C8E', X'5F101E272294C74291F308F7B7923C8E', 1, NULL, NULL, 2, NULL, 0, 'Remove the ground beef from its packaging and pat dry with paper towels. Chop the onion, celery, and carrot in a food processor, or mince them finely.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74657F30C2E335459B5678BBD7B5EB53', X'5F101E272294C74291F308F7B7923C8E', X'5F101E272294C74291F308F7B7923C8E', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the ground beef, onion, celery, carrot, breadcrumbs, ketchup, milk, eggs, kosher salt, pepper, and 1/2 teaspoon of Worcestershire sauce. Using your hands, mix the ingredients until thoroughly blended.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6936FDF73AFF6F4A9E6C24FCC1F74983', X'5F101E272294C74291F308F7B7923C8E', X'5F101E272294C74291F308F7B7923C8E', 3, NULL, NULL, 2, NULL, 0, 'Shape the meat mixture into a round or oblong loaf and place it in a 7-inch baking pan or on a sheet of heavy-duty foil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E375EF1761E0E24190E1DD200FF3E288', X'5F101E272294C74291F308F7B7923C8E', X'5F101E272294C74291F308F7B7923C8E', 4, NULL, NULL, 2, NULL, 0, 'Add 1 1/4 cups of water to the Instant Pot. Place the meatloaf in a sling or on the Instant Pot steamer rack or trivet, and lower it into the pot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'965C8443831FB842B65485CB87EF69B6', X'5F101E272294C74291F308F7B7923C8E', X'5F101E272294C74291F308F7B7923C8E', 5, NULL, NULL, 2, NULL, 0, 'Lock the lid in place and ensure the steam release valve is in the sealing position. Choose the pressure cook or manual button, high pressure, and set the time to 40 minutes. When the time is up, let the pressure release naturally for 15 minutes. Carefully turn the steam release valve to the venting position to release the remaining pressure. To protect your hand from any remaining steam, use the handle of a wooden spoon or another utensil to turn the valve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'164067130584D042B9BB2030B5E2BD8F', X'5F101E272294C74291F308F7B7923C8E', X'5F101E272294C74291F308F7B7923C8E', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a saucepan, combine the 2/3 cup of ketchup, the brown sugar, mustard, and a dash of Worcestershire sauce. Bring the sauce mixture to a simmer just before the meatloaf is ready.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80F15732A41600419CA12B50930FB561', X'5F101E272294C74291F308F7B7923C8E', X'5F101E272294C74291F308F7B7923C8E', 7, NULL, NULL, 2, NULL, 0, 'Remove the meatloaf from the Instant Pot. Carefully drain excess liquids from the pan or foil. Move the loaf to a plate or platter and drizzle it with the sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6712A98FB976DC4D934324360B383741', X'5F101E272294C74291F308F7B7923C8E', X'5F101E272294C74291F308F7B7923C8E', 8, NULL, NULL, 2, NULL, 0, 'Serve meatloaf with boiled or mashed potatoes, or serve it with hot cooked rice.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6193230E10A6C34D976204EB0FB611FA', X'5F101E272294C74291F308F7B7923C8E', X'5F101E272294C74291F308F7B7923C8E', 9, NULL, NULL, 2, NULL, 0, 'Enjoy your meatloaf meal!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C84BEA64565ED40994F14E02EBBEE10', X'CF74C039C9B0484A84869A522F6813EA', X'CF74C039C9B0484A84869A522F6813EA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B69296A6BDCFD44FA92D2BC9A81A0712', X'CF74C039C9B0484A84869A522F6813EA', X'CF74C039C9B0484A84869A522F6813EA', 1, NULL, NULL, 2, NULL, 0, 'Select the Instant Pot''s saut setting and add the oil. When the oil is hot and shimmering, add the ground beef. Cook for 4 minutes, stirring constantly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAA82AEEBC699E49A1544CA0A3B41635', X'CF74C039C9B0484A84869A522F6813EA', X'CF74C039C9B0484A84869A522F6813EA', 2, NULL, NULL, 2, NULL, 0, 'Add the onion, bell pepper, and garlic to the beef and cook for 2 to 3 minutes longer, stirring frequently.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'031EB0958CDA83429D0A20FD6123149A', X'CF74C039C9B0484A84869A522F6813EA', X'CF74C039C9B0484A84869A522F6813EA', 3, NULL, NULL, 2, NULL, 0, 'Add the water to the pot and stir, scraping the bottom of the pot to loosen any stuck-on browned bits.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50EB3EDFF031E94AB15882653B60EBBD', X'CF74C039C9B0484A84869A522F6813EA', X'CF74C039C9B0484A84869A522F6813EA', 4, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the ketchup, brown sugar, Worcestershire sauce, and mustard. Add the mixture to the Instant Pot; do not stir.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8751078659938A4DB1D3AFBA2B7CA8E9', X'CF74C039C9B0484A84869A522F6813EA', X'CF74C039C9B0484A84869A522F6813EA', 5, NULL, NULL, 2, NULL, 0, 'Lock the lid in place and turn the steam release knob to the sealing position. Choose the pressure cook or manual setting (high pressure) and set the timer for 10 minutes. When the time is up, let the pressure come down naturally for 10 minutes and then carefully do a quick release following the manufacturer''s instructions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B33866140744D44FB9B999963F2ED0D3', X'CF74C039C9B0484A84869A522F6813EA', X'CF74C039C9B0484A84869A522F6813EA', 6, NULL, NULL, 2, NULL, 0, 'Cancel the pressure cook function and choose the saut button. Continue cooking the sloppy joe mixture for 2 to 3 minutes, or until reduced and thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A32250DEA860064987CC145231F774C4', X'CF74C039C9B0484A84869A522F6813EA', X'CF74C039C9B0484A84869A522F6813EA', 7, NULL, NULL, 2, NULL, 0, 'Spoon the sloppy joe sauce over toasted buns and serve with chips, fries, or tater tots.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89D83A299474C149B23BFAD5DC139F5E', X'679AAB854727D74CA4119E417DB59514', X'679AAB854727D74CA4119E417DB59514', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'693E1ECC88E49643995258ED7615A14A', X'679AAB854727D74CA4119E417DB59514', X'679AAB854727D74CA4119E417DB59514', 1, NULL, NULL, 2, NULL, 0, 'Open the avocado. Remove the pit and peel and cut the flesh into cubes. Remove the seeds and stems from the sweet peppers and dice them into approximately 1/8 inch pieces. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7727B7115B967A42BCB8D2ECE7E66289', X'679AAB854727D74CA4119E417DB59514', X'679AAB854727D74CA4119E417DB59514', 2, NULL, NULL, 2, NULL, 0, 'Set a medium skillet over high heat and add the vegetable oil. When oil is hot, add the ground beef. Break up the beef as it browns. When beef has mostly browned, lower the heat to medium-low. Add the packet of simmer sauce and stir into the ground beef. Simmer gently for 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BEA41AEBA9AB84CA871502F63B371D6', X'679AAB854727D74CA4119E417DB59514', X'679AAB854727D74CA4119E417DB59514', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, fill a pot with six cups (1 1/2 quarts) of water and bring to a boil over high heat. Add two teaspoons of salt and the macaroni. Cook for seven or eight minutes, stirring occasionally until pasta is tender. Remove from heat and pour into a colander to drain.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50313101274CC443BAD0CBF59AF83544', X'679AAB854727D74CA4119E417DB59514', X'679AAB854727D74CA4119E417DB59514', 4, NULL, NULL, 2, NULL, 0, 'Add the butter and milk to the pot you used to cook the pasta and set it over medium heat. When the butter has melted, whisk the packet the packet of cheese powder into the milk and butter. Add the ground cumin and the cooked pasta. Stir to thoroughly coat the macaroni, then remove from heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6729D171A80714BB86575C77F9FA962', X'679AAB854727D74CA4119E417DB59514', X'679AAB854727D74CA4119E417DB59514', 5, NULL, NULL, 2, NULL, 0, 'Put about 1/2 cup of the broken corn tortilla chips into a serving bowl. Spoon a third of the mac and cheese over the top, followed by one-third of the seasoned beef, avocado and sweet peppers. Repeat with two more bowls. Sweet a little bit of lime over the top and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFE7FC25D897164EAACDBABECD0E954E', X'753545BB086CA14BA32FBCFDDFC9A3BE', X'753545BB086CA14BA32FBCFDDFC9A3BE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8E54BF02F3F144DAC4A99E7A0F003A3', X'753545BB086CA14BA32FBCFDDFC9A3BE', X'753545BB086CA14BA32FBCFDDFC9A3BE', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F. Line a 9 x 13-inch pan with aluminum foil and spray with non-stick spray.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C02AE0BAC33F934BBCD664461621541C', X'753545BB086CA14BA32FBCFDDFC9A3BE', X'753545BB086CA14BA32FBCFDDFC9A3BE', 2, NULL, NULL, 2, NULL, 0, 'Make the glaze. In a medium bowl combine ketchup, brown sugar, and garlic powder until fully incorporated.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BF52DE3912DB3449963CF7F34FB3F0B', X'753545BB086CA14BA32FBCFDDFC9A3BE', X'753545BB086CA14BA32FBCFDDFC9A3BE', 3, NULL, NULL, 2, NULL, 0, 'Make the meatloaf. In a large mixing bowl whisk the ketchup, eggs and Worcestershire sauce until fully incorporated.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'573F04D0D1D970468DE56B954BBF1381', X'753545BB086CA14BA32FBCFDDFC9A3BE', X'753545BB086CA14BA32FBCFDDFC9A3BE', 4, NULL, NULL, 2, NULL, 0, 'Add the meat, onion, bread crumbs, salt, chili powder, thyme, parsley, garlic powder, and brown sugar and mix until well combined.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DB8A2519E5FF34986112F0FCB2F2E0E', X'753545BB086CA14BA32FBCFDDFC9A3BE', X'753545BB086CA14BA32FBCFDDFC9A3BE', 5, NULL, NULL, 2, NULL, 0, 'Add just a little more than half the meat to the pan and form it into a flat loaf. Make a well down the center using your fingers or a fork about an inch deep and an inch wide.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CAC486EEE0B6946BEF5E09D3FEF7B8B', X'753545BB086CA14BA32FBCFDDFC9A3BE', X'753545BB086CA14BA32FBCFDDFC9A3BE', 6, NULL, NULL, 2, NULL, 0, 'Mix the Gruyere and mozzarella together then add the cheese to the well. Cover with remaining meat mixture, leaving about 1/2 inch of room around the edges to form a dome.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03B0C8441FFFD04F8F89396D7AA45245', X'753545BB086CA14BA32FBCFDDFC9A3BE', X'753545BB086CA14BA32FBCFDDFC9A3BE', 7, NULL, NULL, 2, NULL, 0, 'Spread 1/4 cup of the glaze over the top of the meatloaf. Bake for 50 minutes, basting with the remaining sauce every 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63AD5D1F1B72C4409350678878CF6CA7', X'753545BB086CA14BA32FBCFDDFC9A3BE', X'753545BB086CA14BA32FBCFDDFC9A3BE', 8, NULL, NULL, 2, NULL, 0, 'Once the meatloaf is cooked, remove it from the pan, slice it and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8487E7D665FA5A4CBA59E5EAD9818E6B', X'20B5F4BDFE2BF24290B6EDBC24574544', X'20B5F4BDFE2BF24290B6EDBC24574544', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ED93D7F73D68D4D928476FD1BF18436', X'20B5F4BDFE2BF24290B6EDBC24574544', X'20B5F4BDFE2BF24290B6EDBC24574544', 1, NULL, NULL, 2, NULL, 0, 'Put the thawed riced cauliflower on a few doubled sheets of paper towel or a kitchen towel and squeeze out any excess moisture. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA0563E5B2A5B1409ACD2479A475B5E9', X'20B5F4BDFE2BF24290B6EDBC24574544', X'20B5F4BDFE2BF24290B6EDBC24574544', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large skillet over medium heat. Add the onion and garlic and cook, stirring, for 2 minutes. Add the ground beef and bell pepper and continue to cook until the meat is browned.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A7BFCF046BE8B4685F13DB7BEBF0721', X'20B5F4BDFE2BF24290B6EDBC24574544', X'20B5F4BDFE2BF24290B6EDBC24574544', 3, NULL, NULL, 2, NULL, 0, 'Add the taco seasoning and stir until well blended.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63FF4410C3494F4E999FCE8A532A2968', X'20B5F4BDFE2BF24290B6EDBC24574544', X'20B5F4BDFE2BF24290B6EDBC24574544', 4, NULL, NULL, 2, NULL, 0, 'Layer about half of the beef mixture in a 2-quart baking dish or casserole; top with about 1/2 cup of shredded cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'127775DEB44BBC46BFBF52738E1F7F69', X'20B5F4BDFE2BF24290B6EDBC24574544', X'20B5F4BDFE2BF24290B6EDBC24574544', 5, NULL, NULL, 2, NULL, 0, 'Top the ground beef and cheese with the riced cauliflower and another 1/2 cup of shredded cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F2D4B1A02CE0345875FC48F4C8D6E50', X'20B5F4BDFE2BF24290B6EDBC24574544', X'20B5F4BDFE2BF24290B6EDBC24574544', 6, NULL, NULL, 2, NULL, 0, 'Top with the remaining beef and another 1/2 cup of shredded cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D84E301FA68EB418BB2CF7F00868AA9', X'20B5F4BDFE2BF24290B6EDBC24574544', X'20B5F4BDFE2BF24290B6EDBC24574544', 7, NULL, NULL, 2, NULL, 0, 'Spoon the salsa evenly over the second ground beef and cheese layers.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'035435D19F6BEA4DA532F031232BB164', X'20B5F4BDFE2BF24290B6EDBC24574544', X'20B5F4BDFE2BF24290B6EDBC24574544', 8, NULL, NULL, 2, NULL, 0, 'Heat the cream in a saucepan over medium heat. Add 1 cup of shredded cheese and cook, stirring, until the cheese is melted, about two minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A3CB0A8A159A245B1E735505C4B8CCC', X'20B5F4BDFE2BF24290B6EDBC24574544', X'20B5F4BDFE2BF24290B6EDBC24574544', 9, NULL, NULL, 2, NULL, 0, 'Pour the cream and cheese sauce mixture over the salsa layer. Top with the remaining 1/2 cup of shredded cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7741AAF78C107A48968A25B1CC1FCE06', X'20B5F4BDFE2BF24290B6EDBC24574544', X'20B5F4BDFE2BF24290B6EDBC24574544', 10, NULL, NULL, 2, NULL, 0, 'Bake the casserole for 30 to 40 minutes, or until hot and bubbly. Garnish with chopped cilantro, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED4BA036EA211C40B2DE037FEE7E98A1', X'20B5F4BDFE2BF24290B6EDBC24574544', X'20B5F4BDFE2BF24290B6EDBC24574544', 11, NULL, NULL, 2, NULL, 0, 'Serve the casserole with toppings, such as extra salsa, chopped green onions, avocado slices, guacamole, diced tomatoes, sliced olives, jalapeo pepper rings, or sour cream.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'060462834349D044B36F0904B0447173', X'0420DEA57BADEB47BE917838DE23ACE0', X'0420DEA57BADEB47BE917838DE23ACE0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1223F35A0DE5754CBEEBB4CF2EB4FB52', X'0420DEA57BADEB47BE917838DE23ACE0', X'0420DEA57BADEB47BE917838DE23ACE0', 1, NULL, NULL, 2, NULL, 0, 'Peel about 12 to 15 large leaves off of the cabbage head.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCA78B3D406675479787F5F9A4B8D3C5', X'0420DEA57BADEB47BE917838DE23ACE0', X'0420DEA57BADEB47BE917838DE23ACE0', 2, NULL, NULL, 2, NULL, 0, 'Drop the cabbage leaves into a large pot of well salted boiling water; cover and cook for 3 minutes. Drain well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86D6AFD684C88B4D8FBE93B42DCFFB38', X'0420DEA57BADEB47BE917838DE23ACE0', X'0420DEA57BADEB47BE917838DE23ACE0', 3, NULL, NULL, 2, NULL, 0, 'Position a rack in the center of the oven and preheat to 350 F (180 C/Gas 4).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EB3B92F716EE0469D666FA0CC607BA0', X'0420DEA57BADEB47BE917838DE23ACE0', X'0420DEA57BADEB47BE917838DE23ACE0', 4, NULL, NULL, 2, NULL, 0, 'For the filling, combine the ground beef, rice, onion, egg, milk, salt, and pepper in a medium bowl. Mix well and divide into 12 portions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8884B7C218E61B408119C7EBCD5EB829', X'0420DEA57BADEB47BE917838DE23ACE0', X'0420DEA57BADEB47BE917838DE23ACE0', 5, NULL, NULL, 2, NULL, 0, 'Place a portion of the beef mixture (about 2-1/4 oz.) onto the center of each cabbage leaf.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48F50086641DF641ACFF3F447E3C91C1', X'0420DEA57BADEB47BE917838DE23ACE0', X'0420DEA57BADEB47BE917838DE23ACE0', 6, NULL, NULL, 2, NULL, 0, 'Roll the leaf around the filling, burrito-style, folding in the edges to seal it in.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'550B9CEA39B86E4D86B3CD97D9A2A01D', X'0420DEA57BADEB47BE917838DE23ACE0', X'0420DEA57BADEB47BE917838DE23ACE0', 7, NULL, NULL, 2, NULL, 0, 'Fasten the rolls with toothpicks. Place the rolls in a single layer in a large baking dish or an oven-safe Dutch oven.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'044EA753CCB1E845AF5266778CE5800E', X'0420DEA57BADEB47BE917838DE23ACE0', X'0420DEA57BADEB47BE917838DE23ACE0', 8, NULL, NULL, 2, NULL, 0, 'For the sauce, combine the diced tomatoes, tomato sauce, sugar, vinegar, and 1/2 cup of water.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DB24403A9A6CC4E87D8BBDF16AD281C', X'0420DEA57BADEB47BE917838DE23ACE0', X'0420DEA57BADEB47BE917838DE23ACE0', 9, NULL, NULL, 2, NULL, 0, 'Pour over the cabbage rolls. Cover with a lid or aluminum foil (carefully avoiding the toothpicks) and bake in the preheated oven for about 1 hour until the cabbage is tender and the sauce is bubbling.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AE10EA189A1344696D710D6B9DE413A', X'0420DEA57BADEB47BE917838DE23ACE0', X'0420DEA57BADEB47BE917838DE23ACE0', 10, NULL, NULL, 2, NULL, 0, 'Remove the rolls with a slotted spoon and discard the toothpicks.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A84698D25430F45A5DE3D2C2617E8A1', X'0420DEA57BADEB47BE917838DE23ACE0', X'0420DEA57BADEB47BE917838DE23ACE0', 11, NULL, NULL, 2, NULL, 0, 'Carefully pour the juices into a medium saucepan and place over medium heat. Stir the cornstarch with 1/4 cup cold water until dissolved. Add to the sauce and bring to a boil. Reduce to a simmer and cook, whisking constantly until thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C3794F425751C4BB933CC5E09DA0563', X'0420DEA57BADEB47BE917838DE23ACE0', X'0420DEA57BADEB47BE917838DE23ACE0', 12, NULL, NULL, 2, NULL, 0, 'Serve the cabbage rolls topped with the sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBA2C4821B88724A9D2E6BC1F40550C0', X'0420DEA57BADEB47BE917838DE23ACE0', X'0420DEA57BADEB47BE917838DE23ACE0', 13, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13E61F023484BB4CB08008E5397A1137', X'543840322B4E8B449485B672D21A93A1', X'543840322B4E8B449485B672D21A93A1', 0, NULL, NULL, 2, NULL, 0, 'Remove large outer leaves from cabbage and set aside. Remove core from cabbage. Place head in a large pot filled with boiling salted water. Cover and cook three minutes or until softened enough to pull off individual leaves. You will need 18 leaves.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'257BB559130BAD4C8962074A7B44185F', X'543840322B4E8B449485B672D21A93A1', X'543840322B4E8B449485B672D21A93A1', 1, NULL, NULL, 2, NULL, 0, 'When leaves are cool enough to handle, use a paring knife to cut away the thick center stem from each leaf, without cutting all the way through.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F78CC201165A274C94CB0DE16A4C8EA1', X'543840322B4E8B449485B672D21A93A1', X'543840322B4E8B449485B672D21A93A1', 2, NULL, NULL, 2, NULL, 0, 'Chop the remaining cabbage, still reserving large outer leaves, and place it in the bottom of a large greased casserole dish or Dutch oven.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B670A6C5A92527419CCB2011BA5D1793', X'543840322B4E8B449485B672D21A93A1', X'543840322B4E8B449485B672D21A93A1', 3, NULL, NULL, 2, NULL, 0, 'Rinse rice and cook it in 1/2 cup water until water is absorbed. Remove from heat and let cool. Meanwhile, saut chopped onion in butter in a medium skillet until tender and let cool.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'552A4774B8A06E4B9E285759BEFA3A3C', X'543840322B4E8B449485B672D21A93A1', X'543840322B4E8B449485B672D21A93A1', 4, NULL, NULL, 2, NULL, 0, 'In a large bowl, mix cooked and cooled rice, cooled onions, beef, pork, salt and pepper to taste, egg, garlic, paprika, reserved sauerkraut juices, and 1/3 cup of the tomato sauce; blend ingredients thoroughly. Don''t overmix or the meat will become tough.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D124FBA1BD6E648B530487DBD7379E0', X'543840322B4E8B449485B672D21A93A1', X'543840322B4E8B449485B672D21A93A1', 5, NULL, NULL, 2, NULL, 0, 'Place about 1/2 cup of meat on each cabbage leaf. Roll away from you to encase the meat. Flip the right side of the leaf to the middle, then flip the left side. You will have something that looks like an envelope. Roll the leaf into a small tube.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8F7DAAD19AA4A49B409BD6B164A402E', X'543840322B4E8B449485B672D21A93A1', X'543840322B4E8B449485B672D21A93A1', 6, NULL, NULL, 2, NULL, 0, 'Add some tomato sauce in between the layers in the casserole dish or Dutch oven. Place the cabbage rolls on top of the chopped cabbage with sauerkraut and top with all the crushed tomatoes, remaining sauerkraut, tomato sauce, and a few of the reserved whole cabbage leaves.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD0DEC19A28E1A4F99A749CAD7706937', X'543840322B4E8B449485B672D21A93A1', X'543840322B4E8B449485B672D21A93A1', 7, NULL, NULL, 2, NULL, 0, 'Add enough water to cover completely. Bring to a boil, reduce heat to very low, cover and simmer three to four hours. Watch carefully so stuffed cabbages don''t burn.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'551CBB5D627F3044992CCA489AB1C604', X'543840322B4E8B449485B672D21A93A1', X'543840322B4E8B449485B672D21A93A1', 8, NULL, NULL, 2, NULL, 0, 'Serve with pan juices and a drizzle of sour cream if desired, mashed potatoes, and crusty bread.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D29E0A201E226459986C32D54783663', X'543840322B4E8B449485B672D21A93A1', X'543840322B4E8B449485B672D21A93A1', 9, NULL, NULL, 2, NULL, 0, 'Cabbage rolls freeze well before or after cooking and can be made in a slow cooker (see your manufacturer''s instructions).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21BC78E497DD4B4495EFD6830BEB52E0', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83F893AF3FBF504780B2A95AEC353836', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F/204 C. Oil an 8 x 8-inch baking pan, or similarly-sized casserole dish, or coat with non-stick cooking spray. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB8CAEED871D1A4D89C2BC74747FAC0E', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', 2, NULL, NULL, 2, NULL, 0, 'Place the potatoes in a stockpot and add enough water to cover by 1 to 2 inches and bring to a boil. Cover, reduce heat, and simmer just until the potatoes are easily pierced with a knife, and soft enough to mash with a fork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B1D096CCE378E42BBDC59CEA53C0588', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', 3, NULL, NULL, 2, NULL, 0, 'Transfer the potatoes to a colander to drain, then return them to the pot. Mash them with a wire whisk or fork, and the margarine or oil and salt, and mix well. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD668F5E10AD1E43AF7F36A0BB53E97D', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', 4, NULL, NULL, 2, NULL, 0, 'In a chef''s pan or large, deep skillet, heat the 3 tablespoons of oil over medium-high heat. Add the onions, celery, and carrot, and saute until the onions are soft and translucentabout 5 to 7 minutes. Add the garlic and saute one minute more.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3654A5D02FED0E4383B8480ECFC3CEC7', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', 5, NULL, NULL, 2, NULL, 0, 'Add the ground beef or vegetarian meat substitute, and cook until browned. Sprinkle evenly with the flour, stir, and cook until well combined. Stir in the tomato paste, marinara or ketchup, salt, and pepper. Pour in the broth, and add the peas and corn (if using). When well combined and thickened, taste and adjust seasonings as desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC9A90AFF38ACC46A667120D6B5F426F', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', 6, NULL, NULL, 2, NULL, 0, 'Transfer the meat to the baking dish. Cover with the mashed potatoes, spreading them with a spatula into an even layer. Sprinkle with paprika. Bake in the preheated oven for 25 to 30 minutes, or until the potatoes begin to brown. Serve hot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AACC428B4CB5304699A1277DAFB04672', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', X'DEFCA837BEA7A0409AB2DECFCE9F1EFE', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40063ADAFDC9CE4680AFFDC85FCD0821', X'BF4F66982817BB4F9832E356C54FCB01', X'BF4F66982817BB4F9832E356C54FCB01', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BD9C93F50804B47AB2C55E4B6B283EA', X'BF4F66982817BB4F9832E356C54FCB01', X'BF4F66982817BB4F9832E356C54FCB01', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the ground beef, finely chopped onion, cooked rice, and seasonings until well blended.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'070217AF8452D047A7ADEE69E8E353E9', X'BF4F66982817BB4F9832E356C54FCB01', X'BF4F66982817BB4F9832E356C54FCB01', 2, NULL, NULL, 2, NULL, 0, 'Cut the tops off of the peppers and remove the seeds and fibers.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'043F2A28FDB1144C85507AFD027E000B', X'BF4F66982817BB4F9832E356C54FCB01', X'BF4F66982817BB4F9832E356C54FCB01', 3, NULL, NULL, 2, NULL, 0, 'Stuff the peppers with equal portions of the ground beef and rice mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C3A631DC71EC24D9CAED5E079BC576C', X'BF4F66982817BB4F9832E356C54FCB01', X'BF4F66982817BB4F9832E356C54FCB01', 4, NULL, NULL, 2, NULL, 0, 'Place the stuffed peppers in a crock pot; cover with the spaghetti sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7AB7DD79E2EE54C8CC6404B9CB398FC', X'BF4F66982817BB4F9832E356C54FCB01', X'BF4F66982817BB4F9832E356C54FCB01', 5, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW for 6 to 7 hours or on HIGH for 3 to 4 hours. Or until the ground beef is thoroughly cooked and the peppers are tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C47CA1277C7B0C4BB4693F63A7ACE194', X'BF4F66982817BB4F9832E356C54FCB01', X'BF4F66982817BB4F9832E356C54FCB01', 6, NULL, NULL, 2, NULL, 0, 'To check for doneness, insert an instant-read food thermometer into the center of the filling. It should read at least 160 F / 71 C or 165 F / 74 C for ground turkey.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C03879320B985B449CAB4D408402C509', X'212ECC54DCBAFF4DB96A311FF67224B6', X'212ECC54DCBAFF4DB96A311FF67224B6', 0, NULL, NULL, 2, NULL, 0, 'Peel the onion and chop it coarsely.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F996DFA6742014A8C533112A1F7504A', X'212ECC54DCBAFF4DB96A311FF67224B6', X'212ECC54DCBAFF4DB96A311FF67224B6', 1, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large skillet over medium heat. Add the onion to the skillet and cook for about 2 minutes, stirring constantly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A08C552E9C07949A7CFFEB6D8B38C25', X'212ECC54DCBAFF4DB96A311FF67224B6', X'212ECC54DCBAFF4DB96A311FF67224B6', 2, NULL, NULL, 2, NULL, 0, 'Crumble the ground beef into the skillet and cook until it is no longer pink, stirring frequently; drain well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'100FA4411253764396DA1ABC4186D0F0', X'212ECC54DCBAFF4DB96A311FF67224B6', X'212ECC54DCBAFF4DB96A311FF67224B6', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic, tomato sauce, tomato paste, salt and oregano and basil to the skillet. Continue cooking until the mixture begins to simmer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEFEC70ECE55354B9C136AA0AB8F5E03', X'212ECC54DCBAFF4DB96A311FF67224B6', X'212ECC54DCBAFF4DB96A311FF67224B6', 4, NULL, NULL, 2, NULL, 0, 'Spoon a layer of meat sauce onto the bottom of the slow cooker. Add a double layer of broken lasagna noodles and top with a layer of the cheeses. Repeat the layers with more sauce, noodles, and cheeses, ending with cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A86D683742D6B4ABB9A504972E9D77B', X'212ECC54DCBAFF4DB96A311FF67224B6', X'212ECC54DCBAFF4DB96A311FF67224B6', 5, NULL, NULL, 2, NULL, 0, 'Cover the crock pot and cook on low for 6 to 8 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79F7D60E14BBCA48834F77DCE9A31B55', X'0858F42B1CDE58429D69EA5C3D7DD136', X'0858F42B1CDE58429D69EA5C3D7DD136', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4723DB2E1FB413429BB1507AC3C939FC', X'0858F42B1CDE58429D69EA5C3D7DD136', X'0858F42B1CDE58429D69EA5C3D7DD136', 1, NULL, NULL, 2, NULL, 0, 'Cut tops off peppers; remove seeds and membranes. Chop edible part of tops and set aside. Rinse peppers under cold water. Place peppers in a large pot; cover with salted water. Bring to a boil; reduce heat, cover, and simmer for 5 minutes. Drain peppers and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8252822F3668664091C3D254BAE1F4E0', X'0858F42B1CDE58429D69EA5C3D7DD136', X'0858F42B1CDE58429D69EA5C3D7DD136', 2, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a large skillet or saut pan over medium heat until hot. Saut chopped green pepper (from tops), chopped onion, and chopped celery for about 5 minutes, or until vegetables are tender. Add tomatoes, tomato paste, water, crushed garlic, basil, 1 teaspoon salt, and 1/4 teaspoon of pepper. Simmer for about 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A365129A9C5EB49BDD6322E5CD34E64', X'0858F42B1CDE58429D69EA5C3D7DD136', X'0858F42B1CDE58429D69EA5C3D7DD136', 3, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48B392987AEDD04B8330E831D9A1EC30', X'0858F42B1CDE58429D69EA5C3D7DD136', X'0858F42B1CDE58429D69EA5C3D7DD136', 4, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine the beaten egg with remaining 1 teaspoon salt and 1/4 teaspoon pepper, and Worcestershire sauce. Gently stir to blend; add ground beef, cooked rice, and 1 cup of the tomato mixture. Mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85D2A72E277E3549A1592563FC9AB296', X'0858F42B1CDE58429D69EA5C3D7DD136', X'0858F42B1CDE58429D69EA5C3D7DD136', 5, NULL, NULL, 2, NULL, 0, 'Stuff peppers with meat mixture and place in a 3-quart baking dish. Pour remaining tomato mixture over the stuffed peppers.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77B48D452B3E2146901FAF0A16F82E8F', X'0858F42B1CDE58429D69EA5C3D7DD136', X'0858F42B1CDE58429D69EA5C3D7DD136', 6, NULL, NULL, 2, NULL, 0, 'Bake in preheated oven for about 45 minutes, or until the meat mixture is fully cooked. The temperature of the meat mixture should be cooked to a minimum of 160 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B9FB2FE41E3494EAB4114271C009E9A', X'0858F42B1CDE58429D69EA5C3D7DD136', X'0858F42B1CDE58429D69EA5C3D7DD136', 7, NULL, NULL, 2, NULL, 0, 'Top stuffed peppers with a little shredded cheddar cheese just before they are done; bake until cheese is melted.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A07AFB1D6E90024E9AB680785A245E1B', X'0858F42B1CDE58429D69EA5C3D7DD136', X'0858F42B1CDE58429D69EA5C3D7DD136', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08B3890F5C429244892A8E271EB96A83', X'43A4C1B3C5D88B4185C9E836D3C62F77', X'43A4C1B3C5D88B4185C9E836D3C62F77', 0, NULL, NULL, 2, NULL, 0, 'In a large, deep skillet or Dutch oven, heat the olive oil and butter over medium heat. Add the carrot, celery, and onion. Cook, stirring, until onion is softened and beginning to brown. Add the ground beef and sausage and cook, stirring and breaking up, until no longer pink. Add the garlic and cook, stirring, for 1 minute longer. Stir in the crushed tomatoes, tomato sauce, tomato paste, and water. Add the bay leaf and simmer over low heat for about 1 hour. Stir in 1 tablespoon of chopped parsley. Remove the bay leaf and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26AE9E406ADED248951207C7A97D42EA', X'43A4C1B3C5D88B4185C9E836D3C62F77', X'43A4C1B3C5D88B4185C9E836D3C62F77', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 375 F');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB81F5D20508F94B9DCB935AB86F581F', X'43A4C1B3C5D88B4185C9E836D3C62F77', X'43A4C1B3C5D88B4185C9E836D3C62F77', 2, NULL, NULL, 2, NULL, 0, 'Lightly grease a 9-by-13-by-2-inch baking pan. .');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'760EEB4359FF8B47A6F790FD7D3EE381', X'43A4C1B3C5D88B4185C9E836D3C62F77', X'43A4C1B3C5D88B4185C9E836D3C62F77', 3, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the ricotta cheese, Parmesan cheese, 1 tablespoon of parsley, and the large egg. Stir until smooth and well blended.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39C3BB9A917C804D9070E1BA54AE8F34', X'43A4C1B3C5D88B4185C9E836D3C62F77', X'43A4C1B3C5D88B4185C9E836D3C62F77', 4, NULL, NULL, 2, NULL, 0, 'Cover the bottom of the prepared pan with a thin layer of the sauce. Cover the sauce layer with lasagna noodles. Top with about one-third of the remaining sauce and half of the ricotta mixture. Sprinkle with a generous layer of mozzarella cheese. Repeat with another layer of the noodles and another one-third of the sauce. Top with the remaining ricotta mixture and more mozzarella. Cover with another layer of lasagna noodles, then top with the remaining sauce and a layer of mozzarella cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'246A92BD9A8518439F8119BE11EB4807', X'43A4C1B3C5D88B4185C9E836D3C62F77', X'43A4C1B3C5D88B4185C9E836D3C62F77', 5, NULL, NULL, 2, NULL, 0, 'Cover the pan loosely with foil, tenting somewhat so the foil won''t stick to the cheese as it melts.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDE2C92915D6394A94AC76E1434026AE', X'43A4C1B3C5D88B4185C9E836D3C62F77', X'43A4C1B3C5D88B4185C9E836D3C62F77', 6, NULL, NULL, 2, NULL, 0, 'Bake for 45 minutes. Remove the foil and bake for 10 to 15 minutes longer, or until hot and bubbly and the cheese has melted.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'115FE31A0B938742BA74F727A415C458', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 0, NULL, NULL, 2, NULL, 0, 'Begin by peeling your eggplants. Using a vegetable peeler, begin at one end and remove a strip of peel from end to end. Leave a strip of peel about the same width, then peel another strip from end to end.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B496FEABF8D6614C901E79287480BBF0', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 1, NULL, NULL, 2, NULL, 0, 'Repeat this until your eggplant is peeled in a striped pattern. Peel all eggplant this way.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16296D95BB2F35479D0171F6140C07E2', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 2, NULL, NULL, 2, NULL, 0, 'Slice the eggplants about 1/4 inch thick. Put all the eggplant slices in a large bowl of salty water and let them soak for about 30 minutes. This removes the bitterness.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'129ADCD53675914B87D98FD5FC5D0043', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 3, NULL, NULL, 2, NULL, 0, 'Slice the green pepper into thin rings. Grate the tomato. Peel and chop the onion finely and fry it in 1 tablespoon of olive oil until tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B9C745FA8CEC14CA807FE8911178F2A', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 4, NULL, NULL, 2, NULL, 0, 'Add the green pepper slices and continue frying until soft. Add the ground beef and fry until browned. Add the grated tomato, tomato paste, and spices.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00098DB6DC5AA849B6D5A1C1DD77B0CD', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 5, NULL, NULL, 2, NULL, 0, 'Cover the pan and let the meat simmer for about 10 minutes. Remove from the heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DD78AFB6B3898479772EDA6AC0EDC7A', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 6, NULL, NULL, 2, NULL, 0, 'Grate the other three tomatoes and put them in a small saucepan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EED8D59AE9D840439D34CF049D42F8D4', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 7, NULL, NULL, 2, NULL, 0, 'Add the tomato paste, salt, pepper, oregano, and sugar and let the sauce simmer for about 10 minutes. Remove from the heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E350C4158E4E8409CA13E7138B14F05', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 8, NULL, NULL, 2, NULL, 0, 'Remove the eggplant slices from the water and blot them dry with paper towels.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD84C08E2C0BEF498D764754160F75F3', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 9, NULL, NULL, 2, NULL, 0, 'Fry them on both sides in vegetable oil until tender and golden brown on both sides. Drain them well on paper towels.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF6FFDB63EC0C94C9AFF7407D87C4149', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 10, NULL, NULL, 2, NULL, 0, 'Line the bottom of a large baking tray with the fried eggplant slices to make a single layer. Make sure there are no gaps between the pieces.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'211FC4C6F8A9834A9B6692B8641FBEE4', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 11, NULL, NULL, 2, NULL, 0, 'Spread the tomato sauce over the eggplant. Next, cover the tomato sauce layer with the meat mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D06FBB80E94C148ACCD3776EA247DC8', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 12, NULL, NULL, 2, NULL, 0, 'In a large pan, melt the butter over high heat. Add the flour and turn it around a few times with a wooden spoon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BEF5E0D8EA6F048A9409D07AAADEC08', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 13, NULL, NULL, 2, NULL, 0, 'Add the milk and seasonings and use a wire whisk to make the mixture smooth as it thickens. Your bechamel sauce is ready when its the consistency of pudding.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7654E9700D9FC049AB0084DACBBDA7A6', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 14, NULL, NULL, 2, NULL, 0, 'Pour the hot bechamel sauce over the meat layer, covering it completely. Sprinkle the grated cheese evenly over the top of the casserole.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E842C96A7A50544A9CA3535166C24495', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 15, NULL, NULL, 2, NULL, 0, 'Bake the casserole in a 360 F/180 C oven until the top turns golden brown.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71AA4B25EF7491449404391764E1C336', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 16, NULL, NULL, 2, NULL, 0, 'Remove the casserole from the oven and let it rest for at least 10 minutes before cutting it into squares.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F2AF583D981C84F8690FEE3CBAC5772', X'C33AF5B4723ECA47BCA8A038456D8A36', X'C33AF5B4723ECA47BCA8A038456D8A36', 17, NULL, NULL, 2, NULL, 0, 'Serve your musakka with Turkish-style rice pilaf.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2E33DE2736B3C468C30F7699C8F24F7', X'6D3BA20AC200AB48A17181E45FF4C16C', X'6D3BA20AC200AB48A17181E45FF4C16C', 0, NULL, NULL, 2, NULL, 0, 'Mix all ingredients together. Don''t add all of the water at one time. The mixture should be moist but not so that the meatballs fall apart.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0355AFDDDBB11E488D431F50A8F4FB38', X'6D3BA20AC200AB48A17181E45FF4C16C', X'6D3BA20AC200AB48A17181E45FF4C16C', 1, NULL, NULL, 2, NULL, 0, 'Shape meatballs to desired size and place on the oiled rack of a broiler pan. Moisten your hands with water as you work to keep the meatballs from sticking. Alternatively, line a large baking pan or rimmed baking sheet with foil and place a wire cooling rack in the pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5173D79BE2DE8148984A1ADD16E14B9E', X'6D3BA20AC200AB48A17181E45FF4C16C', X'6D3BA20AC200AB48A17181E45FF4C16C', 2, NULL, NULL, 2, NULL, 0, 'Broil about 4 inches from the heat source for about 5 to 7 minutes, or until the tops are nicely browned. Turn the meatballs over and broil the other side.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C3184966A47604C8B152CD6E2189F75', X'6D3BA20AC200AB48A17181E45FF4C16C', X'6D3BA20AC200AB48A17181E45FF4C16C', 3, NULL, NULL, 2, NULL, 0, 'When the meatballs are browned on all sides, add them to a medium saucepan with spaghetti sauce and simmer for at least 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79B4231F008EBE4E870040A17A488725', X'6D3BA20AC200AB48A17181E45FF4C16C', X'6D3BA20AC200AB48A17181E45FF4C16C', 4, NULL, NULL, 2, NULL, 0, 'Serve the meatballs and sauce over hot cooked spaghetti, linguini or another kind of pasta.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8582470AB12F14B8FF3C4412076968C', X'FD670BE9D67C544BBB30916D7D60FFA5', X'FD670BE9D67C544BBB30916D7D60FFA5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29CF05B4EDAEDC4E9F2FA6B0BB5D9C37', X'FD670BE9D67C544BBB30916D7D60FFA5', X'FD670BE9D67C544BBB30916D7D60FFA5', 1, NULL, NULL, 2, NULL, 0, 'Grease a 9-x-5-inch loaf pan or rectangular baking pan or spray it with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08351142FA97E54C902E49F600FDFC1F', X'FD670BE9D67C544BBB30916D7D60FFA5', X'FD670BE9D67C544BBB30916D7D60FFA5', 2, NULL, NULL, 2, NULL, 0, 'Combine the beef and turkey in a large bowl with the eggs and breadcrumbs. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E15436E1F7DFD74DADF46724F4CB71B2', X'FD670BE9D67C544BBB30916D7D60FFA5', X'FD670BE9D67C544BBB30916D7D60FFA5', 3, NULL, NULL, 2, NULL, 0, 'In a food processor, combine the carrot, onion, garlic, and green pepper. Process until finely chopped.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'469B680577EE4F4D93FD793BD793CB2A', X'FD670BE9D67C544BBB30916D7D60FFA5', X'FD670BE9D67C544BBB30916D7D60FFA5', 4, NULL, NULL, 2, NULL, 0, 'In a large skillet, over medium heat, heat the oil. Add the finely chopped vegetables and cook, stirring, for 2 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB848014F9187B43BD223E2D001512B3', X'FD670BE9D67C544BBB30916D7D60FFA5', X'FD670BE9D67C544BBB30916D7D60FFA5', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, process the mushrooms until finely chopped.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A01270787630143BD8A5BD28D2A62C2', X'FD670BE9D67C544BBB30916D7D60FFA5', X'FD670BE9D67C544BBB30916D7D60FFA5', 6, NULL, NULL, 2, NULL, 0, 'Add them to the vegetables and continue cooking for 1 minute. Set aside to cool slightly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D97AACEDA7C63488CF901CE90402F35', X'FD670BE9D67C544BBB30916D7D60FFA5', X'FD670BE9D67C544BBB30916D7D60FFA5', 7, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the ketchup with the mustard, Worcestershire sauce, vinegar, brown sugar, red pepper, salt, and black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D88452275E255B45A9A4DA506F292EEF', X'FD670BE9D67C544BBB30916D7D60FFA5', X'FD670BE9D67C544BBB30916D7D60FFA5', 8, NULL, NULL, 2, NULL, 0, 'Add about three-quarters of the ketchup mixture to the meat and breadcrumb mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E0CB211780AFE41ABAFF30B569568B1', X'FD670BE9D67C544BBB30916D7D60FFA5', X'FD670BE9D67C544BBB30916D7D60FFA5', 9, NULL, NULL, 2, NULL, 0, 'Add the sauteed vegetables and mushrooms to the meat; mix until well blended.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB287267FC635E4893102A77E19FC547', X'FD670BE9D67C544BBB30916D7D60FFA5', X'FD670BE9D67C544BBB30916D7D60FFA5', 10, NULL, NULL, 2, NULL, 0, 'Pack the mixture into a loaf pan or shape into a free-form loaf in the rectangular baking pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'131D67DD02F22644A8A7BB2308BA0B26', X'FD670BE9D67C544BBB30916D7D60FFA5', X'FD670BE9D67C544BBB30916D7D60FFA5', 11, NULL, NULL, 2, NULL, 0, 'Bake the meatloaf for 1 hour and 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10A77B68FF3712448AF3B6753547309B', X'FD670BE9D67C544BBB30916D7D60FFA5', X'FD670BE9D67C544BBB30916D7D60FFA5', 12, NULL, NULL, 2, NULL, 0, 'Carefully pour off any excess grease. Spread remaining ketchup mixture over the loaf and continue cooking for 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'340AF9622AC471479DCAE3F62C272CF6', X'FD670BE9D67C544BBB30916D7D60FFA5', X'FD670BE9D67C544BBB30916D7D60FFA5', 13, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33F24258F002B54CACDFCABF1B09BDAC', X'74558A54E707604A825B71E0AA079425', X'74558A54E707604A825B71E0AA079425', 0, NULL, NULL, 2, NULL, 0, 'Cut the tops off peppers, dice tops only, and set aside. Remove seeds and membranes from peppers. Try not to pierce pepper flesh as this will prevent juices from leaking out during baking.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'176AB7F30D670248808D8436FF9AB232', X'74558A54E707604A825B71E0AA079425', X'74558A54E707604A825B71E0AA079425', 1, NULL, NULL, 2, NULL, 0, 'Blanche peppers and fill a large pot half full with water. Add 1 teaspoon of salt and bring to a boil. Add peppers, reduce heat, cover, and simmer for 5 minutes. Drain peppers and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEA7708875C39B4685A62DCE0545BDFE', X'74558A54E707604A825B71E0AA079425', X'74558A54E707604A825B71E0AA079425', 2, NULL, NULL, 2, NULL, 0, 'Cook one cup of basmati rice according to package directions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CEF7449D86DB44EB1DE4B1F031CDA41', X'74558A54E707604A825B71E0AA079425', X'74558A54E707604A825B71E0AA079425', 3, NULL, NULL, 2, NULL, 0, 'Add olive oil to a large skillet, then heat to medium-high and brown the ground beef. Add diced vegetables and cook the mixture until the vegetables are tender but not overcooked. Add tomatoes, tomato paste, crushed garlic, oregano, basil, 1 teaspoon salt, and 1/2 teaspoon of pepper. Simmer for about 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4ECCC8CE2DA27346BA87DB31569D0342', X'74558A54E707604A825B71E0AA079425', X'74558A54E707604A825B71E0AA079425', 4, NULL, NULL, 2, NULL, 0, 'Add rice and cooked ground beef mixture in a large bowl, then stir to combine. Add half of the shredded cheddar cheese and stir to blend. Stuff peppers with meat mixture and place in a 3-quart baking dish. Top each pepper with remaining cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8DEFC3963267E419F690B37A3090BD5', X'74558A54E707604A825B71E0AA079425', X'74558A54E707604A825B71E0AA079425', 5, NULL, NULL, 2, NULL, 0, 'Bake at 350 F for about 30 minutes or until cheese just begins to bubble and brown.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'563F13699DEAB0428BE65EA90B29AB11', X'CDF21C6B1D614F4CBB919AB18C4A72BD', X'CDF21C6B1D614F4CBB919AB18C4A72BD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50CF893FB884AE42933FF438A60A428D', X'CDF21C6B1D614F4CBB919AB18C4A72BD', X'CDF21C6B1D614F4CBB919AB18C4A72BD', 1, NULL, NULL, 2, NULL, 0, 'First, pre-heat the oven to 375 F and lightly grease a loaf pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F7408339A7A16498CA67D8021F077E1', X'CDF21C6B1D614F4CBB919AB18C4A72BD', X'CDF21C6B1D614F4CBB919AB18C4A72BD', 2, NULL, NULL, 2, NULL, 0, 'Saut the garlic, onion, and bell peppers in olive oil for a few minutes, until onions are very soft.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9224C0E6378054EA63F005E96B9CF41', X'CDF21C6B1D614F4CBB919AB18C4A72BD', X'CDF21C6B1D614F4CBB919AB18C4A72BD', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the onions and peppers with the ground beef substitute, oatmeal, bread crumbs, 3 tablespoons of ketchup, garlic salt, and pepper. Mix well, using your hands if needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02AE10BC4EBA2745BD20ABD4C104288F', X'CDF21C6B1D614F4CBB919AB18C4A72BD', X'CDF21C6B1D614F4CBB919AB18C4A72BD', 4, NULL, NULL, 2, NULL, 0, 'Press into a lightly greased loaf pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDE9831AB42EC7489B0BE0864CF2A25A', X'CDF21C6B1D614F4CBB919AB18C4A72BD', X'CDF21C6B1D614F4CBB919AB18C4A72BD', 5, NULL, NULL, 2, NULL, 0, 'Cover with foil and bake for 30 minutes. Remove from the oven when done.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72B3AABE1263C04697EE414FE32D3305', X'CDF21C6B1D614F4CBB919AB18C4A72BD', X'CDF21C6B1D614F4CBB919AB18C4A72BD', 6, NULL, NULL, 2, NULL, 0, 'Combine ketchup, brown sugar, dry mustard, and optional nutmeg, whisking together until smooth.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25F66C606FC36A40B68B2941A161C834', X'CDF21C6B1D614F4CBB919AB18C4A72BD', X'CDF21C6B1D614F4CBB919AB18C4A72BD', 7, NULL, NULL, 2, NULL, 0, 'Spread or drizzle this topping over the top of the loaf.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A72590A98E13545A29F84F8E583EB24', X'CDF21C6B1D614F4CBB919AB18C4A72BD', X'CDF21C6B1D614F4CBB919AB18C4A72BD', 8, NULL, NULL, 2, NULL, 0, 'Cook, uncovered, for another 15 minutes, or until done.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F5C347C5EB3B746A936B40DC4DB9297', X'CDF21C6B1D614F4CBB919AB18C4A72BD', X'CDF21C6B1D614F4CBB919AB18C4A72BD', 9, NULL, NULL, 2, NULL, 0, 'Let your vegetarian meatloaf cool a fair amount before slicing into it to serve. Keep your loaf uncovered as it cools in order to allow a bit of extra moisture to release. This will help with allowing the loaf to firm up a bit as it cools.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19BEF12CA7521448A959BFAA9155F3BF', X'CDF21C6B1D614F4CBB919AB18C4A72BD', X'CDF21C6B1D614F4CBB919AB18C4A72BD', 10, NULL, NULL, 2, NULL, 0, 'After it has cooled, slice it up and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F298128DADD477418CD18007EC0CAA58', X'4194CCE827CE104B8C1A8C2A7BF6B419', X'4194CCE827CE104B8C1A8C2A7BF6B419', 0, NULL, NULL, 2, NULL, 0, 'Add olive oil to a large saucepan over medium heat. Once hot, add the ground beef and brown.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98509C140251DC4AACE0E7A571ED8FE7', X'4194CCE827CE104B8C1A8C2A7BF6B419', X'4194CCE827CE104B8C1A8C2A7BF6B419', 1, NULL, NULL, 2, NULL, 0, 'Once the beef is no longer pink, add the sauce and soup; stir well and simmer for about 1 hour. The time is not too critical. It just needs to be bubbling for a bit to blend flavors. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80FA5BBF9FF6884FB7F8D09D05E41B9E', X'F628E1982F546543843BB992CF967F87', X'F628E1982F546543843BB992CF967F87', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D022CAF8CFD9340B8E3C8208BFD88BC', X'F628E1982F546543843BB992CF967F87', X'F628E1982F546543843BB992CF967F87', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F / 180 C / Gas 4. Lightly grease or spray a 2 1/2 to 3-quart baking dish with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57C0F756849F3C47AE5F21D208C90B13', X'F628E1982F546543843BB992CF967F87', X'F628E1982F546543843BB992CF967F87', 2, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large skillet over medium heat. Add the ground beef, sausage, and onion. Cook, stirring until the meat is no longer pink and the onion is translucent.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A9E3FED50334F42989A5E38207D3488', X'F628E1982F546543843BB992CF967F87', X'F628E1982F546543843BB992CF967F87', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic to the meat mixture and cook, stirring, for 1 minute longer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F8F7BB1BE502B45BC238ED3B90F5A46', X'F628E1982F546543843BB992CF967F87', X'F628E1982F546543843BB992CF967F87', 4, NULL, NULL, 2, NULL, 0, 'Add the crushed tomatoes, tomato paste, basil, oregano, and crushed red pepper. Cook, stirring occasionally, for about 10 to 12 minutes. Taste and add salt and pepper, as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97FD2A9EFCED2B488C785DD7C2B71B3D', X'F628E1982F546543843BB992CF967F87', X'F628E1982F546543843BB992CF967F87', 5, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the ricotta cheese, 1/2 cup of Parmesan cheese, the egg, and 1 cup of Mozzarella cheese. Stir until well blended.');
