INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E4601B05E0EDD46A9893AB290018510', X'F628E1982F546543843BB992CF967F87', X'F628E1982F546543843BB992CF967F87', 7, NULL, NULL, 2, NULL, 0, 'Add the hot drained pasta to the bowl with the ricotta cheese mixture. Toss lightly to blend.');
