INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E239500939DA1141AF0878562AE812CC', X'F628E1982F546543843BB992CF967F87', X'F628E1982F546543843BB992CF967F87', 10, NULL, NULL, 2, NULL, 0, 'Spread about half of the meat sauce over the pasta, and then top with the remaining Parmesan cheese and half of the remaining Mozzarella.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0F8C2FE1CE21446861B6906C63FF54C', X'F628E1982F546543843BB992CF967F87', X'F628E1982F546543843BB992CF967F87', 11, NULL, NULL, 2, NULL, 0, 'Top with the remaining pasta mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEA3DB4B47DD4D41B4A7C632ADD2A322', X'F628E1982F546543843BB992CF967F87', X'F628E1982F546543843BB992CF967F87', 12, NULL, NULL, 2, NULL, 0, 'Then finish with the remaining meat sauce and remaining Mozzarella cheese and sprinkle with the chopped parsley.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B4F92CD447FCF4E9B1EA1C04A49E58E', X'F628E1982F546543843BB992CF967F87', X'F628E1982F546543843BB992CF967F87', 13, NULL, NULL, 2, NULL, 0, 'Bake for 25 minutes, or until hot and bubbly around the edges.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B66674AC5C0434A89CFA208AED6B7E6', X'F628E1982F546543843BB992CF967F87', X'F628E1982F546543843BB992CF967F87', 14, NULL, NULL, 2, NULL, 0, 'Serve with garlic bread and a tossed green salad or Caesar salad.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A970BCE1660CAD4B8F1E13ECA36D9980', X'D0B976397EE09F4A955ECC0A66EA428C', X'D0B976397EE09F4A955ECC0A66EA428C', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F /180 C.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6ADC95590E8F24DA221098858804479', X'D0B976397EE09F4A955ECC0A66EA428C', X'D0B976397EE09F4A955ECC0A66EA428C', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium heat, cook ground beef, onion, and garlic until meat is browned and no longer pink, stirring often to break up meat. Drain well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0640F654A6B1A4D99836EFDB6D3A4FD', X'D0B976397EE09F4A955ECC0A66EA428C', X'D0B976397EE09F4A955ECC0A66EA428C', 2, NULL, NULL, 2, NULL, 0, 'Return to heat and add chili powder, oregano, cumin, salt, and pepper; simmer for 10 minutes. Add refried beans and 1/2 cup of enchilada sauce. Heat thoroughly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B27399DDE60554FB853EBB34D066F75', X'D0B976397EE09F4A955ECC0A66EA428C', X'D0B976397EE09F4A955ECC0A66EA428C', 3, NULL, NULL, 2, NULL, 0, 'Wrap the flour tortillas in foil and place them in the preheated oven; heat for 10 minutes. Spoon about 1/2 cup of the ground beef mixture onto each tortilla. Roll tightly and place seam-side down on serving platter lined with shredded lettuce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BF1A9B050C4C141A1A8D522E4A691E4', X'D0B976397EE09F4A955ECC0A66EA428C', X'D0B976397EE09F4A955ECC0A66EA428C', 4, NULL, NULL, 2, NULL, 0, 'Spoon the remaining enchilada sauce over tortillas. Garnish and serve with toppings.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB5891712D0F6D4B866DF1164A86E8F3', X'AF6840F9D7D0C640A054DEED9DB6477B', X'AF6840F9D7D0C640A054DEED9DB6477B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A75328B3A4E5CF4B84C59A46F82C9CFB', X'AF6840F9D7D0C640A054DEED9DB6477B', X'AF6840F9D7D0C640A054DEED9DB6477B', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 375 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A70E100637CC354EA9BBD7B8B1D23FF6', X'AF6840F9D7D0C640A054DEED9DB6477B', X'AF6840F9D7D0C640A054DEED9DB6477B', 2, NULL, NULL, 2, NULL, 0, 'Heat the vegetable oil in a large skillet over medium heat. Crumble the ground beef into the skillet and add the chopped onion.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'303A61EB4D2ACC49B1B84057F67FCFF9', X'AF6840F9D7D0C640A054DEED9DB6477B', X'AF6840F9D7D0C640A054DEED9DB6477B', 3, NULL, NULL, 2, NULL, 0, 'Cook until the onion is translucent and the ground beef is no longer pink, stirring constantly. Drain off excess fat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D8A6ADCFB903949A59F3A83E085E2A9', X'AF6840F9D7D0C640A054DEED9DB6477B', X'AF6840F9D7D0C640A054DEED9DB6477B', 4, NULL, NULL, 2, NULL, 0, 'To the beef and onion mixture, add the minced garlic along with the steak or burger seasoning, salt, pepper, and breadcrumbs. Remove from heat and set aside to cool slightly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'322420ABDC163F49B27F8827BD01DC75', X'AF6840F9D7D0C640A054DEED9DB6477B', X'AF6840F9D7D0C640A054DEED9DB6477B', 5, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk the eggs with the mustard, ketchup, and Worcestershire sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'629AA46702F5F448ACF68C5B3ACA8909', X'AF6840F9D7D0C640A054DEED9DB6477B', X'AF6840F9D7D0C640A054DEED9DB6477B', 6, NULL, NULL, 2, NULL, 0, 'Stir in chopped dill pickle, if using.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E7050C3C15A924A8F98B98BF77A85A5', X'AF6840F9D7D0C640A054DEED9DB6477B', X'AF6840F9D7D0C640A054DEED9DB6477B', 7, NULL, NULL, 2, NULL, 0, 'Stir the egg mixture into the cooled ground beef mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61AC272328CBAB41B429502A29A8B9C2', X'AF6840F9D7D0C640A054DEED9DB6477B', X'AF6840F9D7D0C640A054DEED9DB6477B', 8, NULL, NULL, 2, NULL, 0, 'Add about 1/2 cup of the cheese and stir to blend.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8903EF1F1A07C64E94CC95EA41E0E60E', X'AF6840F9D7D0C640A054DEED9DB6477B', X'AF6840F9D7D0C640A054DEED9DB6477B', 9, NULL, NULL, 2, NULL, 0, 'Pack the meat and cheese mixture into the pie shell and bake for 30 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3D83DB6AE2A6649A2181189C7970DC5', X'AF6840F9D7D0C640A054DEED9DB6477B', X'AF6840F9D7D0C640A054DEED9DB6477B', 10, NULL, NULL, 2, NULL, 0, 'Top with the sliced tomatoes and bacon, if using, and then top with remaining shredded cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BC7278CB3D64A47929848115CBB8298', X'AF6840F9D7D0C640A054DEED9DB6477B', X'AF6840F9D7D0C640A054DEED9DB6477B', 11, NULL, NULL, 2, NULL, 0, 'Bake the cheeseburger pie for 5 to 10 minutes longer, or until cheese topping has melted and the crust has browned.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A75D0FD96D80A54683778B5693F2EF01', X'AF6840F9D7D0C640A054DEED9DB6477B', X'AF6840F9D7D0C640A054DEED9DB6477B', 12, NULL, NULL, 2, NULL, 0, 'Serve with your choice of sides and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D05D3C7D10DEEB46AC9BFD6D8CEB0894', X'4832A9AD0F84C9449A9FF660A97EA1A4', X'4832A9AD0F84C9449A9FF660A97EA1A4', 0, NULL, NULL, 2, NULL, 0, 'Divide ground beef into three equal parts. Spread each part of the ground beef out on a piece of plastic wrap, spreading into a thin, large square. The meat should be about a third of an inch thick. Spread about one-third of the macaroni and cheese. This might be too much so adjust as necessary.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09ADF2C94F9B3243AE74D332C9B909AF', X'4832A9AD0F84C9449A9FF660A97EA1A4', X'4832A9AD0F84C9449A9FF660A97EA1A4', 1, NULL, NULL, 2, NULL, 0, 'Using the plastic wrap, roll the whole packed into a large, hot dog shape, making sure to keep the macaroni inside. Seal the edges of the meat to make a stuffed burger dog. Continue with remaining ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82E50ABA89B731478745039816710216', X'4832A9AD0F84C9449A9FF660A97EA1A4', X'4832A9AD0F84C9449A9FF660A97EA1A4', 2, NULL, NULL, 2, NULL, 0, 'Preheat grill to a medium-high heat. Gently place burger dogs over direct heat and grill for about 15 minutes, rolling to cook evenly. Remove from grill, place on hoagie buns and top with melted cheese sauce and jalapenos. Omit jalapenos if preferred.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D3DA3AA9050D3488E5A30EEAF191D14', X'6E41254FBDC042489189FD393567653D', X'6E41254FBDC042489189FD393567653D', 0, NULL, NULL, 2, NULL, 0, 'In a large skillet, cook the ground beef, onion, bell pepper, and garlic until the meat is browned and the vegetables are tender, stirring to break up the meat. Drain well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE15073DFB1366469AF86C2443130051', X'6E41254FBDC042489189FD393567653D', X'6E41254FBDC042489189FD393567653D', 1, NULL, NULL, 2, NULL, 0, 'Add the pasta sauce, diced tomatoes with their liquid, vinegar, sugar, paprika, marjoram, salt, pepper, and water to the skillet with the ground beef and onions and cook for 8 to 10 minutes or until the mixture starts to boil, stirring frequently.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70DE51B8057F3345A997CFFAFE0251F4', X'6E41254FBDC042489189FD393567653D', X'6E41254FBDC042489189FD393567653D', 2, NULL, NULL, 2, NULL, 0, 'Add the pasta to the skillet and bring the mixture back to a simmer. Simmer the food, uncovered, stirring frequently, until the pasta is tender, about 11 to 16 minutes. Sprinkle everything with fresh parsley, if using, and serve immediately.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6199A47102A4F842993C15AD2E6B6B27', X'0A1D9BB76B14AF42A5F2240B2C24164C', X'0A1D9BB76B14AF42A5F2240B2C24164C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFAD24D86AD50D4B9BE852CF4B79597C', X'0A1D9BB76B14AF42A5F2240B2C24164C', X'0A1D9BB76B14AF42A5F2240B2C24164C', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2C2A5F8A769CF489ABC7A6C2D1153FE', X'0A1D9BB76B14AF42A5F2240B2C24164C', X'0A1D9BB76B14AF42A5F2240B2C24164C', 2, NULL, NULL, 2, NULL, 0, 'Combine chili sauce, grape jelly, and mustard in the crockpot and stir well. Cook covered on HIGH while preparing the meatballs.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28BDD96027D2CD49B69BDC8C21F39168', X'0A1D9BB76B14AF42A5F2240B2C24164C', X'0A1D9BB76B14AF42A5F2240B2C24164C', 3, NULL, NULL, 2, NULL, 0, 'Line a rimmed baking sheet with foil. Grease or spray with cooking oil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBD68808C330AA40883ECE52655A02A5', X'0A1D9BB76B14AF42A5F2240B2C24164C', X'0A1D9BB76B14AF42A5F2240B2C24164C', 4, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the ground beef with the beaten egg, bread crumbs, Worcestershire sauce, and salt. Mix thoroughly with a wooden spoon or your hands, if needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09351065E3070F4EA25F12B56FDF01EE', X'0A1D9BB76B14AF42A5F2240B2C24164C', X'0A1D9BB76B14AF42A5F2240B2C24164C', 5, NULL, NULL, 2, NULL, 0, 'Shape into about 2 or 3 dozen (half-ounce) meatballs or 16 to 18 (one ounce) meatballs. Arrange the meatballs in the prepared baking pan and bake for 12 to 18 minutes, or until cooked through. Alternatively, place a cooling rack in the pan and bake the meatballs on the rack.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C21DAA61F4D6D946B80A1BB909D5B2AF', X'0A1D9BB76B14AF42A5F2240B2C24164C', X'0A1D9BB76B14AF42A5F2240B2C24164C', 6, NULL, NULL, 2, NULL, 0, 'Once cooked, discard any liquid from the baking pan and transfer the meatballs to the crockpot. Stir to coat, cover, and cook on LOW for 3 to 4 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89CDA1A0E857E44DACD180161A9AE210', X'0A1D9BB76B14AF42A5F2240B2C24164C', X'0A1D9BB76B14AF42A5F2240B2C24164C', 7, NULL, NULL, 2, NULL, 0, 'Stir well before serving.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'701365833A39EB46ACFFBB136BDF3FFE', X'0A1D9BB76B14AF42A5F2240B2C24164C', X'0A1D9BB76B14AF42A5F2240B2C24164C', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6B7051B8135164F80931248A82B62C3', X'97DDFFE0191C744ABA113A35CA7AD1D2', X'97DDFFE0191C744ABA113A35CA7AD1D2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9DCF536D6E7E74BA52FFBC9BE797F28', X'97DDFFE0191C744ABA113A35CA7AD1D2', X'97DDFFE0191C744ABA113A35CA7AD1D2', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium-low heat, cook the bacon until just crisp. You can either cook the bacon whole or cut the bacon into chunks before cooking. Once crisp, remove the bacon to paper towels to drain. Leave about 2 teaspoons of bacon drippings in the skillet and discard the remaining drippings.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66FDF461D362E2488111BB40B4F73642', X'97DDFFE0191C744ABA113A35CA7AD1D2', X'97DDFFE0191C744ABA113A35CA7AD1D2', 2, NULL, NULL, 2, NULL, 0, 'Place the skillet back over medium heat and add the ground beef and chopped onions. Cook until the beef is no longer pink, stirring and breaking up larger chunks. With a slotted spoon or spatula, transfer the ground beef to the crockery insert of the slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09351BF8D4ED6748AAE01AA8665E8AB6', X'97DDFFE0191C744ABA113A35CA7AD1D2', X'97DDFFE0191C744ABA113A35CA7AD1D2', 3, NULL, NULL, 2, NULL, 0, 'Add the brown sugar, dry mustard, baked beans, two cans of drained beans, ketchup or barbecue sauce, and vinegar. Taste and season with kosher salt and freshly ground black pepper.');
