INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66F4020BC0053148922C61965F83E93C', X'F9420D4FC8891A4E9280E29BA66F6A3C', X'F9420D4FC8891A4E9280E29BA66F6A3C', 3, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F/180 C/Gas Mark 4.');
