INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61925752FF0D134E9939544F85626860', X'F9420D4FC8891A4E9280E29BA66F6A3C', X'F9420D4FC8891A4E9280E29BA66F6A3C', 5, NULL, NULL, 2, NULL, 0, 'Place a portion of the ground beef mixture into the center of a cabbage leaf.');
