INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC17BDD679E406419C1C7072009070BD', X'F9420D4FC8891A4E9280E29BA66F6A3C', X'F9420D4FC8891A4E9280E29BA66F6A3C', 11, NULL, NULL, 2, NULL, 0, 'Remove the cabbage rolls to a bowl with a slotted spoon. Take out the toothpicks and discard.');
