INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57660D645055674DB960408BF6D807AD', X'F9420D4FC8891A4E9280E29BA66F6A3C', X'F9420D4FC8891A4E9280E29BA66F6A3C', 13, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
