INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'323AFF3676C12B4BBA251F3C1A6E7BDA', X'221BEF3EFFF919448987FFA4839DBF8A', X'221BEF3EFFF919448987FFA4839DBF8A', 3, NULL, NULL, 2, NULL, 0, 'Combine the ground beef mixture with the bell pepper, celery, tomatoes, tomato sauce, drained beans, and seasonings. Transfer to the crockery insert of a slow cooker.');
