INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A736F80725142040908AC24D008B27E8', X'221BEF3EFFF919448987FFA4839DBF8A', X'221BEF3EFFF919448987FFA4839DBF8A', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53C3A206E194E94FA0B53E7E5BD937E7', X'38607017E880624F9107ED5542B0D1A3', X'38607017E880624F9107ED5542B0D1A3', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 375 F (190 C/Gas 5).');
