INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3699B2F86010664E83F729FAB3ED5082', X'38607017E880624F9107ED5542B0D1A3', X'38607017E880624F9107ED5542B0D1A3', 3, NULL, NULL, 2, NULL, 0, 'Arrange the zucchini halves in the prepared baking pan, cut and hollowed side up. Set aside while you make the taco beef mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC41E4343F49F24A94E9AA7382ECA3DC', X'38607017E880624F9107ED5542B0D1A3', X'38607017E880624F9107ED5542B0D1A3', 4, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large skillet or saute pan over medium heat. Add the onions and cook, stirring, for about 3 minutes. Add the ground beef and continue cooking, stirring and breaking up until the beef is no longer pink and the onion is translucent. Drain off excess fat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B1CC46EE451B94BACBDD445F8DC7CA7', X'38607017E880624F9107ED5542B0D1A3', X'38607017E880624F9107ED5542B0D1A3', 5, NULL, NULL, 2, NULL, 0, 'Add the garlic to the ground beef and cook for about 2 minutes longer. Stir in the taco seasoning, salsa, and water; blend well and cook, stirring, until hot and bubbly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96DE62309BD26E4791806FAD7C8B09DF', X'38607017E880624F9107ED5542B0D1A3', X'38607017E880624F9107ED5542B0D1A3', 6, NULL, NULL, 2, NULL, 0, 'Spoon about 1/2 cup of the ground beef mixture into each zucchini shell, mounding it a bit.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F17443AA10FC714EB7422A4D554DA4E0', X'38607017E880624F9107ED5542B0D1A3', X'38607017E880624F9107ED5542B0D1A3', 7, NULL, NULL, 2, NULL, 0, 'Bake for about 35 to 45 minutes, until the zucchini is fork-tender. Top the zucchini with the shredded cheese and continue baking for a few minutes longer, or just until the cheese has melted.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91C312C6D9549E44B63F979F1B245180', X'38607017E880624F9107ED5542B0D1A3', X'38607017E880624F9107ED5542B0D1A3', 8, NULL, NULL, 2, NULL, 0, 'Serve with taco sauce and garnish with lettuce and sour cream or guacamole, or use your favorite toppings. Sliced ripe olives, diced tomatoes, jalapeno rings, or more cheese are all excellent choices.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6526A97366075D4282660800702D64F3', X'EDC4AAD2CBC9E24EA7E0A9713281A92F', X'EDC4AAD2CBC9E24EA7E0A9713281A92F', 0, NULL, NULL, 2, NULL, 0, 'In a small bowl or cup, combine the chili powder with the dried minced onion, cumin, oregano, sugar, garlic powder, and the black and cayenne peppers. Blend well and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C14AC23097DA55489348473976655CDF', X'EDC4AAD2CBC9E24EA7E0A9713281A92F', X'EDC4AAD2CBC9E24EA7E0A9713281A92F', 1, NULL, NULL, 2, NULL, 0, 'In a Dutch oven or large saut pan over medium heat, brown the ground beef, turning and breaking up with a spatula as it cooks.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E39E6420E5C62F42B2A6CD4DEBC3F4BB', X'EDC4AAD2CBC9E24EA7E0A9713281A92F', X'EDC4AAD2CBC9E24EA7E0A9713281A92F', 2, NULL, NULL, 2, NULL, 0, 'Drain the ground beef well. Return the beef to the pan over medium heat. Add the chili seasoning mix and stir to blend.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'730C0E9B6E3F264C8853947EB45024DE', X'EDC4AAD2CBC9E24EA7E0A9713281A92F', X'EDC4AAD2CBC9E24EA7E0A9713281A92F', 3, NULL, NULL, 2, NULL, 0, 'Add the 2 cans of tomatoes, tomato sauce, and undrained black beans. Stir to blend ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95D79A2072FD23448BC8DE7DBA393239', X'EDC4AAD2CBC9E24EA7E0A9713281A92F', X'EDC4AAD2CBC9E24EA7E0A9713281A92F', 4, NULL, NULL, 2, NULL, 0, 'Bring the chili to a simmer. Reduce the heat to low and simmer, stirring occasionally, for 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7232963E787D2D409FB735F552462576', X'EDC4AAD2CBC9E24EA7E0A9713281A92F', X'EDC4AAD2CBC9E24EA7E0A9713281A92F', 5, NULL, NULL, 2, NULL, 0, 'Taste and add salt, as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCC9A05A0F26454F95C032DA0B3EAF86', X'EDC4AAD2CBC9E24EA7E0A9713281A92F', X'EDC4AAD2CBC9E24EA7E0A9713281A92F', 6, NULL, NULL, 2, NULL, 0, 'Serve with a Tex-Mex cornbread or classic Southern cornbread, biscuits, crackers, or tortilla chips, along with shredded cheese and your favorite toppings.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EA07FB874A9AB4AB25BAFF2D94A7028', X'4C07E9EDADF6B04F88797E229E5FFB15', X'4C07E9EDADF6B04F88797E229E5FFB15', 0, NULL, NULL, 2, NULL, 0, 'Lightly grease a 2 1/2 to 3-quart baking dish. Heat oven to 350 F (180 C/Gas 4).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EB7506328739B43BDB40DB30460D533', X'4C07E9EDADF6B04F88797E229E5FFB15', X'4C07E9EDADF6B04F88797E229E5FFB15', 1, NULL, NULL, 2, NULL, 0, 'Slice the tortillas into strips about 1 1/2 to 2 inches wide. Arrange half of the strips in the bottom of the baking dish. Set the remaining strips aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E671950E8EC2849801418F1EF806B5E', X'4C07E9EDADF6B04F88797E229E5FFB15', X'4C07E9EDADF6B04F88797E229E5FFB15', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet, brown the ground beef, chorizo, and onion, stirring, until beef and sausage are no longer pink. Add the jalapeo peppers and burrito seasoning and stir to blend. Stir in the refried beans and continue cooking, stirring, until hot and bubbly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E1F9949DBC1DA4EA261C497EDDDAC05', X'4C07E9EDADF6B04F88797E229E5FFB15', X'4C07E9EDADF6B04F88797E229E5FFB15', 3, NULL, NULL, 2, NULL, 0, 'Spoon the hot filling mixture evenly over the tortilla layer. Sprinkle with the diced tomatoes, green onions, and cilantro. Top with the remaining tortilla strips then sprinkle with the cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4FA6558264E4A4EA54FB03FFAC3B35A', X'4C07E9EDADF6B04F88797E229E5FFB15', X'4C07E9EDADF6B04F88797E229E5FFB15', 4, NULL, NULL, 2, NULL, 0, 'Bake for about 10 to 15 minutes, or until the cheese has melted and the casserole is bubbly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28C3E16ED521914F8E401B873AE61624', X'BA55E20560BA1A48BC949133E70C8D2A', X'BA55E20560BA1A48BC949133E70C8D2A', 0, NULL, NULL, 2, NULL, 0, 'Cut the bacon into small pieces; brown in a large skillet. Remove bacon from the rendered fat and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C8CA84942C1B74A85F3D5C437504EE2', X'BA55E20560BA1A48BC949133E70C8D2A', X'BA55E20560BA1A48BC949133E70C8D2A', 1, NULL, NULL, 2, NULL, 0, 'In the bacon drippings, saut the onion and bell pepper until tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98A1CE0CA0DB4F49AE264CA3DC694CA8', X'BA55E20560BA1A48BC949133E70C8D2A', X'BA55E20560BA1A48BC949133E70C8D2A', 2, NULL, NULL, 2, NULL, 0, 'Add the ground beef and brown lightly. Put the meats into the bottom of a crockpot. Add the can of pork and beans.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76DDA68FC717C54D87B42E82014CEFD3', X'BA55E20560BA1A48BC949133E70C8D2A', X'BA55E20560BA1A48BC949133E70C8D2A', 3, NULL, NULL, 2, NULL, 0, 'Drain most of the liquids from the remaining cans of beans and add them to the slow cooker with the remaining ingredients. Mix well to blend.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C841050E47F5E54F95EC55820C58C033', X'BA55E20560BA1A48BC949133E70C8D2A', X'BA55E20560BA1A48BC949133E70C8D2A', 4, NULL, NULL, 2, NULL, 0, 'Cook on high setting for 1 hour, and then set to low and continue cooking for 5 to 6 hours. Stir occasionally.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D85D03C005F9347B3ED6884CC909F57', X'BA55E20560BA1A48BC949133E70C8D2A', X'BA55E20560BA1A48BC949133E70C8D2A', 5, NULL, NULL, 2, NULL, 0, 'Check for seasonings and correct if desired, with a little salt and pepper, or more brown sugar or syrup.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3DBDD5AC1340A40AE1BD3C9F5CD0D82', X'A8288CF82567C14A8BBE7F3AFDD3E355', X'A8288CF82567C14A8BBE7F3AFDD3E355', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC8B7BE526580F4D97C1C025EF02DBD9', X'A8288CF82567C14A8BBE7F3AFDD3E355', X'A8288CF82567C14A8BBE7F3AFDD3E355', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCF79A3B3F4B9A40907471761283BC9D', X'A8288CF82567C14A8BBE7F3AFDD3E355', X'A8288CF82567C14A8BBE7F3AFDD3E355', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, soak the bread crumbs in milk.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF6D74D8A6E13C408CAF54F8303461FB', X'A8288CF82567C14A8BBE7F3AFDD3E355', X'A8288CF82567C14A8BBE7F3AFDD3E355', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, mix the ground beef with the bread crumb mixture, along with the salt, pepper, onion, and the beaten egg.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5EB8DCE8730C14F8F6E781E6D8649A6', X'A8288CF82567C14A8BBE7F3AFDD3E355', X'A8288CF82567C14A8BBE7F3AFDD3E355', 4, NULL, NULL, 2, NULL, 0, 'Pat the meat mixture into a 9-inch pie plate. Bake in the preheated oven for 30 minutes. Remove the ground beef from the oven and carefully pour off the excess fat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B60256812890E42BCCE01A6BC6687B8', X'A8288CF82567C14A8BBE7F3AFDD3E355', X'A8288CF82567C14A8BBE7F3AFDD3E355', 5, NULL, NULL, 2, NULL, 0, 'Top the meat with the mashed potatoes; sprinkle with cheese. If desired, sprinkle lightly with paprika.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A96CBE00FA96CF48A35F2E9BE6FE5FA1', X'A8288CF82567C14A8BBE7F3AFDD3E355', X'A8288CF82567C14A8BBE7F3AFDD3E355', 6, NULL, NULL, 2, NULL, 0, 'Return to oven for about 15 minutes longer, or until the cheese is melted and potatoes are thoroughly heated.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7B2E117F7748941AD82A02B5B7B8265', X'69FF71598D895740A283501023181A12', X'69FF71598D895740A283501023181A12', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23F2B353693BF74398F958D316924DE4', X'69FF71598D895740A283501023181A12', X'69FF71598D895740A283501023181A12', 1, NULL, NULL, 2, NULL, 0, 'Place the ground beef in a bowl and add the marinade ingredients, adding the cornstarch last. Marinate the ground beef for 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07B0121A1DC0EF428DD588BE080F27DC', X'69FF71598D895740A283501023181A12', X'69FF71598D895740A283501023181A12', 2, NULL, NULL, 2, NULL, 0, 'Prepare the other vegetables while the meat is marinating.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5574F01C774E144D844BB5BA945E1325', X'69FF71598D895740A283501023181A12', X'69FF71598D895740A283501023181A12', 3, NULL, NULL, 2, NULL, 0, 'Add the oil to a wok preheated on high heat. When the oil is hot, add the ginger slices, and stir-fry for about 30 seconds. (This is to flavor the oil). Remove the ginger slices.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D588AA5E6F790C46A0E3F4D728C3716E', X'69FF71598D895740A283501023181A12', X'69FF71598D895740A283501023181A12', 4, NULL, NULL, 2, NULL, 0, 'Add the ground beef to the wok and cook, using a spatula to break up the pieces, for 1 to 2 minutes, until it changes color.');
