INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9565CBF73418CC49AE9321B141E05613', X'69FF71598D895740A283501023181A12', X'69FF71598D895740A283501023181A12', 6, NULL, NULL, 2, NULL, 0, 'Turn the heat down to medium, and add 1 tablespoon of curry powder and stir briefly until it becomes aromatic.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE16F68C5067A5499FFD668F32DDC14D', X'69FF71598D895740A283501023181A12', X'69FF71598D895740A283501023181A12', 7, NULL, NULL, 2, NULL, 0, 'Add the rice, stirring and turning it over with a spatula to heat through and break up any clumps. Stir in more curry powder if desired (approximately another 1/2 tablespoon).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BE847F3CE79714EA284356F68BD6B1D', X'69FF71598D895740A283501023181A12', X'69FF71598D895740A283501023181A12', 8, NULL, NULL, 2, NULL, 0, 'Add the peas and the shredded carrot and combine thoroughly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'638A65E6D24CFD40A1A634B4B46FEDCF', X'69FF71598D895740A283501023181A12', X'69FF71598D895740A283501023181A12', 9, NULL, NULL, 2, NULL, 0, 'Make a small amount of room in a corner of the pan and add the beaten egg. Scramble the egg and then stir to mix in with the rice. Stir in the scallion.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FB0336243CC2E49B5D37B9798AFFE6E', X'69FF71598D895740A283501023181A12', X'69FF71598D895740A283501023181A12', 10, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8334032C9DD714EA4C4B90DF893C5F3', X'69FF71598D895740A283501023181A12', X'69FF71598D895740A283501023181A12', 11, NULL, NULL, 2, NULL, 0, 'Serve hot and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AFA111C31B7BC4E966493158B2C7DAB', X'319E9143D227024D8F36F275179FFCCB', X'319E9143D227024D8F36F275179FFCCB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'592E27970894D74CB22FA58E88CE3F31', X'319E9143D227024D8F36F275179FFCCB', X'319E9143D227024D8F36F275179FFCCB', 1, NULL, NULL, 2, NULL, 0, 'Line a rimmed baking pan with foil; lightly oil the foil or spray with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C1845523ADF634CB7944A7F7B3CCC14', X'319E9143D227024D8F36F275179FFCCB', X'319E9143D227024D8F36F275179FFCCB', 2, NULL, NULL, 2, NULL, 0, 'Cut the bacon strips in half crosswise and coat them with the 2 tablespoons of brown sugar, if using. Arrange the bacon on a foil-lined rimmed baking sheet and bake for 15 to 20 minutes, or until almost crisp. Remove the bacon from the oven and let it cool.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AE940B6B85C1840B9B8E7914FC948D1', X'319E9143D227024D8F36F275179FFCCB', X'319E9143D227024D8F36F275179FFCCB', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the ground beef, pork, onion, bell pepper, breadcrumbs, cheese, milk, egg, Cajun seasonings, Worcestershire sauce, salt, and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E7E4089DA3FBA4FAD3E0EC199128A6F', X'319E9143D227024D8F36F275179FFCCB', X'319E9143D227024D8F36F275179FFCCB', 4, NULL, NULL, 2, NULL, 0, 'Put the ground meat mixture on the prepared foil-lined baking pan and shape into a loaf.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E72DE70EDD86F24E947A922192983803', X'319E9143D227024D8F36F275179FFCCB', X'319E9143D227024D8F36F275179FFCCB', 5, NULL, NULL, 2, NULL, 0, 'Spread barbecue sauce over the loaf and arrange the bacon strips over the top.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6F7D681485C5844B3F03D5354609FB1', X'319E9143D227024D8F36F275179FFCCB', X'319E9143D227024D8F36F275179FFCCB', 6, NULL, NULL, 2, NULL, 0, 'Bake the meatloaf until the bacon is crispy and loaf is firm, about 1 hour to 1 hour and 10 minutes. If unsure, check the loaf with an instant-read food thermometer inserted into the center of the loaf. The minimum safe temperature for ground meat is 160 F/71 C.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA91FABE1B7FDA429114C75AB77E964F', X'319E9143D227024D8F36F275179FFCCB', X'319E9143D227024D8F36F275179FFCCB', 7, NULL, NULL, 2, NULL, 0, 'Let stand for 10 minutes before slicing or moving to a serving platter.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C86C408AA0E2945A6CCB206C427A01C', X'BC63C11F4BC4E84FA507328A31CC368F', X'BC63C11F4BC4E84FA507328A31CC368F', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine ground beef with the beaten egg, 1 tablespoon cornstarch, the salt and pepper, 2 teaspoons of soy sauce, bread crumbs, and minced onion. Shape into 1 1/2 in meatballs.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'208129282AEE794C9AFDDC472A40CFD6', X'BC63C11F4BC4E84FA507328A31CC368F', X'BC63C11F4BC4E84FA507328A31CC368F', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium heat, lightly brown meatballs in the vegetable oil. Drain well. If you prefer, you may also bake the meatballs in a foil-lined rimmed baking pan in a 350 F oven for about 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9F987E679CB444D9CD05E924DFA28AB', X'BC63C11F4BC4E84FA507328A31CC368F', X'BC63C11F4BC4E84FA507328A31CC368F', 2, NULL, NULL, 2, NULL, 0, 'Transfer the browned meatballs to slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D35E53DCCA785488E3156DEF17916CC', X'BC63C11F4BC4E84FA507328A31CC368F', X'BC63C11F4BC4E84FA507328A31CC368F', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine vinegar, cornstarch, sugar, 1 tablespoon soy sauce, and bell pepper; stir to blend. Pour over meatballs.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97AA5E3EA02C664C8EE61DEDA7804DC1', X'BC63C11F4BC4E84FA507328A31CC368F', X'BC63C11F4BC4E84FA507328A31CC368F', 4, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 5 to 7 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'859A5A8D1DAC3F42A4D04A1C87C02747', X'BC63C11F4BC4E84FA507328A31CC368F', X'BC63C11F4BC4E84FA507328A31CC368F', 5, NULL, NULL, 2, NULL, 0, 'In the last 30 minutes of cooking, stir in pineapple with juice. If thicker juices are desired, combine more cornstarch with a little cold water and add, stirring, until thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75201BDF1CBED04EBA50E708C88CA59C', X'BC63C11F4BC4E84FA507328A31CC368F', X'BC63C11F4BC4E84FA507328A31CC368F', 6, NULL, NULL, 2, NULL, 0, 'Serve these sweet and sour meatballs with hot cooked rice or noodles.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEA1C873984BA847B51812D030C8C609', X'A1959E99697D5141862DE8E500EC9F83', X'A1959E99697D5141862DE8E500EC9F83', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CB667E841046E4DB6B5913A9519167D', X'A1959E99697D5141862DE8E500EC9F83', X'A1959E99697D5141862DE8E500EC9F83', 1, NULL, NULL, 2, NULL, 0, 'Chop the tomato (or measure out and drain canned tomatoes) and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14788893E651A64FA341EB5251A0356D', X'A1959E99697D5141862DE8E500EC9F83', X'A1959E99697D5141862DE8E500EC9F83', 2, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9918BC466024D444816BAA6632F1BB90', X'A1959E99697D5141862DE8E500EC9F83', X'A1959E99697D5141862DE8E500EC9F83', 3, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water and the tablespoon salt to a boil and carefully drop the peppers into the boiling water. Bring back to a boil, and boil the peppers for 5 minutes. Remove and drain.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E3D9F9D4CE32B4A8FA5CBC1B121C19F', X'A1959E99697D5141862DE8E500EC9F83', X'A1959E99697D5141862DE8E500EC9F83', 4, NULL, NULL, 2, NULL, 0, 'Prepare the peppers by cutting around the stem on the top and removing the stem. Remove as many of the seeds as possible without cutting into the bottoms or sides of the peppers.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06F29E3BD3104045BCD791D5A2A1551D', X'A1959E99697D5141862DE8E500EC9F83', X'A1959E99697D5141862DE8E500EC9F83', 5, NULL, NULL, 2, NULL, 0, 'In a large skillet, cook the ground pork and beef over medium-high heat until the meat is browned, then add the hot water. Cook until the water evaporates, about 5 minutes, then add the tomato and onion and cook for 10 minutes, stirring often.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3390D87ADD3604F891E9460C9CA20DD', X'A1959E99697D5141862DE8E500EC9F83', X'A1959E99697D5141862DE8E500EC9F83', 6, NULL, NULL, 2, NULL, 0, 'Stir the cooked rice, 1 teaspoon salt, black pepper, and white pepper into the meat mixture and stir to combine. Remove from the heat and stuff the peppers with the meat mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3764D1C461995D48A18135AEA346A06C', X'A1959E99697D5141862DE8E500EC9F83', X'A1959E99697D5141862DE8E500EC9F83', 7, NULL, NULL, 2, NULL, 0, 'Put the stuffed peppers into a baking dish that holds them snugly, and bake in the preheated oven, uncovered, for 30 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7FC40EAEE747F499233304D24048676', X'A1959E99697D5141862DE8E500EC9F83', X'A1959E99697D5141862DE8E500EC9F83', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B091908790150489A73934B693D33D6', X'27E6F7C2C279D2429AA1B007D5BF9C77', X'27E6F7C2C279D2429AA1B007D5BF9C77', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'358E689E49AF58488FFF28B6620B0B31', X'27E6F7C2C279D2429AA1B007D5BF9C77', X'27E6F7C2C279D2429AA1B007D5BF9C77', 1, NULL, NULL, 2, NULL, 0, 'In a 3-quart microwave-safe casserole, place beef, onions, and garlic, crumbling beef to break into small pieces.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'355E237C1D500E4AA3AF6A7E13A65F13', X'27E6F7C2C279D2429AA1B007D5BF9C77', X'27E6F7C2C279D2429AA1B007D5BF9C77', 2, NULL, NULL, 2, NULL, 0, 'Microwave on high for 3 to 4 minutes, stirring once to break up meat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E530D3AF7906DE479E7E39F98ACBCC8C', X'27E6F7C2C279D2429AA1B007D5BF9C77', X'27E6F7C2C279D2429AA1B007D5BF9C77', 3, NULL, NULL, 2, NULL, 0, 'Microwave on high for 2 more minutes until the beef is browned and thoroughly cooked. Drain off any excess fat, if necessary.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6F5DD6BE1EEF24292A6B128080ED0DD', X'27E6F7C2C279D2429AA1B007D5BF9C77', X'27E6F7C2C279D2429AA1B007D5BF9C77', 4, NULL, NULL, 2, NULL, 0, 'Add spaghetti sauce, water, basil, and uncooked spaghetti in the casserole dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12EC7530409ACA4E970B3C01C7398AF8', X'27E6F7C2C279D2429AA1B007D5BF9C77', X'27E6F7C2C279D2429AA1B007D5BF9C77', 5, NULL, NULL, 2, NULL, 0, 'Mix gently to combine.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B12D2820D95634DB2FFED8046B81381', X'27E6F7C2C279D2429AA1B007D5BF9C77', X'27E6F7C2C279D2429AA1B007D5BF9C77', 6, NULL, NULL, 2, NULL, 0, 'Cover with microwave-safe plastic wrap, venting one corner, and microwave on high for 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'056C14E33363E84BA9238B3B5FA56E93', X'27E6F7C2C279D2429AA1B007D5BF9C77', X'27E6F7C2C279D2429AA1B007D5BF9C77', 7, NULL, NULL, 2, NULL, 0, 'Stir well. Cover casserole again and microwave on high for 8 to 10 minutes longer. Stir well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'482B4C708564244FAD35846BFA92A009', X'27E6F7C2C279D2429AA1B007D5BF9C77', X'27E6F7C2C279D2429AA1B007D5BF9C77', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle with cheese, cover again, and microwave on high for 7 to 8 minutes longer until spaghetti is tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2C06A9AE0B64242977339686985BD8F', X'27E6F7C2C279D2429AA1B007D5BF9C77', X'27E6F7C2C279D2429AA1B007D5BF9C77', 9, NULL, NULL, 2, NULL, 0, 'Let stand 4 minutes before serving; don''t skip this step! The food has to stand so the heat distributes evenly and there are no super hot spots you could burn yourself on.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E56F43CAF2D12F419398B2E13C2DB3A2', X'27E6F7C2C279D2429AA1B007D5BF9C77', X'27E6F7C2C279D2429AA1B007D5BF9C77', 10, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A977518130C0944A72E191C7ECBDFA6', X'DB34388366ABB74D88943F77B1FE6C03', X'DB34388366ABB74D88943F77B1FE6C03', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 375 F/190 C/Gas 5.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89B27C121F21B145A3A8A8300AFD4806', X'DB34388366ABB74D88943F77B1FE6C03', X'DB34388366ABB74D88943F77B1FE6C03', 1, NULL, NULL, 2, NULL, 0, 'Unroll the crescent roll dough and press into the bottom of an ungreased 13-by-9-by-2-inch baking pan to form the thin crust (see the photo).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD51F5163226194BB9613FBE7E0EBA93', X'DB34388366ABB74D88943F77B1FE6C03', X'DB34388366ABB74D88943F77B1FE6C03', 2, NULL, NULL, 2, NULL, 0, 'Heat the vegetable oil in a skillet over medium heat. Add the ground beef, onions, and mushrooms to the skillet. Cook, stirring, until the ground beef is no longer pink and the mushrooms and onions are tender. Add the paprika and season with salt and pepper, to taste. Stir in sour cream until well blended.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9195FA84C8E6A945821B78A231964786', X'DB34388366ABB74D88943F77B1FE6C03', X'DB34388366ABB74D88943F77B1FE6C03', 3, NULL, NULL, 2, NULL, 0, 'Spoon the ground beef mixture into the baking pan and spread over the crust. Sprinkle evenly with the shredded cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67DAF64CD1CF6B40BB639396239BA47C', X'DB34388366ABB74D88943F77B1FE6C03', X'DB34388366ABB74D88943F77B1FE6C03', 4, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 25 to 30 minutes, or until crust is baked and browned around the edges.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0A0D31D00BC624482AC57156EFEA7F5', X'DB34388366ABB74D88943F77B1FE6C03', X'DB34388366ABB74D88943F77B1FE6C03', 5, NULL, NULL, 2, NULL, 0, 'Cut into squares to serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'515CA725CEC3FD4B9724531FA6C3283A', X'DB34388366ABB74D88943F77B1FE6C03', X'DB34388366ABB74D88943F77B1FE6C03', 6, NULL, NULL, 2, NULL, 0, 'Serve with a side vegetable or tossed salad. Steamed broccoli or roasted Brussels sprouts are excellent choices or serve with your family''s favorite veggies.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54B15E7A9CBF114680661468E8B19151', X'32A528D5ED2BB543AA1E7BEB0DCCE567', X'32A528D5ED2BB543AA1E7BEB0DCCE567', 0, NULL, NULL, 2, NULL, 0, 'Soak the bread in some water in a medium-sized bowl. Add 1/2 cup of sweet red wine and let the bread absorb the liquid for a few minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AA61FD1993ACC44B4C4BAFB964FC7DF', X'32A528D5ED2BB543AA1E7BEB0DCCE567', X'32A528D5ED2BB543AA1E7BEB0DCCE567', 1, NULL, NULL, 2, NULL, 0, 'Squeeze the liquid out from the bread, then tear it into small pieces and add it to the ground meat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEB1FCFF72333143BD15B809B815AF92', X'32A528D5ED2BB543AA1E7BEB0DCCE567', X'32A528D5ED2BB543AA1E7BEB0DCCE567', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic, cumin, salt, pepper and eggs. Mix well to incorporate all the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E72D458D5DDEB046ADA881AA5DB49703', X'32A528D5ED2BB543AA1E7BEB0DCCE567', X'32A528D5ED2BB543AA1E7BEB0DCCE567', 3, NULL, NULL, 2, NULL, 0, 'Using portions of the meat mixture about the size of a large walnut, form sausage-like rolls about 4 inches long and 1 inches thick.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F12F7D853206A243AAFD024C77217C84', X'32A528D5ED2BB543AA1E7BEB0DCCE567', X'32A528D5ED2BB543AA1E7BEB0DCCE567', 4, NULL, NULL, 2, NULL, 0, 'Heat an inch of vegetable oil in a skillet and carefully add the soutzoukakia to the pan. Fry them until they''re lightly browned on all sides. Drain on paper towels and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6A24E80F4FE0444A9D39FD0A73E8A7B', X'32A528D5ED2BB543AA1E7BEB0DCCE567', X'32A528D5ED2BB543AA1E7BEB0DCCE567', 5, NULL, NULL, 2, NULL, 0, 'Prepare the sauce in a Dutch oven while the soutzoukakia are frying. Add 1/4 cup olive oil to the bottom of the pan, then add the tomato sauce, sugar, cinnamon and the remaining cup of wine.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'003F176CBAAA804C8D9EEF5A855D678D', X'32A528D5ED2BB543AA1E7BEB0DCCE567', X'32A528D5ED2BB543AA1E7BEB0DCCE567', 6, NULL, NULL, 2, NULL, 0, 'Fill the empty tomato sauce can with water and add this to the Dutch oven as well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83002907D63CBB4F972F6AC9CBEC2A9F', X'32A528D5ED2BB543AA1E7BEB0DCCE567', X'32A528D5ED2BB543AA1E7BEB0DCCE567', 7, NULL, NULL, 2, NULL, 0, 'Boil the sauce over medium-high heat until some of the water has evaporated and the sauce has thickened. Add the soutzoukakia to the sauce when they''ve finished frying.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E58720229D94C48BC8785F1DEE61712', X'32A528D5ED2BB543AA1E7BEB0DCCE567', X'32A528D5ED2BB543AA1E7BEB0DCCE567', 8, NULL, NULL, 2, NULL, 0, 'Heat the sauce and the soutzoukakia to a boil and remove from the heat. Allow the sausages to sit and absorb the sauce for a few minutes before serving.');
