INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EAB729518A5E14BB883FA2B9E4F5C40', X'3AFB532D382AE344A90F7E88C0BAC5D0', X'3AFB532D382AE344A90F7E88C0BAC5D0', 4, NULL, NULL, 2, NULL, 0, 'Add the cumin and paprika and cook 2 minutes more, stirring. Add the ground beef and cook until browned.');
