INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC318A0C61419F4186CD0A8F49A198F9', X'3AFB532D382AE344A90F7E88C0BAC5D0', X'3AFB532D382AE344A90F7E88C0BAC5D0', 7, NULL, NULL, 2, NULL, 0, 'Remove beef mixture from heat and let cool for 5 minutes. Stir in 1/2 cup crumbled queso fresco.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'704173BD6F5FB940B319616838FCFDFF', X'3AFB532D382AE344A90F7E88C0BAC5D0', X'3AFB532D382AE344A90F7E88C0BAC5D0', 8, NULL, NULL, 2, NULL, 0, 'While the beef is cooking, bring a large pot of salted water to a rolling boil. Slice the tops off of the peppers and reserve. Clean the inside of the peppers, removing the seeds and veins.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BE0EBE0BFDAB34F883F2D7C22E0244F', X'3AFB532D382AE344A90F7E88C0BAC5D0', X'3AFB532D382AE344A90F7E88C0BAC5D0', 9, NULL, NULL, 2, NULL, 0, 'Cook the peppers in the boiling water until bright red and just tender, about 8 to 10 minutes. Drain peppers and blot dry with paper towels.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'493511C18583F348913B3732E1948429', X'3AFB532D382AE344A90F7E88C0BAC5D0', X'3AFB532D382AE344A90F7E88C0BAC5D0', 10, NULL, NULL, 2, NULL, 0, 'Fill each pepper with some of the seasoned ground beef mixtures. Top with remaining queso fresco.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13F7FC12A16CB84D92D2560F5C127BC1', X'3AFB532D382AE344A90F7E88C0BAC5D0', X'3AFB532D382AE344A90F7E88C0BAC5D0', 11, NULL, NULL, 2, NULL, 0, 'Place the peppers on a baking sheet, with the tops alongside them (but not covering the filling). Bake the peppers for 10 to 15 minutes, or until the cheese is melted and the filling is heated through.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42436CD2D334EB4998201BEA9EE82731', X'3AFB532D382AE344A90F7E88C0BAC5D0', X'3AFB532D382AE344A90F7E88C0BAC5D0', 12, NULL, NULL, 2, NULL, 0, 'Serve warm.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4D9B48178B4B445860B741AC98068DF', X'3AFB532D382AE344A90F7E88C0BAC5D0', X'3AFB532D382AE344A90F7E88C0BAC5D0', 13, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7291FA9C2FCF214892B1C3EC5329D037', X'05778C7CC27BA6498EE171B2B1B1240E', X'05778C7CC27BA6498EE171B2B1B1240E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
