INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A670011E8DBB2046A992BCE72AF53304', X'05778C7CC27BA6498EE171B2B1B1240E', X'05778C7CC27BA6498EE171B2B1B1240E', 2, NULL, NULL, 2, NULL, 0, 'Add chili powder, minced garlic, jalapeno pepper, tomatoes and tomato sauce, cumin, marjoram, and thyme. Stir to blend and bring to a boil. Cover the pan and reduce the heat to low; simmer for about 1 1/2 to 2 hours, stirring occasionally.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D93170FF9C1F345AAE7132D0B521B26', X'05778C7CC27BA6498EE171B2B1B1240E', X'05778C7CC27BA6498EE171B2B1B1240E', 3, NULL, NULL, 2, NULL, 0, 'Pour the canned beans into a colander; rinse and drain well. Add the beans to the chili and heat through.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0BC65207025754F9CD5BAB340FC32B4', X'05778C7CC27BA6498EE171B2B1B1240E', X'05778C7CC27BA6498EE171B2B1B1240E', 4, NULL, NULL, 2, NULL, 0, 'For serving, consider freshly baked cornbread or cornbread muffins.');
