INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5D9FB8DF0BB6F4AAABF2F9026DAF36D', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 5, NULL, NULL, 2, NULL, 0, 'Place a heavy casserole or bowl on top of the eggplant, pressing down to squeeze out excess water.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'432B09AA36B09E41AB1571164443512D', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 6, NULL, NULL, 2, NULL, 0, 'Pre-heat oven to 400 F. Arrange half of the drained eggplant slices in a 13x9x2-inch baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F67258D1583E76469DB75A1F1794A1E0', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 7, NULL, NULL, 2, NULL, 0, 'In a large skillet, brown ground beef; drain off excess fat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DB36499BA9D134A899CEF54ECB529BB', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 8, NULL, NULL, 2, NULL, 0, 'Add drained tomatoes, breaking up with a spoon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CB11E8227CBC4489C95015191E087D7', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 9, NULL, NULL, 2, NULL, 0, 'Add tomato sauce, garlic powder, 1/2 teaspoon salt, pepper, oregano, and cinnamon. Simmer for 5 minutes, stirring occasionally.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E0B8EFB1EBC494B9736D471D1066592', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 10, NULL, NULL, 2, NULL, 0, 'Spread the meat sauce mixture over the eggplant.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E1FE6C63176944EA4641D870ACA5541', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 11, NULL, NULL, 2, NULL, 0, 'Top meat mixture with remaining eggplant slices.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD8AE93D452FC44EB860C1CBDCA9031A', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 12, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, melt butter.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D317070574AE5409F68683DAAF03690', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 13, NULL, NULL, 2, NULL, 0, 'Stir in flour; cook, stirring, for about 1 minute. Remove from heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2AAD2A43995414BBC6D5A7F7444D310', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 14, NULL, NULL, 2, NULL, 0, 'Gradually stir in milk; return to heat and cook until thickened, stirring constantly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'187A3ED8B2706C41951636065E8E6913', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 15, NULL, NULL, 2, NULL, 0, 'Add shredded cheese and continue stirring until cheese is melted and sauce is smooth.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5F0A0E2E27AAE4292ED95C3CAADE31D', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 16, NULL, NULL, 2, NULL, 0, 'Add a small portion of the mixture to the slightly beaten eggs, beating quickly, then return egg mixture to sauce in the saucepan, whisking as you pour.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A215204F96187341945C2C1CEEF161D2', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 17, NULL, NULL, 2, NULL, 0, 'Spoon the cheese sauce over the eggplant casserole.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0C41329952F6E45B4C9E86917E4FFC6', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 18, NULL, NULL, 2, NULL, 0, 'Bake in preheated oven for 30 minutes, or until topping is set.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9871574A230DCA4188063D84ECB3E5DA', X'4123A43C3BE90041A4C6E2B3D0C1D060', X'4123A43C3BE90041A4C6E2B3D0C1D060', 19, NULL, NULL, 2, NULL, 0, 'Let stand 5 minutes before serving.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D2BC48D82869E4A8348F4E7F76B43FF', X'61A2FBDBBCE88840AC5DA72D8E9843A3', X'61A2FBDBBCE88840AC5DA72D8E9843A3', 0, NULL, NULL, 2, NULL, 0, 'Partially freeze the beef to make it easier to cut.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D06C3E1AF80F24C9AA1FB5E04D20443', X'61A2FBDBBCE88840AC5DA72D8E9843A3', X'61A2FBDBBCE88840AC5DA72D8E9843A3', 1, NULL, NULL, 2, NULL, 0, 'If making homemade ginger juce, grate the ginger and squeeze out the juice until you have 2 tablespoons. Cut the partially frozen beef along the grain into thin strips the approximately length and width of matchsticks. Add the marinade ingredients and marinate the beef for 25 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3EC31BAF1F55B47BD55B6C483D50009', X'61A2FBDBBCE88840AC5DA72D8E9843A3', X'61A2FBDBBCE88840AC5DA72D8E9843A3', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the soy sauce, rice wine or sherry, vinegar, sugar, water and hot chili oil. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9748E04554C8C545B0A9B32B64C2BBC5', X'61A2FBDBBCE88840AC5DA72D8E9843A3', X'61A2FBDBBCE88840AC5DA72D8E9843A3', 3, NULL, NULL, 2, NULL, 0, 'While the beef is marinating, prepare the vegetables and sauce. Cut the celery, red bell pepper, and carrot into thin strips.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5CFED8DD51AAC42AEB91024494993CD', X'61A2FBDBBCE88840AC5DA72D8E9843A3', X'61A2FBDBBCE88840AC5DA72D8E9843A3', 4, NULL, NULL, 2, NULL, 0, 'To prepare the batter, combine the flour and cornstarch. Stir in the vegetable oil, and the hot chili oil if using. Add a much water as is needed to make a smooth batter. It should not be too dry or too runny, but should lightly drop off the back of a wooden spoon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7EB8E252FDCEF47BF51A19275D58ABB', X'61A2FBDBBCE88840AC5DA72D8E9843A3', X'61A2FBDBBCE88840AC5DA72D8E9843A3', 5, NULL, NULL, 2, NULL, 0, 'Heat the oil for deep-frying to 360 degrees Fahrenheit. Dip the marinated beef pieces into the batter. When the oil is hot, add the beef and deep-fry until it is golden brown. Remove with a slotted spoon and drain on paper towels.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2DC3ED52AEF6443BA88BA4F3FFB7A3B', X'61A2FBDBBCE88840AC5DA72D8E9843A3', X'61A2FBDBBCE88840AC5DA72D8E9843A3', 6, NULL, NULL, 2, NULL, 0, 'Increase the heat to 400 degrees Fahrenheit. Deep-fry the beef a second time, to make it extra cripsy. Remove and drain. Clean out the wok.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'404E6ECCF19953429C1B45D1A7E7E95E', X'61A2FBDBBCE88840AC5DA72D8E9843A3', X'61A2FBDBBCE88840AC5DA72D8E9843A3', 7, NULL, NULL, 2, NULL, 0, 'Heat 2 tablespoons oil in the wok. When the oil is hot, add the chilies, minced garlic and ginger. Stir-fry until the chilies begin to blister. Add the carrot. Stir-fry briefly, then add the celery, and then the red pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76F291C78D69F64F8EE50BB497AA875C', X'61A2FBDBBCE88840AC5DA72D8E9843A3', X'61A2FBDBBCE88840AC5DA72D8E9843A3', 8, NULL, NULL, 2, NULL, 0, 'Push the vegetables up to the sides of the wok. Add the sauce in the middle. Heat to boiling, then add the deep-fried beef back into the pan. Mix all the ingredients together. Remove from the heat. Stir in the sesame oil. Serve hot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC6E3F83D31E6F429ED2F226BAAA1871', X'A2A17FBDE12FA44CB782CA67399B6A59', X'A2A17FBDE12FA44CB782CA67399B6A59', 0, NULL, NULL, 2, NULL, 0, 'Wash the green beans, drain thoroughly, and trim the ends. Cut the green beans on the diagonal into slices approximately 2 inches long.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1031EF9C4387544882090DEF982D3E8', X'A2A17FBDE12FA44CB782CA67399B6A59', X'A2A17FBDE12FA44CB782CA67399B6A59', 1, NULL, NULL, 2, NULL, 0, 'Cut the beef across the grain into very thin strips about 2 inches long.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE3292EF8708F748B0A5043D5A511139', X'A2A17FBDE12FA44CB782CA67399B6A59', X'A2A17FBDE12FA44CB782CA67399B6A59', 2, NULL, NULL, 2, NULL, 0, 'Chop the garlic, ginger, and scallions. In a small bowl, combine the dark soy sauce, light soy sauce, and sugar. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE11466C83C07E4FA3634112C52A2980', X'A2A17FBDE12FA44CB782CA67399B6A59', X'A2A17FBDE12FA44CB782CA67399B6A59', 3, NULL, NULL, 2, NULL, 0, 'Heat the wok over medium heat. When hot, add 2 tablespoons of oil, drizzling the oil down the sides of the wok.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D36DB03642977046A52EB9C93A050F7A', X'A2A17FBDE12FA44CB782CA67399B6A59', X'A2A17FBDE12FA44CB782CA67399B6A59', 4, NULL, NULL, 2, NULL, 0, 'When the oil is hot, add the green beans. Stir-fry for 7 to 10 minutes, until their skins pucker and turn brown and the green beans are tender without being mushy. Remove the beans from the wok.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FA521CEA73ACF429E96EACA6BA00E65', X'A2A17FBDE12FA44CB782CA67399B6A59', X'A2A17FBDE12FA44CB782CA67399B6A59', 5, NULL, NULL, 2, NULL, 0, 'Heat 2 tablespoons oil in the wok on high heat, drizzling the oil down the sides of the wok. When the oil is hot, add the garlic, ginger, and scallions. Stir-fry for a few seconds, then add the dried chilies or chili paste and stir-fry for a few more seconds until aromatic.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06DE99157F29FE42A0B2A6F7C2C58804', X'A2A17FBDE12FA44CB782CA67399B6A59', X'A2A17FBDE12FA44CB782CA67399B6A59', 6, NULL, NULL, 2, NULL, 0, 'Add the beef strips to the wok. Sear briefly without moving, and then stir-fry until the beef changes color. Splash the beef with the rice wine while cooking.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1E68AEE9B0F5E45A9C8741EEB865B4C', X'A2A17FBDE12FA44CB782CA67399B6A59', X'A2A17FBDE12FA44CB782CA67399B6A59', 7, NULL, NULL, 2, NULL, 0, 'Add the green beans and the sauce. Stir in the scallions. Taste and adjust seasoning, adding black pepper to taste, and salt if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B0D95DBEB93ED43815986E6135419DF', X'A2A17FBDE12FA44CB782CA67399B6A59', X'A2A17FBDE12FA44CB782CA67399B6A59', 8, NULL, NULL, 2, NULL, 0, 'Remove from the heat and add sesame oil if using. Serve hot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4EEEF333553FD4FA65C45A2E14D9364', X'41A75B506B04B248AC1F68CE4A95B792', X'41A75B506B04B248AC1F68CE4A95B792', 0, NULL, NULL, 2, NULL, 0, 'Cut the steak across the grain into thin strips 1 1/2 - 2 inches long.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F19F47D91B181B40B90981D943E85ED2', X'41A75B506B04B248AC1F68CE4A95B792', X'41A75B506B04B248AC1F68CE4A95B792', 1, NULL, NULL, 2, NULL, 0, 'Place the beef strips in a bowl and add the marinade ingredients (the rice wine or dry sherry, soy sauces, Asian sesame oil, black pepper, sugar, and cornstarch) one at a time, adding the cornstarch last.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7685555C00AAF948B10B337A74B7E2B0', X'41A75B506B04B248AC1F68CE4A95B792', X'41A75B506B04B248AC1F68CE4A95B792', 2, NULL, NULL, 2, NULL, 0, 'Mix well with the beef and marinate for 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04E2E96E53521342A0208ABFC5C471B7', X'41A75B506B04B248AC1F68CE4A95B792', X'41A75B506B04B248AC1F68CE4A95B792', 3, NULL, NULL, 2, NULL, 0, 'Heat a wok or heavy skillet on medium-high to high heat, and add 2 tablespoons oil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD3A93640A3627489E1E7566C8F60A79', X'41A75B506B04B248AC1F68CE4A95B792', X'41A75B506B04B248AC1F68CE4A95B792', 4, NULL, NULL, 2, NULL, 0, 'When the oil is hot, add the beef. Let sear (brown) for a minute, then stir-fry until the redness is gone and the beef is nearly cooked.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7EBFD7054935A41AD33607D7DE0DF9B', X'41A75B506B04B248AC1F68CE4A95B792', X'41A75B506B04B248AC1F68CE4A95B792', 5, NULL, NULL, 2, NULL, 0, 'Remove the beef from the pan and drain. Wipe out the pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDA2A636EAABDD45924E73FB3A842B80', X'41A75B506B04B248AC1F68CE4A95B792', X'41A75B506B04B248AC1F68CE4A95B792', 6, NULL, NULL, 2, NULL, 0, 'Heat 1 tablespoon oil in the wok or skillet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7BF1E6FC79A864EB7C95F4D7A67CCEE', X'41A75B506B04B248AC1F68CE4A95B792', X'41A75B506B04B248AC1F68CE4A95B792', 7, NULL, NULL, 2, NULL, 0, 'When the oil is hot, add the garlic and the snow peas.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71B7546E7ECB264594609E185A0E4D09', X'41A75B506B04B248AC1F68CE4A95B792', X'41A75B506B04B248AC1F68CE4A95B792', 8, NULL, NULL, 2, NULL, 0, 'Stir-fry the snow peas, stirring continually, until they turn dark green (up to 2 minutes). Sprinkle the salt over the snow peas while stir-frying.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B96452389AB2540BE1487D018475DC3', X'41A75B506B04B248AC1F68CE4A95B792', X'41A75B506B04B248AC1F68CE4A95B792', 9, NULL, NULL, 2, NULL, 0, 'Add the beef back into the pan. Add the chicken broth, water or rice wine.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71064B9F3A98894199EF7724A13A3406', X'41A75B506B04B248AC1F68CE4A95B792', X'41A75B506B04B248AC1F68CE4A95B792', 10, NULL, NULL, 2, NULL, 0, 'Stir for another minute to mix everything together.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1D9820E6CA7034BB039F6AFDC246570', X'41A75B506B04B248AC1F68CE4A95B792', X'41A75B506B04B248AC1F68CE4A95B792', 11, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82D3717B34A5B94EA7D75F7FB3A7871B', X'5392DD877D4FD040BA90A9470D2566E5', X'5392DD877D4FD040BA90A9470D2566E5', 0, NULL, NULL, 2, NULL, 0, 'Cut the beef across the grain into thin strips, approximately 2 inches long. Add the marinade ingredients, using your fingers to mix them in and adding the cornstarch last. Marinate the beef for thirty minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A26137696459C445A8F98131B863526D', X'5392DD877D4FD040BA90A9470D2566E5', X'5392DD877D4FD040BA90A9470D2566E5', 1, NULL, NULL, 2, NULL, 0, 'While the beef is marinating, prepare the vegetables and sauce: Wash all the vegetables and drain thoroughly, particularly the mung bean sprouts. String the snow peas. Cut the red pepper in half, remove the seeds, and cut into thin slices approximately 2 inches long. Cut the green onion on the diagonal into 1-inch slices. Chop the ginger and garlic.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8E9F41695ED5244B5149E2A2295F007', X'5392DD877D4FD040BA90A9470D2566E5', X'5392DD877D4FD040BA90A9470D2566E5', 2, NULL, NULL, 2, NULL, 0, 'To prepare the sauce, combine the water, honey, and black rice vinegar. Mix together the cornstarch and water in a separate small bowl. Set both aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDCFFF9AE795E74DABE66F3B9CD1395D', X'5392DD877D4FD040BA90A9470D2566E5', X'5392DD877D4FD040BA90A9470D2566E5', 3, NULL, NULL, 2, NULL, 0, 'Preheat a wok or frying pan and add 2 - 3 Tbsp oil. When the oil is hot, add the beef. Sear the meat briefly and then stir-fry until it changes color and is nearly cooked. (Stir-fry in two batches if necessary). Remove and drain on paper towels.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59467A8F76B5354090F94DD9571DCF66', X'5392DD877D4FD040BA90A9470D2566E5', X'5392DD877D4FD040BA90A9470D2566E5', 4, NULL, NULL, 2, NULL, 0, 'Wipe the wok clean with a paper towel and add about 2 Tbsp more oil. When the oil is hot, add the ginger and garlic and stir-fry until aromatic. Add the snow peas. Stir-fry briefly, and then add the red pepper, and finally the bean sprouts.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'778B78AC7878F9419E1D2E4313FDC4EE', X'5392DD877D4FD040BA90A9470D2566E5', X'5392DD877D4FD040BA90A9470D2566E5', 5, NULL, NULL, 2, NULL, 0, 'Make a well in the middle of the wok by pushing the vegetables up to the side. Add the sauce in the middle. Turn up the heat, give the cornstarch/water mixture a quick re-stir and add it to the sauce, stirring quickly to thicken. Mix the sauce in with the vegetables.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF59A17B6E58094CA356DDCE6936EA98', X'5392DD877D4FD040BA90A9470D2566E5', X'5392DD877D4FD040BA90A9470D2566E5', 6, NULL, NULL, 2, NULL, 0, 'Add the beef. Turn down the heat and simmer for a few minutes until everything is cooked through. Stir in the green onion. Serve hot over rice.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21D9E8C791ACC441BA7898ABDE50542A', X'B5D29F085B8FB649BA5930510B5E4665', X'B5D29F085B8FB649BA5930510B5E4665', 0, NULL, NULL, 2, NULL, 0, 'Lay flank steak flat on a cutting board and carefully slice it in half horizontally almost all the way, cutting in the same direction as the fibers. Open the steak up like a book, and lay flat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0EB704D5906C3459D4129A88338DAEC', X'B5D29F085B8FB649BA5930510B5E4665', X'B5D29F085B8FB649BA5930510B5E4665', 1, NULL, NULL, 2, NULL, 0, 'Squeeze lime juice over the steak and sprinkle with black pepper, kosher salt, oregano, and minced garlic. Use a meat tenderizing hammer to pound meat until it''s flattened and evenly thick and in the approximate shape of a rectangle. The grain of the meat should run parallel to the long side of the rectangle.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B257998D066B3043923F74A40DC6F282', X'B5D29F085B8FB649BA5930510B5E4665', X'B5D29F085B8FB649BA5930510B5E4665', 2, NULL, NULL, 2, NULL, 0, 'Lay the spinach leaves over the meat, leaving 1 to 2 inches of one long side uncovered. Sprinkle the cheese over the spinach.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61C9007E7CCE4D4E8CC37E0155698965', X'B5D29F085B8FB649BA5930510B5E4665', X'B5D29F085B8FB649BA5930510B5E4665', 3, NULL, NULL, 2, NULL, 0, 'Peel the carrots and slice them into quarters lengthwise. Lay the carrots over the spinach, in the direction of the meat fibers. Cut the peppers into strips and spread them over the spinach. Cut the eggs into wedges and distribute pieces over the spinach. Add the olives, if using.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CB38EAABED0424B8105A37018F32070', X'B5D29F085B8FB649BA5930510B5E4665', X'B5D29F085B8FB649BA5930510B5E4665', 4, NULL, NULL, 2, NULL, 0, 'Carefully roll up the flank steak with the vegetables inside, rolling from one long side to the other, and tie the roll up securely with kitchen twine. Season the outside with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'842D5263F226FE419602141922FEBF43', X'B5D29F085B8FB649BA5930510B5E4665', X'B5D29F085B8FB649BA5930510B5E4665', 5, NULL, NULL, 2, NULL, 0, 'To cook on the grill:');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57F0E0EF2EC15C40857D980D98F0DD82', X'B5D29F085B8FB649BA5930510B5E4665', X'B5D29F085B8FB649BA5930510B5E4665', 6, NULL, NULL, 2, NULL, 0, 'Wrap the stuffed flank steak tightly with foil, and place it directly on the coals. Cook for 1 to 1 1/2 hours, or until meat is cooked to desired doneness. Unwrap and let cool, then slice crosswise into thin slices to serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83C50BC0D3E32A4F978D262D2ACE04D7', X'B5D29F085B8FB649BA5930510B5E4665', X'B5D29F085B8FB649BA5930510B5E4665', 7, NULL, NULL, 2, NULL, 0, 'To cook stovetop:');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1A480FFCF32F748B0B38A598859C9CF', X'B5D29F085B8FB649BA5930510B5E4665', X'B5D29F085B8FB649BA5930510B5E4665', 8, NULL, NULL, 2, NULL, 0, 'Brown the flank steak roll in a large stockpot over medium-high heat in a little bit of olive oil, until well browned on all sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27C3AADEC8452546A509D8D7D2376153', X'B5D29F085B8FB649BA5930510B5E4665', X'B5D29F085B8FB649BA5930510B5E4665', 9, NULL, NULL, 2, NULL, 0, 'Add 2 cups red wine and enough water (or beef broth) to cover the meat. Simmer gently until meat is tender when pierced with a fork. Drain and cool, then slice crosswise into thin slices to serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5895592E8A70D243825E8D7450CB4DB9', X'B5D29F085B8FB649BA5930510B5E4665', X'B5D29F085B8FB649BA5930510B5E4665', 10, NULL, NULL, 2, NULL, 0, 'Matambre slices easiest after it has cooled some, and can be served at room temperature with a salad as an appetizer or starter course. Serve with chimichurri sauce, or with a sauce made from the wine cooking liquid, if desired.?');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0A1EEBA9829174299273A0E1A96AA41', X'29ADBB9E0B490B4EADE0F78B1EA4822D', X'29ADBB9E0B490B4EADE0F78B1EA4822D', 0, NULL, NULL, 2, NULL, 0, 'Cut the beef across the grain into thin slices about 2 inches long.');
