INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91AF55D469D80747A92596F82175637B', X'E9102BB0386C7B4C838198600CE7267C', X'E9102BB0386C7B4C838198600CE7267C', 5, NULL, NULL, 2, NULL, 0, 'Add the sauce to the wok. Bring to a boil and cook for another 2 to 3 minutes.');
