INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E81B0217C4E67240AED25A3005580D87', X'C397685CD41EB44B9A20C87011A36D22', X'C397685CD41EB44B9A20C87011A36D22', 9, NULL, NULL, 2, NULL, 0, 'Add the thickened broth to the meat and vegetable mixture. Remove the cloves.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2520D40E9503B24D9CFF8D6FA59FBECA', X'1B855DDCF4963E45BAB857092705AFC9', X'1B855DDCF4963E45BAB857092705AFC9', 0, NULL, NULL, 2, NULL, 0, 'Remove 3 tablespoons of crushed pineapple from can and place into a bowl. Carefully dip out 3 tablespoons of pineapple juice from the can. If this proves difficult to do, place the remaining crushed pineapple in a strainer over a bowl. Press down with a spatula to release as much liquid as you can. This should give you the necessary amount for the recipe. Add remaining the marinade ingredients (soy sauce, minced shallots, olive oil, cider vinegar, honey, minced garlic, minced ginger, black pepper, salt) to the bowl and combine.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42E59A1900825B4A9AC1C016AE5586F0', X'1B855DDCF4963E45BAB857092705AFC9', X'1B855DDCF4963E45BAB857092705AFC9', 1, NULL, NULL, 2, NULL, 0, 'Place the flank steak into a glass baking dish or plastic container and coat it on both sides with the marinade mixture. Cover dish tightly with plastic wrap and place into refrigerator for 2 to 12 hours. Turn the steak a few times during the marinating process and replace the plastic wrap over the dish each time.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26B5EE2B23405446BA919B5E768F31D6', X'1B855DDCF4963E45BAB857092705AFC9', X'1B855DDCF4963E45BAB857092705AFC9', 2, NULL, NULL, 2, NULL, 0, 'Preheat the grill to medium-high heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96ED5BE0DAA42A459888CB6534B28411', X'1B855DDCF4963E45BAB857092705AFC9', X'1B855DDCF4963E45BAB857092705AFC9', 3, NULL, NULL, 2, NULL, 0, 'Remove the steak from the glass dish. Place the steak on the heated grill. Cook about 5 to 7 minutes per side, depending on the thickness of the cut, until done. Remove the steaks from grill once they''ve reached the desired doneness and let them rest for 5 to 7 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BC1B70459B8224F95570BD91A35CE60', X'1B855DDCF4963E45BAB857092705AFC9', X'1B855DDCF4963E45BAB857092705AFC9', 4, NULL, NULL, 2, NULL, 0, 'Slice the flank steaks into strips across the grain.');
