INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72EBF9734AAE3A48AF0961C70098AE90', X'73F0576EA7BE5742A1DE975B2FD5354C', X'73F0576EA7BE5742A1DE975B2FD5354C', 1, NULL, NULL, 2, NULL, 0, 'Season rib-eye steaks with salt, black pepper, and onion powder.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F69EADFE034BC4D8767253D1F70EE9C', X'73F0576EA7BE5742A1DE975B2FD5354C', X'73F0576EA7BE5742A1DE975B2FD5354C', 2, NULL, NULL, 2, NULL, 0, 'Place onto the grill and cook for 4-5 minutes per side, depending on thickness.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00EFED012A89CB4CB16EA501AB2C47C9', X'73F0576EA7BE5742A1DE975B2FD5354C', X'73F0576EA7BE5742A1DE975B2FD5354C', 3, NULL, NULL, 2, NULL, 0, 'Once cooked to your preference (135 F for medium rare and 145 F for medium), remove from grill and let rest for about five minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABADC62BBAD895449004EFB64FCE5C70', X'73F0576EA7BE5742A1DE975B2FD5354C', X'73F0576EA7BE5742A1DE975B2FD5354C', 4, NULL, NULL, 2, NULL, 0, 'Serve on warmed deli buns with steak sauce, mayonnaise, onions, lettuce, and tomato.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A282B81955068F45BE9B5683C9A5F04D', X'BEE71E3E85046643A338BD9D6E931C5A', X'BEE71E3E85046643A338BD9D6E931C5A', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the beef broth, oyster sauce, and soy sauce and mix well. Add the steak and stir. Let the steak marinate at room temperature while you cook the potatoes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0C8A84438A2874A99836525B2147B78', X'BEE71E3E85046643A338BD9D6E931C5A', X'BEE71E3E85046643A338BD9D6E931C5A', 1, NULL, NULL, 2, NULL, 0, 'Open the package of potatoes or prepare the russet potatoes. Place the potato slices on a kitchen towel and blot to dry. Heat 1/4 cup oil in a large skillet over medium-high heat until a drop of water sizzles as soon as it hits the oil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78850680F672474E99CA47D78EC1B127', X'BEE71E3E85046643A338BD9D6E931C5A', X'BEE71E3E85046643A338BD9D6E931C5A', 2, NULL, NULL, 2, NULL, 0, 'Fry the potatoes, a handful at a time, until golden brown and crisp. You may need to add more oil as the batches cook. Drain potatoes on a paper towel as you work. When all of the potatoes are fried, drain any oil from the skillet and wipe it out.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'797B375886F94A47A8558A9EEC174CE2', X'BEE71E3E85046643A338BD9D6E931C5A', X'BEE71E3E85046643A338BD9D6E931C5A', 3, NULL, NULL, 2, NULL, 0, 'Remove steak from the marinade; reserve marinade. Heat 1 tablespoon oil in the skillet and add the steak; stir-fry for 2 to 3 minutes until brown. Remove steak from the skillet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB177270B7CBEE43854F25B02BE2F892', X'BEE71E3E85046643A338BD9D6E931C5A', X'BEE71E3E85046643A338BD9D6E931C5A', 4, NULL, NULL, 2, NULL, 0, 'Add more oil to the skillet, if necessary, and stir-fry the onion, garlic, and mushrooms until the vegetables are tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F62A8313BC3FB4A8B13B69D945ADC9A', X'BEE71E3E85046643A338BD9D6E931C5A', X'BEE71E3E85046643A338BD9D6E931C5A', 5, NULL, NULL, 2, NULL, 0, 'Add the cornstarch to the reserved marinade and whisk to blend. Add to the vegetables in the skillet along with the reserved beef and stir-fry until the sauce thickens.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3E6D7482569ED49990597B6E44BFCFD', X'BEE71E3E85046643A338BD9D6E931C5A', X'BEE71E3E85046643A338BD9D6E931C5A', 6, NULL, NULL, 2, NULL, 0, 'Gently stir in the fried potatoes and stir-fry for a few minutes to heat through. Serve immediately');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E737CDD87C3BA4B8D76703CE17D3AAD', X'541FB822211F984BA7D7EA92B0B95323', X'541FB822211F984BA7D7EA92B0B95323', 0, NULL, NULL, 2, NULL, 0, 'Cut 1-inch diamonds about 1/8 inch deep in both sides of the flank steak.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7DE489123A8094C94B708D8F4AEFE14', X'541FB822211F984BA7D7EA92B0B95323', X'541FB822211F984BA7D7EA92B0B95323', 1, NULL, NULL, 2, NULL, 0, 'Combine crushed garlic, olive oil, red wine vinegar salt, and black pepper in a small non-metallic bowl.');
