INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48AEC3620AD2F54F82D33A18133E9741', X'F1059A7F49FD294AB5385BDB0DC53F86', X'F1059A7F49FD294AB5385BDB0DC53F86', 1, NULL, NULL, 2, NULL, 0, 'Whisk in the garlic, cumin, SazonGoya and salt and pepper to taste.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C7BAC432F9EE04BA2550A9B2D87E3F6', X'F1059A7F49FD294AB5385BDB0DC53F86', X'F1059A7F49FD294AB5385BDB0DC53F86', 2, NULL, NULL, 2, NULL, 0, 'Place the flank steak inside of a plastic bag, and more the marinade mixture into the bag with the steak. Seal bag tightly, then leave the steak to marinate overnight in the fridge.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E8E5E983EDF04439BC69F598663E202', X'F1059A7F49FD294AB5385BDB0DC53F86', X'F1059A7F49FD294AB5385BDB0DC53F86', 3, NULL, NULL, 2, NULL, 0, 'Heat the grill to medium-high. Remove steak from marinade and grill for 5 to 8 minutes on each side, or until it reaches desired doneness.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75EAC7CE4B6DF8498385335BE880CCE3', X'F1059A7F49FD294AB5385BDB0DC53F86', X'F1059A7F49FD294AB5385BDB0DC53F86', 4, NULL, NULL, 2, NULL, 0, 'Let steak rest for 5 minutes, then slice very thinly against the grain and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C773418122D0AD4B849BB01EB9D876D8', X'7EBA6F31C5A2024CA89E1E6B39C5905E', X'7EBA6F31C5A2024CA89E1E6B39C5905E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8520731951B9347998298802A27C2D7', X'7EBA6F31C5A2024CA89E1E6B39C5905E', X'7EBA6F31C5A2024CA89E1E6B39C5905E', 1, NULL, NULL, 2, NULL, 0, 'Place steaks in a resealable plastic bag. Place remaining ingredients in blender or food processor. Blend mixture until smooth.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AD7177299747845996707C35E8340F1', X'7EBA6F31C5A2024CA89E1E6B39C5905E', X'7EBA6F31C5A2024CA89E1E6B39C5905E', 2, NULL, NULL, 2, NULL, 0, 'Pour half of mixture into bag with steaks. Make sure meat is well coated.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A3E27C74705784A80DFCB5FF1FBB44C', X'7EBA6F31C5A2024CA89E1E6B39C5905E', X'7EBA6F31C5A2024CA89E1E6B39C5905E', 3, NULL, NULL, 2, NULL, 0, 'Release air from the bag, seal, and place steaks into a refrigerator for 6-24 hours. Store remaining half of marinade in an airtight plastic container in a refrigerator.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'971DFFE49B803941AC325B1464262DFC', X'7EBA6F31C5A2024CA89E1E6B39C5905E', X'7EBA6F31C5A2024CA89E1E6B39C5905E', 4, NULL, NULL, 2, NULL, 0, 'Preheat grill. Remove steaks from bag and discard marinade. Grill steaks on medium- heat until they reach the desired doneness. Once cooked, remove heat and let rest.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85A379469BF10F44AAEC3A78EE20C220', X'7EBA6F31C5A2024CA89E1E6B39C5905E', X'7EBA6F31C5A2024CA89E1E6B39C5905E', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, place reserved marinade in a small saucepan. Bring mixture to a boil, then reduce heat to medium-low and let simmer for 5-6 minutes, stirring often.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43741492BE94424F990B3AD28C6459A3', X'7EBA6F31C5A2024CA89E1E6B39C5905E', X'7EBA6F31C5A2024CA89E1E6B39C5905E', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and serve sauce with steaks.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'630518D14C77704BB50F4EA998CCCFC4', X'1379292DF7CF954AAC6A472E70572E2C', X'1379292DF7CF954AAC6A472E70572E2C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9F41B492399CE47A67427D3BA40AADA', X'1379292DF7CF954AAC6A472E70572E2C', X'1379292DF7CF954AAC6A472E70572E2C', 1, NULL, NULL, 2, NULL, 0, 'Cut the beef into 1-inch cubes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1E3FA908675A3408C3827F0E39B3901', X'1379292DF7CF954AAC6A472E70572E2C', X'1379292DF7CF954AAC6A472E70572E2C', 2, NULL, NULL, 2, NULL, 0, 'Place the beef in a 4 to 5-quart crock pot along with the carrots, onions, and garlic.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27F19D5C399ADA4D9A0DC53398292E7A', X'1379292DF7CF954AAC6A472E70572E2C', X'1379292DF7CF954AAC6A472E70572E2C', 3, NULL, NULL, 2, NULL, 0, 'Combine the beef stock, soy sauce, ginger root, pepper, and hoisin sauce in a small bowl and mix well. Pour over the beef and vegetables in the slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4F959A0C4EA534BA6E1B63DD0C47B25', X'1379292DF7CF954AAC6A472E70572E2C', X'1379292DF7CF954AAC6A472E70572E2C', 4, NULL, NULL, 2, NULL, 0, 'Cover crock pot and cook on low for 10 to 12 hours or until the beef and vegetables are tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D152EC18E68CB4AA5896D3DB1AEF527', X'1379292DF7CF954AAC6A472E70572E2C', X'1379292DF7CF954AAC6A472E70572E2C', 5, NULL, NULL, 2, NULL, 0, 'Turn the crock pot to high heat setting.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4C2A0CBEEE9BA4FBF9FE8E00543A776', X'1379292DF7CF954AAC6A472E70572E2C', X'1379292DF7CF954AAC6A472E70572E2C', 6, NULL, NULL, 2, NULL, 0, 'Mix the cornstarch and water in a small bowl using a wire whisk, and stir into the crockpot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C27CDBC885FF434195678E9642E1FF4E', X'1379292DF7CF954AAC6A472E70572E2C', X'1379292DF7CF954AAC6A472E70572E2C', 7, NULL, NULL, 2, NULL, 0, 'Cover and cook on high for 15 to 20 minutes until thickened. Stir in peas and cook another 10 to 15 minutes until hot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B90002200892544CB31CDDA454A32008', X'1379292DF7CF954AAC6A472E70572E2C', X'1379292DF7CF954AAC6A472E70572E2C', 8, NULL, NULL, 2, NULL, 0, 'Serve over cooked rice. Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D95C377C80F84D4795D2D29CAC57DBFF', X'62DFB08A2E8DE14B8C2E575ED50A14FA', X'62DFB08A2E8DE14B8C2E575ED50A14FA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A97B72BA55C7C348A64BB58D5CCAAD19', X'62DFB08A2E8DE14B8C2E575ED50A14FA', X'62DFB08A2E8DE14B8C2E575ED50A14FA', 1, NULL, NULL, 2, NULL, 0, 'Combine the oil, vinegar, soy sauce, Worcestershire, ketchup, mustard, garlic, pepper, oregano, and crushed red pepper flakes in a large measuring cup or bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2246F927417C894D9DD12516B82A5F68', X'62DFB08A2E8DE14B8C2E575ED50A14FA', X'62DFB08A2E8DE14B8C2E575ED50A14FA', 2, NULL, NULL, 2, NULL, 0, 'Put the steak in a large food storage bag or nonreactive container and pour the marinade over it. Seal the bag and turn it several times to coat the meat; refrigerate the steak for 4 to 8 hours. Turn the steak frequently during its time in the refrigerator so the marinade covers both sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E3AC8448FCB434F9858F8E6B87F2521', X'1C6B9153EEC9D94A9E38001C7676E429', X'1C6B9153EEC9D94A9E38001C7676E429', 0, NULL, NULL, 2, NULL, 0, 'Cut the round steak into six pieces. Place the meat between sheets of plastic wrap and flatten with a rolling pin, beating firmly but not so hard that you tear the meat. Remove the plastic wrap and spread each piece of meat with some mustard and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9A809F6ED1D464189B3D5EB8F84A01C', X'1C6B9153EEC9D94A9E38001C7676E429', X'1C6B9153EEC9D94A9E38001C7676E429', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet, cook bacon until crisp over medium heat. Remove the bacon and drain it on paper towels; crumble and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EB94DC313599E449E7CC2D1868FEFE9', X'1C6B9153EEC9D94A9E38001C7676E429', X'1C6B9153EEC9D94A9E38001C7676E429', 2, NULL, NULL, 2, NULL, 0, 'In the drippings remaining in the skillet saut the onion and garlic until tender over medium heat, about 5 minutes. Add the bell pepper and cook for 2 minutes longer. Remove onions, garlic, and bell pepper from the skillet with a slotted spoon. Combine the vegetables with the bacon and pickles in a medium bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E758C0F8AB7F9745BA34AB2E57095F40', X'1C6B9153EEC9D94A9E38001C7676E429', X'1C6B9153EEC9D94A9E38001C7676E429', 3, NULL, NULL, 2, NULL, 0, 'Divide this mixture between the beef pieces and roll-up. Tie the bundles with kitchen string. Sprinkle each bundle with salt and pepper to taste. Dredge each roll in 1/4 cup flour; shake off excess.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FC832DC7C376D43A64342697140C45A', X'1C6B9153EEC9D94A9E38001C7676E429', X'1C6B9153EEC9D94A9E38001C7676E429', 4, NULL, NULL, 2, NULL, 0, 'Add the butter to the skillet; brown beef rolls, about 1 to 2 minutes on each side.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F2B8D9D1C676B4B8ED3998BFB9E3E24', X'1C6B9153EEC9D94A9E38001C7676E429', X'1C6B9153EEC9D94A9E38001C7676E429', 5, NULL, NULL, 2, NULL, 0, 'Place the carrots in bottom of 4 to 6 quart slow cooker. Top with the beef rolls as they brown. Pour the beef broth overall.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D07A669F18DF54BA68CA0949AA2111B', X'1C6B9153EEC9D94A9E38001C7676E429', X'1C6B9153EEC9D94A9E38001C7676E429', 6, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 6 to 7 hours until carrots are tender and beef is very tender. Remove the beef rolls from the crockpot; carefully remove kitchen string. Remove carrots from crockpot using a slotted spoon.?');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB827F0FD5CD9A4AA3963A3FAA7E5CE1', X'1C6B9153EEC9D94A9E38001C7676E429', X'1C6B9153EEC9D94A9E38001C7676E429', 7, NULL, NULL, 2, NULL, 0, 'In a small bowl combine the sour cream and 1 tablespoon flour; mix well, then stir into liquid remaining in a crockpot. Stir to combine, turn heat to high, and return carrots, then beef rolls to crockpot. Cook for 10 to 20 minutes longer or until sauce thickens; season with salt and pepper to taste. Serve the carrots and beef rolls with sour cream sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC6C376D022F7C4BB9301BC5090C93A7', X'2B2277B0777AD34BA4DB4E7792389B9F', X'2B2277B0777AD34BA4DB4E7792389B9F', 0, NULL, NULL, 2, NULL, 0, 'Place flat-iron steaks into a resealable plastic bag. Combine marinade ingredients and pour over steaks, making sure every inch of meat is coated well. Gently, release air from bag, seal, and place into the refrigerator for 2 to 8 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D44637E9528592429C516D91DACD2579', X'2B2277B0777AD34BA4DB4E7792389B9F', X'2B2277B0777AD34BA4DB4E7792389B9F', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill for high heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FA2A43280AB7348B0579FD9FA34D5CE', X'2B2277B0777AD34BA4DB4E7792389B9F', X'2B2277B0777AD34BA4DB4E7792389B9F', 2, NULL, NULL, 2, NULL, 0, 'Remove steaks from bag and discard any leftover marinade. Place steaks onto the grill and cook for 2 to 3 minutes per side. Reduce heat to medium and cook for another 12 to 15 minutes, depending on the thickness of the meat and desired doneness.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD86F439813C874DA11FCC64A72E28AA', X'2B2277B0777AD34BA4DB4E7792389B9F', X'2B2277B0777AD34BA4DB4E7792389B9F', 3, NULL, NULL, 2, NULL, 0, 'Once cooked, remove steaks from heat and place onto a large cutting board. Let meat rest for a few minutes before carving. Slice meat into thin pieces and place onto a serving platter. Cover platter with foil to keep warm while you get the other ingredients ready.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5136CFA2E1AC9F4794ED5587B66486BD', X'2B2277B0777AD34BA4DB4E7792389B9F', X'2B2277B0777AD34BA4DB4E7792389B9F', 4, NULL, NULL, 2, NULL, 0, 'To assemble: Simply place 3 to 4 strips of meat into warmed corn tortillas and top with guacamole, pico de gallo, and any other condiments of choice. Serve with seasoned rice, refried beans, and lime wedges.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2764110B98F42D4093F40C25D3832812', X'63175B1D6BCD804CAA1F52E2250E51EB', X'63175B1D6BCD804CAA1F52E2250E51EB', 0, NULL, NULL, 2, NULL, 0, 'Combine vinegar, mustard, salt, black pepper, allspice, rosemary and oil in large, shallow glass dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A0324BD8553F24FBEB78C3318004B3C', X'63175B1D6BCD804CAA1F52E2250E51EB', X'63175B1D6BCD804CAA1F52E2250E51EB', 1, NULL, NULL, 2, NULL, 0, 'Add steak and onions; turn to coat. Let stand 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1D26CB8311B5A4BBEE1CE23A85D02A9', X'63175B1D6BCD804CAA1F52E2250E51EB', X'63175B1D6BCD804CAA1F52E2250E51EB', 2, NULL, NULL, 2, NULL, 0, 'Grill steak and onions 12 to 16 minutes (depending on thickness and desired doneness), turning a few time? and brushing with marinade during the first half of cooking.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB14180FE27E194B87F1A7E5B6960017', X'63175B1D6BCD804CAA1F52E2250E51EB', X'63175B1D6BCD804CAA1F52E2250E51EB', 3, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from heat, let meat rest for 5 minutes and then slice steak across the grain.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B297EC8097A904589612750C18E34E3', X'63175B1D6BCD804CAA1F52E2250E51EB', X'63175B1D6BCD804CAA1F52E2250E51EB', 4, NULL, NULL, 2, NULL, 0, 'Serve with onions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4791D0A7596E4C45AD4BACF210206C57', X'91AC8B19BC214C42899017B33531AD22', X'91AC8B19BC214C42899017B33531AD22', 0, NULL, NULL, 2, NULL, 0, 'Cut four of the peppers in half lengthwise. Remove the seeds and ribs. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'404B4ADE5B4F4A42AC857B6FDC2E275A', X'91AC8B19BC214C42899017B33531AD22', X'91AC8B19BC214C42899017B33531AD22', 1, NULL, NULL, 2, NULL, 0, 'Remove the seeds and ribs from the fifth pepper, and chop it coarsely. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5881A460CB45F4F89E4DE3B53D44573', X'91AC8B19BC214C42899017B33531AD22', X'91AC8B19BC214C42899017B33531AD22', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet or saute pan, melt the butter with 1 tablespoon of vegetable oil. Add the sliced onions and cook over medium heat, stirring frequently, until browned - about 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE3CAADF7340B94694100CCB2986FA34', X'91AC8B19BC214C42899017B33531AD22', X'91AC8B19BC214C42899017B33531AD22', 3, NULL, NULL, 2, NULL, 0, 'Add the chopped peppers and mushrooms, if using. Continue cooking until mushrooms are golden and the peppers are tender. Remove to a plate, and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5724C7EE96709742BE729BA30784BD7F', X'91AC8B19BC214C42899017B33531AD22', X'91AC8B19BC214C42899017B33531AD22', 4, NULL, NULL, 2, NULL, 0, 'If necessary, add another tablespoon of oil to the pan, and cook the shaved steak, stirring, until evenly browned. Add the onion and pepper mixture back to the pan. Season the mixture with salt and pepper and the Worcestershire sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5453376C4B944644873758C7334F2F95', X'91AC8B19BC214C42899017B33531AD22', X'91AC8B19BC214C42899017B33531AD22', 5, NULL, NULL, 2, NULL, 0, 'Heat the oven to 400 F. Line a 9 x 13 x 2-inch baking pan with foil, or spray with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60D694EE1775F744AD680C6F7617E797', X'91AC8B19BC214C42899017B33531AD22', X'91AC8B19BC214C42899017B33531AD22', 6, NULL, NULL, 2, NULL, 0, 'Fill a large saucepan, Dutch oven, or deep saute pan with enough salted water to cover the peppers (don''t add them yet). Bring the water to a boil, and then add the pepper halves. Cover and simmer for about 4 minutes, or until just tender but not too soft.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18794860F8A0B24AB40DDADBC33F3168', X'91AC8B19BC214C42899017B33531AD22', X'91AC8B19BC214C42899017B33531AD22', 7, NULL, NULL, 2, NULL, 0, 'Carefully drain the peppers and arrange them -- cut side up -- in the prepared baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC07CF088F02594A8741B2B1A459DEFA', X'91AC8B19BC214C42899017B33531AD22', X'91AC8B19BC214C42899017B33531AD22', 8, NULL, NULL, 2, NULL, 0, 'Fill each pepper with the steak and onion mixture. Top each filled bell pepper with a generous amount of shredded cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'256025DEC31F2E43BA0A9A387DF995A8', X'91AC8B19BC214C42899017B33531AD22', X'91AC8B19BC214C42899017B33531AD22', 9, NULL, NULL, 2, NULL, 0, 'Bake for about 10 to 12 minutes or until the cheese is melted and the filling is hot. For a cheesier topping, add more cheese just before the peppers are done baking.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'790B34665FAAF845963203E6483AC9C1', X'3078EAC29AF34143B8FEE9D7752C4863', X'3078EAC29AF34143B8FEE9D7752C4863', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F6A28037263E743B85710FB559DE46E', X'3078EAC29AF34143B8FEE9D7752C4863', X'3078EAC29AF34143B8FEE9D7752C4863', 1, NULL, NULL, 2, NULL, 0, 'In a heavy duty ziplock bag, combine the olive oil, garlic, garlic powder, soy sauce, lemon juice, mustard, salt, and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63B7E7E554A15F4BBBC9079F1EC83CA4', X'3078EAC29AF34143B8FEE9D7752C4863', X'3078EAC29AF34143B8FEE9D7752C4863', 2, NULL, NULL, 2, NULL, 0, 'Cut the steak into serving-sized pieces and add to the bag. Seal bag and massage marinade into steaks with your hands.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45AAD425C1E3A9469E9BAA5E67D715CE', X'3078EAC29AF34143B8FEE9D7752C4863', X'3078EAC29AF34143B8FEE9D7752C4863', 3, NULL, NULL, 2, NULL, 0, 'Place the bag into a large casserole dish and refrigerate for 20 minutes up to 8 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D143CCA8E377054F81979BE58E2A6C6B', X'3078EAC29AF34143B8FEE9D7752C4863', X'3078EAC29AF34143B8FEE9D7752C4863', 4, NULL, NULL, 2, NULL, 0, 'When you''re ready to cook, preheat either your grill or your broiler for cooking.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F07D57968236E4199A68FCF6442FF2F', X'62BD6533CC773E46975AEB29EF704A7F', X'62BD6533CC773E46975AEB29EF704A7F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A2608AF31C8FE4E8A2701D55EE2A893', X'62BD6533CC773E46975AEB29EF704A7F', X'62BD6533CC773E46975AEB29EF704A7F', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet, heat the 2 tablespoons of butter and the olive oil over medium heat. Add the onion and mushrooms and season with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'526FA96447804E418CEF9DE1B8E9D987', X'62BD6533CC773E46975AEB29EF704A7F', X'62BD6533CC773E46975AEB29EF704A7F', 2, NULL, NULL, 2, NULL, 0, 'Cook and stir over medium heat until mixture is browned and the moisture evaporates, about 8 to 12 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16D5B10858A34F42B10EBD357E9C427B', X'62BD6533CC773E46975AEB29EF704A7F', X'62BD6533CC773E46975AEB29EF704A7F', 3, NULL, NULL, 2, NULL, 0, 'Add the chicken livers, garlic, and marjoram. Cook and stir for 4 to 5 minutes longer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6343F373D5C0F649B7DBBCAD1F0CB4F1', X'62BD6533CC773E46975AEB29EF704A7F', X'62BD6533CC773E46975AEB29EF704A7F', 4, NULL, NULL, 2, NULL, 0, 'Add the beef broth and cook and stir for 3 to 4 minutes until the liquid evaporates. Remove from the heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CEDA3D3E3BE4C489931F760B9C3F696', X'62BD6533CC773E46975AEB29EF704A7F', X'62BD6533CC773E46975AEB29EF704A7F', 5, NULL, NULL, 2, NULL, 0, 'In ?a nonstick skillet, melt the remaining 2 tablespoons of butter over medium heat. Working in batches, sear the filet mignons on all sides, leaving the centers medium-rare. Remove from the heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDF5EDB898A1BD48BCEFC4A5E9C03549', X'62BD6533CC773E46975AEB29EF704A7F', X'62BD6533CC773E46975AEB29EF704A7F', 6, NULL, NULL, 2, NULL, 0, 'Roll out puff pastry on? a lightly floured surface to 13-inch squares. Cut each sheet into four 4 1/2 inch squares.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70C20D3039261244884AF78121C5A72F', X'62BD6533CC773E46975AEB29EF704A7F', X'62BD6533CC773E46975AEB29EF704A7F', 7, NULL, NULL, 2, NULL, 0, 'Divide the mushroom mixture between the squares and spread to cover.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2D26CF356F651419810EF5128CF5978', X'62BD6533CC773E46975AEB29EF704A7F', X'62BD6533CC773E46975AEB29EF704A7F', 8, NULL, NULL, 2, NULL, 0, 'Top each square with a filet mignon. Brush the edges with the beaten egg and then bring the edges together and seal.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B7351E334B7604BBED579F4C234A2AA', X'62BD6533CC773E46975AEB29EF704A7F', X'62BD6533CC773E46975AEB29EF704A7F', 9, NULL, NULL, 2, NULL, 0, 'Place the Wellingtons seam side down on a rimmed baking sheet and brush with more beaten egg.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F54A08343EA9914C82DAD6C7FD68326A', X'62BD6533CC773E46975AEB29EF704A7F', X'62BD6533CC773E46975AEB29EF704A7F', 10, NULL, NULL, 2, NULL, 0, 'Bake for 15 to 25 minutes or until the pastry is golden brown. Let cool on wire racks for 10 minutes, then serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A4D04D96875554EA504A013F9B1E203', X'D967A25B9BE9E246996A011BD8E897F8', X'D967A25B9BE9E246996A011BD8E897F8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57CF988BF92790409F74F2D97659B811', X'D967A25B9BE9E246996A011BD8E897F8', X'D967A25B9BE9E246996A011BD8E897F8', 1, NULL, NULL, 2, NULL, 0, 'In a large saucepan, heat 2 tablespoons of the oil over moderate heat. Add the garlic and cook, stirring constantly, for 1 minute or until fragrant.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51D12514AC503C4FB026813A1EE8DA73', X'D967A25B9BE9E246996A011BD8E897F8', X'D967A25B9BE9E246996A011BD8E897F8', 2, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, balsamic vinegar, fresh oregano, crushed fennel seeds, red pepper flakes, 1/2 teaspoon of the salt, and 1/4 teaspoon of the pepper. Reduce the heat and simmer, partially covered, until the sauce thickens, about 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03380D21C13047409F42D2875B8E9992', X'D967A25B9BE9E246996A011BD8E897F8', X'D967A25B9BE9E246996A011BD8E897F8', 3, NULL, NULL, 2, NULL, 0, 'In a large frying pan or cast iron skillet, heat the remaining 2 tablespoons oil over moderate heat. Season both sides of the steak with the remaining 1/4 teaspoon salt and 1/4 teaspoon pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AB8C61C4753534CA2FAAC7BEDB4F42F', X'D967A25B9BE9E246996A011BD8E897F8', X'D967A25B9BE9E246996A011BD8E897F8', 4, NULL, NULL, 2, NULL, 0, 'Cook the steak over medium high heat for 5 minutes on one side until nicely seared. Turn and cook the other side until the steak has achieved the desired doneness to your taste. This will take about 5 minutes longer for a medium rare steak. Remove the steak and allow it to rest in a warm place for about 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0A46758F041EC46BEF27B724C60BA0B', X'D967A25B9BE9E246996A011BD8E897F8', X'D967A25B9BE9E246996A011BD8E897F8', 5, NULL, NULL, 2, NULL, 0, 'Cut the steak diagonally across the grain into thin slices.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A84FC69E2C6E1D4BB798AE9111D6E1CB', X'D967A25B9BE9E246996A011BD8E897F8', X'D967A25B9BE9E246996A011BD8E897F8', 6, NULL, NULL, 2, NULL, 0, 'Top with the warm tomato sauce and additional sprigs of fresh oregano, if desired. Serve with arugula tossed with additional olive oil and balsamic vinegar.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1CD869A54EAF840897365936F893300', X'7A60406F25DFBC47817ABFB622813E07', X'7A60406F25DFBC47817ABFB622813E07', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0F8DD2A636D3240A42128A0C78E8771', X'7A60406F25DFBC47817ABFB622813E07', X'7A60406F25DFBC47817ABFB622813E07', 1, NULL, NULL, 2, NULL, 0, 'Whisk the buttermilk, eggs, 1 teaspoon of the salt, and 1 teaspoon of the pepper in a large bowl. Add the cubed steaks to the bowl and cover the bowl and refrigerate for at least one hour but up to overnight. Make sure to submerge the steaks in the buttermilk mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10F53797133D33488E5064625F6F50D3', X'7A60406F25DFBC47817ABFB622813E07', X'7A60406F25DFBC47817ABFB622813E07', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the flour, remaining salt, pepper, onion powder, and paprika in a shallow dish. Set aside 2 tablespoons of the seasoned flour for the gravy.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD52FBB0F5A02A41A70F207977EA3CD9', X'7A60406F25DFBC47817ABFB622813E07', X'7A60406F25DFBC47817ABFB622813E07', 3, NULL, NULL, 2, NULL, 0, 'Remove 1 steak from the buttermilk mixture and dredge it in the flour mixture, coating both sides. Place the steak back into the buttermilk mixture, coating both sides. Then place the steak back into the flour mixture, coating once more. Repeat with the remaining steaks.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CEDEED7AB54AF4E9D0C50D5910D5F3E', X'7A60406F25DFBC47817ABFB622813E07', X'7A60406F25DFBC47817ABFB622813E07', 4, NULL, NULL, 2, NULL, 0, 'Place the steaks on top of a cooling rack over a sheet pan and refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C89C5863F7419E4CA63D4FC917875969', X'7A60406F25DFBC47817ABFB622813E07', X'7A60406F25DFBC47817ABFB622813E07', 5, NULL, NULL, 2, NULL, 0, 'Add the oil to a large cast-iron skillet or other heavy-bottomed pan. Heat the oil to 350 F. Add the steaks, frying for 3 to 4 minutes per side. Remove the steaks and place them onto another clean cooling rack over a baking sheet. Keep warm in a 200 F oven.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B45C6809FD335846AC830B4FAABC88F7', X'7A60406F25DFBC47817ABFB622813E07', X'7A60406F25DFBC47817ABFB622813E07', 6, NULL, NULL, 2, NULL, 0, 'Gather the ingredients for the gravy.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEA5428A6B90BC4FB97B63954F759B67', X'7A60406F25DFBC47817ABFB622813E07', X'7A60406F25DFBC47817ABFB622813E07', 7, NULL, NULL, 2, NULL, 0, 'Add the butter to the remaining oil and then add the 2 tablespoons of reserved flour. Whisk until the flour is incorporated into the oil. Cook for 2 to 3 minutes or until the flour is lightly browned.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4219DE0AF0B4C3458044B4220BB65C67', X'7A60406F25DFBC47817ABFB622813E07', X'7A60406F25DFBC47817ABFB622813E07', 8, NULL, NULL, 2, NULL, 0, 'Pour the beef broth and hot sauce into the pan while whisking. Bring the gravy to a boil and then turn the heat to low and simmer, whisking occasionally for another 3 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAF98CD03C3C144C8DF491E5E719C859', X'7A60406F25DFBC47817ABFB622813E07', X'7A60406F25DFBC47817ABFB622813E07', 9, NULL, NULL, 2, NULL, 0, 'Remove the steaks form the warm oven, top them with the gravy, and serve alongside your favorite sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E29E7EB0AA0E84D91EEB72B1EBD4674', X'090B8575F7D8DA4CA1027166069B2913', X'090B8575F7D8DA4CA1027166069B2913', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FD14458E1E70A4F82434A7808F2B185', X'090B8575F7D8DA4CA1027166069B2913', X'090B8575F7D8DA4CA1027166069B2913', 1, NULL, NULL, 2, NULL, 0, 'Place elk steaks in a resealable plastic bag. Combine remaining ingredients and pour over meat, making sure that all surfaces are well coated. Release air from bag, seal, and let meat marinate for 12 to 24 hours in refrigerator.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'928C916080105846BB2A1B6F84E7CF83', X'090B8575F7D8DA4CA1027166069B2913', X'090B8575F7D8DA4CA1027166069B2913', 2, NULL, NULL, 2, NULL, 0, 'Preheat grill on medium-high heat. Remove steaks from bag and discard marinade.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A0FFC9B5BEF494A81BA36FE857969C7', X'090B8575F7D8DA4CA1027166069B2913', X'090B8575F7D8DA4CA1027166069B2913', 3, NULL, NULL, 2, NULL, 0, 'Place steaks on grill and cook for 6 to 7 minutes per side.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F20497A6B762754E97B9FE0C671A6EB1', X'090B8575F7D8DA4CA1027166069B2913', X'090B8575F7D8DA4CA1027166069B2913', 4, NULL, NULL, 2, NULL, 0, 'Once steaks have cooked to desired doneness, remove and let rest for 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32D7BB59491F7F489ABF52F0A4009EAF', X'090B8575F7D8DA4CA1027166069B2913', X'090B8575F7D8DA4CA1027166069B2913', 5, NULL, NULL, 2, NULL, 0, 'Serve with sauteed onions and grilled vegetables.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B296E30770E758439EFBD6E0786B70E5', X'DE9B133CF947234BAA89D19B847E81D8', X'DE9B133CF947234BAA89D19B847E81D8', 0, NULL, NULL, 2, NULL, 0, 'Place the flank steak in a pot with the bouillon, and cover with water.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1FA339EDBC9864BAAE22A22D9F5267C', X'DE9B133CF947234BAA89D19B847E81D8', X'DE9B133CF947234BAA89D19B847E81D8', 1, NULL, NULL, 2, NULL, 0, 'Bring to a boil and simmer on low for 1 1/2 hours, or until the meat is tender. Remove from heat and let the meat cool in the pot with the juices.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F4FE4935E4E4B4C996368B99644458B', X'DE9B133CF947234BAA89D19B847E81D8', X'DE9B133CF947234BAA89D19B847E81D8', 2, NULL, NULL, 2, NULL, 0, 'Saute 2 teaspoons minced garlic in 4 tablespoons vegetable oil for 1 to 2 minutes. Add 2 cups water and 1 teaspoon salt and bring to a boil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6ADCFBECDA0C8646B20D2E8924558933', X'DE9B133CF947234BAA89D19B847E81D8', X'DE9B133CF947234BAA89D19B847E81D8', 3, NULL, NULL, 2, NULL, 0, 'Add the rice to the boiling water, lower the heat, and simmer the rice, covered, for 10 to 15 minutes. Turn off the heat and leave the rice covered for 5 minutes more.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D6B08ADB28126479300508A58EA7AEA', X'DE9B133CF947234BAA89D19B847E81D8', X'DE9B133CF947234BAA89D19B847E81D8', 4, NULL, NULL, 2, NULL, 0, 'Add 2 tablespoons vegetable oil to a skillet, and saute half the chopped onions with the rest of the minced garlic until soft.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C97872CEE1B0D4BBD354D55E5E2EAA3', X'DE9B133CF947234BAA89D19B847E81D8', X'DE9B133CF947234BAA89D19B847E81D8', 5, NULL, NULL, 2, NULL, 0, 'Add the can of black beans (undrained), 1/2 cup water, chicken bouillon, cumin, garlic powder, vinegar, and 1/2 teaspoon salt, and simmer on low heat for about 10 minutes, until the liquid is reduced.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA589B3DB9893F4BA9A9C89E49E3508D', X'DE9B133CF947234BAA89D19B847E81D8', X'DE9B133CF947234BAA89D19B847E81D8', 6, NULL, NULL, 2, NULL, 0, 'Slice the cooled steak against the grain into thin slices. The meat should be very tender and falling apart. Shred the larger pieces into bite-size pieces with your fingers.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94C18812DE857D43BCDD76A417738471', X'DE9B133CF947234BAA89D19B847E81D8', X'DE9B133CF947234BAA89D19B847E81D8', 7, NULL, NULL, 2, NULL, 0, 'Add 2 tablespoons butter to a skillet and cook the rest of the chopped onions until soft. Add the tomatoes, 1 cup of the steak pan juices, and the sliced steak and simmer for 3 to 5 minutes. Remove from heat and cool.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42012105D3037542BF33AA52B78FA47C', X'DE9B133CF947234BAA89D19B847E81D8', X'DE9B133CF947234BAA89D19B847E81D8', 8, NULL, NULL, 2, NULL, 0, 'Arrange the meat and tomatoes on 1/3 of a serving platter. Place the rice next to the meat, and then place the beans on the other side of the rice, to resemble the stripes of a tri-color flag.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5852548ABE80E4DB133014037ADED5F', X'DE9B133CF947234BAA89D19B847E81D8', X'DE9B133CF947234BAA89D19B847E81D8', 9, NULL, NULL, 2, NULL, 0, 'Serve with fried plantains on the side and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F078EB711F3F1E44BEB641E1CD645D45', X'E4434B42737C8A4BB34F44BA27AE33D7', X'E4434B42737C8A4BB34F44BA27AE33D7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D17F4D7A9CAA5D4B8D7FD031A11F212F', X'E4434B42737C8A4BB34F44BA27AE33D7', X'E4434B42737C8A4BB34F44BA27AE33D7', 1, NULL, NULL, 2, NULL, 0, 'Combine the marinade ingredients in a small bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1590DF6C045F0444A2CE771EE6454B34', X'E4434B42737C8A4BB34F44BA27AE33D7', X'E4434B42737C8A4BB34F44BA27AE33D7', 2, NULL, NULL, 2, NULL, 0, 'Place flank steak in resealable plastic bag and pour marinade over meat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6BB55745BEB5F41BEC615A68A25F3C5', X'E4434B42737C8A4BB34F44BA27AE33D7', X'E4434B42737C8A4BB34F44BA27AE33D7', 3, NULL, NULL, 2, NULL, 0, 'Using the outside of the bag, massage the marinade into the meat, making sure every part of the steak is coated.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4E5CF61B9E03A44A29E100D7E00B344', X'E4434B42737C8A4BB34F44BA27AE33D7', X'E4434B42737C8A4BB34F44BA27AE33D7', 4, NULL, NULL, 2, NULL, 0, 'Seal bag and place in the refrigerator for 2 to 8 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7FA70AADF740E4AA9D0F8934BF0DAE8', X'E4434B42737C8A4BB34F44BA27AE33D7', X'E4434B42737C8A4BB34F44BA27AE33D7', 5, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80496B08AFE0844B81E2DF7523F6615D', X'E4434B42737C8A4BB34F44BA27AE33D7', X'E4434B42737C8A4BB34F44BA27AE33D7', 6, NULL, NULL, 2, NULL, 0, 'Remove steak from bag and place onto grill.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'376D88F3E8705B45B378B898C11FC0CE', X'E4434B42737C8A4BB34F44BA27AE33D7', X'E4434B42737C8A4BB34F44BA27AE33D7', 7, NULL, NULL, 2, NULL, 0, 'Cook steak for 18 to 20 minutes turning occasionally.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'702B9FDF4FB16A4CB1A28B6E88511C39', X'E4434B42737C8A4BB34F44BA27AE33D7', X'E4434B42737C8A4BB34F44BA27AE33D7', 8, NULL, NULL, 2, NULL, 0, 'Once the meat has reached the desired doneness, remove from grill and allow to rest for 3 to 5 minutes before slicing.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B7D1C2BAD6BE74F92BC4C7DD9B90505', X'93E34D1579ED2344A7E3C990F3EE6332', X'93E34D1579ED2344A7E3C990F3EE6332', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47B17A48D859F743A323EBF412A84372', X'93E34D1579ED2344A7E3C990F3EE6332', X'93E34D1579ED2344A7E3C990F3EE6332', 1, NULL, NULL, 2, NULL, 0, 'Make the chimichurri sauce first. Whisk together the fresh parsley, cilantro, olive oil, lime juice, garlic, a teaspoon of the salt, and the crushed red pepper flakes in a small bowl. You can also process this sauce in the food processor for a finer minced sauce. You can do this step up to a day ahead of time. The longer it sits, the more flavorful it will get.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E4DD8F07FEBC2418AED24426B96CE5A', X'93E34D1579ED2344A7E3C990F3EE6332', X'93E34D1579ED2344A7E3C990F3EE6332', 2, NULL, NULL, 2, NULL, 0, 'Generously salt and pepper both sides of the steak.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDFD2CD259D4404A82C69930BAAAA3F9', X'93E34D1579ED2344A7E3C990F3EE6332', X'93E34D1579ED2344A7E3C990F3EE6332', 3, NULL, NULL, 2, NULL, 0, 'Heat a cast iron skillet or another heavy-bottomed pan on high heat. Once hot, add a tablespoon of butter to the pan and add the steak. Sear on high heat for 3 minutes. Do not move until the three minutes are up. This will guarantee good color and an even sear.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82379B1F90D5F444A552C7B567D19788', X'93E34D1579ED2344A7E3C990F3EE6332', X'93E34D1579ED2344A7E3C990F3EE6332', 4, NULL, NULL, 2, NULL, 0, 'Flip the steak and add the other tablespoon of butter to the pan and crack the two eggs onto the other side of the pan. Generously season the eggs with salt and pepper too. You can fry them in a different pan if you wish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78FAFA75C444AE4AB4967EC1BEAD063D', X'93E34D1579ED2344A7E3C990F3EE6332', X'93E34D1579ED2344A7E3C990F3EE6332', 5, NULL, NULL, 2, NULL, 0, 'Cook the steak and the eggs another 3 minutes for medium rare. Youll know the eggs are done when the whites are set and the yolks are still jiggly. Heat for longer if you want a more well done steak. You can also flip the eggs if you like them over easy.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14635F16C5430B41BC9D4FD28B874630', X'93E34D1579ED2344A7E3C990F3EE6332', X'93E34D1579ED2344A7E3C990F3EE6332', 6, NULL, NULL, 2, NULL, 0, 'Top the steak with the chimichurri sauce and serve with a little hot sauce if you like. Wait a few minutes to allow the steak to rest before slicing. Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A76A37578B11D24D9B875AC2BB077602', X'2CDFD9194239874A90987BB0FB701A3D', X'2CDFD9194239874A90987BB0FB701A3D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A245042978461A44A966948B4459A1D1', X'2CDFD9194239874A90987BB0FB701A3D', X'2CDFD9194239874A90987BB0FB701A3D', 1, NULL, NULL, 2, NULL, 0, 'Melt the butter in a heavy medium pan on a medium heat. Add the onion and saut until soft and translucent but not brown, about 8 minutes, watch carefully to make sure this does not burn.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB08DDE899B1384AB4DFC5B274EEFE86', X'2CDFD9194239874A90987BB0FB701A3D', X'2CDFD9194239874A90987BB0FB701A3D', 2, NULL, NULL, 2, NULL, 0, 'Combine ground pork and chopped bacon in large bowl. Using fork or fingertips, mix until well blended.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'423FFE4546348546844181EA2F8FDEA3', X'2CDFD9194239874A90987BB0FB701A3D', X'2CDFD9194239874A90987BB0FB701A3D', 3, NULL, NULL, 2, NULL, 0, 'Add sauted onion, garlic, salt, thyme, allspice, cognac, and pepper to bowl with pork mixture and stir until incorporated. Add eggs. Stir until well blended.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9643690474E2EB449517651A4EFBF317', X'2CDFD9194239874A90987BB0FB701A3D', X'2CDFD9194239874A90987BB0FB701A3D', 4, NULL, NULL, 2, NULL, 0, 'Line a 9 x 5 x 3-inch metal loaf pan with bacon slices, arranging 8 slices across the width of the pan and 3 slices on each short side of the pan and overlapping pan on all the sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5497BFD68AD5874AAF4AF99F89C2B4BD', X'2CDFD9194239874A90987BB0FB701A3D', X'2CDFD9194239874A90987BB0FB701A3D', 5, NULL, NULL, 2, NULL, 0, 'Using hands, lightly and evenly press half of the meat mixture (about 3 1/4 cups) onto the bottom of pan atop bacon slices.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A9F3B24B43428499E48D604108989B6', X'2CDFD9194239874A90987BB0FB701A3D', X'2CDFD9194239874A90987BB0FB701A3D', 6, NULL, NULL, 2, NULL, 0, 'Arrange gammon ham strips over in a single layer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2923D9EB361E1242A7443C457D96ECAA', X'2CDFD9194239874A90987BB0FB701A3D', X'2CDFD9194239874A90987BB0FB701A3D', 7, NULL, NULL, 2, NULL, 0, 'Top with remaining meat mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5951451AD3BAB42A52A4528C57177F7', X'2CDFD9194239874A90987BB0FB701A3D', X'2CDFD9194239874A90987BB0FB701A3D', 8, NULL, NULL, 2, NULL, 0, 'Fold bacon slices over, covering pt.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4659AB2C9FED4B4A83C5BC5D7774B57F', X'2CDFD9194239874A90987BB0FB701A3D', X'2CDFD9194239874A90987BB0FB701A3D', 9, NULL, NULL, 2, NULL, 0, 'Cover pan tightly with foil. Place the loaf pan into a baking pan. Pour boiling water into baking pan to come halfway up sides of the loaf pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94516DEB36B36C4F98B77C7F17F0AE1B', X'2CDFD9194239874A90987BB0FB701A3D', X'2CDFD9194239874A90987BB0FB701A3D', 10, NULL, NULL, 2, NULL, 0, 'Bake pt at 350 F until a thermometer inserted through foil into the center registers 155 F, about 2 hours 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62A72E6FE847684F86C7EB6A166161F5', X'2CDFD9194239874A90987BB0FB701A3D', X'2CDFD9194239874A90987BB0FB701A3D', 11, NULL, NULL, 2, NULL, 0, 'Remove loaf pan from baking pan and transfer to a rimmed baking sheet. Place heavy skillet or 2 to 3 heavy cans atop pt to weigh down. Chill overnight.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C330ED8F2F8304C936ECDA49DD04621', X'EF6F000C4EB2874F821998C067F279B0', X'EF6F000C4EB2874F821998C067F279B0', 0, NULL, NULL, 2, NULL, 0, 'Place flank steak into a resealable plastic bag.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0A349F063AD0A45A099355EA5BB6435', X'EF6F000C4EB2874F821998C067F279B0', X'EF6F000C4EB2874F821998C067F279B0', 1, NULL, NULL, 2, NULL, 0, 'In a small glass or plastic bowl, combine 1/4 cup olive oil, white vinegar, garlic, onion, powder, salt, and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'504E0E4696779248AF4E899561BA8481', X'EF6F000C4EB2874F821998C067F279B0', X'EF6F000C4EB2874F821998C067F279B0', 2, NULL, NULL, 2, NULL, 0, 'Pour mixture over meat, making sure to coat all surfaces.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AC77084AF0590408DD359B6F03F40E7', X'EF6F000C4EB2874F821998C067F279B0', X'EF6F000C4EB2874F821998C067F279B0', 3, NULL, NULL, 2, NULL, 0, 'Release air from the resealable bag, seal, and place into the refrigerator for 4 to 8 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C014FB7E423C3644A184610998150D42', X'EF6F000C4EB2874F821998C067F279B0', X'EF6F000C4EB2874F821998C067F279B0', 4, NULL, NULL, 2, NULL, 0, 'Preheat grill for high heat. Prepare the topping for flank steak by combining roasted red peppers with parsley, basil, 1 tablespoon olive oil, balsamic vinegar, honey and a pinch of salt and black pepper. Cover with plastic wrap and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9C65AB89141B94195C3D5B04B24CE45', X'EF6F000C4EB2874F821998C067F279B0', X'EF6F000C4EB2874F821998C067F279B0', 5, NULL, NULL, 2, NULL, 0, 'Remove steak from marinade and place onto the grill grate.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14BDDABF968CD244B21F3276D252089A', X'EF6F000C4EB2874F821998C067F279B0', X'EF6F000C4EB2874F821998C067F279B0', 6, NULL, NULL, 2, NULL, 0, 'Cook for 8 to 10 minutes per side or until it reaches the desired doneness.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96478F6EAC1DAF4987777D78ACA38A8F', X'EF6F000C4EB2874F821998C067F279B0', X'EF6F000C4EB2874F821998C067F279B0', 7, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from heat and let the steak rest for 5 to 10 minutes before carving. Serve with roasted red pepper topping.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76227D8DAE5B7B4AB866A7C1676E3EC8', X'23295F528962E84C8164519909C0EFB9', X'23295F528962E84C8164519909C0EFB9', 0, NULL, NULL, 2, NULL, 0, 'Cut the beef across the grain into thin strips approximately 1 1/2 inches long and 3/4 inch wide.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BE719DFBDB91C47AF2AF3A824150DAF', X'23295F528962E84C8164519909C0EFB9', X'23295F528962E84C8164519909C0EFB9', 1, NULL, NULL, 2, NULL, 0, 'Add the marinade ingredients, adding the cornstarch last. Marinade the beef for 25 to 30 minutes. This recipe contains dry sherry because it is more widely available, but if you want a more authentic option, you can use traditional Chinese rice wine.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB533ECF66F40E4C84A154FD17066233', X'23295F528962E84C8164519909C0EFB9', X'23295F528962E84C8164519909C0EFB9', 2, NULL, NULL, 2, NULL, 0, 'While the beef is marinating, prepare the vegetables. Peel and chop the onion. Blanch the peas in boiling water for 1 minute then prepare the vegetables. Peel and chop the onion. Blanch the peas in boiling water for 1 minute. Rinse in cold water, and drain thoroughly. Combine the chicken broth with the dark soy sauce and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0AEC27DFCF29C449640225725B140EE', X'23295F528962E84C8164519909C0EFB9', X'23295F528962E84C8164519909C0EFB9', 3, NULL, NULL, 2, NULL, 0, 'Preheat the wok on medium-high heat (the wok is ready when a couple of drops of water sizzle when added to the wok). Add 2 tablespoons oil (you can also substitute peanut oil for the canola oil) to the wok, drizzling to coat the sides. When the oil is sizzling, add the ginger. Cook for 2 to 3 minutes until the ginger is browned. Discard the ginger.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F82E4786A9D20A478F60F2529DF17C2A', X'23295F528962E84C8164519909C0EFB9', X'23295F528962E84C8164519909C0EFB9', 4, NULL, NULL, 2, NULL, 0, 'Add the beef to the wok (to avoid overcrowding the wok, cook the beef in 2 batches if needed). Sear the beef for about 30 seconds, then stir-fry until it is about 80 percent cooked and changes color. Remove the beef from the wok. Turn the heat down to medium.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B66EDD265911642841BF88625629BED', X'23295F528962E84C8164519909C0EFB9', X'23295F528962E84C8164519909C0EFB9', 5, NULL, NULL, 2, NULL, 0, 'Add the onion to the wok. Let the onion cook for 2 minutes to brown, then add the curry powder. Add enough water to make a paste. Let the onion cook for a couple of minutes, then add peas.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E944F6C8981B68489BFD366E7909F02C', X'23295F528962E84C8164519909C0EFB9', X'23295F528962E84C8164519909C0EFB9', 6, NULL, NULL, 2, NULL, 0, 'Add the beef. Return the heat to medium-high, and add the chicken broth and dark soy sauce. Bring to a boil. Stir in the salt and sugar. Cook for about another 3 minutes, and serve hot. You can serve this beef curry with cooked white rice or naan bread as you wish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E9CFA011DD0AF498BDF74CFDC4DB140', X'BC0292D36BCC6F4498E78F101AC14223', X'BC0292D36BCC6F4498E78F101AC14223', 0, NULL, NULL, 2, NULL, 0, 'Cut the beef into paper thin rectangular slices. (Freeze the beef for 1 to 2 hours to make cutting easier, or ask the butcher to cut it for you).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80161E96E820F14DA2749DF0D3DD94EC', X'BC0292D36BCC6F4498E78F101AC14223', X'BC0292D36BCC6F4498E78F101AC14223', 1, NULL, NULL, 2, NULL, 0, 'Prepare the side dishes (see suggested list below), washing and draining the vegetables. If using in the fondue, cut the mushrooms into bite-sized pieces. Shred the lettuce or chop as desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'270844C18E639344A60458AE03CC7790', X'BC0292D36BCC6F4498E78F101AC14223', X'BC0292D36BCC6F4498E78F101AC14223', 2, NULL, NULL, 2, NULL, 0, 'Lay the beef and side dishes on separate platters on the table. Place the dipping sauces on the table in small individual bowls.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AB8EF3D1A90A648819CCD7CED0F604E', X'BC0292D36BCC6F4498E78F101AC14223', X'BC0292D36BCC6F4498E78F101AC14223', 3, NULL, NULL, 2, NULL, 0, 'Make sure each guest has a complete place setting, including a dipping fork (color-coded if possible) and a small bowl for placing the cooked food.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9735D1BB707984B8D9FBBE9606DB12F', X'BC0292D36BCC6F4498E78F101AC14223', X'BC0292D36BCC6F4498E78F101AC14223', 4, NULL, NULL, 2, NULL, 0, 'Combine the water and beef bouillon and bring to a boil. Turn down to a simmer and add the white wine, soy sauce, green onion, and ginger. Transfer enough broth so that the fondue pot is approximately 2/3 to 3/4 full. (How much broth you need will depend on the size of the fondue pot).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5063AB6B105AC045AA5EABA4A967D926', X'BC0292D36BCC6F4498E78F101AC14223', X'BC0292D36BCC6F4498E78F101AC14223', 5, NULL, NULL, 2, NULL, 0, 'Place the fondue pot on the burner, and keep it simmering throughout the meal. Keep the remaining broth warming on the stovetop.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00FD6CDB169E254B95340FCDD70D7F09', X'BC0292D36BCC6F4498E78F101AC14223', X'BC0292D36BCC6F4498E78F101AC14223', 6, NULL, NULL, 2, NULL, 0, 'Use dipping forks to cook the food in the hot broth, and then dip in the sauces as desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8201A22A8E5BFD43A43330EAF528CEF0', X'5188B1FDD641DA439760471178588179', X'5188B1FDD641DA439760471178588179', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B3FF2B5D094CF4D9C768F89AFB9F8F9', X'5188B1FDD641DA439760471178588179', X'5188B1FDD641DA439760471178588179', 1, NULL, NULL, 2, NULL, 0, 'Cut the flank steak into strips about 1 1/3 inches wide.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EE38FC91A908F40B723D895EA88F14A', X'5188B1FDD641DA439760471178588179', X'5188B1FDD641DA439760471178588179', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the soy sauce, Hawaiian salt, sugar, minced garlic, crushed ginger, and crushed red chile pepper, if using, to create the marinade.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74CF237B4493AA45BA8302768560D31D', X'5188B1FDD641DA439760471178588179', X'5188B1FDD641DA439760471178588179', 3, NULL, NULL, 2, NULL, 0, 'Put the beef in a bowl and cover it with plastic wrap, or place the steak in a resealable plastic bag. Refrigerate and marinate overnight');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8B91AB0D190A648AD01AFDD77CB0CE5', X'5188B1FDD641DA439760471178588179', X'5188B1FDD641DA439760471178588179', 4, NULL, NULL, 2, NULL, 0, 'Pre heat oven to 175 degrees F. Place meat on a rack, such as a cookie cooling rack. Place rack on a cookie sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A24215D960766441BEFDEE66831EE101', X'5188B1FDD641DA439760471178588179', X'5188B1FDD641DA439760471178588179', 5, NULL, NULL, 2, NULL, 0, 'Dry meat in the oven for 7 hours. Store the jerky in the refrigerator.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1ED1EF844B8B404AA4B215871547ACA9', X'DD910926D5815142893178684B71A929', X'DD910926D5815142893178684B71A929', 0, NULL, NULL, 2, NULL, 0, 'Dredge the meat with flour seasoned with salt, pepper, and a little garlic powder.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB873B65D1FF894A9EFEACBFD06EA29F', X'DD910926D5815142893178684B71A929', X'DD910926D5815142893178684B71A929', 1, NULL, NULL, 2, NULL, 0, 'Dip into beaten egg.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A3D50A30FB3A44492E3559ECD97CB6A', X'DD910926D5815142893178684B71A929', X'DD910926D5815142893178684B71A929', 2, NULL, NULL, 2, NULL, 0, 'Press bread crumbs or soda cracker crumbs into the meat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F7029A483B58D46A74BD1E80820F22B', X'DD910926D5815142893178684B71A929', X'DD910926D5815142893178684B71A929', 3, NULL, NULL, 2, NULL, 0, 'Fry in about 1/2 inch of hot oil (approximately 350 F) until golden brown.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A77A7D790B1964AA2D94894A1DDA4F7', X'DDEAD9654904DF4A87C8850AD692F4E5', X'DDEAD9654904DF4A87C8850AD692F4E5', 0, NULL, NULL, 2, NULL, 0, 'Use a sharp carving knife or a cleaver to cut the steak into thin slices that are about 1/8 inch thick and 1 1/2 to 2 inches long. If you like, go one step further and lay the strips on top of one another and cut again into thin strips.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C3872284B279C4588A1E7FF4396551A', X'DDEAD9654904DF4A87C8850AD692F4E5', X'DDEAD9654904DF4A87C8850AD692F4E5', 1, NULL, NULL, 2, NULL, 0, 'Measure out the stir-fry sauce into a small bowl or a measuring cup. Chop the garlic and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'659FFC7286BBDE40B8B64A78652A3854', X'DDEAD9654904DF4A87C8850AD692F4E5', X'DDEAD9654904DF4A87C8850AD692F4E5', 2, NULL, NULL, 2, NULL, 0, 'Heat a wok or heavy skillet over medium-high heat until it is almost smoking. Add 2 tablespoons of the oil, tilting the wok and drizzling the oil so that it coats the sides of the pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8AE004BAEEBE542A4DB8B8E3289EDE6', X'DDEAD9654904DF4A87C8850AD692F4E5', X'DDEAD9654904DF4A87C8850AD692F4E5', 3, NULL, NULL, 2, NULL, 0, 'When the oil is hot, add half of the chopped garlic. You can test to see if the oil is hot by dropping in a small piece of garlic. If it sizzles, the oil is ready.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83856505C9D1C7429C3CA64A48447F95', X'DDEAD9654904DF4A87C8850AD692F4E5', X'DDEAD9654904DF4A87C8850AD692F4E5', 4, NULL, NULL, 2, NULL, 0, 'Stir fry the garlic for 10 seconds.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7052EFCB8C6C0B43A8375B8792AD21BF', X'DDEAD9654904DF4A87C8850AD692F4E5', X'DDEAD9654904DF4A87C8850AD692F4E5', 5, NULL, NULL, 2, NULL, 0, 'Add the beef by laying it flat in the pan. Let it brown briefly, then stir-fry the beef until it loses its redness and is nearly cooked through.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8846A8172AD5545AD9AA08AE777CBA0', X'DDEAD9654904DF4A87C8850AD692F4E5', X'DDEAD9654904DF4A87C8850AD692F4E5', 6, NULL, NULL, 2, NULL, 0, 'Remove the stir-fried beef and drain in a colander or on paper towels.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D9C46858806224480A4001F8ACAD8EE', X'DDEAD9654904DF4A87C8850AD692F4E5', X'DDEAD9654904DF4A87C8850AD692F4E5', 7, NULL, NULL, 2, NULL, 0, 'Heat the remaining 1 tablespoon oil of in the same wok or skillet. When the oil is hot, add the remainder of the garlic. Stir-fry for 10 seconds.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE0E4A3487EA7E44ADCAAB8F2A9AFD11', X'DDEAD9654904DF4A87C8850AD692F4E5', X'DDEAD9654904DF4A87C8850AD692F4E5', 8, NULL, NULL, 2, NULL, 0, 'Add the frozen vegetables. Stir-fry for 3 to 5 minutes or according to package directions. Splash the vegetables with a bit of water if the vegetables begin to dry out while you are stir-frying.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'282E1BBF4CC905419141D54B0A5A8437', X'DDEAD9654904DF4A87C8850AD692F4E5', X'DDEAD9654904DF4A87C8850AD692F4E5', 9, NULL, NULL, 2, NULL, 0, 'Add the stir-fried beef back into the pan. Add the stir-fry sauce and bring to a boil. Stir-fry for 1 to 2 more minutes to mix everything together.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB6B802101D3DF41832CA40988A0EC2A', X'DDEAD9654904DF4A87C8850AD692F4E5', X'DDEAD9654904DF4A87C8850AD692F4E5', 10, NULL, NULL, 2, NULL, 0, 'Serve hot over cooked rice.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED34BAA6BC43164A816910045AA362AA', X'558C6EB3CC60FC429A6F32F52EEECDF9', X'558C6EB3CC60FC429A6F32F52EEECDF9', 0, NULL, NULL, 2, NULL, 0, 'Cut steak into 2` x 1/2` strips and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BE651EDF46344418A5755387566197C', X'558C6EB3CC60FC429A6F32F52EEECDF9', X'558C6EB3CC60FC429A6F32F52EEECDF9', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine broth, vinegar, ketchup, soy sauce, brown sugar, cornstarch, ginger, and pepper; mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3A664AF3FBDC84D807EA87E40A97556', X'558C6EB3CC60FC429A6F32F52EEECDF9', X'558C6EB3CC60FC429A6F32F52EEECDF9', 2, NULL, NULL, 2, NULL, 0, 'Add meat and set aside for about 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'344DC76DD7E97F47982A88C73AE77087', X'558C6EB3CC60FC429A6F32F52EEECDF9', X'558C6EB3CC60FC429A6F32F52EEECDF9', 3, NULL, NULL, 2, NULL, 0, 'Prepare all vegetables and place them, in the order they will be cooked, on plates next to the stove.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97CAA24D323DF648BDD66AFFBC7617F9', X'558C6EB3CC60FC429A6F32F52EEECDF9', X'558C6EB3CC60FC429A6F32F52EEECDF9', 4, NULL, NULL, 2, NULL, 0, 'In large skillet or wok, heat oil over medium-high heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6570FD03DDC5D948A8975500B9D9DB04', X'558C6EB3CC60FC429A6F32F52EEECDF9', X'558C6EB3CC60FC429A6F32F52EEECDF9', 5, NULL, NULL, 2, NULL, 0, 'Remove steak from marinade; reserve marinade.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E59634A7D0D6F34487922815BE8D1457', X'558C6EB3CC60FC429A6F32F52EEECDF9', X'558C6EB3CC60FC429A6F32F52EEECDF9', 6, NULL, NULL, 2, NULL, 0, 'Add steak to the hot oil and stir-fry until browned but still pink inside. Remove from wok.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDDE6316C88BD144A5894783E2474E96', X'558C6EB3CC60FC429A6F32F52EEECDF9', X'558C6EB3CC60FC429A6F32F52EEECDF9', 7, NULL, NULL, 2, NULL, 0, 'Add onion to wok; stir-fry for 2 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AD1098FA80D8442BCE661D0DC1318F3', X'558C6EB3CC60FC429A6F32F52EEECDF9', X'558C6EB3CC60FC429A6F32F52EEECDF9', 8, NULL, NULL, 2, NULL, 0, 'Add garlic, bell pepper, and mushrooms; stir-fry for 3 to 4 minutes longer until crisp-tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C13AF63D2466D42A72C24448C6668E9', X'558C6EB3CC60FC429A6F32F52EEECDF9', X'558C6EB3CC60FC429A6F32F52EEECDF9', 9, NULL, NULL, 2, NULL, 0, 'Return beef and any accumulated juices to pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ED64B9F95B19E47AA42D9EB62E3716C', X'558C6EB3CC60FC429A6F32F52EEECDF9', X'558C6EB3CC60FC429A6F32F52EEECDF9', 10, NULL, NULL, 2, NULL, 0, 'Stir the reserved marinade and add to wok; stir-fry until sauce thickens.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62CE97F4E8F13148B906AD90D91846EA', X'558C6EB3CC60FC429A6F32F52EEECDF9', X'558C6EB3CC60FC429A6F32F52EEECDF9', 11, NULL, NULL, 2, NULL, 0, 'Add grape tomatoes and stir-fry for one minute.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C8B9888F274BD41B8389953805DF45D', X'558C6EB3CC60FC429A6F32F52EEECDF9', X'558C6EB3CC60FC429A6F32F52EEECDF9', 12, NULL, NULL, 2, NULL, 0, 'Serve over hot cooked rice, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71E947595E541844B9275717425584DC', X'49949C28F9F5D145BEE4B74C684ECC19', X'49949C28F9F5D145BEE4B74C684ECC19', 0, NULL, NULL, 2, NULL, 0, 'Mix all the ingredients except the flank steak together and mix well. Place the flank steak in a shallow baking dish or resealable bag with the marinade for 4 to 6 hours in the refrigerator.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0605D0AB14A9CC4089E92C2E7D62D7B9', X'49949C28F9F5D145BEE4B74C684ECC19', X'49949C28F9F5D145BEE4B74C684ECC19', 1, NULL, NULL, 2, NULL, 0, 'Remove steaks from marinade and reserve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18F38F0EED2211459A4705C1058D9234', X'49949C28F9F5D145BEE4B74C684ECC19', X'49949C28F9F5D145BEE4B74C684ECC19', 2, NULL, NULL, 2, NULL, 0, 'Grill steaks to desired doneness (about 5 minutes on each side).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DF69557B3629847AF063FB171E2B76D', X'49949C28F9F5D145BEE4B74C684ECC19', X'49949C28F9F5D145BEE4B74C684ECC19', 3, NULL, NULL, 2, NULL, 0, 'Heat the marinade in a saucepan for 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E512AF4306BD30469C59553D41301515', X'49949C28F9F5D145BEE4B74C684ECC19', X'49949C28F9F5D145BEE4B74C684ECC19', 4, NULL, NULL, 2, NULL, 0, 'Cut steaks diagonally into thin slices. Pour marinade over steaks and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDFA0B4E4D68C342A376665F2FF29490', X'F924EDDD10D08043B3B07D65F05FE85D', X'F924EDDD10D08043B3B07D65F05FE85D', 0, NULL, NULL, 2, NULL, 0, 'In a large plastic zip-top bag or plastic container with a tight lid, combine the steak, Dijon mustard, garlic, Guinness, salt, and pepper. Close the bag or container and shake it well to evenly distribute the marinade.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDDE4944B3AD6A438228698FF8B956BD', X'F924EDDD10D08043B3B07D65F05FE85D', X'F924EDDD10D08043B3B07D65F05FE85D', 1, NULL, NULL, 2, NULL, 0, 'Put the marinating steak in the refrigerator for 2 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D416DE88CE19BE4B8DEF5D64BFF4140D', X'F924EDDD10D08043B3B07D65F05FE85D', X'F924EDDD10D08043B3B07D65F05FE85D', 2, NULL, NULL, 2, NULL, 0, 'Remove the steak from the fridge and let it come to room temperature while you prepare the grill.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F228D315E615C47A7515DB62E9A6584', X'F924EDDD10D08043B3B07D65F05FE85D', X'F924EDDD10D08043B3B07D65F05FE85D', 3, NULL, NULL, 2, NULL, 0, 'Set the grill to high heat, or about 450 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A49CE29F6FF6824194FFA539DEFB4FDD', X'F924EDDD10D08043B3B07D65F05FE85D', X'F924EDDD10D08043B3B07D65F05FE85D', 4, NULL, NULL, 2, NULL, 0, 'Grill the steak for 4 to 5 minutes for each side, double-checking the internal temperature with an internal meat thermometer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D41C473C5ADD194293BAE143210EF895', X'F924EDDD10D08043B3B07D65F05FE85D', X'F924EDDD10D08043B3B07D65F05FE85D', 5, NULL, NULL, 2, NULL, 0, 'Remove the steak from the grill, and let it rest for 5 to 10 minutes before serving.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63C3AA4845E2C543AFB30D2AA9F67FC0', X'A4BFF057ACA5414A82150C3E9744DBB4', X'A4BFF057ACA5414A82150C3E9744DBB4', 0, NULL, NULL, 2, NULL, 0, 'Place fish steaks on a large rimmed baking tray. Drizzle lemon/lime juice over them and sprinkle salt to taste. Turn pieces to coat with marinade thoroughly. Set aside for 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C00DFEE096BEC449AAC0D15ADB25F65', X'A4BFF057ACA5414A82150C3E9744DBB4', X'A4BFF057ACA5414A82150C3E9744DBB4', 1, NULL, NULL, 2, NULL, 0, 'In a blender, add coconut, chilies, coriander, and mint leaves, ginger and garlic, cumin and turmeric powders, and salt. Blend to a smooth paste. Add as little water as possible, if necessary.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C9931380F82D3459182543637A7C980', X'A4BFF057ACA5414A82150C3E9744DBB4', X'A4BFF057ACA5414A82150C3E9744DBB4', 2, NULL, NULL, 2, NULL, 0, 'Divide paste into 2 portions, and use each portion to coat the pieces of fish on each side thoroughly. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DB3BE4EB14BCE40BB79BFEDBBD11893', X'A4BFF057ACA5414A82150C3E9744DBB4', X'A4BFF057ACA5414A82150C3E9744DBB4', 3, NULL, NULL, 2, NULL, 0, 'Pat dry the banana leaf pieces and smear lightly with oil on the smooth side. Place one piece of fish in the center of each piece of leaf and wrap into a neat parcel. Tie with twine or cotton string.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1EDF1024BC1764288A319621CD72675', X'A4BFF057ACA5414A82150C3E9744DBB4', X'A4BFF057ACA5414A82150C3E9744DBB4', 4, NULL, NULL, 2, NULL, 0, 'Cook in a steamer for 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AB27963B99E454D9BCA2584311A354A', X'A4BFF057ACA5414A82150C3E9744DBB4', X'A4BFF057ACA5414A82150C3E9744DBB4', 5, NULL, NULL, 2, NULL, 0, 'Place each piece in the center of a plate and serve immediately.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD60FDA5C8694946A71E9BA2384A9C43', X'7CA772FECD0D494ABBDF792D3C30070E', X'7CA772FECD0D494ABBDF792D3C30070E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EECB7F7D5DD7C84ABA7968C44AA0743A', X'7CA772FECD0D494ABBDF792D3C30070E', X'7CA772FECD0D494ABBDF792D3C30070E', 1, NULL, NULL, 2, NULL, 0, 'Combine the mayonnaise, sour cream, ranch dressing mix, and pepper in a medium bowl. Cover the bowl and refrigerate the dressing mixture until it is time to make the salad.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49A2BE17A22F6D4C9316BB3072E2B5EB', X'7CA772FECD0D494ABBDF792D3C30070E', X'7CA772FECD0D494ABBDF792D3C30070E', 2, NULL, NULL, 2, NULL, 0, 'Put the diced bacon in a large skillet and place it over medium-low heat. Cook, stirring frequently, until it is browned and beginning to crisp. Use a slotted spatula to remove the bacon to paper towels to drain. Let the bacon drippings cool slightly and then pour them into a container to use in future dishes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA26043CF0F30541A82EB03C5D785013', X'7CA772FECD0D494ABBDF792D3C30070E', X'7CA772FECD0D494ABBDF792D3C30070E', 3, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to a boil; add the rotini and turn the heat down to maintain a gentle boil and cook according to the package directions, usually about 7 to 9 minutes. Drain the pasta and let it cool.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D131F056D452884795DC12B5CE49F8C9', X'7CA772FECD0D494ABBDF792D3C30070E', X'7CA772FECD0D494ABBDF792D3C30070E', 4, NULL, NULL, 2, NULL, 0, 'Put the cooled pasta in a large bowl and add the cooked drained bacon, the halved tomatoes, diced celery, and olives to the bowl. Toss gently to combine.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A108521A9E86804C9ED7205352A1D6B7', X'7CA772FECD0D494ABBDF792D3C30070E', X'7CA772FECD0D494ABBDF792D3C30070E', 5, NULL, NULL, 2, NULL, 0, 'Add the dressing mixture and gently fold it in until the pasta and vegetables are well coated.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E333F059B635346B1D8AA730BC43084', X'7CA772FECD0D494ABBDF792D3C30070E', X'7CA772FECD0D494ABBDF792D3C30070E', 6, NULL, NULL, 2, NULL, 0, 'Transfer the pasta salad to a serving bowl; cover and refrigerate until serving time.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B395B7051BE444C9E89B7E878A043A8', X'C3F4316DA05BEE4D937BE55E365BC817', X'C3F4316DA05BEE4D937BE55E365BC817', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D00BA906E95824BB3F6B6428E86490C', X'C3F4316DA05BEE4D937BE55E365BC817', X'C3F4316DA05BEE4D937BE55E365BC817', 1, NULL, NULL, 2, NULL, 0, 'Rinse the pork belly under cold water.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABF6577C266D2B49BBBDA28BC0154CD7', X'C3F4316DA05BEE4D937BE55E365BC817', X'C3F4316DA05BEE4D937BE55E365BC817', 2, NULL, NULL, 2, NULL, 0, 'Pat it dry with paper towels or a clean dishcloth.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CEBE8B42FE67545915B794496B7A6DB', X'C3F4316DA05BEE4D937BE55E365BC817', X'C3F4316DA05BEE4D937BE55E365BC817', 3, NULL, NULL, 2, NULL, 0, 'Combine the maple syrup, salt, pepper, and curing salt (if using) in a small bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1E979F5384E2B4F8538903670A202FF', X'C3F4316DA05BEE4D937BE55E365BC817', X'C3F4316DA05BEE4D937BE55E365BC817', 4, NULL, NULL, 2, NULL, 0, 'Rub the seasoning mixture into all sides of the pork belly, using your scrupulously cleaned hands. Spend a couple of minutes massaging the seasoning/curing mixture into the meat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBCC7EE530E640408EF0374CC5931CD4', X'C3F4316DA05BEE4D937BE55E365BC817', X'C3F4316DA05BEE4D937BE55E365BC817', 5, NULL, NULL, 2, NULL, 0, 'Place the pork belly, along with any leftover curing mixture, into a plastic bag and seal it shut.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A87B93FA21C4564DB24C1A16506934B1', X'C3F4316DA05BEE4D937BE55E365BC817', X'C3F4316DA05BEE4D937BE55E365BC817', 6, NULL, NULL, 2, NULL, 0, 'Store it lengthwise in the refrigerator for 10 to 14 days, turning the bag over occasionally. The bacon should be fully cured at this point, with a firm texture and no soft spots.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBCF5B68C26AA44F94691F580F612280', X'C3F4316DA05BEE4D937BE55E365BC817', X'C3F4316DA05BEE4D937BE55E365BC817', 7, NULL, NULL, 2, NULL, 0, 'Preheat oven to 200 F. Rinse the bacon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A38391C0C001F448BC7C7742EF58BE19', X'C3F4316DA05BEE4D937BE55E365BC817', X'C3F4316DA05BEE4D937BE55E365BC817', 8, NULL, NULL, 2, NULL, 0, 'Again pat it thoroughly dry with paper towels or a clean, dry dishtowel.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFAC7E05EA0B014AA8A5FDAB31921357', X'C3F4316DA05BEE4D937BE55E365BC817', X'C3F4316DA05BEE4D937BE55E365BC817', 9, NULL, NULL, 2, NULL, 0, 'Roast the cured bacon in a 200 F oven until the internal temperature reaches 150 F. This should take about two hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A6079CA54427946850E15589ED0DA88', X'C3F4316DA05BEE4D937BE55E365BC817', X'C3F4316DA05BEE4D937BE55E365BC817', 10, NULL, NULL, 2, NULL, 0, 'Store the bacon in a tightly sealed container or bag in the refrigerator for up to a month or in the freezer for up to a year.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82F67648EB90894095282286B4D36232', X'EE921C0F3095D34496329380FF3CF94E', X'EE921C0F3095D34496329380FF3CF94E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B15A82EBFB0505458997B82AB48BE47B', X'EE921C0F3095D34496329380FF3CF94E', X'EE921C0F3095D34496329380FF3CF94E', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 445 F / 170 C / Gas 3.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34DFFEDD568A0E48B269A788AA3E8E53', X'EE921C0F3095D34496329380FF3CF94E', X'EE921C0F3095D34496329380FF3CF94E', 2, NULL, NULL, 2, NULL, 0, 'Mince all the roughly chopped meats in a hand mincer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12BBA04C4409D44EBF72E07269AE2CF7', X'EE921C0F3095D34496329380FF3CF94E', X'EE921C0F3095D34496329380FF3CF94E', 3, NULL, NULL, 2, NULL, 0, 'Place the minced meat into a large bowl. Add the breadcrumbs, the onion, the herbs, spices, and a pinch of salt and pepper. Mix thoroughly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1602CAF3CBFDA74C9BFA349BEF71862C', X'EE921C0F3095D34496329380FF3CF94E', X'EE921C0F3095D34496329380FF3CF94E', 4, NULL, NULL, 2, NULL, 0, 'Wet your hands under running cold water, then divide and roll the minced meat mixture into 8 evenly-sized balls.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66680EF2D6B3E74CAA0D76C77005F9C9', X'EE921C0F3095D34496329380FF3CF94E', X'EE921C0F3095D34496329380FF3CF94E', 5, NULL, NULL, 2, NULL, 0, 'Wrap each ball in caul or streaky bacon, making sure the caul or bacon overlaps and is secure.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'691CED11DA709241B6EBF20230EBD2CA', X'EE921C0F3095D34496329380FF3CF94E', X'EE921C0F3095D34496329380FF3CF94E', 6, NULL, NULL, 2, NULL, 0, 'Place the faggots onto a baking sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'889711A48ECA5844A599F5A9A202CCC1', X'EE921C0F3095D34496329380FF3CF94E', X'EE921C0F3095D34496329380FF3CF94E', 7, NULL, NULL, 2, NULL, 0, 'Bake in the hot oven for 50 to 60 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71C5D396DBD0B245A6F35196107E3000', X'EE921C0F3095D34496329380FF3CF94E', X'EE921C0F3095D34496329380FF3CF94E', 8, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from the oven and serve immediately. If you are not ready to eat the faggots then wrap in foil and keep warm.');
