INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA3751EACC1035439BBB34C3C1EEBE30', X'F15EC9F50818304F906190C19FC928AC', X'F15EC9F50818304F906190C19FC928AC', 2, NULL, NULL, 2, NULL, 0, 'Bring the water to boil in a small saucepan along with the whites of the scallion, the butter, and a teaspoon of the salt.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'096C9D61F5326D4AB7735091C56D8F10', X'F15EC9F50818304F906190C19FC928AC', X'F15EC9F50818304F906190C19FC928AC', 3, NULL, NULL, 2, NULL, 0, 'Stir in the quick-cooking oats and cook for 2 to 3 minutes or until soft. Cover and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8062A7795D38D54EBFF203355371B1A4', X'F15EC9F50818304F906190C19FC928AC', X'F15EC9F50818304F906190C19FC928AC', 4, NULL, NULL, 2, NULL, 0, 'While the oats are cooking, turn the heat to medium low on the saute pan filled with bacon grease. Crack the egg into the bacon grease in the small pan. Season the top of the egg with the remaining salt and cracked black pepper. Cook on medium low heat for about 3 minutes or until the whites have set and the yolk is still runny. Baste the top of the eggs with the hot bacon grease to slightly set the top of the egg. Leave the egg in the pan and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C72D1A9B2EE694B8EEED78019E05E7F', X'F15EC9F50818304F906190C19FC928AC', X'F15EC9F50818304F906190C19FC928AC', 5, NULL, NULL, 2, NULL, 0, 'Heat the heavy cream in the microwave for about 40 seconds or until hot. Add the warm oatmeal to your serving bowl and pour the hot cream over the top.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'107FBE8B2A1CB840A9FE5A835E580E85', X'F15EC9F50818304F906190C19FC928AC', X'F15EC9F50818304F906190C19FC928AC', 6, NULL, NULL, 2, NULL, 0, 'Top the oatmeal with the shredded cheddar cheese and then the egg, bacon bits, and scallion greens.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65690766133B69489DD624B1DFF0674D', X'F15EC9F50818304F906190C19FC928AC', X'F15EC9F50818304F906190C19FC928AC', 7, NULL, NULL, 2, NULL, 0, 'Serve immediately with more salt and pepper if desired. You can also add a drizzle of hot sauce or Sriracha.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F678F60A455E1F45981D30ACFA3E40DA', X'65AF94641966274AB9D5DEDA6C17F911', X'65AF94641966274AB9D5DEDA6C17F911', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat oven to 350 F.');
