INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62CBE388AAC5CA44AC53DB749D56E495', X'65AF94641966274AB9D5DEDA6C17F911', X'65AF94641966274AB9D5DEDA6C17F911', 3, NULL, NULL, 2, NULL, 0, 'Pour the eggs into the omelet pan and scramble until completely cooked. Set aside.');
