INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47A8A46A08770B4485C97519FA4733CD', X'65AF94641966274AB9D5DEDA6C17F911', X'65AF94641966274AB9D5DEDA6C17F911', 5, NULL, NULL, 2, NULL, 0, 'Unroll the crescent dough and separate the triangles.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F79736940C5EA646884DB63B228C4CFC', X'65AF94641966274AB9D5DEDA6C17F911', X'65AF94641966274AB9D5DEDA6C17F911', 6, NULL, NULL, 2, NULL, 0, 'Place a tablespoon of the egg mixture, and a sprinkle of bacon and cheese over the egg.');
