INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFC065B9AC943F44BAD2BC0AE314CA8F', X'65AF94641966274AB9D5DEDA6C17F911', X'65AF94641966274AB9D5DEDA6C17F911', 9, NULL, NULL, 2, NULL, 0, 'Bake for 18 to 20 minutes or until golden brown. Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2E21CEC4CF7514EAC877E4B4B35AF5A', X'D20CE88B5678BC4D97DAA310F5EE2AFA', X'D20CE88B5678BC4D97DAA310F5EE2AFA', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to a boil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDC5C8790A985248A192710EFD930700', X'D20CE88B5678BC4D97DAA310F5EE2AFA', X'D20CE88B5678BC4D97DAA310F5EE2AFA', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a large mixing bowl, combine the cream cheese, milk, and cream.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F79C2AF88DC1042B310A214AAE680AE', X'D20CE88B5678BC4D97DAA310F5EE2AFA', X'D20CE88B5678BC4D97DAA310F5EE2AFA', 2, NULL, NULL, 2, NULL, 0, 'Microwave on high for 1 minute, then remove from the microwave oven and stir with a wire whisk.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBB2F9DD5A036B47AEB7D64F740BB7B3', X'D20CE88B5678BC4D97DAA310F5EE2AFA', X'D20CE88B5678BC4D97DAA310F5EE2AFA', 3, NULL, NULL, 2, NULL, 0, 'Microwave for 1 minute longer, remove, and stir again until the mixture is creamy and smooth.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'589EAC05B4779D45910947A3C612E305', X'D20CE88B5678BC4D97DAA310F5EE2AFA', X'D20CE88B5678BC4D97DAA310F5EE2AFA', 4, NULL, NULL, 2, NULL, 0, 'Let cool for 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC7E66A6E7D8F44495C4E119F17A8D0B', X'D20CE88B5678BC4D97DAA310F5EE2AFA', X'D20CE88B5678BC4D97DAA310F5EE2AFA', 5, NULL, NULL, 2, NULL, 0, 'Beat the eggs and 1/3 cup Parmesan cheese into the cream cheese mixture with a wire whisk and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4138E4505E33B84C9F1A9A4C8556197D', X'D20CE88B5678BC4D97DAA310F5EE2AFA', X'D20CE88B5678BC4D97DAA310F5EE2AFA', 6, NULL, NULL, 2, NULL, 0, 'Cook the pasta when the water comes to a boil until just al dente according to package directions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDEC8CDBF556754E93D980E44E9D8FEF', X'D20CE88B5678BC4D97DAA310F5EE2AFA', X'D20CE88B5678BC4D97DAA310F5EE2AFA', 7, NULL, NULL, 2, NULL, 0, 'Reserve 1/2 cup of the pasta cooking water in a small bowl, then drain the pasta into a colander placed in the sink.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F328BD07EEB0A46AEEB1D32C784106B', X'D20CE88B5678BC4D97DAA310F5EE2AFA', X'D20CE88B5678BC4D97DAA310F5EE2AFA', 8, NULL, NULL, 2, NULL, 0, 'Return the pasta to the hot pot and immediately stir in the cream cheese mixture, adding enough reserved pasta water as needed to make a smooth and creamy sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3FFEEB019EDE94880A40AE2A3F9006B', X'D20CE88B5678BC4D97DAA310F5EE2AFA', X'D20CE88B5678BC4D97DAA310F5EE2AFA', 9, NULL, NULL, 2, NULL, 0, 'Stir in the bacon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1B9F4B9858E4B469DCF8B4637F1F336', X'D20CE88B5678BC4D97DAA310F5EE2AFA', X'D20CE88B5678BC4D97DAA310F5EE2AFA', 10, NULL, NULL, 2, NULL, 0, 'Serve immediately, topped with more Parmesan cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C67BCBAAE3CF548BFB05460E412E059', X'AF867C47F3FBB7449A26BD0C2B607EA3', X'AF867C47F3FBB7449A26BD0C2B607EA3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1476CE7F3BB8540B9848B2A4D11E72F', X'AF867C47F3FBB7449A26BD0C2B607EA3', X'AF867C47F3FBB7449A26BD0C2B607EA3', 1, NULL, NULL, 2, NULL, 0, 'Preheat the air fryer to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'966AFAC5F6FC104FA07B389C78E06AAF', X'AF867C47F3FBB7449A26BD0C2B607EA3', X'AF867C47F3FBB7449A26BD0C2B607EA3', 2, NULL, NULL, 2, NULL, 0, 'Place about 6 strips of the bacon across the air fryer. For flatter bacon you can trim it to fit the basket exactly. If you dont mind the bacon curling, you can just add the longer pieces and the edges will roll over.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A3C813EEDE94A4EBEAEB5EF292FB7BF', X'AF867C47F3FBB7449A26BD0C2B607EA3', X'AF867C47F3FBB7449A26BD0C2B607EA3', 3, NULL, NULL, 2, NULL, 0, 'Cook for 8 to 10 minutes or until the bacon is crispy. If you want the bacon less crispy you can take it out a minute earlier. If you like it crispier then leave it in for an additional minute.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C870D771BE78C488B2D1AAE04B79A62', X'AF867C47F3FBB7449A26BD0C2B607EA3', X'AF867C47F3FBB7449A26BD0C2B607EA3', 4, NULL, NULL, 2, NULL, 0, 'If you are using a thicker cut of bacon then leave it in for a minute or two longer. You can pause the air fryer as much as you like in order to get the bacon to your ideal doneness.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15E662860B156D4BBE070D2AF3F1CAB1', X'AF867C47F3FBB7449A26BD0C2B607EA3', X'AF867C47F3FBB7449A26BD0C2B607EA3', 5, NULL, NULL, 2, NULL, 0, 'After you are finished cooking the first batch, make sure to pour out some of the grease from the bottom of the air fryer. This will help prevent excessive smoking.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92CC65A4AC0FA443B13C8CC5643C66B9', X'C9F47816F793064086E7B55974B7A184', X'C9F47816F793064086E7B55974B7A184', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'471444BD53EC6D4081D15CBAC53AB679', X'C9F47816F793064086E7B55974B7A184', X'C9F47816F793064086E7B55974B7A184', 1, NULL, NULL, 2, NULL, 0, 'Place a large skillet over medium heat. When the skillet is hot, add the bacon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A82FA31D7DD2346893429AF7BB8A8E8', X'C9F47816F793064086E7B55974B7A184', X'C9F47816F793064086E7B55974B7A184', 2, NULL, NULL, 2, NULL, 0, 'Cook until the bacon is browned and crisp, turning frequently.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D9D5A1024C7FA4EB6A07F78C3EF75A7', X'C9F47816F793064086E7B55974B7A184', X'C9F47816F793064086E7B55974B7A184', 3, NULL, NULL, 2, NULL, 0, 'Remove the bacon to a paper towel-lined plate to drain. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1F93B255509BA479E1D13E7384F6D28', X'C9F47816F793064086E7B55974B7A184', X'C9F47816F793064086E7B55974B7A184', 4, NULL, NULL, 2, NULL, 0, 'Cook macaroni in boiling salted water following package directions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CACFB7046652CC40A9C3105A5080EB2F', X'C9F47816F793064086E7B55974B7A184', X'C9F47816F793064086E7B55974B7A184', 5, NULL, NULL, 2, NULL, 0, 'Drain the macaroni in a colander, rinse with hot water, and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5CA752890799A4CB5359ADF2F3E5718', X'C9F47816F793064086E7B55974B7A184', X'C9F47816F793064086E7B55974B7A184', 6, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F (180 C/Gas 4).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'748CBC1EE0393A44807751B745085004', X'C9F47816F793064086E7B55974B7A184', X'C9F47816F793064086E7B55974B7A184', 7, NULL, NULL, 2, NULL, 0, 'Lightly grease a 2 1/2-quart baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C16D2EDADB688F429B579A65427996AF', X'C9F47816F793064086E7B55974B7A184', X'C9F47816F793064086E7B55974B7A184', 8, NULL, NULL, 2, NULL, 0, 'In a medium saucepan over medium heat, melt the 3 tablespoons of butter. Add the green onions and garlic powder. Cook, stirring, for about 1 minute.');
