INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C711D58E2F19A64BB2D0296FD83FF977', X'861F09ACC4373A4B810E8740FD671A6E', X'861F09ACC4373A4B810E8740FD671A6E', 5, NULL, NULL, 2, NULL, 0, 'With a slotted spoon, remove the bacon pieces to paper towels to drain; set aside. Leave about 2 tablespoons of the bacon drippings in the pot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA48072E46B2E949B4F3DBD051759D3D', X'861F09ACC4373A4B810E8740FD671A6E', X'861F09ACC4373A4B810E8740FD671A6E', 6, NULL, NULL, 2, NULL, 0, 'Add the chopped onion to the bacon drippings and cook over medium heat until tender and lightly browned, stirring frequently. This step will take about 4 to 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAFC014837FC2E419BB54BA5A28F50C0', X'861F09ACC4373A4B810E8740FD671A6E', X'861F09ACC4373A4B810E8740FD671A6E', 7, NULL, NULL, 2, NULL, 0, 'Coarsely chop the cabbage.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B297F85EBCB1464E920A78937A41304C', X'861F09ACC4373A4B810E8740FD671A6E', X'861F09ACC4373A4B810E8740FD671A6E', 8, NULL, NULL, 2, NULL, 0, 'Add the chopped cabbage to the browned onions and add 1/2 cup of chicken broth.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB97557B55A6B642A1AA777455EE26AF', X'861F09ACC4373A4B810E8740FD671A6E', X'861F09ACC4373A4B810E8740FD671A6E', 9, NULL, NULL, 2, NULL, 0, 'Cover the pot tightly and reduce the heat to low. Simmer for 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'681157E54255C046845F29862C6D9640', X'861F09ACC4373A4B810E8740FD671A6E', X'861F09ACC4373A4B810E8740FD671A6E', 10, NULL, NULL, 2, NULL, 0, 'Add more broth or water, as needed, and cook for about 5 to 10 minutes longer, or until the cabbage is tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20ECC265F00F1F42B461326E154E4CE8', X'861F09ACC4373A4B810E8740FD671A6E', X'861F09ACC4373A4B810E8740FD671A6E', 11, NULL, NULL, 2, NULL, 0, 'Add the bacon pieces and stir. Heat through and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6855D3DDCDE9643800C02BC7B525E23', X'861F09ACC4373A4B810E8740FD671A6E', X'861F09ACC4373A4B810E8740FD671A6E', 12, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'433E5EC225934542BF8EBA93D1E5853E', X'6DAF7B4DA2833D45BBAE773D812BAB32', X'6DAF7B4DA2833D45BBAE773D812BAB32', 0, NULL, NULL, 2, NULL, 0, 'Add vinegar to a very large pot of water and bring to a boil. Klara says the vinegar prevents the cabbage from falling apart. Boil the cabbages for about 5 minutes until the leaves begin to release from the heads. Peel the leaves off and place in a colander to drain and cool. Reserve the cabbage hearts.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74D295453B0E0A45A7338030602AC863', X'6DAF7B4DA2833D45BBAE773D812BAB32', X'6DAF7B4DA2833D45BBAE773D812BAB32', 1, NULL, NULL, 2, NULL, 0, 'Place onions, bacon, and garlic in a food processor and finely chop. In a very large Dutch oven or roaster, saute onion-bacon-garlic mixture. Meanwhile, finely chop the celery and parsley in the food processor and add to the onion-bacon-garlic mixture along with the beef, pork, and veal. Cook until meat is evenly browned. Remove from heat and, if desired, drain off excess fat. Allow it to cool.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6984A6BD30221C4285AEC7AF89ABF927', X'6DAF7B4DA2833D45BBAE773D812BAB32', X'6DAF7B4DA2833D45BBAE773D812BAB32', 2, NULL, NULL, 2, NULL, 0, 'Heat oven to 500 degrees. If the inner leaves of the cabbage heads aren''t pliable, return them to the boiling water for a few minutes. Trim the thick center vein of each leaf without piercing it. Add rice to meat mixture, and season with cinnamon, nutmeg, paprika, and salt. Mix in eggs thoroughly. Place a handful of meat filling on each leaf. Flip up the bottom, then fold the sides in, and roll up. Any leftover filling can be frozen for later use.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A338C3679C16054FA2454B6110C9D095', X'6DAF7B4DA2833D45BBAE773D812BAB32', X'6DAF7B4DA2833D45BBAE773D812BAB32', 3, NULL, NULL, 2, NULL, 0, 'Chop the reserved cabbage hearts and, in a large bowl, mix with sauerkraut and tomato puree. Season the dish with salt and pepper. Place some of this mixture on the bottom of a deep, large roasting pan. Cover with a layer of cabbage rolls, tightly packed. Press down and spoon on more sauerkraut mixture. Add another layer of cabbage rolls, running at a right angle to the first layer. Continue in this manner until all the cabbage rolls are in the pantop with remaining sauerkraut mixture and pour some of the tomato juice over it. Gently shake the pan to distribute the juice and add the rest of the tomato juice.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95D11F86B7948F41A7303355F93F1CBA', X'6DAF7B4DA2833D45BBAE773D812BAB32', X'6DAF7B4DA2833D45BBAE773D812BAB32', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle with sugar and cloves. Cover and place in a 500-degree oven. When the mixture comes to a boil, reduce the heat to 350 degrees and bake 2 1/2 hours. Remove cloves before serving. Present two or three cabbage rolls per person with boiled potatoes and some of the sauerkraut mixture, and sprinkle with chopped parsley and paprika. Cooked stuffed cabbage will keep a long time in the freezer if they are well sealed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'294E3A8A95E9BC4F86601F97B1960689', X'927CAD1549920A4E85AE5F4F7490C39F', X'927CAD1549920A4E85AE5F4F7490C39F', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 F. Prepare a baking sheet by lining it with aluminum foil, then place an oven-safe wire rack on top of the pan. Spray the rack liberally with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DDDDDE2BE672647AEC1D462BC592B04', X'927CAD1549920A4E85AE5F4F7490C39F', X'927CAD1549920A4E85AE5F4F7490C39F', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the brown sugar, the cinnamon, and the cayenne pepper (if using). Toss them together until the spices are well-distributed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'609166298D33B34CA5C1C78DD58A6156', X'927CAD1549920A4E85AE5F4F7490C39F', X'927CAD1549920A4E85AE5F4F7490C39F', 2, NULL, NULL, 2, NULL, 0, 'Take a strip of bacon and press it into the sugar mixture, then turn it over and coat the other side with sugar. Place it on the wire rack, then repeat until all of the slices of bacon are covered with sugar on both sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6091BBC1FEA74B4BAED471B945945294', X'927CAD1549920A4E85AE5F4F7490C39F', X'927CAD1549920A4E85AE5F4F7490C39F', 3, NULL, NULL, 2, NULL, 0, 'If you have any remaining sugar, sprinkle it over the top of the bacon. ?Bake the bacon in your preheated oven for 20 to 30 minutes. When it is done, it will be firm and crispy and dark along the edges.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A219A561804024EB4A033820D677F83', X'927CAD1549920A4E85AE5F4F7490C39F', X'927CAD1549920A4E85AE5F4F7490C39F', 4, NULL, NULL, 2, NULL, 0, 'Soon after you take it out of the oven, use a spatula to lift the bacon off the racksometimes the sugar and fat cause the bacon to stick, and if it''s not removed when it is still warm, it''s difficult to get off of the rack.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A9EDF3D1388F3419D05062E36C8B07B', X'927CAD1549920A4E85AE5F4F7490C39F', X'927CAD1549920A4E85AE5F4F7490C39F', 5, NULL, NULL, 2, NULL, 0, 'Let the bacon cool completely, and then enjoy! Candied bacon can be eaten on its own, crumbled to use as a salad topping, or enjoyed on top of other desserts like cakes or cupcakes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C50A40A4188C5C4AA594C0A0E7EAEA56', X'94D45759C4026D46AC5971A3C805D51E', X'94D45759C4026D46AC5971A3C805D51E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and equipment.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73261C64D68BFE43BB92A62504A6BF72', X'94D45759C4026D46AC5971A3C805D51E', X'94D45759C4026D46AC5971A3C805D51E', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F. Position the cooking rack in the middle of the oven. There will be little to no splattering as the bacon cooks.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'014BB3964A11104DA3AD054A005C3354', X'94D45759C4026D46AC5971A3C805D51E', X'94D45759C4026D46AC5971A3C805D51E', 2, NULL, NULL, 2, NULL, 0, 'Line the jelly roll pan with the tinfoil to catch all of the grease, so you can just lift off the tinfoil and throw it in the trash when you''re done.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'330FF2E577B75E49A0A1FB2290EE726E', X'94D45759C4026D46AC5971A3C805D51E', X'94D45759C4026D46AC5971A3C805D51E', 3, NULL, NULL, 2, NULL, 0, 'Place a cookie cooling rack on top of the foil lined sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1542728A7E3D44F9AB283861761394C', X'94D45759C4026D46AC5971A3C805D51E', X'94D45759C4026D46AC5971A3C805D51E', 4, NULL, NULL, 2, NULL, 0, 'Lay out the strips of bacon onto the cooling rack. The strips can touch as they will shrink when cooking. You may have to lay a few pieces of the bacon width-wise as well as length-wise.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE4DEDF14A1A164AB21E2FCF70B57EBC', X'94D45759C4026D46AC5971A3C805D51E', X'94D45759C4026D46AC5971A3C805D51E', 5, NULL, NULL, 2, NULL, 0, 'Place the jelly roll pan in the oven on the middle rack.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28049FF63309784C92A2A644AF7D603F', X'94D45759C4026D46AC5971A3C805D51E', X'94D45759C4026D46AC5971A3C805D51E', 6, NULL, NULL, 2, NULL, 0, 'Cook for about 10 to 12 minutes or until the bacon is cooked to your desired crispiness. It should lift off of the cooling rack easily.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F305BFC53A6DA8458457CB23F7722F1B', X'94D45759C4026D46AC5971A3C805D51E', X'94D45759C4026D46AC5971A3C805D51E', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D6B175B1ED9F146AC8A1A1C73087E1D', X'1386CFCDD158604782EA4ADCBDDFD577', X'1386CFCDD158604782EA4ADCBDDFD577', 0, NULL, NULL, 2, NULL, 0, 'Preheat about two inches of oil in a frying pan or deep fryer to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21AA3D496531154E8671261EBAA232F9', X'1386CFCDD158604782EA4ADCBDDFD577', X'1386CFCDD158604782EA4ADCBDDFD577', 1, NULL, NULL, 2, NULL, 0, 'While the oil is heating up, peel the yuca and cut it into half-inch rounds or pieces.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C832E55F92E00C4D80070F2946458984', X'1386CFCDD158604782EA4ADCBDDFD577', X'1386CFCDD158604782EA4ADCBDDFD577', 2, NULL, NULL, 2, NULL, 0, 'Fry the prepared yuca until fork-tender. This will take approximately 6 to 8 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95ED972B33EE4A43959B287563E83FA5', X'1386CFCDD158604782EA4ADCBDDFD577', X'1386CFCDD158604782EA4ADCBDDFD577', 3, NULL, NULL, 2, NULL, 0, 'Remove cooked yuca from the fryer and allow it to drain on paper towels.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B250B90D3A8E0745B0E9C1C4F5AD6AA1', X'AFE2A51B81BF64448FDF9780902D1D0E', X'AFE2A51B81BF64448FDF9780902D1D0E', 0, NULL, NULL, 2, NULL, 0, 'Soak 2 cups sweet (sticky) rice in 3-4 cups water, or enough so water exceeds the rice by at least 1 inch. Soak for at least 40 minutes (if you lose track of time, it''s no problem - sticky rice can soak up to 4 hours or longer). Note: Sweet rice has a hard outer shell that needs to soften before it can be cooked.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AFEEF23CD62B849A51823B950CAF4CC', X'AFE2A51B81BF64448FDF9780902D1D0E', X'AFE2A51B81BF64448FDF9780902D1D0E', 1, NULL, NULL, 2, NULL, 0, 'While rice is soaking, prepare your steamer. Line a bamboo steamer or colander with 2 layers of banana/bamboo leaf, OR use cheesecloth, or a clean kitchen cloth. To clean and thaw the leaf (if frozen), rinse under hot water. Cut the leaf large enough that it extends over the top. Place steamer over a large pot, and have a tight-fitting lid ready.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1A95665972DDB40B2561161F99C8C6C', X'AFE2A51B81BF64448FDF9780902D1D0E', X'AFE2A51B81BF64448FDF9780902D1D0E', 2, NULL, NULL, 2, NULL, 0, 'Place bacon in a large frying pan/wok together with 1-2 Tbsp. water. Fry over medium-high heat until cooked (chewy, not crisp). Remove from heat, leaving all or half the fat in the frying pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D022046A8B236C49B71BE284A0F8779A', X'AFE2A51B81BF64448FDF9780902D1D0E', X'AFE2A51B81BF64448FDF9780902D1D0E', 3, NULL, NULL, 2, NULL, 0, 'Remove bacon from the pan and cut into small pieces (I use a clean pair of scissors for this, like they do in Asia - much faster and easier than a knife!). Place it back in the frying pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F448583024ACD4796A3EB0D71E1C33F', X'AFE2A51B81BF64448FDF9780902D1D0E', X'AFE2A51B81BF64448FDF9780902D1D0E', 4, NULL, NULL, 2, NULL, 0, 'Also add to the frying pan the shallots or onion, mushrooms, garlic, and chili. Return to medium-high heat and stir-fry 1-2 minutes, until fragrant. If your pan becomes dry, add a little water (1-2 Tbsp.).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF05893FD2C87F40AD50D2AB5CBD3CAF', X'AFE2A51B81BF64448FDF9780902D1D0E', X'AFE2A51B81BF64448FDF9780902D1D0E', 5, NULL, NULL, 2, NULL, 0, 'Turn heat to low. Add the drained sticky rice, plus the fish sauce and soy sauces. Also add half the green onion. Stir until the color of the rice has consistently darkened . Remove from heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70699601DEEF064C8B1BCA47DEAF650F', X'AFE2A51B81BF64448FDF9780902D1D0E', X'AFE2A51B81BF64448FDF9780902D1D0E', 6, NULL, NULL, 2, NULL, 0, 'Transfer the rice from the frying pan into your prepared steamer. Make sure there is at least 1 inch of water in the pot. Cover with the lid and steam over high heat for 30-35 minutes. (You may have to add more water to your steamer partway through.)');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'854FAEDF44B36144AF4522966407A87A', X'AFE2A51B81BF64448FDF9780902D1D0E', X'AFE2A51B81BF64448FDF9780902D1D0E', 7, NULL, NULL, 2, NULL, 0, 'After 30 minutes, remove from heat. Allow to sit (with lid on) for 5 minutes. The rice should be soft, sticky, and slightly translucent. If there are any hard grains, steam 5 minutes longer. (Dig down into the middle of the rice when testing for `done-ness`.)');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C23707ED83A5F4FBBBE08EE174FA2D2', X'AFE2A51B81BF64448FDF9780902D1D0E', X'AFE2A51B81BF64448FDF9780902D1D0E', 8, NULL, NULL, 2, NULL, 0, 'Taste-test the rice for salt. Sprinkle over a little more fish sauce if not salty or flavorful enough. If too salty, add a sqeeze of lime juice. Gently stir rice with a fork or chopsticks to mix in.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BA81AD1D2631C48AADE0F16FA6E0298', X'AFE2A51B81BF64448FDF9780902D1D0E', X'AFE2A51B81BF64448FDF9780902D1D0E', 9, NULL, NULL, 2, NULL, 0, 'To serve, slide the banana/bamboo leaf directly onto a serving plate. Or, if you want to pretty it up, line a serving bowl or plate with a fresh banana leaf. Mound the rice on top and sprinkle with remaining spring onion (add optional garnishes too if desired). ENJOY!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'319EA75C6EF4F544B2A52CA70AB063BB', X'E8FAB4901CAFB44E91B2B6546548D281', X'E8FAB4901CAFB44E91B2B6546548D281', 0, NULL, NULL, 2, NULL, 0, 'Place 3 cups of the lettuce in bottom of a large bowl; sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78C09C34FB972B41BB8EF7690CD9A6CD', X'E8FAB4901CAFB44E91B2B6546548D281', X'E8FAB4901CAFB44E91B2B6546548D281', 1, NULL, NULL, 2, NULL, 0, 'Layer the hard-boiled egg slices over the lettuce in the bowl and sprinkle with more salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FCAB4D4BB16784EA7CADA22F73AB069', X'E8FAB4901CAFB44E91B2B6546548D281', X'E8FAB4901CAFB44E91B2B6546548D281', 2, NULL, NULL, 2, NULL, 0, 'Continue to layer vegetables in this order: peas, remaining lettuce, crumbled bacon, and shredded cheese, along with light sprinklings of salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F4ACB7EFEE7D64C8D4D50C492F2D363', X'E8FAB4901CAFB44E91B2B6546548D281', X'E8FAB4901CAFB44E91B2B6546548D281', 3, NULL, NULL, 2, NULL, 0, 'Combine mayonnaise and sugar; spread over top, spreading to edge of the bowl to cover entire salad.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2A793CACB9FCF40A75346B8DB9A8BC3', X'E8FAB4901CAFB44E91B2B6546548D281', X'E8FAB4901CAFB44E91B2B6546548D281', 4, NULL, NULL, 2, NULL, 0, 'Cover and chill the salad overnight, or up to 24 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49634BEFB7AEDB41A3D882F757815A50', X'E8FAB4901CAFB44E91B2B6546548D281', X'E8FAB4901CAFB44E91B2B6546548D281', 5, NULL, NULL, 2, NULL, 0, 'Just before serving, garnish the salad with green onions and a little paprika. Toss and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E472A418C6E43B4996B97136C64CF1B1', X'ACE41C94864EB048A8256BD55CDB405B', X'ACE41C94864EB048A8256BD55CDB405B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95BA5A82FA20ED429F4A10697CD9C7B3', X'ACE41C94864EB048A8256BD55CDB405B', X'ACE41C94864EB048A8256BD55CDB405B', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E54E57357D98A441A2043627A93028FB', X'ACE41C94864EB048A8256BD55CDB405B', X'ACE41C94864EB048A8256BD55CDB405B', 2, NULL, NULL, 2, NULL, 0, 'Coat both sides of each slice of bacon with the brown sugar. The best strategy is to place the brown sugar on a plate and place the bacon over the top, packing the brown sugar on one side and then flipping.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6386C8208BB074D900CE3A6DECFF2BC', X'ACE41C94864EB048A8256BD55CDB405B', X'ACE41C94864EB048A8256BD55CDB405B', 3, NULL, NULL, 2, NULL, 0, 'Place the coated bacon onto a cooling rack that has been sprayed with non-stick cooking spray. Place the cooling rack over an aluminum foil-lined baking sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCD9C4F34378DE4FAF12B99D7DF9E133', X'ACE41C94864EB048A8256BD55CDB405B', X'ACE41C94864EB048A8256BD55CDB405B', 4, NULL, NULL, 2, NULL, 0, 'Bake for about 15 to 20 minutes, until they the strips of bacon look caramelized and firm. Keep a close watch on them and make sure they do not burn.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1395265A40F06D44A8F97F83CA9ABFCC', X'ACE41C94864EB048A8256BD55CDB405B', X'ACE41C94864EB048A8256BD55CDB405B', 5, NULL, NULL, 2, NULL, 0, 'Let the bacon cool slightly so that it is no longer stick and remove it from the cooling rack. Place on a piece of parchment paper and allow them to continue to cool.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2BDB1F336046E4C9D60A93336081F6E', X'ACE41C94864EB048A8256BD55CDB405B', X'ACE41C94864EB048A8256BD55CDB405B', 6, NULL, NULL, 2, NULL, 0, 'Whip the cream with a whisk, hand mixer, or KitchenAid with the whisk attachment, until it forms sturdy peaks. Be careful not to whip for too long, otherwise it will become butter.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C5818A4FBCA4048A5D807C5A21AE2C1', X'ACE41C94864EB048A8256BD55CDB405B', X'ACE41C94864EB048A8256BD55CDB405B', 7, NULL, NULL, 2, NULL, 0, 'Gently whisk the sweetened condensed milk, vanilla extract, and maple extract into the whipped cream until it is completely combined.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'965E27B3263AC14DAE1FB00080046841', X'ACE41C94864EB048A8256BD55CDB405B', X'ACE41C94864EB048A8256BD55CDB405B', 8, NULL, NULL, 2, NULL, 0, 'Pour the mixture into a large casserole or baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D9A77AD47EB8747BDC6FB695C8422E3', X'ACE41C94864EB048A8256BD55CDB405B', X'ACE41C94864EB048A8256BD55CDB405B', 9, NULL, NULL, 2, NULL, 0, 'Chop the candied bacon and sprinkle over the top of the ice cream. Freeze for about 6 hours (make sure it is nice and firm).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98749AB651FFEB4DA760618C2792E8C2', X'ACE41C94864EB048A8256BD55CDB405B', X'ACE41C94864EB048A8256BD55CDB405B', 10, NULL, NULL, 2, NULL, 0, 'Once frozen, drizzle with the maple syrup and serve!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D32B9F057776FD44BEEC6FC5D8C68795', X'C58B4BD1479B2E49A00042F0E4838730', X'C58B4BD1479B2E49A00042F0E4838730', 0, NULL, NULL, 2, NULL, 0, 'In a large, heavy skillet or saute pan, cook the bacon until fat has rendered.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7F3985B7C58114F91DA8C77C3AE358E', X'C58B4BD1479B2E49A00042F0E4838730', X'C58B4BD1479B2E49A00042F0E4838730', 1, NULL, NULL, 2, NULL, 0, 'With a slotted spoon, transfer the bacon to a large Dutch oven or stockpot; stir in 3 cans of black-eyed peas.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C24132B28B3BD841AEF30F4070CAF3E9', X'C58B4BD1479B2E49A00042F0E4838730', X'C58B4BD1479B2E49A00042F0E4838730', 2, NULL, NULL, 2, NULL, 0, 'To the bacon drippings in the skillet add the chopped onions, green and red pepper, and celery. Cook, stirring until the onion is translucent and the vegetables are tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A499747DFE0258418DC128E04891BABA', X'C58B4BD1479B2E49A00042F0E4838730', X'C58B4BD1479B2E49A00042F0E4838730', 3, NULL, NULL, 2, NULL, 0, 'Transfer the cooked onion and pepper mixture to the Dutch oven and then add the diced tomatoes, chopped mild chile peppers and chopped jalapeo peppers, if using.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1494476DB1C90F438CEA4681BFA3C7EF', X'C58B4BD1479B2E49A00042F0E4838730', X'C58B4BD1479B2E49A00042F0E4838730', 4, NULL, NULL, 2, NULL, 0, 'Bring the mixture to a boil over medium heat. Reduce heat to low; cover and simmer for 30 to 40 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5281583167CBF646B72F77FC0F56F8C4', X'C58B4BD1479B2E49A00042F0E4838730', X'C58B4BD1479B2E49A00042F0E4838730', 5, NULL, NULL, 2, NULL, 0, 'Add salt and freshly ground black pepper, to taste. Add 1/2 to 1 teaspoon of ground cayenne pepper, if desired, for spicier peas.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FDD4149716F124DBB2326EFF57D7A85', X'C58B4BD1479B2E49A00042F0E4838730', X'C58B4BD1479B2E49A00042F0E4838730', 6, NULL, NULL, 2, NULL, 0, 'Serve these delicious spicy peas hot with freshly baked cornbread.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AE6905D6C592649BC99643C054EC6A1', X'240AD60F776E8A42A0BD82A8580F0CAC', X'240AD60F776E8A42A0BD82A8580F0CAC', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11872A18F4C10A47B8E555295AC09A08', X'240AD60F776E8A42A0BD82A8580F0CAC', X'240AD60F776E8A42A0BD82A8580F0CAC', 1, NULL, NULL, 2, NULL, 0, 'With a sharp knife, remove the silver skin from the pork, along with any excess fat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAA8BB90054050438B959DE15BA28618', X'240AD60F776E8A42A0BD82A8580F0CAC', X'240AD60F776E8A42A0BD82A8580F0CAC', 2, NULL, NULL, 2, NULL, 0, 'Peel the onion and chop it finely; set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B8DE606570246429963B2650E174F8C', X'240AD60F776E8A42A0BD82A8580F0CAC', X'240AD60F776E8A42A0BD82A8580F0CAC', 3, NULL, NULL, 2, NULL, 0, 'Arrange bacon slices over pork tenderloin. Place tenderloin on rack in a shallow roasting pan. Tuck thin end of meat under. Roast, uncovered, for about 25 to 35 minutes, or until internal temperature is at least 145 F. To check for doneness, use a reliable instant-read thermometer inserted into the center of the thickest part of the pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26A4E23A386C304AB9D37443A807BC8F', X'240AD60F776E8A42A0BD82A8580F0CAC', X'240AD60F776E8A42A0BD82A8580F0CAC', 4, NULL, NULL, 2, NULL, 0, 'While the pork cooks, melt the butter in a medium saucepan over medium-low heat. Cook the chopped onion in the butter until it is tender and translucent.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40AA41C40CF0F740BC0699F53769EC82', X'240AD60F776E8A42A0BD82A8580F0CAC', X'240AD60F776E8A42A0BD82A8580F0CAC', 5, NULL, NULL, 2, NULL, 0, 'To the cooked onion, add the tomato sauce, sweet pickle, vinegar, parsley flakes and sugar. Simmer, uncovered, for 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D5111E30F8CDB4FB017850B21F2D472', X'240AD60F776E8A42A0BD82A8580F0CAC', X'240AD60F776E8A42A0BD82A8580F0CAC', 6, NULL, NULL, 2, NULL, 0, 'Remove the pork tenderloin from the oven; cover it loosely with foil and let it rest for 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE3578A56E4BFC4489A888C6747BDA12', X'240AD60F776E8A42A0BD82A8580F0CAC', X'240AD60F776E8A42A0BD82A8580F0CAC', 7, NULL, NULL, 2, NULL, 0, 'Serve the pork tenderloin sliced with the sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90BAB4AB5E30FE45ABE209A77BFC77E5', X'5F82B62FC0C58E41A06B9676524BCE07', X'5F82B62FC0C58E41A06B9676524BCE07', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68DB0033C4F30F449189F79CBCA06CFC', X'5F82B62FC0C58E41A06B9676524BCE07', X'5F82B62FC0C58E41A06B9676524BCE07', 1, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, mix together flour and salt.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AAA69A14711CB4F9BE7A01F26AFA7C6', X'5F82B62FC0C58E41A06B9676524BCE07', X'5F82B62FC0C58E41A06B9676524BCE07', 2, NULL, NULL, 2, NULL, 0, 'Cut in cold butter with a pastry blender until coarse crumbs form.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FD922CD8FBC37479D5105DC39091A9F', X'5F82B62FC0C58E41A06B9676524BCE07', X'5F82B62FC0C58E41A06B9676524BCE07', 3, NULL, NULL, 2, NULL, 0, 'Add water, a little at a time, until dough holds together and forms a ball.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'019CD03011C4524886DDF67AD72F9F71', X'5F82B62FC0C58E41A06B9676524BCE07', X'5F82B62FC0C58E41A06B9676524BCE07', 4, NULL, NULL, 2, NULL, 0, 'Shape into a disk, wrap in plastic wrap, and refrigerate for about 30 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'104F5FE0C08DB14F9F60790F2671F853', X'5F82B62FC0C58E41A06B9676524BCE07', X'5F82B62FC0C58E41A06B9676524BCE07', 5, NULL, NULL, 2, NULL, 0, 'On a lightly floured surface with a floured rolling pin, roll out dough into a circle about 12 inches in diameter.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8714B6336522AC4DAD951E752D69D17B', X'5F82B62FC0C58E41A06B9676524BCE07', X'5F82B62FC0C58E41A06B9676524BCE07', 6, NULL, NULL, 2, NULL, 0, 'Fit dough into a 9-inch pie plate or quiche pan. Trim edges, leaving a little overhang, about 1/4-inch all around the edge. Prick dough with a fork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'636906E9B6FAB6459110A2634BE50043', X'5F82B62FC0C58E41A06B9676524BCE07', X'5F82B62FC0C58E41A06B9676524BCE07', 7, NULL, NULL, 2, NULL, 0, 'Line with foil and fill with dried beans or pie weights.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'508B0C29CC07AF4B8D90972E90F6CAFD', X'5F82B62FC0C58E41A06B9676524BCE07', X'5F82B62FC0C58E41A06B9676524BCE07', 8, NULL, NULL, 2, NULL, 0, 'Bake quiche crust for 10 minutes at 375 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52A1A77279DD6544B8C864A03287621D', X'5F82B62FC0C58E41A06B9676524BCE07', X'5F82B62FC0C58E41A06B9676524BCE07', 9, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the eggs, milk, thyme, and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E3DABA0E7301A429D273235BCD8274D', X'5F82B62FC0C58E41A06B9676524BCE07', X'5F82B62FC0C58E41A06B9676524BCE07', 10, NULL, NULL, 2, NULL, 0, 'Pour into the baked crust and crumble bacon over the top with the shredded Cheddar cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F6DFF4DC91F7C4F8A0448B09ED31C79', X'5F82B62FC0C58E41A06B9676524BCE07', X'5F82B62FC0C58E41A06B9676524BCE07', 11, NULL, NULL, 2, NULL, 0, 'Bake at 375 F for about 30 minutes, or until quiche filling is set and the top is lightly browned.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87D633C572B6DC44B9A114CF16A64454', X'5F82B62FC0C58E41A06B9676524BCE07', X'5F82B62FC0C58E41A06B9676524BCE07', 12, NULL, NULL, 2, NULL, 0, 'Serve the quiche with fresh tomato wedges or a tossed salad.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66FE770089A51B44A3BBF3BC9BE5D0F1', X'2F7B1CF3DD8B5B4E9108789BEE195149', X'2F7B1CF3DD8B5B4E9108789BEE195149', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven (or warming drawer) to 200 F to 250 F. This is to keep the squash warm while you finish the sauce. Alternatively, you can just warm it quickly in the microwave when the sauce is ready.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCA4528E1C5CCD49AB833036AF1B8650', X'2F7B1CF3DD8B5B4E9108789BEE195149', X'2F7B1CF3DD8B5B4E9108789BEE195149', 1, NULL, NULL, 2, NULL, 0, 'Slice off the stem end and blossom end of the spaghetti squash. Cut the squash in half lengthwise and scoop out the seeds. Scrape the inside of the squash with a spoon to remove any soft fibers. Place it in a microwave-safe baking dish, cut side down. Add about an inch of hot water to the baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3882734EB5EB643A4EE9098EEC6C1F6', X'2F7B1CF3DD8B5B4E9108789BEE195149', X'2F7B1CF3DD8B5B4E9108789BEE195149', 2, NULL, NULL, 2, NULL, 0, 'Microwave the squash, uncovered, for 12 to 15 minutes. The time depends on the size of the squash and the power of the microwave oven. Test it by piercing with a fork. It should be easy to pierce, but not too soft.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A26AC06FCFAE6744B9419084F374AA11', X'2F7B1CF3DD8B5B4E9108789BEE195149', X'2F7B1CF3DD8B5B4E9108789BEE195149', 3, NULL, NULL, 2, NULL, 0, 'Scrape the inside of the squash with a fork, transferring the `spaghetti` strands to an oven-safe bowl or container. Cover the bowl with foil and keep the squash warm in the oven or warming drawer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1643AF9EAAED2F45A3E99163C69F750D', X'2F7B1CF3DD8B5B4E9108789BEE195149', X'2F7B1CF3DD8B5B4E9108789BEE195149', 4, NULL, NULL, 2, NULL, 0, 'In a deep skillet or saute pan over medium heat, fry the bacon until crisp. Remove to paper towels to drain. Crumble the bacon and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F81A299BA9914F429D46007462C84D65', X'2F7B1CF3DD8B5B4E9108789BEE195149', X'2F7B1CF3DD8B5B4E9108789BEE195149', 5, NULL, NULL, 2, NULL, 0, 'Drain the bacon drippings into a cup or bowl, leaving about 1 tablespoon in the pan. Refrigerate the excess bacon drippings for another use.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E52F9D9B96C35F46BB6D12ADE8661F6F', X'2F7B1CF3DD8B5B4E9108789BEE195149', X'2F7B1CF3DD8B5B4E9108789BEE195149', 6, NULL, NULL, 2, NULL, 0, 'Add the onions to the pan and saute until just translucent. Add the garlic and continue cooking, stirring, for 2 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC921E06C55C50419741C8206E4DF475', X'2F7B1CF3DD8B5B4E9108789BEE195149', X'2F7B1CF3DD8B5B4E9108789BEE195149', 7, NULL, NULL, 2, NULL, 0, 'Add the cream to the pan and bring to a simmer. Continue cooking for about 4 minutes, until reduced and thickened. Add the Parmesan cheese and cook, stirring, until melted. Set aside a few teaspoons of crumbled bacon and add the rest to the sauce; heat through. If the sauce is too thick, add a little more cream or milk to thin.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C8622EC577FE94681E90544C25B6713', X'2F7B1CF3DD8B5B4E9108789BEE195149', X'2F7B1CF3DD8B5B4E9108789BEE195149', 8, NULL, NULL, 2, NULL, 0, 'Gently toss the sauce with the hot spaghetti squash.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F2028393A4FDF4B8EF22FBD0D0C7F02', X'2F7B1CF3DD8B5B4E9108789BEE195149', X'2F7B1CF3DD8B5B4E9108789BEE195149', 9, NULL, NULL, 2, NULL, 0, 'Garnish the `spaghetti` with a little parsley and more Parmesan cheese and bacon crumbs, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82B038A116B65A4B8B6AB1DF60D207EB', X'2F7B1CF3DD8B5B4E9108789BEE195149', X'2F7B1CF3DD8B5B4E9108789BEE195149', 10, NULL, NULL, 2, NULL, 0, 'Serve with a side green salad or sliced tomatoes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DC2056ECC8D0643B7B111FB02A8CD2E', X'FE57860D6B01904F8F3501B77D748E5D', X'FE57860D6B01904F8F3501B77D748E5D', 0, NULL, NULL, 2, NULL, 0, 'Fill the sink with cold water. Wash the collard greens in 3 to 4 changes of water, until there is absolutely no grit on the bottom of the sink. Cut thick stems out of the greens and chop the leaves or cut them crosswise into strips.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7923655610F68B4BAEA194F9B11C32E1', X'FE57860D6B01904F8F3501B77D748E5D', X'FE57860D6B01904F8F3501B77D748E5D', 1, NULL, NULL, 2, NULL, 0, 'If using bacon, cook it until cooked but not crisp in a large skillet. Remove the bacon to paper towels to drain.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D26A9E3458C124589F5FBB8D32ABADF', X'FE57860D6B01904F8F3501B77D748E5D', X'FE57860D6B01904F8F3501B77D748E5D', 2, NULL, NULL, 2, NULL, 0, 'Add the chopped onion to the skillet and cook until soft.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5526E8B46BD77B4BA88D95FC63ADD391', X'FE57860D6B01904F8F3501B77D748E5D', X'FE57860D6B01904F8F3501B77D748E5D', 3, NULL, NULL, 2, NULL, 0, 'Combine the beans, bacon, onions, garlic, chicken stock, tomato paste, vinegar, bay leaves, and red pepper flakes in the slow cooker. The liquid should cover the top of the beans.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F20333798C370349A0640D6DF720B213', X'FE57860D6B01904F8F3501B77D748E5D', X'FE57860D6B01904F8F3501B77D748E5D', 4, NULL, NULL, 2, NULL, 0, 'Cover the crockpot and cook on low for 6 to 7 hours or on high for 3 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA841AE979DC0D4D891546DFB08B6074', X'FE57860D6B01904F8F3501B77D748E5D', X'FE57860D6B01904F8F3501B77D748E5D', 5, NULL, NULL, 2, NULL, 0, 'Open the lid and add the greens. Return the lid and cook for one more hour.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DE3780EB3BC614F973CBD07498C1460', X'FE57860D6B01904F8F3501B77D748E5D', X'FE57860D6B01904F8F3501B77D748E5D', 6, NULL, NULL, 2, NULL, 0, 'Taste and adjust seasonings with salt and freshly ground black pepper, as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38574CD16A04F248AD115FB2B29139BD', X'67B86CE9BB96564896778F1426F88D83', X'67B86CE9BB96564896778F1426F88D83', 0, NULL, NULL, 2, NULL, 0, 'Place the beans in a large saucepan and cover with cold water. Bring the beans to a gentle simmer over medium heat. Cover and cook them until they begin to turn tender, about 1 hour and 15 minutes. Add one teaspoon salt and 1/4 teaspoon ground black pepper to the beans and cook until the liquid has absorbed about 25 additional minutes. Remove the beans from the heat, drain and set the beans aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2653F4F1ED919942A51F6C9398528E87', X'67B86CE9BB96564896778F1426F88D83', X'67B86CE9BB96564896778F1426F88D83', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium-high heat, cook the lardons for 5 minutes, until they brown. Crumble them coarsely and transfer to a plate. Add the onions and saut for 10 minutes, until they turn tender. Lower the heat, and cook the onions on low, occasionally stirring, for 20 to 30 minutes until caramelized and golden brown. Transfer to the plate with the lardons.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D54E8E672ECFCA4EB80E46AC4B67B6C2', X'67B86CE9BB96564896778F1426F88D83', X'67B86CE9BB96564896778F1426F88D83', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350. Drain all but four tablespoons of the fat from the skillet. Add the chicken breast pieces to the fat and brown over medium-high heat. Transfer the chicken to a clean bowl. Lower the heat to medium-low, melt one tablespoon butter and saut the chopped garlic for 90 seconds, stirring constantly. Add the cubed squash, increase the heat to medium-high, and saut until lightly caramelized. Take care to not burn the garlic.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3ECE3A5EB013F40837D4530B4B59E16', X'67B86CE9BB96564896778F1426F88D83', X'67B86CE9BB96564896778F1426F88D83', 3, NULL, NULL, 2, NULL, 0, 'Combine the lardons, onions, chicken pieces and stock, bouquet garni, thyme, squash, garlic, 3/4 teaspoon salt, and 1/4 teaspoon ground black pepper in a large casserole dish. Cover with a lid and bake for 25 minutes. Add the carrots to the casserole, cover, and bake for an additional 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E0F266FDAAEC44691FB6995A7FABB0B', X'67B86CE9BB96564896778F1426F88D83', X'67B86CE9BB96564896778F1426F88D83', 4, NULL, NULL, 2, NULL, 0, 'Add the white beans to the casserole and gently stir. In a small bowl, toss the bread crumbs, parsley, and thyme with the melted butter. Sprinkle the bread crumbs over the casserole and bake uncovered for 1 hour. Discard the bouquet garni and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AE090E63AB1C34FA5E314C98C4A088B', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat the oven to 375 F/190 C.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E2C53308672DD4F892449139FA1A6A7', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', 1, NULL, NULL, 2, NULL, 0, 'Lightly grease a baking tray and cover with baking parchment. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C239DE38422AA4F8A23A444B010D379', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', 2, NULL, NULL, 2, NULL, 0, 'On a chopping board, lay the slices of streaky bacon side by side. Using the back of a dinner knife, stretch the bacon as far as it will go without tearing it. Cut each slice into thirds.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7BC50E321D12846BFF08C1AAC4BC390', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', 3, NULL, NULL, 2, NULL, 0, 'Take each cocktail sausage and wrap it tightly with the bacon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBB12608D7EC85409582F31F3947AE21', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', 4, NULL, NULL, 2, NULL, 0, 'Lay the wrapped sausage on the prepared baking tray with the seam underneath. Continue until you have used up all the bacon and sausages.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BB6DA45A0D77F4A9C594FC1E32BC71D', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', 5, NULL, NULL, 2, NULL, 0, 'At this point, the tray can be covered and placed in the refrigerator to cook later.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C78DF2957B5F7A4E9CED56750D61E620', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', 6, NULL, NULL, 2, NULL, 0, 'As soon as you need your pigs in a blanket, pop them into the oven and cook for about 20 minutes or until the bacon is crisp and golden and the sausage is cooked through.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02E0E01F858FAC4CBFD04AAB37603D52', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', X'48E0FBCDFEAA4B42AD9F984DBB6EDDF7', 7, NULL, NULL, 2, NULL, 0, 'Serve alongside your roast turkey or goose on the Christmas table or even cold the next day on your Boxing Day buffet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7CEC1119A52454E9185C3BF120AEA23', X'9A0E7063C0719245A05E608D331D0D62', X'9A0E7063C0719245A05E608D331D0D62', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'083C6F45F5F2404CBFADCDFF732CA2FF', X'9A0E7063C0719245A05E608D331D0D62', X'9A0E7063C0719245A05E608D331D0D62', 1, NULL, NULL, 2, NULL, 0, 'Generously butter a 2-quart baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DBBFB93D29FC548823C64A8EB751F44', X'9A0E7063C0719245A05E608D331D0D62', X'9A0E7063C0719245A05E608D331D0D62', 2, NULL, NULL, 2, NULL, 0, 'In a deep skillet or saute pan, cook the bacon until just crisp. Remove it to paper towels and set aside to drain.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6752151067357B43B95AA51B16481DCC', X'9A0E7063C0719245A05E608D331D0D62', X'9A0E7063C0719245A05E608D331D0D62', 3, NULL, NULL, 2, NULL, 0, 'Leave about 2 tablespoons of bacon drippings in the skillet and add 3 tablespoons of butter. Add the onion to the pan and saute until softened.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE58362A21B5604ABBCF0673E2542BA5', X'9A0E7063C0719245A05E608D331D0D62', X'9A0E7063C0719245A05E608D331D0D62', 4, NULL, NULL, 2, NULL, 0, 'Add the flour and continue cooking, stirring, for about 1 minute. Gradually stir in the milk. Cook, stirring constantly, until the sauce is thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB1E24E8CE36EF42B2B498F9727E1E8A', X'9A0E7063C0719245A05E608D331D0D62', X'9A0E7063C0719245A05E608D331D0D62', 5, NULL, NULL, 2, NULL, 0, 'Stir in the salt, pepper, and nutmeg, if using. Stir in the cooked bacon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'780859364A92894EA342A58F9ABAB8C8', X'9A0E7063C0719245A05E608D331D0D62', X'9A0E7063C0719245A05E608D331D0D62', 6, NULL, NULL, 2, NULL, 0, 'Layer half of the potato slices in the prepared baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD69C75F84A4A64CA6D55D3BCC527675', X'9A0E7063C0719245A05E608D331D0D62', X'9A0E7063C0719245A05E608D331D0D62', 7, NULL, NULL, 2, NULL, 0, 'Spoon half of the sauce mixture over the potatoes. Top with the remaining potatoes. Spoon the remaining sauce over all.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C0BABE1F380C9408C464D25718FF03E', X'9A0E7063C0719245A05E608D331D0D62', X'9A0E7063C0719245A05E608D331D0D62', 8, NULL, NULL, 2, NULL, 0, 'Cover the casserole tightly with foil and bake for 1 hour.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A648B7642D0CB742BF31EF647D3476D6', X'9A0E7063C0719245A05E608D331D0D62', X'9A0E7063C0719245A05E608D331D0D62', 9, NULL, NULL, 2, NULL, 0, 'Remove the foil, return to the oven, and bake for about 20 to 30 minutes longer or until the potato slices are fork-tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADF4E27136A05D4D9DCAB985902BA243', X'28C5E1A46EBC0D4185FB61ACA4C00A12', X'28C5E1A46EBC0D4185FB61ACA4C00A12', 0, NULL, NULL, 2, NULL, 0, 'Pour the ingredients into a cocktail shaker filled with ice.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BD6FCE55569FA48A502B7E95D53A17C', X'28C5E1A46EBC0D4185FB61ACA4C00A12', X'28C5E1A46EBC0D4185FB61ACA4C00A12', 1, NULL, NULL, 2, NULL, 0, 'Stir well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7B8E97D2BBD3A458E6299EBAE4FF5BB', X'28C5E1A46EBC0D4185FB61ACA4C00A12', X'28C5E1A46EBC0D4185FB61ACA4C00A12', 2, NULL, NULL, 2, NULL, 0, 'Strain into a chilled cocktail glass.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F48A9B4C8205954E95AC1D2B2894B9A9', X'28C5E1A46EBC0D4185FB61ACA4C00A12', X'28C5E1A46EBC0D4185FB61ACA4C00A12', 3, NULL, NULL, 2, NULL, 0, 'Garnish with a brandied cherry and a small strip of bacon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A0F089B81582049A7353B583190F195', X'0B022D5BCC24B34FA206351ADF3AE139', X'0B022D5BCC24B34FA206351ADF3AE139', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4030CACFE11EB49836995B1BDBCC12E', X'0B022D5BCC24B34FA206351ADF3AE139', X'0B022D5BCC24B34FA206351ADF3AE139', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium-low heat, cook the bacon until just crisp. You can either cook the bacon whole or cut the bacon into chunks before cooking. Once crisp, remove the bacon to paper towels to drain. Leave about 2 teaspoons of bacon drippings in the skillet and discard the remaining drippings.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F281A9978433F48A6CCEBD60286C012', X'0B022D5BCC24B34FA206351ADF3AE139', X'0B022D5BCC24B34FA206351ADF3AE139', 2, NULL, NULL, 2, NULL, 0, 'Place the skillet back over medium heat and add the ground beef and chopped onions. Cook until the beef is no longer pink, stirring and breaking up larger chunks. With a slotted spoon or spatula, transfer the ground beef to the crockery insert of the slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1672F60852EC6842A3EF61E5AF036A62', X'0B022D5BCC24B34FA206351ADF3AE139', X'0B022D5BCC24B34FA206351ADF3AE139', 3, NULL, NULL, 2, NULL, 0, 'Add the brown sugar, dry mustard, baked beans, two cans of drained beans, ketchup or barbecue sauce, and vinegar. Taste and season with kosher salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE69551DF48D2340B306EA19B97F1BD5', X'0B022D5BCC24B34FA206351ADF3AE139', X'0B022D5BCC24B34FA206351ADF3AE139', 4, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 3 to 5 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'990A1F1EC6AF6F489299DA6D2B6565B8', X'144706D90F754146A059D1BC8477F4AE', X'144706D90F754146A059D1BC8477F4AE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B5380172541444FA1A20A0094A4F26E', X'144706D90F754146A059D1BC8477F4AE', X'144706D90F754146A059D1BC8477F4AE', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F. Butter an 8-inch square baking dish or spray with baking spray.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00A793CCE4F7A74195758CFFBD2B5D13', X'144706D90F754146A059D1BC8477F4AE', X'144706D90F754146A059D1BC8477F4AE', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet or saut pan cook the diced bacon until fat is rendered and the bacon is crisp. Remove to paper towels.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'633572A442C631458228BE547D2E1A55', X'144706D90F754146A059D1BC8477F4AE', X'144706D90F754146A059D1BC8477F4AE', 3, NULL, NULL, 2, NULL, 0, 'Pour off all but about 1 teaspoon of the bacon grease. Save the bacon grease to use in cornbread or for a flavorful fat for sauteing onions or other vegetables.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29FD49197308AC409AC686BA263AB3EA', X'144706D90F754146A059D1BC8477F4AE', X'144706D90F754146A059D1BC8477F4AE', 4, NULL, NULL, 2, NULL, 0, 'Add the onion to the pan and cook over medium heat, stirring, until just tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79FDE4792787964D9D69049984EBB156', X'144706D90F754146A059D1BC8477F4AE', X'144706D90F754146A059D1BC8477F4AE', 5, NULL, NULL, 2, NULL, 0, 'Add the spinach leaves and cook, stirring constantly, until spinach is wilted and any excess water clinging to the leaves has evaporated.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81AE3973F88CE4469EC1B7B3834B70F3', X'144706D90F754146A059D1BC8477F4AE', X'144706D90F754146A059D1BC8477F4AE', 6, NULL, NULL, 2, NULL, 0, 'Transfer the spinach mixture to the baking dish. Top evenly with the bacon, then with the cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D270251E172B3F4781F4938C9AEC4569', X'144706D90F754146A059D1BC8477F4AE', X'144706D90F754146A059D1BC8477F4AE', 7, NULL, NULL, 2, NULL, 0, 'In a bowl, whisk together the milk and flour until smooth.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08A98E8D0245AF4191E6EAC87756EF33', X'144706D90F754146A059D1BC8477F4AE', X'144706D90F754146A059D1BC8477F4AE', 8, NULL, NULL, 2, NULL, 0, 'Whisk in the eggs and egg white, then whisk in the pepper, salt, onion powder, and oregano.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE1C79FA6D0E3642B74805A3343042A1', X'144706D90F754146A059D1BC8477F4AE', X'144706D90F754146A059D1BC8477F4AE', 9, NULL, NULL, 2, NULL, 0, 'Pour over evenly over the spinach and cheese mixture. Sprinkle very lightly with the paprika, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E85A7217C42A9419F4AB974E36295B9', X'144706D90F754146A059D1BC8477F4AE', X'144706D90F754146A059D1BC8477F4AE', 10, NULL, NULL, 2, NULL, 0, 'Bake for 30 to 40 minutes, or until the quiche is firm.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F802B1CEA677BA47A0953B06305E3232', X'D5A90F9CF414D844A3339CF1D8A70034', X'D5A90F9CF414D844A3339CF1D8A70034', 0, NULL, NULL, 2, NULL, 0, 'Cut the bacon into small pieces; brown in a large skillet. Remove bacon from the rendered fat and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD0FDB3769755F428D638209C89B4BD5', X'D5A90F9CF414D844A3339CF1D8A70034', X'D5A90F9CF414D844A3339CF1D8A70034', 1, NULL, NULL, 2, NULL, 0, 'In the bacon drippings, saut the onion and bell pepper until tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A217F793A60364E859A76E4C68B66DB', X'D5A90F9CF414D844A3339CF1D8A70034', X'D5A90F9CF414D844A3339CF1D8A70034', 2, NULL, NULL, 2, NULL, 0, 'Add the ground beef and brown lightly. Put the meats into the bottom of a crockpot. Add the can of pork and beans.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29352136DA86144990B7B1AA070980AA', X'D5A90F9CF414D844A3339CF1D8A70034', X'D5A90F9CF414D844A3339CF1D8A70034', 3, NULL, NULL, 2, NULL, 0, 'Drain most of the liquids from the remaining cans of beans and add them to the slow cooker with the remaining ingredients. Mix well to blend.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21749C7FBF1799458FC3D51560C17CA8', X'D5A90F9CF414D844A3339CF1D8A70034', X'D5A90F9CF414D844A3339CF1D8A70034', 4, NULL, NULL, 2, NULL, 0, 'Cook on high setting for 1 hour, and then set to low and continue cooking for 5 to 6 hours. Stir occasionally.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C1809D878A6764EA4EB9AA3FA827127', X'D5A90F9CF414D844A3339CF1D8A70034', X'D5A90F9CF414D844A3339CF1D8A70034', 5, NULL, NULL, 2, NULL, 0, 'Check for seasonings and correct if desired, with a little salt and pepper, or more brown sugar or syrup.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'536BE6FE35C8F34DAD862450F0B70712', X'D26A0FE9DAD7334B8B1B0D9A5E035C7E', X'D26A0FE9DAD7334B8B1B0D9A5E035C7E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41636104ED01F344BF50930940E40D5C', X'D26A0FE9DAD7334B8B1B0D9A5E035C7E', X'D26A0FE9DAD7334B8B1B0D9A5E035C7E', 1, NULL, NULL, 2, NULL, 0, 'Spray the crockery insert of your slow cooker with non-stick cooking spray. Butter flavor is good if you have it. Or use an olive oil or vegetable oil spray.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C53D85521516247959EE24638127346', X'D26A0FE9DAD7334B8B1B0D9A5E035C7E', X'D26A0FE9DAD7334B8B1B0D9A5E035C7E', 2, NULL, NULL, 2, NULL, 0, 'Mix the condensed cream of chicken soup with 1/2 cup milk.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E78904EC640274A9160E53FDB944022', X'D26A0FE9DAD7334B8B1B0D9A5E035C7E', X'D26A0FE9DAD7334B8B1B0D9A5E035C7E', 3, NULL, NULL, 2, NULL, 0, 'Layer the ingredients in your slow cooker or Crock Pot in this order: About one-quarter of the potato slices, one-quarter of the cheddar cheese, some bacon bits, salt and pepper, and about one-quarter of the soup mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09DA09667FAA1E409028480D1B74FEE8', X'D26A0FE9DAD7334B8B1B0D9A5E035C7E', X'D26A0FE9DAD7334B8B1B0D9A5E035C7E', 4, NULL, NULL, 2, NULL, 0, 'Repeat the layers ending with soup mixture and then cover and cook on low for 7 to 9 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'087892D1AE814B41B6863CDADCED4015', X'1B8D5FF45EC981468E4E059C5E0F683E', X'1B8D5FF45EC981468E4E059C5E0F683E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58CE9C69AF5208458769329C982F13B5', X'1B8D5FF45EC981468E4E059C5E0F683E', X'1B8D5FF45EC981468E4E059C5E0F683E', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet, fry the bacon until crisp.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11EC44C305205B4FB1A197BCACAFE6BC', X'1B8D5FF45EC981468E4E059C5E0F683E', X'1B8D5FF45EC981468E4E059C5E0F683E', 2, NULL, NULL, 2, NULL, 0, 'Remove the bacon from the skillet and drain on paper towels. (Don''t clean out the skillet.)');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBC1660DFFEC344E964068279F7C8A35', X'1B8D5FF45EC981468E4E059C5E0F683E', X'1B8D5FF45EC981468E4E059C5E0F683E', 3, NULL, NULL, 2, NULL, 0, 'Crumble the bacon and set it aside, covered, in the refrigerator.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C84F390535CEA0438552A1972C644A4B', X'1B8D5FF45EC981468E4E059C5E0F683E', X'1B8D5FF45EC981468E4E059C5E0F683E', 4, NULL, NULL, 2, NULL, 0, 'In the skillet with the bacon drippings, cook the chicken breasts over medium heat for 3 to 4 minutes per side, until the outside has formed a nice light brown coating.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA647D228A10944AB63F80A5EE7DD3D6', X'1B8D5FF45EC981468E4E059C5E0F683E', X'1B8D5FF45EC981468E4E059C5E0F683E', 5, NULL, NULL, 2, NULL, 0, 'Place the browned chicken in a 4- to 6-quart slow cooker. Top with the drained mushrooms.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1CD4EEE75FC73418FB495C5ED3B56D8', X'1B8D5FF45EC981468E4E059C5E0F683E', X'1B8D5FF45EC981468E4E059C5E0F683E', 6, NULL, NULL, 2, NULL, 0, 'Using the same skillet you used to brown the chicken, heat the soup, mixing well and scraping up the pan drippings.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CA40E3449E2C849ADE21EF2027FA6B9', X'1B8D5FF45EC981468E4E059C5E0F683E', X'1B8D5FF45EC981468E4E059C5E0F683E', 7, NULL, NULL, 2, NULL, 0, 'Pour the soup sauce over the chicken.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5530D203B686E4C95D13E86D290C883', X'1B8D5FF45EC981468E4E059C5E0F683E', X'1B8D5FF45EC981468E4E059C5E0F683E', 8, NULL, NULL, 2, NULL, 0, 'Cover the slow cooker and cook on low for 4 to 5 hours, or until the chicken registers 165 F at the thickest part of the meat using a meat thermometer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AD0DB2D68F4D64CAAE9FB0DAE8D60BD', X'1B8D5FF45EC981468E4E059C5E0F683E', X'1B8D5FF45EC981468E4E059C5E0F683E', 9, NULL, NULL, 2, NULL, 0, 'After 4 to 5 hours, top the chicken with the diced cheese and sprinkle with the reserved bacon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD33DC68CD7DFF4D82F20FF2BC5F19FB', X'1B8D5FF45EC981468E4E059C5E0F683E', X'1B8D5FF45EC981468E4E059C5E0F683E', 10, NULL, NULL, 2, NULL, 0, 'Cover again and cook on high for 10 to 15 minutes or until the cheese has completely melted.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB42434A0D7B174A9DF8458DB05DA702', X'262B429A94CA394EA35C5F716716F659', X'262B429A94CA394EA35C5F716716F659', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat the oven to 300 F/150 C/Gas 2.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C57342F090E5B41B2D7B48089AAACF9', X'262B429A94CA394EA35C5F716716F659', X'262B429A94CA394EA35C5F716716F659', 1, NULL, NULL, 2, NULL, 0, 'Wrap 1 strip of bacon around a chicken breast half; repeat with the remaining halves.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F327FD828BBC204EBE90E298E7E8A111', X'262B429A94CA394EA35C5F716716F659', X'262B429A94CA394EA35C5F716716F659', 2, NULL, NULL, 2, NULL, 0, 'Line a large lightly buttered casserole or baking dish with the cut up chipped beef.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8CF591CEB77EB458642AB1788D37472', X'262B429A94CA394EA35C5F716716F659', X'262B429A94CA394EA35C5F716716F659', 3, NULL, NULL, 2, NULL, 0, 'Place the 8 chicken breast halves on the chipped beef.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F3153CA47E5F243B400F9347D84DB89', X'262B429A94CA394EA35C5F716716F659', X'262B429A94CA394EA35C5F716716F659', 4, NULL, NULL, 2, NULL, 0, 'In a bowl combine sour cream and soup until well blended; pour over the chicken.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A85B1E09FA7A6B47B495228495D93733', X'262B429A94CA394EA35C5F716716F659', X'262B429A94CA394EA35C5F716716F659', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle lightly with freshly ground black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'469074725810B342A83C5F13B7EE9D5B', X'262B429A94CA394EA35C5F716716F659', X'262B429A94CA394EA35C5F716716F659', 6, NULL, NULL, 2, NULL, 0, 'Bake slowly, at 300 F for 2 hours, or until chicken is cooked and bacon is crispy.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2A9375D289A04418C25408818754847', X'262B429A94CA394EA35C5F716716F659', X'262B429A94CA394EA35C5F716716F659', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7D1E4EA4E4AAD4BAEE43F417B920C15', X'650BC3F3AE399140B607C6F97301697B', X'650BC3F3AE399140B607C6F97301697B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4AF10A8130F6A419ECEBA199BC57948', X'650BC3F3AE399140B607C6F97301697B', X'650BC3F3AE399140B607C6F97301697B', 1, NULL, NULL, 2, NULL, 0, 'Bake the bacon for 10 minutes. You can also brown in a pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF1EF025D1221D4197CCB5334CB2E9D7', X'650BC3F3AE399140B607C6F97301697B', X'650BC3F3AE399140B607C6F97301697B', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the mayonnaise, lemon juice, and garlic powder in a small bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4F9F638D10479498AC516E14B518BB3', X'650BC3F3AE399140B607C6F97301697B', X'650BC3F3AE399140B607C6F97301697B', 3, NULL, NULL, 2, NULL, 0, 'Toast the bread so that it is still slightly soft, but also a nice light brown color. Place it on a cooling rack if you can, so that it doesn''t get soggy while you prepare the sandwich.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5758317B4A88EF42AFC98F213D185CDD', X'650BC3F3AE399140B607C6F97301697B', X'650BC3F3AE399140B607C6F97301697B', 4, NULL, NULL, 2, NULL, 0, 'Place one slice of toast on a cutting board. Add two slices of turkey, top with a small handful of fresh spinach, a slice of tomato, and 2 slices of bacon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2350A59C2F2A74DAD49EE2533B37EA4', X'650BC3F3AE399140B607C6F97301697B', X'650BC3F3AE399140B607C6F97301697B', 5, NULL, NULL, 2, NULL, 0, 'Layer another slice of toast on top. Place three more slices of turkey, 4 slices of fresh avocado, and two more pieces of bacon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFC5594C4141A44697F3AFBF9F1E11B3', X'650BC3F3AE399140B607C6F97301697B', X'650BC3F3AE399140B607C6F97301697B', 6, NULL, NULL, 2, NULL, 0, 'Spread some of the aioli onto the last slice of toast. Place the toast aioli side down on top of the sandwich.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF5A5C4FFBD9AC44840CB17D8678E563', X'650BC3F3AE399140B607C6F97301697B', X'650BC3F3AE399140B607C6F97301697B', 7, NULL, NULL, 2, NULL, 0, 'Cut into four triangles, secure each piece with a toothpick.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'889FE5DDB713924BB679A98BFB393DBE', X'650BC3F3AE399140B607C6F97301697B', X'650BC3F3AE399140B607C6F97301697B', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21FFD145DB894644AADBA64E71CD5D49', X'CA1678FCA2D17048AE5971EBAE836B6C', X'CA1678FCA2D17048AE5971EBAE836B6C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A10EB89C08B854E8214C9F6764AAC3A', X'CA1678FCA2D17048AE5971EBAE836B6C', X'CA1678FCA2D17048AE5971EBAE836B6C', 1, NULL, NULL, 2, NULL, 0, 'Put the rice in a 2-quart baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8417533DB79E74988042A992FDC2B92', X'CA1678FCA2D17048AE5971EBAE836B6C', X'CA1678FCA2D17048AE5971EBAE836B6C', 2, NULL, NULL, 2, NULL, 0, 'Spoon the corn evenly over the rice layer and sprinkle lightly with salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D0126C5C7C362478EAB7508A6C5A5C2', X'CA1678FCA2D17048AE5971EBAE836B6C', X'CA1678FCA2D17048AE5971EBAE836B6C', 3, NULL, NULL, 2, NULL, 0, 'Combine the 1 can of tomato sauce with 1/2 cup of water and pour over the corn layer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26CE3E65D10A2942A06B9976DA2BB67F', X'CA1678FCA2D17048AE5971EBAE836B6C', X'CA1678FCA2D17048AE5971EBAE836B6C', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle with the chopped onion and bell pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7D6471A01F87043A5742D94A3192756', X'CA1678FCA2D17048AE5971EBAE836B6C', X'CA1678FCA2D17048AE5971EBAE836B6C', 5, NULL, NULL, 2, NULL, 0, 'Top the onion and pepper layer with the ground beef and sprinkle lightly with salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64858E27346C4D4E92C2E500137D3A22', X'CA1678FCA2D17048AE5971EBAE836B6C', X'CA1678FCA2D17048AE5971EBAE836B6C', 6, NULL, NULL, 2, NULL, 0, 'Add the can of diced tomatoes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD28BA0969220142990E751579A70794', X'CA1678FCA2D17048AE5971EBAE836B6C', X'CA1678FCA2D17048AE5971EBAE836B6C', 7, NULL, NULL, 2, NULL, 0, 'Top with the strips of bacon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16C0ABD43018EF458DEED731A96FEFD7', X'CA1678FCA2D17048AE5971EBAE836B6C', X'CA1678FCA2D17048AE5971EBAE836B6C', 8, NULL, NULL, 2, NULL, 0, 'Cover tightly with foil and bake at 350 F for 1 hour.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'114548F5C6476C44AE14F79B7C152E6C', X'CA1678FCA2D17048AE5971EBAE836B6C', X'CA1678FCA2D17048AE5971EBAE836B6C', 9, NULL, NULL, 2, NULL, 0, 'Uncover and cook 30 minutes longer, until bacon is crisp.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CF057C59098C643AAB3F4736569E912', X'CA1678FCA2D17048AE5971EBAE836B6C', X'CA1678FCA2D17048AE5971EBAE836B6C', 10, NULL, NULL, 2, NULL, 0, 'If desired, top with shredded cheese, return to the oven and continue baking just until the cheese has melted.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A9CD3D887AB9C4AB29E744B99CADEFC', X'236E605C63C5CB41A89842E39016AEE6', X'236E605C63C5CB41A89842E39016AEE6', 0, NULL, NULL, 2, NULL, 0, 'Prepare the empanada dough and let the dough rest in the refrigerator for at least one hour (overnight is best).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'269EC883CF45334D81A697B6B9851E29', X'236E605C63C5CB41A89842E39016AEE6', X'236E605C63C5CB41A89842E39016AEE6', 1, NULL, NULL, 2, NULL, 0, 'Cook the bacon until crispy and drain on paper towels. Crumble the bacon roughly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D76AEC45B827AF42A459D1558F06051E', X'236E605C63C5CB41A89842E39016AEE6', X'236E605C63C5CB41A89842E39016AEE6', 2, NULL, NULL, 2, NULL, 0, 'Whisk the eggs with the milk and season with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACA84D4042E0584DAD2744FBE866A52D', X'236E605C63C5CB41A89842E39016AEE6', X'236E605C63C5CB41A89842E39016AEE6', 3, NULL, NULL, 2, NULL, 0, 'Melt the butter over medium heat in a nonstick skillet. Add the chopped scallions and cook for 2 to 3 minutes, until soft.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'726CD35ED7128E489234A0D0D7B43793', X'236E605C63C5CB41A89842E39016AEE6', X'236E605C63C5CB41A89842E39016AEE6', 4, NULL, NULL, 2, NULL, 0, 'Pour in the eggs and cook, stirring, until well scrambled and just cooked. Stir in the cream cheese just before removing eggs from heat. The cheese does not have to be mixed in well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DB75813029F3B4E85EA849B80863D88', X'236E605C63C5CB41A89842E39016AEE6', X'236E605C63C5CB41A89842E39016AEE6', 5, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BA4BF73C8B1EE479AB15DD936A55560', X'236E605C63C5CB41A89842E39016AEE6', X'236E605C63C5CB41A89842E39016AEE6', 6, NULL, NULL, 2, NULL, 0, 'Roll out the empanada dough and cut into 8 6-inch circles of dough. Let circles rest for 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39A6ACCAA4164F43A9A55457992ECAD2', X'236E605C63C5CB41A89842E39016AEE6', X'236E605C63C5CB41A89842E39016AEE6', 7, NULL, NULL, 2, NULL, 0, 'Place spoonfuls of the egg mixture in the middle of each dough circle, dividing the mixture evenly between the empanadas. Sprinkle some crumbled bacon and grated pepper jack cheese on top of the egg mixture, dividing the cheese and bacon evenly between the 8 empanadas.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'768B1525156D4B44A4FC63BD1F569262', X'236E605C63C5CB41A89842E39016AEE6', X'236E605C63C5CB41A89842E39016AEE6', 8, NULL, NULL, 2, NULL, 0, 'Fold and seal the empanadas (see how to make empanadas for step-by-step instructions on shaping empanadas).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7E501DFE56C5E418AF7212B99D0B908', X'236E605C63C5CB41A89842E39016AEE6', X'236E605C63C5CB41A89842E39016AEE6', 9, NULL, NULL, 2, NULL, 0, 'Place empanadas on a baking sheet. Whisk an egg with a tablespoon of water, and brush empanadas carefully with the egg wash.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5CE805A999D7A47AE1E81BFFB6F40FB', X'236E605C63C5CB41A89842E39016AEE6', X'236E605C63C5CB41A89842E39016AEE6', 10, NULL, NULL, 2, NULL, 0, 'Bake empanadas for 25 to 30 minutes, or until puffed and golden brown.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB12D99DF4739E48BD42508774BFF75A', X'236E605C63C5CB41A89842E39016AEE6', X'236E605C63C5CB41A89842E39016AEE6', 11, NULL, NULL, 2, NULL, 0, 'Remove from oven and let cool. Empanadas can be wrapped well and frozen. Thaw empanadas and reheat in the oven or microwave.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50FF37B8844F0144A350BDE727B2062A', X'E97CAD989B6DB84A9197FB1CB5EDD535', X'E97CAD989B6DB84A9197FB1CB5EDD535', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D92F20EBA7C2934F8C07E4183D794AC9', X'E97CAD989B6DB84A9197FB1CB5EDD535', X'E97CAD989B6DB84A9197FB1CB5EDD535', 1, NULL, NULL, 2, NULL, 0, 'Line a rimmed baking pan with foil; lightly oil the foil or spray with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'519BD1EC82F61E42841240557CBCC667', X'E97CAD989B6DB84A9197FB1CB5EDD535', X'E97CAD989B6DB84A9197FB1CB5EDD535', 2, NULL, NULL, 2, NULL, 0, 'Cut the bacon strips in half crosswise and coat them with the 2 tablespoons of brown sugar, if using. Arrange the bacon on a foil-lined rimmed baking sheet and bake for 15 to 20 minutes, or until almost crisp. Remove the bacon from the oven and let it cool.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'009C3E1E4445914494794A180BA1E3FD', X'E97CAD989B6DB84A9197FB1CB5EDD535', X'E97CAD989B6DB84A9197FB1CB5EDD535', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the ground beef, pork, onion, bell pepper, breadcrumbs, cheese, milk, egg, Cajun seasonings, Worcestershire sauce, salt, and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A07AE62DC7233847AB21F116A4AA69D6', X'E97CAD989B6DB84A9197FB1CB5EDD535', X'E97CAD989B6DB84A9197FB1CB5EDD535', 4, NULL, NULL, 2, NULL, 0, 'Put the ground meat mixture on the prepared foil-lined baking pan and shape into a loaf.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'842A03747D2027478ECCD670B3296846', X'E97CAD989B6DB84A9197FB1CB5EDD535', X'E97CAD989B6DB84A9197FB1CB5EDD535', 5, NULL, NULL, 2, NULL, 0, 'Spread barbecue sauce over the loaf and arrange the bacon strips over the top.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09BC061A1CB3044093ADE1A5711DC3CE', X'E97CAD989B6DB84A9197FB1CB5EDD535', X'E97CAD989B6DB84A9197FB1CB5EDD535', 6, NULL, NULL, 2, NULL, 0, 'Bake the meatloaf until the bacon is crispy and loaf is firm, about 1 hour to 1 hour and 10 minutes. If unsure, check the loaf with an instant-read food thermometer inserted into the center of the loaf. The minimum safe temperature for ground meat is 160 F/71 C.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8195E478E8DC6743954D344DE10562E1', X'E97CAD989B6DB84A9197FB1CB5EDD535', X'E97CAD989B6DB84A9197FB1CB5EDD535', 7, NULL, NULL, 2, NULL, 0, 'Let stand for 10 minutes before slicing or moving to a serving platter.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B66C760950F25546BE4418653675117E', X'1BB34A27CC0CFF4C851DAB0BC3F93CBB', X'1BB34A27CC0CFF4C851DAB0BC3F93CBB', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the broccoli florets, raisins, sunflower seeds, crumbled bacon, chopped onion, and thawed frozen peas; toss to combine.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'639C4BAC239CA5429E3B3B8FFBDC20F1', X'1BB34A27CC0CFF4C851DAB0BC3F93CBB', X'1BB34A27CC0CFF4C851DAB0BC3F93CBB', 1, NULL, NULL, 2, NULL, 0, 'In a separate bowl or large cup, whisk together mayonnaise, vinegar, and sugar.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BF3218CF272CA48A2919D344041B9D0', X'1BB34A27CC0CFF4C851DAB0BC3F93CBB', X'1BB34A27CC0CFF4C851DAB0BC3F93CBB', 2, NULL, NULL, 2, NULL, 0, 'Add the dressing to the salad and toss to mix well; chill thoroughly before serving.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B9A4B0554C70D4597489EE20471073A', X'B68AAE2891AA204D92CA88903477C2CF', X'B68AAE2891AA204D92CA88903477C2CF', 0, NULL, NULL, 2, NULL, 0, 'Place the beans in a large saucepan and cover with salted water by 2 to 3 inches. Soak the beans overnight.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFA2B161869AC54FB6CF43AA64CC47A7', X'B68AAE2891AA204D92CA88903477C2CF', X'B68AAE2891AA204D92CA88903477C2CF', 1, NULL, NULL, 2, NULL, 0, 'The next day, drain and rinse the beans. Return the beans to the saucepan and cover with fresh water by about 4 inches. Bring to a boil, then lower the heat and simmer for 1 to 1 1/2 hours or until the beans are tender but not bursting open.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47090EDD7E7D6F41B0604A0CF6AB0D80', X'B68AAE2891AA204D92CA88903477C2CF', X'B68AAE2891AA204D92CA88903477C2CF', 2, NULL, NULL, 2, NULL, 0, 'While the beans are cooking, mix the ketchup, molasses, brown sugar, Worcestershire sauce, mustard, chili powder, and Magic Dust together in a large bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43180B6D7B6FB74495BB6245CD81A921', X'B68AAE2891AA204D92CA88903477C2CF', X'B68AAE2891AA204D92CA88903477C2CF', 3, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F (177 degrees C).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3813149E96F03F439CEA56E460146760', X'B68AAE2891AA204D92CA88903477C2CF', X'B68AAE2891AA204D92CA88903477C2CF', 4, NULL, NULL, 2, NULL, 0, 'Cook the bacon in a large skillet over medium heat until crisp. Remove the bacon with a slotted spoon. Drain the bacon on paper towels, crumble it, and set aside. Add the onion and garlic to the bacon drippings and cook over medium heat, stirring constantly, for 2 minutes. The onion should still be a little crunchy.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9268A14EE43EE43B9CF99AE89E8C452', X'B68AAE2891AA204D92CA88903477C2CF', X'B68AAE2891AA204D92CA88903477C2CF', 5, NULL, NULL, 2, NULL, 0, 'Drain the beans, reserving 2 cups/480 mL of the bean water. Pour the beans and bean water into the bowl with the sauce. Add the bacon and onion and stir to combine well. Pour into a 13 x 9-inch baking dish and bake for 1 hour or until bubbly. Will keep, refrigerated, for up to 1 week.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93BF849AEE19504DBB577EC7A3B77229', X'F39E626131BEAF47BD439555DD9F6ABC', X'F39E626131BEAF47BD439555DD9F6ABC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F632603EBA485E4AB119D9DE46408A09', X'F39E626131BEAF47BD439555DD9F6ABC', X'F39E626131BEAF47BD439555DD9F6ABC', 1, NULL, NULL, 2, NULL, 0, 'In a large saucepan over high heat, cook the bacon until it turns crisp. Transfer the bacon to a paper-towel-lined plate to drain.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A87A699F118E840B4EA7040AC3A930F', X'F39E626131BEAF47BD439555DD9F6ABC', X'F39E626131BEAF47BD439555DD9F6ABC', 2, NULL, NULL, 2, NULL, 0, 'Pour all but 2 tablespoons of bacon grease from the pan. Saute the onions, carrots, and celery in the bacon grease for 5 minutes, until the vegetables turn soft. Transfer them to a bowl with a slotted spoon and set it aside for a moment.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FF19AC7637E044EB506295C84F85A94', X'F39E626131BEAF47BD439555DD9F6ABC', X'F39E626131BEAF47BD439555DD9F6ABC', 3, NULL, NULL, 2, NULL, 0, 'Season the beef with the salt and pepper and brown it in the remaining bacon grease. Once all sides of the beef are browned, sprinkle the parsley, thyme, rosemary, allspice, and flour over the beef.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79B6220BC76D0D4F84CE04CF5BA816CE', X'F39E626131BEAF47BD439555DD9F6ABC', X'F39E626131BEAF47BD439555DD9F6ABC', 4, NULL, NULL, 2, NULL, 0, 'Stir in the tomato paste and cook the spiced beef for 1 minute.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDEF1688E214EA428CF084ACBC9F26DC', X'F39E626131BEAF47BD439555DD9F6ABC', X'F39E626131BEAF47BD439555DD9F6ABC', 5, NULL, NULL, 2, NULL, 0, 'Add the mushrooms, cooked vegetables, crisped bacon, red wine, and beef stock, into the pan with the beef, and then cook the mixture over low heat, covered, for 1 hour and 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20D3DAFF1B026D44B5E2EDBF79F66DEB', X'F39E626131BEAF47BD439555DD9F6ABC', X'F39E626131BEAF47BD439555DD9F6ABC', 6, NULL, NULL, 2, NULL, 0, 'Though this is the quick version and ready to eat the day it is made, it will richen by being left overnight and rewarmed and eaten the next day.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'774624D0D3266C4194ECC9F723169892', X'F39E626131BEAF47BD439555DD9F6ABC', X'F39E626131BEAF47BD439555DD9F6ABC', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A97FBF63CB9B6A40ADA0EAAB7F1603BE', X'75146D8B3BC4004CB104817ACE1BE047', X'75146D8B3BC4004CB104817ACE1BE047', 0, NULL, NULL, 2, NULL, 0, 'Rinse the beans and soak them overnight in water.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'028D418278619A47BD7FDD5ED2E3F1AB', X'75146D8B3BC4004CB104817ACE1BE047', X'75146D8B3BC4004CB104817ACE1BE047', 1, NULL, NULL, 2, NULL, 0, 'Drain the beans, and cover them with fresh water (or chicken stock or beef stock if desired). Add chopped bell pepper. Bring the beans to a simmer on the stove, and cook them over low heat until they are just tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E167EAE3CA0A454586C5580CC95270D3', X'75146D8B3BC4004CB104817ACE1BE047', X'75146D8B3BC4004CB104817ACE1BE047', 2, NULL, NULL, 2, NULL, 0, 'If using bacon, cook it in a heavy skillet until crispy. Remove the bacon from the skillet and reserve for another use. Add the chopped onion and minced garlic to the bacon fat remaining in the skillet and cook them over low heat until they are soft, translucent and fragrant. (If not using bacon, cook the onion and garlic in a couple of tablespoons of olive oil).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19A4963BEAA86641962F928F4D93C135', X'75146D8B3BC4004CB104817ACE1BE047', X'75146D8B3BC4004CB104817ACE1BE047', 3, NULL, NULL, 2, NULL, 0, 'Add the cumin and the brown sugar to the onions and continue to cook until browned, soft and fragrant.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41AB88B9382EDD4797DE67A8B48A5FF7', X'75146D8B3BC4004CB104817ACE1BE047', X'75146D8B3BC4004CB104817ACE1BE047', 4, NULL, NULL, 2, NULL, 0, 'Once the beans are tender, remove two cups of the cooked beans and process them in a blender with onion mixture. Add the blended beans and onion back to the rest of the beans. According to taste, season with salt, pepper, and Worcestershire sauce (optional).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25979037244A934693C60EC6EA005510', X'75146D8B3BC4004CB104817ACE1BE047', X'75146D8B3BC4004CB104817ACE1BE047', 5, NULL, NULL, 2, NULL, 0, 'Continue to cook beans until they are very tender, adding water if necessary or letting? the excess water boil away until the desired texture is reached.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38BD6059159FA84CA430438A508B554D', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72AB690AECC37C45B5CBAB439317072C', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', 1, NULL, NULL, 2, NULL, 0, 'In a small pan, cook pork shoulder with just enough water to cover, for 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20F48553957BB34188F2EC69682CCBB4', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', 2, NULL, NULL, 2, NULL, 0, 'When cool enough to handle, cut meat and fat into large pieces. Reserve the cooking water and let it cool.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C795BFDD6FD924B9026CDE4840554EB', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a small skillet, saut bacon until golden.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B72D9546DEEDD448CDFE45D83979570', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', 4, NULL, NULL, 2, NULL, 0, 'Add onions and saut 5 minutes more, remove from heat and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B87F728BE1AE43498CF11DD652D016D0', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', 5, NULL, NULL, 2, NULL, 0, 'Using a medium grinding blade, push the cooled pork pieces (fat and all) and potatoes down the chute of your grinder using a wooden pestle. Don''t ever put your fingers in a grinder, running or not running!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35154B8730AEB143BD000D73371A11F5', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', 6, NULL, NULL, 2, NULL, 0, 'Add cooked bacon and onions, salt, pepper, marjoram, and peppermint leaves to the water the meat was cooked in and mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EA7D7AD8229A44F8C2EC23EF4F7C9B4', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', 7, NULL, NULL, 2, NULL, 0, 'Then combine with meat-potato mixture until just moistened.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42C32ECBCF8802478B4FC9DF94C68B16', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', 8, NULL, NULL, 2, NULL, 0, 'Fry a small patty of meat and taste to make sure the seasonings are just right.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11E16FC4A23164449CFFA4CDC8FE230D', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', 9, NULL, NULL, 2, NULL, 0, 'Using the procedure for stuffing Polish sausage, stuff the meat-potato mixture into the casings. Store refrigerated and covered up to two days until ready to cook.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3086308295AEA748A7BA96CA52635843', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', 10, NULL, NULL, 2, NULL, 0, 'Before cooking, prick sausage along the length of the link to allow air bubbles to escape. Otherwise, they will explode in the cooking water.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3C5314CCCEA7A40903365334D38BBA8', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', 11, NULL, NULL, 2, NULL, 0, 'Place sausage in a large pot and cover with water. Bring to a boil over high heat. Reduce heat to medium-low and simmer uncovered for 45 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFECA3024851F04FA83FEB4CE4C449F0', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', X'A0F7F2E5BF0AAF4BB7F7F90D215ECFEE', 12, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E6332250BB3024099E8B84187B06AC1', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'393922A9F1E2454A917D9B50D3D62B89', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', 1, NULL, NULL, 2, NULL, 0, 'Cook bacon gently until partially cooked but not crisp and still flexible.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70181F5D62FEFC43B65169334C881D2B', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', 2, NULL, NULL, 2, NULL, 0, 'Rinse scallops under running cold water, pat dry with paper towels. If you notice any tag-like pieces on the side of the scallops, this is a tough muscle and should be removed. Just pinch it with your fingers to remove.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEC4920E41A9AA43AEBFEA8768F7A62C', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', 3, NULL, NULL, 2, NULL, 0, 'Cut each bacon slice crosswise in half; wrap each half around a scallop and secure with a toothpick.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3465559B7B2004BA7315BDC9836DA43', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle scallops lightly with seasoned pepper. Preheat broiler.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'277FFE88D2DB1147A965E98DE2DE6FB1', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', 5, NULL, NULL, 2, NULL, 0, 'Place scallops on a rack in broiling pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50B114EDB0E4C148B61F0793C0CA6F6D', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', 6, NULL, NULL, 2, NULL, 0, 'Broil 4 to 5 inches from heat for 8 to 10 minutes, or until scallops turn opaque throughout, using tongs to turn scallops frequently so the bacon browns evenly on all sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42A3132C03F2624E8FCA6AEB81AD0BA2', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', X'F4CB59BFC092FF488F4EAE3E41C1C0FE', 7, NULL, NULL, 2, NULL, 0, 'Serve as an appetizer or an entre.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8E96C253E2D6E4DB5A73089DA21514D', X'FAB29C534EB03E4CAAFC606C3DA890CC', X'FAB29C534EB03E4CAAFC606C3DA890CC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F39EB12A37F8F42BB4F0BC746E99FE9', X'FAB29C534EB03E4CAAFC606C3DA890CC', X'FAB29C534EB03E4CAAFC606C3DA890CC', 1, NULL, NULL, 2, NULL, 0, 'Wash and dry potatoes. Pierce each one multiple times with a fork. Place onto a baking sheet and bake for 40-45 minutes at 375 degrees. If using larger potatoes, extend the time to 50-55 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'268ABC968B4325408A7B892E1648C85F', X'FAB29C534EB03E4CAAFC606C3DA890CC', X'FAB29C534EB03E4CAAFC606C3DA890CC', 2, NULL, NULL, 2, NULL, 0, 'To test for doneness, pierce with a fork. If it is tender and goes right through, the potatoes are done. Remove from oven and let potatoes cool for 15 minutes or so before handling.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAB1D5B527B2F34F834FB20FF5117D41', X'FAB29C534EB03E4CAAFC606C3DA890CC', X'FAB29C534EB03E4CAAFC606C3DA890CC', 3, NULL, NULL, 2, NULL, 0, 'Slit potatoes vertically, but only cut halfway through. Carefully scoop out 3/4 of the cooked flesh. You do not need to remove all of it and should leave some behind to help keep the potato shells sturdy.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B0042615865894780A15A4532961445', X'FAB29C534EB03E4CAAFC606C3DA890CC', X'FAB29C534EB03E4CAAFC606C3DA890CC', 4, NULL, NULL, 2, NULL, 0, 'Add the scooped out portion into a large bowl. Add the yogurt, cream cheese, butter and milk. Using a hand held mixer, blend ingredients together. Use more milk if needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B5CAE57DF0441468BC69C010B6C3F14', X'FAB29C534EB03E4CAAFC606C3DA890CC', X'FAB29C534EB03E4CAAFC606C3DA890CC', 5, NULL, NULL, 2, NULL, 0, 'Add remaining ingredients, except for the reserved portions of bacon and spring onion listed above. Use a spoon to combine the ingredients. Taste for salt.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D7D650490C19742B5517EBD6B4A1E08', X'FAB29C534EB03E4CAAFC606C3DA890CC', X'FAB29C534EB03E4CAAFC606C3DA890CC', 6, NULL, NULL, 2, NULL, 0, 'Scoop equal portions into the 6 potato shells. Garnish with reserved bacon and spring onion. Return potatoes to a 425-degree oven for 10-12 minutes. Once the potatoes begin to form a slightly golden crust, remove from oven and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69455801016ABE45B0C1149D0371F45F', X'16FA4D6834F4194D990DC27E80971A3D', X'16FA4D6834F4194D990DC27E80971A3D', 0, NULL, NULL, 2, NULL, 0, 'In a large skillet or saut pan over medium heat, cook the diced bacon until almost crisp and fat has been rendered. With a slotted spoon, transfer the bacon to paper towels and leave 1 tablespoon of drippings in the skillet. Refrigerate the bacon; it will be added to the stew near the end of the cooking time.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'561372EECE26D342ABC1AA88352DA59D', X'16FA4D6834F4194D990DC27E80971A3D', X'16FA4D6834F4194D990DC27E80971A3D', 1, NULL, NULL, 2, NULL, 0, 'In a food storage bag, combine the flour, salt, and pepper. Add the beef pieces and toss to coat with the flour mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D0586F7A8929648AE611F63BECC37AC', X'16FA4D6834F4194D990DC27E80971A3D', X'16FA4D6834F4194D990DC27E80971A3D', 2, NULL, NULL, 2, NULL, 0, 'Cook the beef in the bacon drippings over medium heat, stirring constantly, until the beef is nicely browned on all sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA0DBA1357969D4F92F190F8C1C00FF7', X'16FA4D6834F4194D990DC27E80971A3D', X'16FA4D6834F4194D990DC27E80971A3D', 3, NULL, NULL, 2, NULL, 0, 'Transfer the beef to the crockery insert of a slow cooker. Top with the mushrooms, thinly sliced carrots, and thawed onions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B1B02C95B5D0B4EB592CDBAD3829CF1', X'16FA4D6834F4194D990DC27E80971A3D', X'16FA4D6834F4194D990DC27E80971A3D', 4, NULL, NULL, 2, NULL, 0, 'Combine the wine, beef broth, tomato paste, and garlic. Pour over the beef and vegetables. Cover and cook on LOW for 8 to 9 hours or on HIGH for 4 to 5 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C5AE1149CF47C4784ECCD7F535AD757', X'16FA4D6834F4194D990DC27E80971A3D', X'16FA4D6834F4194D990DC27E80971A3D', 5, NULL, NULL, 2, NULL, 0, 'Stir in the thyme, rosemary, and the reserved bacon. Continue cooking on HIGH, uncovered, for 15 minutes longer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD4E830ADDF9B948889A9DB924C49416', X'8C386A2AA659DB47A1EF4343E563451C', X'8C386A2AA659DB47A1EF4343E563451C', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to a boil and cook the pasta as directed on the package. Meanwhile, cook the bacon in a large skillet until crisp. Drain the bacon on paper towels and crumble; set aside. Discard the bacon fat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA0874061E10484D88205A4426651150', X'8C386A2AA659DB47A1EF4343E563451C', X'8C386A2AA659DB47A1EF4343E563451C', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl beat together the egg yolks with the cream; season with the salt and pepper. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D72C8DC528B1244893EADE64F5DED917', X'8C386A2AA659DB47A1EF4343E563451C', X'8C386A2AA659DB47A1EF4343E563451C', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet, heat the olive oil over low heat and add the salmon, peas, and cooked bacon; gently heat. Shake the pan from time to time, so the food doesn''t stick.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C95D501101BF14AAB6E46496BE1D22A', X'8C386A2AA659DB47A1EF4343E563451C', X'8C386A2AA659DB47A1EF4343E563451C', 3, NULL, NULL, 2, NULL, 0, 'When the pasta is done, drain well and add immediately to the skillet with the salmon; do not stir.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DED181BBC925745A8D5D7DE9525C8B3', X'8C386A2AA659DB47A1EF4343E563451C', X'8C386A2AA659DB47A1EF4343E563451C', 4, NULL, NULL, 2, NULL, 0, 'Immediately pour the egg mixture over the hot pasta. Turn the heat under the skillet to medium. Using tongs, gently lift and turn the pasta, egg mixture, salmon, peas, and bacon together until creamy sauce forms on the pasta. The egg will cook on contact with hot pasta. Sprinkle the food with the cheese and toss gently. Serve immediately.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E71D518018B204C858EB0C8D5DC0A80', X'87052442137CB043AC3E400EBB9AA590', X'87052442137CB043AC3E400EBB9AA590', 0, NULL, NULL, 2, NULL, 0, 'Prepare the dairy-free pie dough according to the instructions, plate in a lightly oiled 9` tart or pie pan, cover with plastic wrap, and place in the refrigerator for at least 30 minutes before filling with the quiche filling and baking.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'147F847B05A9F441B6A7A140203672F5', X'87052442137CB043AC3E400EBB9AA590', X'87052442137CB043AC3E400EBB9AA590', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2596016FF9543C4C8B5DE8C9058E07B9', X'87052442137CB043AC3E400EBB9AA590', X'87052442137CB043AC3E400EBB9AA590', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the filling. In a large skillet over medium heat, heat 2 tablespoons of the olive oil, adding the onions once hot. Cook, stirring occasionally until the onions are lightly caramelized, about 6-8 minutes. Add the remaining olive oil, garlic, vegan bacon, 1/2 teaspoon salt and red pepper flakes and cook until the garlic smells fragrant, about 4-5 minutes more. Remove from heat and transfer the mixture to a bowl. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17E9E83E1B1F5A4682F825A01F382F6A', X'87052442137CB043AC3E400EBB9AA590', X'87052442137CB043AC3E400EBB9AA590', 3, NULL, NULL, 2, NULL, 0, 'In a steam basket set over boiling water, steam the broccoli for 3-5 minutes, or until bright green and lightly crisp. Toss with the onion-vegan bacon mixture and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4E38B5A5A14ED43BB6179A18EF3E4B7', X'87052442137CB043AC3E400EBB9AA590', X'87052442137CB043AC3E400EBB9AA590', 4, NULL, NULL, 2, NULL, 0, 'In the bowl of a food processor, process the tofu, lemon juice, tahini, rice vinegar, and remaining 1/2 teaspoon salt until smooth. Fold in the broccoli-onion mixture, stirring until evenly mixed. Using your hands, crumble the vegan cheese evenly into the bottom of the pie crust. Pour the filling on top of the vegan cheese in the prepared pie crust and bake for 45-55 minutes, or until golden brown and set. Serve hot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A19F30B3DA6DF0438026AD0A65148F83', X'CD6938CB38B3954684E0EA8671B4619F', X'CD6938CB38B3954684E0EA8671B4619F', 0, NULL, NULL, 2, NULL, 0, 'Put the black-eyed peas in a large bowl or pot and cover them completely with cool water. Soak the black-eyed peas for 6 hours or overnight.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25F848C615A9594DB881D789182AE936', X'CD6938CB38B3954684E0EA8671B4619F', X'CD6938CB38B3954684E0EA8671B4619F', 1, NULL, NULL, 2, NULL, 0, 'Rinse and drain the black-eyed peas several times. Set them aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90F0D7912BA6E641996C78D9A7FBFBAA', X'CD6938CB38B3954684E0EA8671B4619F', X'CD6938CB38B3954684E0EA8671B4619F', 2, NULL, NULL, 2, NULL, 0, 'In a large Dutch oven or stockpot over medium-high heat, sear the pork pieces in the vegetable oil until browned.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6F05734A214FB4C9CEEDF1C3267A2CC', X'CD6938CB38B3954684E0EA8671B4619F', X'CD6938CB38B3954684E0EA8671B4619F', 3, NULL, NULL, 2, NULL, 0, 'Add the bacon to the pot and cook, stirring, for 2 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FACF1553F3EF9843A62B437C817D25DB', X'CD6938CB38B3954684E0EA8671B4619F', X'CD6938CB38B3954684E0EA8671B4619F', 4, NULL, NULL, 2, NULL, 0, 'Add the onion and celery and continue cooking, stirring, for 5 to 7 minutes, or until the bacon is cooked and the onion is lightly browned.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8FFC869725804488B8EADBEED18EB75', X'CD6938CB38B3954684E0EA8671B4619F', X'CD6938CB38B3954684E0EA8671B4619F', 5, NULL, NULL, 2, NULL, 0, 'Add the garlic and cook for 1 minute.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD88D5CE94629C47918F8E0D7EFF7BA8', X'CD6938CB38B3954684E0EA8671B4619F', X'CD6938CB38B3954684E0EA8671B4619F', 6, NULL, NULL, 2, NULL, 0, 'Add 3 cups of the chicken broth and the garlic powder, black pepper, and cayenne. Bring it to a boil. Reduce the heat to low and simmer for 45 minutes or until the pork pieces feel fork tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EC0041961D1A044800B8409B08BFDF0', X'CD6938CB38B3954684E0EA8671B4619F', X'CD6938CB38B3954684E0EA8671B4619F', 7, NULL, NULL, 2, NULL, 0, 'Add the drained black-eyed peas to the pot along with 1 to 1 1/2 cups of water to cover. Put the lid on the pot and simmer for about 1 to 1 1/2 hours, or until the peas turn tender but retain their shape. Add more chicken broth as necessary to keep the peas covered while they cook.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38FEF74FD4D827429105C95D27FA26A3', X'CD6938CB38B3954684E0EA8671B4619F', X'CD6938CB38B3954684E0EA8671B4619F', 8, NULL, NULL, 2, NULL, 0, 'Taste and add the Cajun or Creole seasoning and salt, as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24E5501557815E42B1D5BACBDD268FC4', X'7160E4EE9EA2454787CDC6BD2D824F5F', X'7160E4EE9EA2454787CDC6BD2D824F5F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'774489435580364B8087D0302D76BE43', X'7160E4EE9EA2454787CDC6BD2D824F5F', X'7160E4EE9EA2454787CDC6BD2D824F5F', 1, NULL, NULL, 2, NULL, 0, 'Scrub whole potatoes of the same size and cook in salted water until easily pierced with a fork. Let cool and peel while still warm.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'180B65B8BC3C3E4DA795F1D2BD019611', X'7160E4EE9EA2454787CDC6BD2D824F5F', X'7160E4EE9EA2454787CDC6BD2D824F5F', 2, NULL, NULL, 2, NULL, 0, 'Chop bacon or `Bauchspeck` into small pieces and cook in a large frying pan (11 or 12 inches) until limp.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8595F1C8126794F918E3371F9C3EB14', X'7160E4EE9EA2454787CDC6BD2D824F5F', X'7160E4EE9EA2454787CDC6BD2D824F5F', 3, NULL, NULL, 2, NULL, 0, 'Remove from the pan but keep the grease in the pan. Add the butter and melt, but don''t brown.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36958B75BB71B64BBD687C017AAD8821', X'7160E4EE9EA2454787CDC6BD2D824F5F', X'7160E4EE9EA2454787CDC6BD2D824F5F', 4, NULL, NULL, 2, NULL, 0, 'Slice the cold potatoes into 1/4-inch slices (5 mm) and place a single layer in the hot fat. Place any extra potatoes on top of the first layer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C17E3960E45234BB5BA29299881DF48', X'7160E4EE9EA2454787CDC6BD2D824F5F', X'7160E4EE9EA2454787CDC6BD2D824F5F', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle the potatoes with the onions and bacon and let them cook over medium heat for 10 to 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD24CE93E5AFA74BBB7913EFD659D72C', X'7160E4EE9EA2454787CDC6BD2D824F5F', X'7160E4EE9EA2454787CDC6BD2D824F5F', 6, NULL, NULL, 2, NULL, 0, 'Flip them when they become golden brown on the underside, but don''t stir them.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5DF2119571EE2408D2F646075B6B8AB', X'7160E4EE9EA2454787CDC6BD2D824F5F', X'7160E4EE9EA2454787CDC6BD2D824F5F', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle with marjoram, caraway, salt, and pepper and cook for 5 to 10 more minutes. Add more butter if necessary, to facilitate browning.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCA35DD9E56E1C4D96C6583A47D78E22', X'7160E4EE9EA2454787CDC6BD2D824F5F', X'7160E4EE9EA2454787CDC6BD2D824F5F', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F19818B670125499E628685E5CC298B', X'8E82D2D5709CD846BC17DBAC3BD56C70', X'8E82D2D5709CD846BC17DBAC3BD56C70', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4A52234C21F434C848E7BE25FAE7B79', X'8E82D2D5709CD846BC17DBAC3BD56C70', X'8E82D2D5709CD846BC17DBAC3BD56C70', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8E31CB33B6F844FB7F70B87A6261096', X'8E82D2D5709CD846BC17DBAC3BD56C70', X'8E82D2D5709CD846BC17DBAC3BD56C70', 2, NULL, NULL, 2, NULL, 0, 'Use a cookie scoop to form the sausage mixture into 1-inch balls. Line a baking sheet with parchment paper and add the sausage balls to the sheet. Place them slightly apart so they are not touching.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A985D8FF91CC47419A4F5D061C4815E5', X'8E82D2D5709CD846BC17DBAC3BD56C70', X'8E82D2D5709CD846BC17DBAC3BD56C70', 3, NULL, NULL, 2, NULL, 0, 'Bake for 15 to 20 minutes or until golden brown and cooked through. The internal temperature should read 165 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74B59E6899DEFA4B94FBE85B4CF59DBD', X'8E82D2D5709CD846BC17DBAC3BD56C70', X'8E82D2D5709CD846BC17DBAC3BD56C70', 4, NULL, NULL, 2, NULL, 0, 'As they are baking, prepare the dipping sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A19913FE1225448AD435699A5D6F67D', X'8E82D2D5709CD846BC17DBAC3BD56C70', X'8E82D2D5709CD846BC17DBAC3BD56C70', 5, NULL, NULL, 2, NULL, 0, 'Whisk all of the dipping sauce ingredients in a small bowl until thoroughly combined. You can make the sauce a few days ahead of time. The longer it sits, the more the flavor will develop.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'480207B4781C60478FB6C95D71AA6DE9', X'8E82D2D5709CD846BC17DBAC3BD56C70', X'8E82D2D5709CD846BC17DBAC3BD56C70', 6, NULL, NULL, 2, NULL, 0, 'Serve the sausage balls with some chopped chives and the dipping sauce. You can serve them hot or at room temperature.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C18067A05E0DDC4990A567D1D87F619C', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD3AC58F8D75BF42B248BCC4DD17E62F', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 1, NULL, NULL, 2, NULL, 0, 'Clean the tripe under cold running water. Set in a bowl of cold water mixed with the vinegar for 20 minutes, then rinse thoroughly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CC7C80ECDCA3A47BF6531E9769D80AE', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 2, NULL, NULL, 2, NULL, 0, 'Cut the tripe into pieces approximately 3-inches square. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B6FB6F0BFB1AF40BE807EBA99F03110', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 3, NULL, NULL, 2, NULL, 0, 'Coarsely chop 1 onion. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'725736FA6719854C9B8F78F87D168EB0', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 4, NULL, NULL, 2, NULL, 0, 'Remove and peel each clove from the bulb of garlic. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79479AFC4C29FE4689B45715DD936691', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 5, NULL, NULL, 2, NULL, 0, 'In a large heavy-bottomed pot, place the tripe pieces and the split calf or pig foot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6C2E5CE7953244BAF2020E8D5F518A4', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 6, NULL, NULL, 2, NULL, 0, 'Cover with water and bring to a boil. Allow to boil for 1 minute.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C55B58839126C4288B3D0430E87C741', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 7, NULL, NULL, 2, NULL, 0, 'Drain into a colander and rinse the froth from the meat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15F6CF5FFD1D19488C2399055553E27A', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 8, NULL, NULL, 2, NULL, 0, 'Return the tripe and foot to the cooking pot and add 4 to 6 cups of water, or enough to cover.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B596F59631DAFE4BB2201A13F920188A', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 9, NULL, NULL, 2, NULL, 0, 'Put chopped onion and all but 3 peeled cloves of garlic, bay leaves, and peppercorns in? the pot. Bring to a boil, then reduce heat. Simmer for 3 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEC16E4DECCC9540B55D0D9B2FBDBE33', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 10, NULL, NULL, 2, NULL, 0, 'Finely chop the remaining onion and 3 cloves of garlic.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AD96C89B8B8364CB9178480410635F5', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 11, NULL, NULL, 2, NULL, 0, 'Cut the serrano ham into small squares. Slice the chorizo sausage into rounds.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F7C49CF1A6AEE45A46D0ED4204F76D9', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 12, NULL, NULL, 2, NULL, 0, 'In a skillet, heat the olive oil and saut the chopped onion, garlic, ham, and chorizo for 5 minutes, or until onions are translucent.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A890A03E9441D4593846292806889CB', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 13, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in the paprika.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4D1C82B89AC344498492DF8C413D8B6', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 14, NULL, NULL, 2, NULL, 0, 'Add the mixture to the pot with the tripe.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFD346B6B022A0448C574E127FD12888', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 15, NULL, NULL, 2, NULL, 0, 'Cut the morcilla into rounds and place in the pot. If using the optional garbanzo beans, add them now and simmer for 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0760AD16013674EB2013FCB0DEDF0DA', X'A0B8E1675DC31444A10751841200259A', X'A0B8E1675DC31444A10751841200259A', 16, NULL, NULL, 2, NULL, 0, 'The traditional way to serve callos is in earthenware bowls accompanied by rustic bread. Enjoy.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A33F8758D6D6A64AB3B2732C8F7A17D9', X'0828477F6567A446A3CCE6DE453E2615', X'0828477F6567A446A3CCE6DE453E2615', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C84F51AFCB7CBA428C69D672B8D0B6CB', X'0828477F6567A446A3CCE6DE453E2615', X'0828477F6567A446A3CCE6DE453E2615', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F/200 C/Gas 6.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD71953A2EFAC341992322770F99B708', X'0828477F6567A446A3CCE6DE453E2615', X'0828477F6567A446A3CCE6DE453E2615', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large frying pan, gently cook the chopped onion in the oil for 5 minutes, or until the onions are soft and translucent but not browned. Add the soy sauce, the lemon juice, garlic, and chili and cook for a further 2 minutes, again taking care not to burn.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'667111404680ED42B8A55427946699FC', X'0828477F6567A446A3CCE6DE453E2615', X'0828477F6567A446A3CCE6DE453E2615', 3, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat, place the onion mixture on a plate and leave to cool, the mixture does not need to be cold.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A0B135294E21A4DA1B8D0FCB9C91909', X'0828477F6567A446A3CCE6DE453E2615', X'0828477F6567A446A3CCE6DE453E2615', 4, NULL, NULL, 2, NULL, 0, 'Place the sausage meat in a large mixing bowl, add the cooled onion mixture and 2 of the eggs. Mix well until all the ingredients are thoroughly and evenly mixed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41EDDDA7AEDCDE48A88517BB3C7A8428', X'0828477F6567A446A3CCE6DE453E2615', X'0828477F6567A446A3CCE6DE453E2615', 5, NULL, NULL, 2, NULL, 0, 'On a lightly floured surface, roll out half the pastry to a 20cm x 25cm (8` x 10`) rectangle. Cut lengthwise into 2 strips. Repeat with the remaining pastry. Rest the pastry for at least 10 mins in the refrigerator.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD9D8BCB7C948C44ACCC73B1B8A48B12', X'0828477F6567A446A3CCE6DE453E2615', X'0828477F6567A446A3CCE6DE453E2615', 6, NULL, NULL, 2, NULL, 0, 'Form the sausage meat mixture into 4 long sausages the length of the pastry strips. Place each sausage into the center of each pastry strip. Beat the 3rd egg in a basin and lightly brush the edges of the pastry with the beaten egg.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D05BC0FD869204C8828BBD2389A1D01', X'0828477F6567A446A3CCE6DE453E2615', X'0828477F6567A446A3CCE6DE453E2615', 7, NULL, NULL, 2, NULL, 0, 'Fold the pastry over the meat filling to form 2 long rolls. Brush the top surface with beaten egg.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9CA14F31E3AEE478FAA7B8EA8B60834', X'0828477F6567A446A3CCE6DE453E2615', X'0828477F6567A446A3CCE6DE453E2615', 8, NULL, NULL, 2, NULL, 0, 'Cut the rolls into 4cm / 1 / 1/2` lengths. Place on a lightly greased baking sheet and cook in the preheated oven for 20 minutes or until golden brown.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11C9274407A5D14AB82B32487041D60A', X'0828477F6567A446A3CCE6DE453E2615', X'0828477F6567A446A3CCE6DE453E2615', 9, NULL, NULL, 2, NULL, 0, 'Cool, the sausage rolls on a cooling rack, they are delicious eaten hot or cold. If popping into a lunch box or a picnic basket, let the rolls cool completely.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A63B738CCA37644A2FED2A16FE636C8', X'A6A065113D51AB45AFBD9D280CEA4AED', X'A6A065113D51AB45AFBD9D280CEA4AED', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17BA85BB0C82CA47AA8489D03AEC590B', X'A6A065113D51AB45AFBD9D280CEA4AED', X'A6A065113D51AB45AFBD9D280CEA4AED', 1, NULL, NULL, 2, NULL, 0, 'Cut off the root end of the green onions and slice them thinly, keeping the white and light green parts separate from the dark green parts. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D09B9A2771DDAB4DBFA47A0E14C897BA', X'A6A065113D51AB45AFBD9D280CEA4AED', X'A6A065113D51AB45AFBD9D280CEA4AED', 2, NULL, NULL, 2, NULL, 0, 'Add 1 tablespoon of vegetable oil to the Instant Pot inner pot and select the saut button. When the oil is hot and shimmering, add the sliced andouille sausage. Cook the sausage slices for about 3 minutes, or until lightly browned, stirring frequently. With a slotted spoon, remove the sausage to paper towels to drain and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DB8DB6EE5EB0043B46E20BCB1F972BF', X'A6A065113D51AB45AFBD9D280CEA4AED', X'A6A065113D51AB45AFBD9D280CEA4AED', 3, NULL, NULL, 2, NULL, 0, 'Add the diced chicken to the pot along with the remaining 1/2 tablespoon of vegetable oil; cook for 2 to 3 minutes, or until lightly browned, stirring frequently. With a slotted spoon, remove the chicken to a plate and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1893FA01E59FBB4DBC1F3EE7EAEC12B9', X'A6A065113D51AB45AFBD9D280CEA4AED', X'A6A065113D51AB45AFBD9D280CEA4AED', 4, NULL, NULL, 2, NULL, 0, 'Add the chopped yellow onion to the pot along with the bell pepper and celery. If necessary, add more vegetable oil. Cook for about 3 minutes, or until the onion is translucent, stirring frequently.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'744595134AB90B45A0E1CDF59194D777', X'A6A065113D51AB45AFBD9D280CEA4AED', X'A6A065113D51AB45AFBD9D280CEA4AED', 5, NULL, NULL, 2, NULL, 0, 'Add the minced garlic, Creole seasonings, and thyme to the vegetables and cook for 1 minute longer. Cancel the saut function.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E98BFA4B6F21FF4E85259F5752BA9A61', X'A6A065113D51AB45AFBD9D280CEA4AED', X'A6A065113D51AB45AFBD9D280CEA4AED', 6, NULL, NULL, 2, NULL, 0, 'Add the bay leaf and chicken stock. Stir, scraping the bottom to ensure there are no browned bits stuck to the pan. Taste and add salt if needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B0D27FC006126428C5842F1C83B1B7F', X'A6A065113D51AB45AFBD9D280CEA4AED', X'A6A065113D51AB45AFBD9D280CEA4AED', 7, NULL, NULL, 2, NULL, 0, 'Add the tomatoes (with their juices) along with the rice and the white and light green parts of the sliced green onions. Return the sausage and chicken to the pot. Stir to combine the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9963212A34EBF4BBE7558F547F5B15A', X'A6A065113D51AB45AFBD9D280CEA4AED', X'A6A065113D51AB45AFBD9D280CEA4AED', 8, NULL, NULL, 2, NULL, 0, 'Lock the lid in place and turn the steam release knob to the sealing position. Select the manual or pressure cook button (high pressure) and set the timer to 7 minutes. When the time is up, do a quick release following the manufacturer''s instructions. When the float valve drops, remove the coverthe pot will automatically switch to the keep warm setting.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE705398F6BCD54DA9AACF8B2915578F', X'A6A065113D51AB45AFBD9D280CEA4AED', X'A6A065113D51AB45AFBD9D280CEA4AED', 9, NULL, NULL, 2, NULL, 0, 'Add the shrimp to the pot and stir to combine the ingredients. Cover the pot and let it stand on the warm setting for 10 minutes. The shrimp should be cooked through.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A251757A0BF273469BC49C7C3D07AEEC', X'A6A065113D51AB45AFBD9D280CEA4AED', X'A6A065113D51AB45AFBD9D280CEA4AED', 10, NULL, NULL, 2, NULL, 0, 'Remove the cover and stir in about half of the reserved dark green parts of the sliced green onions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1833B0FB5417AA4F92523D52437E7570', X'A6A065113D51AB45AFBD9D280CEA4AED', X'A6A065113D51AB45AFBD9D280CEA4AED', 11, NULL, NULL, 2, NULL, 0, 'Garnish servings with the remaining dark green parts of the green onions and chopped parsley, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C3EF4E28678E94084600BF6DF07CF57', X'35947819C530894F9B11EC67576881E7', X'35947819C530894F9B11EC67576881E7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B439C8B2CF0AB74EB08DE66749F3D564', X'35947819C530894F9B11EC67576881E7', X'35947819C530894F9B11EC67576881E7', 1, NULL, NULL, 2, NULL, 0, 'Boil squash whole until just tender; about 6 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E2F2D908E24B24AB3F2467751EA6879', X'35947819C530894F9B11EC67576881E7', X'35947819C530894F9B11EC67576881E7', 2, NULL, NULL, 2, NULL, 0, 'Drain and carefully split the squash lengthwise.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA589A5383AD13448CFEF94C415000B3', X'35947819C530894F9B11EC67576881E7', X'35947819C530894F9B11EC67576881E7', 3, NULL, NULL, 2, NULL, 0, 'Using a spoon, scoop out pulp, taking care not to break the outer shells.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1455861A67F31E4F8116FCB8714C56C8', X'35947819C530894F9B11EC67576881E7', X'35947819C530894F9B11EC67576881E7', 4, NULL, NULL, 2, NULL, 0, 'Set pulp aside. Place squash shells in a baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF801559A57BC54BB339CFCAAFD3CE9F', X'35947819C530894F9B11EC67576881E7', X'35947819C530894F9B11EC67576881E7', 5, NULL, NULL, 2, NULL, 0, 'In a skillet over medium-high heat, cook sausage with onion. Add salt and pepper to taste; remove from heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'790FFA3A7273FC4F9D170CEEF4A4CB78', X'35947819C530894F9B11EC67576881E7', X'35947819C530894F9B11EC67576881E7', 6, NULL, NULL, 2, NULL, 0, 'Add squash pulp, breadcrumbs, and seasoned salt. Add a little water if the mixture is too dry.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA523573DA2CDF4CBE7433748DA027D4', X'35947819C530894F9B11EC67576881E7', X'35947819C530894F9B11EC67576881E7', 7, NULL, NULL, 2, NULL, 0, 'Stuff squash shells with the sausage mixture and sprinkle with Parmesan cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A434B087D7F8344DA5AD74D6CE654962', X'35947819C530894F9B11EC67576881E7', X'35947819C530894F9B11EC67576881E7', 8, NULL, NULL, 2, NULL, 0, 'Bake at 375 F for 15 to 20 minutes or until hot and browned.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'348F37C781611F4FB0F4BA8A5E86484F', X'C08F58395573264EA47EE1A707D510B8', X'C08F58395573264EA47EE1A707D510B8', 0, NULL, NULL, 2, NULL, 0, 'Wash the beans and soak them overnight. Drain well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA4EFC7CA596F44692FACB1781413A50', X'C08F58395573264EA47EE1A707D510B8', X'C08F58395573264EA47EE1A707D510B8', 1, NULL, NULL, 2, NULL, 0, 'In a large kettle, combine the soaked beans with onion, garlic, bay leaf, seasoned salt, Kosher salt, pepper, and ham hocks; cover with cold water. Bring to boil, reduce heat and simmer, for about 2 to 3 hours, or until done.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0A949E670D94349B92F6A4964E3EDE9', X'C08F58395573264EA47EE1A707D510B8', X'C08F58395573264EA47EE1A707D510B8', 2, NULL, NULL, 2, NULL, 0, 'About 30 minutes before the dish is done, add sliced smoked sausage, if using. Continue cooking for about 30 minutes longer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'039F6553FC94C0488A4C9382450D7C27', X'C08F58395573264EA47EE1A707D510B8', X'C08F58395573264EA47EE1A707D510B8', 3, NULL, NULL, 2, NULL, 0, 'Serve the beans with hot cooked rice.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'500BE6905DB81641893FD0ACB64D69A3', X'EB25DB736A934C45B12DD6A5605E21A1', X'EB25DB736A934C45B12DD6A5605E21A1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'719F7476FE53674192B3DA5D3B86EB45', X'EB25DB736A934C45B12DD6A5605E21A1', X'EB25DB736A934C45B12DD6A5605E21A1', 1, NULL, NULL, 2, NULL, 0, 'Preheat your air fryer to 375 F. Add the hot dogs to the basked of your air fryer in one even layer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4071E5C1EAF7F343AD526B9B1634A7A4', X'EB25DB736A934C45B12DD6A5605E21A1', X'EB25DB736A934C45B12DD6A5605E21A1', 2, NULL, NULL, 2, NULL, 0, 'Place the insert in the air fryer and air fry for five minutes. You can shake the basket to turn the hot dogs half way through cooking.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F532E3470D0D7D4086CAD5492C5B71B1', X'EB25DB736A934C45B12DD6A5605E21A1', X'EB25DB736A934C45B12DD6A5605E21A1', 3, NULL, NULL, 2, NULL, 0, 'If you like your buns to be toasted, then add the hot dogs to the buns and add them back into the air fryer for another 2 minutes. This gives the buns a little crisp and wont overcook the hot dogs.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6D7688C3F092A49BF68CDD4089D25CB', X'EB25DB736A934C45B12DD6A5605E21A1', X'EB25DB736A934C45B12DD6A5605E21A1', 4, NULL, NULL, 2, NULL, 0, 'Top with your favorite toppings and enjoy immediately.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60FF5F19530EB448BBE03FFF0DC7C72E', X'EB25DB736A934C45B12DD6A5605E21A1', X'EB25DB736A934C45B12DD6A5605E21A1', 5, NULL, NULL, 2, NULL, 0, 'Preparing them from frozen.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'920E99A6FE3CF844806C17D14BC5BF8C', X'C1292D16181E1C46AFAFD52937DCD88D', X'C1292D16181E1C46AFAFD52937DCD88D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat the oven to 375 F/190 C.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F32DC46D2FB8334BA4681D7033CFE1A2', X'C1292D16181E1C46AFAFD52937DCD88D', X'C1292D16181E1C46AFAFD52937DCD88D', 1, NULL, NULL, 2, NULL, 0, 'Lightly grease a baking tray and cover with baking parchment. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A80C5727AB2C764B883DC1031D69E1D1', X'C1292D16181E1C46AFAFD52937DCD88D', X'C1292D16181E1C46AFAFD52937DCD88D', 2, NULL, NULL, 2, NULL, 0, 'On a chopping board, lay the slices of streaky bacon side by side. Using the back of a dinner knife, stretch the bacon as far as it will go without tearing it. Cut each slice into thirds.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89A98B2048D5424C9BE147343F8E13F3', X'C1292D16181E1C46AFAFD52937DCD88D', X'C1292D16181E1C46AFAFD52937DCD88D', 3, NULL, NULL, 2, NULL, 0, 'Take each cocktail sausage and wrap it tightly with the bacon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1DF4EE9C225064697C011DBFCEBE8B0', X'C1292D16181E1C46AFAFD52937DCD88D', X'C1292D16181E1C46AFAFD52937DCD88D', 4, NULL, NULL, 2, NULL, 0, 'Lay the wrapped sausage on the prepared baking tray with the seam underneath. Continue until you have used up all the bacon and sausages.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F00713245B87B34AB4EE7A08C6E13DF3', X'C1292D16181E1C46AFAFD52937DCD88D', X'C1292D16181E1C46AFAFD52937DCD88D', 5, NULL, NULL, 2, NULL, 0, 'At this point, the tray can be covered and placed in the refrigerator to cook later.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CA349552BDB3D4B8B6B3BF529EF9997', X'C1292D16181E1C46AFAFD52937DCD88D', X'C1292D16181E1C46AFAFD52937DCD88D', 6, NULL, NULL, 2, NULL, 0, 'As soon as you need your pigs in a blanket, pop them into the oven and cook for about 20 minutes or until the bacon is crisp and golden and the sausage is cooked through.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB854CD696CADC44902E9EFF1A448BFB', X'C1292D16181E1C46AFAFD52937DCD88D', X'C1292D16181E1C46AFAFD52937DCD88D', 7, NULL, NULL, 2, NULL, 0, 'Serve alongside your roast turkey or goose on the Christmas table or even cold the next day on your Boxing Day buffet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12836C576E6C564FB995D0C0920CF964', X'6422237DF730AF42988290B3D1515491', X'6422237DF730AF42988290B3D1515491', 0, NULL, NULL, 2, NULL, 0, 'Brown the breakfast sausage in a large pan. Be careful not to overcook as it will be cooked in the crock pot as well. Drain the excess oil and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93D564BC6AEE784AB5563CA247B42AAF', X'6422237DF730AF42988290B3D1515491', X'6422237DF730AF42988290B3D1515491', 1, NULL, NULL, 2, NULL, 0, 'Grease the crock pot with a non-stick spray. Place the frozen hash browns in the bottom of the crock pot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F519DEACB8A60043A9E52335C8040392', X'6422237DF730AF42988290B3D1515491', X'6422237DF730AF42988290B3D1515491', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle the onions and green pepper over the top of the potatoes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8E9BB025052C844A57C72F930F74176', X'6422237DF730AF42988290B3D1515491', X'6422237DF730AF42988290B3D1515491', 3, NULL, NULL, 2, NULL, 0, 'Beat the eggs together with the cheese, hot sauce, pepper, and season salt. Pour the eggs over the onions and potatoes. Then top with the cooked breakfast sausage.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CBAB599A719D948AFDBBBD6DFF22F65', X'6422237DF730AF42988290B3D1515491', X'6422237DF730AF42988290B3D1515491', 4, NULL, NULL, 2, NULL, 0, 'Cook on low, overnight. The low setting cooks for eight hours; be careful not to let it cook much longer than eight hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FB4B1B7FE2498449CA8088198B13F5E', X'6422237DF730AF42988290B3D1515491', X'6422237DF730AF42988290B3D1515491', 5, NULL, NULL, 2, NULL, 0, 'Open the crock pot and stir the ingredients together so that they are completely combined.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8997F5980897EA48A1D9A7FD71862225', X'6422237DF730AF42988290B3D1515491', X'6422237DF730AF42988290B3D1515491', 6, NULL, NULL, 2, NULL, 0, 'Warm the tortillas, place the sliced avocado, shredded cheese, sour cream, and salsa into bowls. Allow guests to assemble their burritos on their own.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF976E0BFCC9414585801CEE5826D7B0', X'6422237DF730AF42988290B3D1515491', X'6422237DF730AF42988290B3D1515491', 7, NULL, NULL, 2, NULL, 0, 'If you are making them to freeze, place a 1/2 cup of filling into the center of each tortilla and top with any combination of toppings that you wish. (Note: Avocado does not freeze as well, so be mindful of that.) Roll and fold the burrito, wrap with plastic wrap, and then wrap with aluminum foil. Place in the freezer. When ready to reheat, remove the foil and microwave for 2 to 3 minutes. The burritos will keep frozen for 6 to 8 months.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5ED26EB839EA0D47B570B491F1F8CF7E', X'847EB4C954F181488617A277DF830427', X'847EB4C954F181488617A277DF830427', 0, NULL, NULL, 2, NULL, 0, 'One hour ahead of time, prepare the rice and lotus leaves. Soak the lotus leaves in hot water for 1 hour. Pat dry. Cover the rice with water and let soak for 1 hour. Drain.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'619F14E955629248BA30EBA07F3A9B66', X'847EB4C954F181488617A277DF830427', X'847EB4C954F181488617A277DF830427', 1, NULL, NULL, 2, NULL, 0, 'Next, steam the rice. Line a bamboo steamer with parchment paper or cabbage leaf. Fill a wok approximately to the halfway point with water so that the steamer will be sitting above the water without touching. Bring the water to a boil cover the rice and steam for about 20 minutes. Remove the rice, cover and keep warm while preparing the remainder of the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1731E9F884E45C4F9DF597518316C1EF', X'847EB4C954F181488617A277DF830427', X'847EB4C954F181488617A277DF830427', 2, NULL, NULL, 2, NULL, 0, 'Soften the dried mushrooms by soaking in hot water for 20 to 30 minutes. Squeeze out any excess water, remove the stems and finely chop.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'501176C0CD5AB24096A73F6098D5409B', X'847EB4C954F181488617A277DF830427', X'847EB4C954F181488617A277DF830427', 3, NULL, NULL, 2, NULL, 0, 'Cut the chicken into small cubes about the size of a postage stamp. Add the salt, 1 tablespoon rice wine and 1 teaspoon cornstarch. Marinate the chicken for 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72B450C70B4D4A449FD57A81B2FD2F1D', X'847EB4C954F181488617A277DF830427', X'847EB4C954F181488617A277DF830427', 4, NULL, NULL, 2, NULL, 0, 'Finely chop the sausages. Peel and chop the garlic. In a small bowl, combine the rice wine, light soy, and dark soy sauce. In a separate small bowl, dissolve the cornstarch in the water, and whisk into the sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC841C9D0E93DF419BD4A3E7D63C47DE', X'847EB4C954F181488617A277DF830427', X'847EB4C954F181488617A277DF830427', 5, NULL, NULL, 2, NULL, 0, 'Heat a wok and add 2 tablespoons oil. When the oil is hot, add the garlic and stir-fry until aromatic (about 30 seconds). Add the chicken cubes. Stir-fry until they turn white and are 80 percent cooked through.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5505E35394EAE648896E0FA09550F697', X'847EB4C954F181488617A277DF830427', X'847EB4C954F181488617A277DF830427', 6, NULL, NULL, 2, NULL, 0, 'Add the sausages and the mushrooms. Stir-fry for a minute. Give the sauce mixture a quick re-stir then add in the middle, stirring quickly to thicken. Season with pepper to taste. Cook for 1 to 2 more minutes to mix everything together and heat through. Remove from the heat and stir in the sesame oil. Cool.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01BAFDC3887DF54CA1913E8432EC2CD8', X'847EB4C954F181488617A277DF830427', X'847EB4C954F181488617A277DF830427', 7, NULL, NULL, 2, NULL, 0, 'To make the wraps, separate the rice and the filling into 8 equal sections, 1 section for each wrap. Lay a lotus leaf in front of you. Place a portion of the rice mixture into the center of a lotus leaf. Add the meat and vegetable mixture over top, shaping the rice with your hands so that it forms a ring around the filling. Add more rice to cover.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAF95AEA6EBA42429049751F5771D3B2', X'847EB4C954F181488617A277DF830427', X'847EB4C954F181488617A277DF830427', 8, NULL, NULL, 2, NULL, 0, 'Form a square parcel with the lotus leaf and tie it up with twine. Repeat with the remaining lotus leaves.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5544BEA420EDB24392CC9F4A5FBD3255', X'847EB4C954F181488617A277DF830427', X'847EB4C954F181488617A277DF830427', 9, NULL, NULL, 2, NULL, 0, 'Steam the lotus leaf parcels, covered, on a heatproof plate in a bamboo steamer for 15 minutes, or until they are done.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7840A9FA07C1E43817A4252F6999A8C', X'B09B981F3783B94490631D4DD76E084C', X'B09B981F3783B94490631D4DD76E084C', 0, NULL, NULL, 2, NULL, 0, 'Butter a 9-by-13-inch baking dish. Heat the oven to 325 F (165 C/Gas 3).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'230FC580B82369468072DB97D8B3A162', X'B09B981F3783B94490631D4DD76E084C', X'B09B981F3783B94490631D4DD76E084C', 1, NULL, NULL, 2, NULL, 0, 'Cube bread and place evenly in the prepared baking dish. Sprinkle evenly with sausage and cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD10D3710B54954B80DF48E8A3BDECC4', X'B09B981F3783B94490631D4DD76E084C', X'B09B981F3783B94490631D4DD76E084C', 2, NULL, NULL, 2, NULL, 0, 'In a bowl, whisk the eggs with the milk, salt, and pepper. Pour over the layers in the pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'133DC58E4BE72548BEA805204688657E', X'B09B981F3783B94490631D4DD76E084C', X'B09B981F3783B94490631D4DD76E084C', 3, NULL, NULL, 2, NULL, 0, 'Bake 45 minutes or until set. Cut into squares to serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'221324F529BB80459D496B522C1A8411', X'B09B981F3783B94490631D4DD76E084C', X'B09B981F3783B94490631D4DD76E084C', 4, NULL, NULL, 2, NULL, 0, 'This casserole may also be prepared the night before. Just cover and refrigerate the unbaked casserole. Take it out of the refrigerator about 15 minutes before baking time; bake as directed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0CF56D01E2ED147AB8188254F954D5D', X'3F2447309094584A821FA08D7DE7EE2E', X'3F2447309094584A821FA08D7DE7EE2E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73EA3247EB0D674EBCD59795455E8B0B', X'3F2447309094584A821FA08D7DE7EE2E', X'3F2447309094584A821FA08D7DE7EE2E', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven for 350 F/175 C.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF3079A1C4B5B849967FF4D5F8029BFB', X'3F2447309094584A821FA08D7DE7EE2E', X'3F2447309094584A821FA08D7DE7EE2E', 2, NULL, NULL, 2, NULL, 0, 'Roll out one of the dough balls to form a 14-inch pizza round.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C68119FD3162794CB34BF5EA9E103747', X'3F2447309094584A821FA08D7DE7EE2E', X'3F2447309094584A821FA08D7DE7EE2E', 3, NULL, NULL, 2, NULL, 0, 'Transfer to pizza stone and place it into the oven. Bake for 5 to 6 minutes until the dough has firmed up a bit.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E51B5CC27E1F1641AED95B695B0D3D4F', X'3F2447309094584A821FA08D7DE7EE2E', X'3F2447309094584A821FA08D7DE7EE2E', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, roll out the second dough ball.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'459E9B918E14D3478729CB41355644A3', X'3F2447309094584A821FA08D7DE7EE2E', X'3F2447309094584A821FA08D7DE7EE2E', 5, NULL, NULL, 2, NULL, 0, 'Remove the first crust from oven.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1941F6D4B8EFC42BD238A63AE5A7FF8', X'3F2447309094584A821FA08D7DE7EE2E', X'3F2447309094584A821FA08D7DE7EE2E', 6, NULL, NULL, 2, NULL, 0, 'Warm pulled pork (do not make it hot) combine with 1/2 cup barbecue sauce (you can add another 1/4 cup if the meat is too dry).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65A2A970197F6045AF2E64DA85215C01', X'3F2447309094584A821FA08D7DE7EE2E', X'3F2447309094584A821FA08D7DE7EE2E', 7, NULL, NULL, 2, NULL, 0, 'Place onto semi-cooked pizza dough making sure to spread evenly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61F86608D57BD048809B43B8A057FD65', X'3F2447309094584A821FA08D7DE7EE2E', X'3F2447309094584A821FA08D7DE7EE2E', 8, NULL, NULL, 2, NULL, 0, 'Add uncooked rolled dough on top. It will be bigger since the partially cooked dough will shrink a bit in the oven. Gently tuck excess dough underneath, pressing to seal edges.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D55EFB662BC59B46959FE93CFFD1DF89', X'3F2447309094584A821FA08D7DE7EE2E', X'3F2447309094584A821FA08D7DE7EE2E', 9, NULL, NULL, 2, NULL, 0, 'Place into the oven for 5 to 6 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A42C02A70DACBC43A63AB1DEB5422438', X'3F2447309094584A821FA08D7DE7EE2E', X'3F2447309094584A821FA08D7DE7EE2E', 10, NULL, NULL, 2, NULL, 0, 'Remove from oven and coat top with 1 cup of barbecue sauce. Add chopped brisket, chopped sausage, sliced onion, and cheese. Brush olive oil on edges of the crust.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8713374A4E88C449AA60457C6ED0DC97', X'3F2447309094584A821FA08D7DE7EE2E', X'3F2447309094584A821FA08D7DE7EE2E', 11, NULL, NULL, 2, NULL, 0, 'Place pizza back into the oven for 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12A244DFDD2E424986634D78A25F5A05', X'3F2447309094584A821FA08D7DE7EE2E', X'3F2447309094584A821FA08D7DE7EE2E', 12, NULL, NULL, 2, NULL, 0, 'Once cooked through and crust takes on a golden brown color, remove from oven and let sit for a few minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1ECE297E265B24CA728D4AA4F1F8281', X'3F2447309094584A821FA08D7DE7EE2E', X'3F2447309094584A821FA08D7DE7EE2E', 13, NULL, NULL, 2, NULL, 0, 'Slice pizza, and serve. Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77E417E46C464C46A41B2BD8F4F8772F', X'A1026BFD8F63C54EAAD86466721370C7', X'A1026BFD8F63C54EAAD86466721370C7', 0, NULL, NULL, 2, NULL, 0, 'Soak the garbanzo beans in a large pot of cold water for 12 hours. (Garbanzo beans are much harder than other types of beans, such as pintos.) Be sure that the water covers the garbanzo beans entirely so that they soften.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82D2DE37DF99154B852DB83AD9B0C4B8', X'A1026BFD8F63C54EAAD86466721370C7', X'A1026BFD8F63C54EAAD86466721370C7', 1, NULL, NULL, 2, NULL, 0, 'In a large pot with 13 cups of water, put the beef, ham bone, pig''s foot, bacon, and ham. Bring water to a boil and skim any fat, etc. off the top. Add the half chicken, garbanzo beans, carrot, turnip, and onion. Simmer for 2 1/2 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'541D2692B071014A92830EB0F0C67FC8', X'A1026BFD8F63C54EAAD86466721370C7', X'A1026BFD8F63C54EAAD86466721370C7', 2, NULL, NULL, 2, NULL, 0, 'Peel and wash the potatoes. Cut in half and add to the pot after two hours. Add salt to taste. Check pot often and add additional water if too much evaporates.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36532CCB1B589B4882E8A6BE8DBF1F5F', X'A1026BFD8F63C54EAAD86466721370C7', X'A1026BFD8F63C54EAAD86466721370C7', 3, NULL, NULL, 2, NULL, 0, 'Chop the cabbage into eighths. Remove 2 cups of broth from the stew and put into a large stockpot. Place the cabbage in the pot with 2 cups broth and 2 cups water and boil with the sausages until cabbage is soft.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7DD21049D477545ACA77CC6F229AF1D', X'A1026BFD8F63C54EAAD86466721370C7', X'A1026BFD8F63C54EAAD86466721370C7', 4, NULL, NULL, 2, NULL, 0, 'Take as much stock from the pot of meat as you need and mix it with an equal amount of stock from the cabbage. Bring to a boil. Break angel hair pasta pieces in quarters. Add pasta to pot and cook until al dente.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23FF381F66F63F4885DDA008EAAE7810', X'A1026BFD8F63C54EAAD86466721370C7', X'A1026BFD8F63C54EAAD86466721370C7', 5, NULL, NULL, 2, NULL, 0, 'This delicious cocoa madrilene is served from two dishes. Serve up the pasta with broth in soup bowls as a first course.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C54739B230EC974DAC256212762C4045', X'A1026BFD8F63C54EAAD86466721370C7', X'A1026BFD8F63C54EAAD86466721370C7', 6, NULL, NULL, 2, NULL, 0, 'In a deep open serving dish, arrange garbanzo beans (well-drained) on one side, then on the other side place beef, cabbage and sausages cut in half.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A4FE3DEB5A38648866747F7812E2390', X'A1026BFD8F63C54EAAD86466721370C7', X'A1026BFD8F63C54EAAD86466721370C7', 7, NULL, NULL, 2, NULL, 0, 'Let everyone mix the ingredients and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99477F1AF7BF8C45957E7CA73B0B5ACA', X'5A8D5B1E1680A640B96021203DD966AE', X'5A8D5B1E1680A640B96021203DD966AE', 0, NULL, NULL, 2, NULL, 0, 'Place the sauerkraut, wine, butter, thyme, and bay leaf in a saucepan and bring to the boil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEEF1B67AD42C546BB2EE7F27D35C756', X'5A8D5B1E1680A640B96021203DD966AE', X'5A8D5B1E1680A640B96021203DD966AE', 1, NULL, NULL, 2, NULL, 0, 'Turn the heat down and leave to simmer for 15 minutes, stirring every now and then.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6556C8C90E69294C8EE9B155796C8A58', X'5A8D5B1E1680A640B96021203DD966AE', X'5A8D5B1E1680A640B96021203DD966AE', 2, NULL, NULL, 2, NULL, 0, 'Season to taste with salt & pepper and allow to cool.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8E86F8A464BE94391D5803123F2D899', X'5A8D5B1E1680A640B96021203DD966AE', X'5A8D5B1E1680A640B96021203DD966AE', 3, NULL, NULL, 2, NULL, 0, 'Remove the bay leaf.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBD68C4EF5525C43B638CE0D38885837', X'5A8D5B1E1680A640B96021203DD966AE', X'5A8D5B1E1680A640B96021203DD966AE', 4, NULL, NULL, 2, NULL, 0, 'Mix the mayonnaise, mustard, chives, and parsley together in a small bowl. Set aside until later.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'757A05E2B636A948B3D686EEB134E100', X'5A8D5B1E1680A640B96021203DD966AE', X'5A8D5B1E1680A640B96021203DD966AE', 5, NULL, NULL, 2, NULL, 0, 'Heat the rookworsten (see Tips).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E33BD299F2FCA47907569FA638908A1', X'5A8D5B1E1680A640B96021203DD966AE', X'5A8D5B1E1680A640B96021203DD966AE', 6, NULL, NULL, 2, NULL, 0, 'Cut the sausages on a deep diagonal into thin, broad slices. Split the bread rolls and spread generously with the mustard-mayonnaise.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E9CC320FF93584F803752EA180B6C59', X'5A8D5B1E1680A640B96021203DD966AE', X'5A8D5B1E1680A640B96021203DD966AE', 7, NULL, NULL, 2, NULL, 0, 'Top with sauerkraut and the slices of rookworst. Serve immediately.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B84EF35E3BF024FBC84F0E80AD91BA7', X'AE3840BA4BA07F439DACF2BFD901CAC3', X'AE3840BA4BA07F439DACF2BFD901CAC3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C900DE70E559346BAEB8169CF10A96A', X'AE3840BA4BA07F439DACF2BFD901CAC3', X'AE3840BA4BA07F439DACF2BFD901CAC3', 1, NULL, NULL, 2, NULL, 0, 'Remove the core from the cabbage. Chop the leaves crosswise into even pieces about 1 1/2 inches wide. Thinly slice the sausage on the diagonal. Chop the green onion into 1-inch pieces.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6673D033B52E1409BDF321919A1E92D', X'AE3840BA4BA07F439DACF2BFD901CAC3', X'AE3840BA4BA07F439DACF2BFD901CAC3', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the chicken broth, hot bean sauce, and sugar. In a separate small bowl, stir together the cornstarch and water.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88D939B823D221449C0712164B2FE70F', X'AE3840BA4BA07F439DACF2BFD901CAC3', X'AE3840BA4BA07F439DACF2BFD901CAC3', 3, NULL, NULL, 2, NULL, 0, 'Preheat a wok over medium-high heat and add 2 tablespoons oil. When the oil is hot, add the garlic and salt. Stir for 10 - 15 seconds.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0A99F6D03EB3E49BEB0AD0CB9B361C9', X'AE3840BA4BA07F439DACF2BFD901CAC3', X'AE3840BA4BA07F439DACF2BFD901CAC3', 4, NULL, NULL, 2, NULL, 0, 'Add the lower white parts of the green onion, and the sausage. Stir-fry the sausage for 2 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'694DDCFF455E0D4FB1B4713EB2A0E080', X'AE3840BA4BA07F439DACF2BFD901CAC3', X'AE3840BA4BA07F439DACF2BFD901CAC3', 5, NULL, NULL, 2, NULL, 0, 'Push the sausage to the sides of the pan. Add the chopped cabbage. Sprinkle the rice wine or sherry over. Stir-fry for about 1 1/2 minutes, until the green leaves darken slightly.');
