INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E37AABD581597B4A8FB762D7AC57845B', X'0A40FC7E60DCDB4BA3B3C60782E8E1D1', X'0A40FC7E60DCDB4BA3B3C60782E8E1D1', 5, NULL, NULL, 2, NULL, 0, 'Bake the quiche in the preheated oven for 30 to 35 minutes, or until a knife inserted in center comes out clean. Remove to a rack and let stand for 8 to 10 minutes before slicing and serving.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0697AC78780974FAC0A8E4B44B20BE0', X'085ECCA8DD9AAE4D81C5C2DF0E85BDB7', X'085ECCA8DD9AAE4D81C5C2DF0E85BDB7', 0, NULL, NULL, 2, NULL, 0, 'Put the beans in a large bowl and cover with water to a depth of about 2 to 3 inches above the beans. The beans will absorb some water as they soak. Cover the bowl and let stand at room temperature overnight (or at least 4 to 6 hours).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3866EC2461A67347A653641DBDB42CA0', X'085ECCA8DD9AAE4D81C5C2DF0E85BDB7', X'085ECCA8DD9AAE4D81C5C2DF0E85BDB7', 1, NULL, NULL, 2, NULL, 0, 'Drain the beans thoroughly and transfer them to a stockpot or Dutch oven.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6EB1C92175B2041B47238ED58CCB221', X'085ECCA8DD9AAE4D81C5C2DF0E85BDB7', X'085ECCA8DD9AAE4D81C5C2DF0E85BDB7', 2, NULL, NULL, 2, NULL, 0, 'Add fresh water to coverabout 6 cupsalong with the sausage and ham, onion, garlic, thyme, pepper, sage, and cayenne. Bring the beans to boil over medium-high heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D335E3ACE2171D4AB8B1D6E06CDDC9B1', X'085ECCA8DD9AAE4D81C5C2DF0E85BDB7', X'085ECCA8DD9AAE4D81C5C2DF0E85BDB7', 3, NULL, NULL, 2, NULL, 0, 'Reduce heat to low and cover the pan. Simmer the beans until they are tender, adding more water if necessary to keep them from drying out. This will take 2 to 3 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1F05E58C5E3354283C33876BFF5BC1B', X'085ECCA8DD9AAE4D81C5C2DF0E85BDB7', X'085ECCA8DD9AAE4D81C5C2DF0E85BDB7', 4, NULL, NULL, 2, NULL, 0, 'Add salt to taste. Discard the ham bones.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6C358549D6C0149874DC4B121E02A1A', X'085ECCA8DD9AAE4D81C5C2DF0E85BDB7', X'085ECCA8DD9AAE4D81C5C2DF0E85BDB7', 5, NULL, NULL, 2, NULL, 0, 'Remove about 1/4 cup of beans from the mixture and mash them. Return the mashed beans to the Dutch oven and stir to blend.');
