INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9061FE74378306428B79F57702D1841D', X'B98402DEDFE6EC4997360A07F234B62E', X'B98402DEDFE6EC4997360A07F234B62E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
