INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F78CC4CF906FF47B65934149EE52FB5', X'B98402DEDFE6EC4997360A07F234B62E', X'B98402DEDFE6EC4997360A07F234B62E', 5, NULL, NULL, 2, NULL, 0, 'Divide the sausage meat into 4 equal portions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'734D281C17EFD7438DEDC81A3CED103B', X'B98402DEDFE6EC4997360A07F234B62E', X'B98402DEDFE6EC4997360A07F234B62E', 6, NULL, NULL, 2, NULL, 0, 'Form each portion into a flat cake large enough to fit around the egg. Work the sausage meat around the egg as evenly as possible whilst working hard to keep the egg shape and making sure there are no cracks. Place the meat coated eggs in the fridge for 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1197CEAC0EB13F49BECEF795C0F15C7F', X'B98402DEDFE6EC4997360A07F234B62E', X'B98402DEDFE6EC4997360A07F234B62E', 7, NULL, NULL, 2, NULL, 0, 'Roll the boiled eggs in the seasoned flour followed by the beaten egg, then into the crumbs and press lightly to ensure a good ''fix''.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8897317E2BEE4C4886182238DBBC0D90', X'B98402DEDFE6EC4997360A07F234B62E', X'B98402DEDFE6EC4997360A07F234B62E', 8, NULL, NULL, 2, NULL, 0, 'Heat 4 cm / 1 1/2` of oil in a deep frying pan. Heat the oil to 180 C / 350 F (or until a cube of bread turns golden brown) in less than a minute. Place the coated eggs in the oil, raise the heat slightly and cook the eggs, turning frequently for 3 minutes. Remove the eggs and then place on a tray and pop into the preheated oven for 6 minutes or until golden brown all over.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EF103F750785848A573D34F5FA802D9', X'B98402DEDFE6EC4997360A07F234B62E', X'B98402DEDFE6EC4997360A07F234B62E', 9, NULL, NULL, 2, NULL, 0, 'Drain on kitchen paper and serve warm or cool if for a picnic or lunch box.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFE02DDA3E8B304BA951F722A3C10888', X'B98402DEDFE6EC4997360A07F234B62E', X'B98402DEDFE6EC4997360A07F234B62E', 10, NULL, NULL, 2, NULL, 0, 'Scotch eggs can be made the day before and kept in the fridge.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82B217EA54A06D45A58500B160A7AE72', X'683B53F36FDCF344BF7753111E9991A9', X'683B53F36FDCF344BF7753111E9991A9', 0, NULL, NULL, 2, NULL, 0, 'Brown sausage well then drain off grease. Add bread cubes, apples, celery, raisins, onion, 1/4 cup of the melted butter, 2 tablespoons of the chicken broth, sage, poultry seasoning, and salt; mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DE26C9FF3954A4BA1E9C9464127154C', X'683B53F36FDCF344BF7753111E9991A9', X'683B53F36FDCF344BF7753111E9991A9', 1, NULL, NULL, 2, NULL, 0, 'In a separate or cup, combine remaining 1/2 cup melted butter and 3 tablespoons chicken broth. Set aside to use for basting.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDB536265AF51C4FAFD81AAF456C005C', X'683B53F36FDCF344BF7753111E9991A9', X'683B53F36FDCF344BF7753111E9991A9', 2, NULL, NULL, 2, NULL, 0, 'Lightly stuff Cornish hens with dressing mixture; close the openings with skewers. Place hens in a large roasting pan; bake at 400 F for 1 hour, or until the Cornish hens are tender. Baste Cornish hens occasionally with combined remaining margarine and chicken broth.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEE1953518572A4CA975993DB121EC38', X'A05755541C81DD4AAA9F5A6E9B28C81C', X'A05755541C81DD4AAA9F5A6E9B28C81C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
