INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A97F46EBBB5A4944B479035B23A0C496', X'170A348DA352FD4ABEF8D1AAC4341413', X'170A348DA352FD4ABEF8D1AAC4341413', 2, NULL, NULL, 2, NULL, 0, 'Leave 3 tablespoons of drippings in the skillet. If there aren''t 3 tablespoons left, add some butter, shortening, or bacon drippings to make 3 tablespoons.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F4D2D9D8D63F5469B2C2685CE0A71E5', X'170A348DA352FD4ABEF8D1AAC4341413', X'170A348DA352FD4ABEF8D1AAC4341413', 3, NULL, NULL, 2, NULL, 0, 'Place the skillet back over medium heat and sprinkle the flour over the drippings. Cook, stirring constantly until the roux is lightly browned.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75A272D40D733545BF0AE36122F3ED74', X'170A348DA352FD4ABEF8D1AAC4341413', X'170A348DA352FD4ABEF8D1AAC4341413', 4, NULL, NULL, 2, NULL, 0, 'Gradually add 1 cup of the milk. Stir to loosen any bits of cooked sausage from the bottom of the pan.');
