INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'655FC8D173A71D49A347C675F2A9F7FD', X'170A348DA352FD4ABEF8D1AAC4341413', X'170A348DA352FD4ABEF8D1AAC4341413', 6, NULL, NULL, 2, NULL, 0, 'Add the sausage to the gravy, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2259095B8C6B704F99A6C3D3BBAB3DF0', X'170A348DA352FD4ABEF8D1AAC4341413', X'170A348DA352FD4ABEF8D1AAC4341413', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy on top of biscuits.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5EF8018189DE54A84D997820C80F44C', X'28200CD4A322DC4D9DEBB5C0F352F1D5', X'28200CD4A322DC4D9DEBB5C0F352F1D5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65BF739725C2304D85039A24B5367978', X'28200CD4A322DC4D9DEBB5C0F352F1D5', X'28200CD4A322DC4D9DEBB5C0F352F1D5', 1, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large Dutch oven or kettle over medium heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26DC391AF286D5418C77D09224622361', X'28200CD4A322DC4D9DEBB5C0F352F1D5', X'28200CD4A322DC4D9DEBB5C0F352F1D5', 2, NULL, NULL, 2, NULL, 0, 'Add onion, carrots, celery, and sliced sausage to the pan. Saut for 3 to 4 minutes, or until onion is wilted.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07D58EB46FDCBC46839458F25C032D25', X'28200CD4A322DC4D9DEBB5C0F352F1D5', X'28200CD4A322DC4D9DEBB5C0F352F1D5', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic and cook, stirring, for 1 more minute.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'124C7C88D1AF874FBDCEE12AA8E4E4A5', X'28200CD4A322DC4D9DEBB5C0F352F1D5', X'28200CD4A322DC4D9DEBB5C0F352F1D5', 4, NULL, NULL, 2, NULL, 0, 'Add the cabbage, apple juice, broth, vinegar, brown sugar, and pepper. Simmer uncovered for 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F1FEC03E6D64F4D810C2BAF0E204AC6', X'28200CD4A322DC4D9DEBB5C0F352F1D5', X'28200CD4A322DC4D9DEBB5C0F352F1D5', 5, NULL, NULL, 2, NULL, 0, 'Cover and simmer for 45 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DB1117BB8DFDA40AC2E82FA384DF2AC', X'28200CD4A322DC4D9DEBB5C0F352F1D5', X'28200CD4A322DC4D9DEBB5C0F352F1D5', 6, NULL, NULL, 2, NULL, 0, 'Add the beans, parsley, and tomatoes. Taste for salt.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83C70166B3810348967C1416ACFA647A', X'28200CD4A322DC4D9DEBB5C0F352F1D5', X'28200CD4A322DC4D9DEBB5C0F352F1D5', 7, NULL, NULL, 2, NULL, 0, 'Cover and simmer for 15 more minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'620FB8A0F7944548BEA8A5FFAB212146', X'28200CD4A322DC4D9DEBB5C0F352F1D5', X'28200CD4A322DC4D9DEBB5C0F352F1D5', 8, NULL, NULL, 2, NULL, 0, 'Ladle in individual bowls and garnish with parsley.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92827FD9F1019B4189552777908B1069', X'28200CD4A322DC4D9DEBB5C0F352F1D5', X'28200CD4A322DC4D9DEBB5C0F352F1D5', 9, NULL, NULL, 2, NULL, 0, 'Enjoy hot with freshly baked bread or biscuits.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F0C8D8DEB3C86469AD3370C46298CC7', X'09CA87FC67A2E4478D1C8D579D375940', X'09CA87FC67A2E4478D1C8D579D375940', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1596C0173205949A2DD576C62D87AC4', X'09CA87FC67A2E4478D1C8D579D375940', X'09CA87FC67A2E4478D1C8D579D375940', 1, NULL, NULL, 2, NULL, 0, 'Fire up the grill.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A97FF98588CCD149B958A98D6416A530', X'09CA87FC67A2E4478D1C8D579D375940', X'09CA87FC67A2E4478D1C8D579D375940', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, mix the softened cream cheese and shredded colby jack together. Transfer to a pastry bag fitted with a round tip. If you don''t have a pastry bag, use a zip-lock freezer bag, then use scissors to cut away one of the corners.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'162BA4301C5F1243BEC1E62B355AA3AB', X'09CA87FC67A2E4478D1C8D579D375940', X'09CA87FC67A2E4478D1C8D579D375940', 3, NULL, NULL, 2, NULL, 0, 'Cut off the stem end of the jalapenos. Use a small, thin knife - such as a grapefruit or steak knife - to remove the seeds and soft white membranes inside the peppers, leaving a hollow cone, open at one end. Take care not to split the peppers.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B8AA0D336EDEE41BE00182E20F3E6C1', X'09CA87FC67A2E4478D1C8D579D375940', X'09CA87FC67A2E4478D1C8D579D375940', 4, NULL, NULL, 2, NULL, 0, 'Pipe the cheese mixture into the peppers.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5664F8F37EE56D4CA816C7D3853274C0', X'09CA87FC67A2E4478D1C8D579D375940', X'09CA87FC67A2E4478D1C8D579D375940', 5, NULL, NULL, 2, NULL, 0, 'Divide the sausage into 12 4-ounce portions. Flatten each of the portions into a thin oval, then wrap the sausage around the stuffed peppers, encasing them completely. If your jalapenos vary in size and shape you may have to make some adjustments in order to fully wrap them.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'222CA477158A39438F0DEA92C1E29DAE', X'09CA87FC67A2E4478D1C8D579D375940', X'09CA87FC67A2E4478D1C8D579D375940', 6, NULL, NULL, 2, NULL, 0, 'Sear the armadillo eggs on the grill with direct heat for about 2 minutes on each side, then reduce the heat and move them to a spot with indirect heat, if possible, and close the grill. Cook for about 15 minutes, or until the cheese begins to escape, bubbling through its sausage enclosure. Remove from the grill and serve immediately.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D49FEA793FD9143B3C1B67670F1B556', X'CC8F7C899CA58E45AD99ABFD00B0C21C', X'CC8F7C899CA58E45AD99ABFD00B0C21C', 0, NULL, NULL, 2, NULL, 0, 'Brine the pork tenderloin: combine the Kosher salt, brown sugar, oregano sprigs and water in a large pot over low heat, stirring to dissolve the salt and sugar. Take the pot off the heat and let the brine cool completely (you can add several ice cubes to assist in the cooling down).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEC9745BD6C59544B7CD1B7351F9C8CD', X'CC8F7C899CA58E45AD99ABFD00B0C21C', X'CC8F7C899CA58E45AD99ABFD00B0C21C', 1, NULL, NULL, 2, NULL, 0, 'When the brine is cooled, lay the pork tenderloins in a glass baking dish (or alternatively, in food-storage plastic bags), and cover with the brine (including oregano sprigs). Cover with plastic wrap and refrigerate 4 to 6 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82113865CD05BB41AFF81FBD09099A23', X'CC8F7C899CA58E45AD99ABFD00B0C21C', X'CC8F7C899CA58E45AD99ABFD00B0C21C', 2, NULL, NULL, 2, NULL, 0, 'Prepare the stuffing: heat the olive oil in a small saute pan over medium heat. Add the minced onion, garlic and minced oregano leaves, and saute 2 to 3 minutes until softened. Allow to cool completely, then mix together with the pork sausage, 1/2 teaspoon Kosher salt and several good grinds of fresh pepper in a bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FFEDC6672257E4B94BD7B45C370E86B', X'CC8F7C899CA58E45AD99ABFD00B0C21C', X'CC8F7C899CA58E45AD99ABFD00B0C21C', 3, NULL, NULL, 2, NULL, 0, 'Remove the tenderloins from brine, and discard the brine. Pat the tenderloins dry with paper towels. Cut each tenderloin down the center length-wise, without cutting all the way through, leaving 1/2-inch thickness.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED75B1A866913A47AD23C3509BBBDBF7', X'CC8F7C899CA58E45AD99ABFD00B0C21C', X'CC8F7C899CA58E45AD99ABFD00B0C21C', 4, NULL, NULL, 2, NULL, 0, 'Lay each tenderloin between 2 pieces of plastic wrap, and gently pound to 1/4-inch thickness into a rectangle. Discard the plastic wrap.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B09C7709B6BFD142916CCCAED661C49B', X'CC8F7C899CA58E45AD99ABFD00B0C21C', X'CC8F7C899CA58E45AD99ABFD00B0C21C', 5, NULL, NULL, 2, NULL, 0, 'Spread the sausage mixture evenly over each tenderloin, leaving a 1-inch border all around. Cover the sausage layer with the sliced provolone.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'749898416556FE4C9FA52CD6AD63927E', X'CC8F7C899CA58E45AD99ABFD00B0C21C', X'CC8F7C899CA58E45AD99ABFD00B0C21C', 6, NULL, NULL, 2, NULL, 0, 'With the long side facing you, tightly roll up the tenderloin away from you, tucking in the ends. Secure the tenderloins with kitchen string at 2-inch intervals. Lightly brush the tenderloins with vegetable oil and season with Kosher salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E23423D9390D16498DA9EE7F01E5F2A6', X'CC8F7C899CA58E45AD99ABFD00B0C21C', X'CC8F7C899CA58E45AD99ABFD00B0C21C', 7, NULL, NULL, 2, NULL, 0, 'Heat the grill to high with the cover closed. Keep the far right burner on, but turn off the other burners. Oil the grill grate, then lay the tenderloins on the unlit side. Cover the grill and cook 20 to 25 minutes, until the internal temperature reaches 138F to 140F on an instant-read thermometer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'174131A48BD238489D77F647060997D6', X'CC8F7C899CA58E45AD99ABFD00B0C21C', X'CC8F7C899CA58E45AD99ABFD00B0C21C', 8, NULL, NULL, 2, NULL, 0, 'Transfer the tenderloins to a cutting board and loosely cover with foil for 20 minutes. Cut off the kitchen string, slice the tenderloins into 1/2-inch slices, and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4DB1363C6D88D4A9AD3F58D3A3D633E', X'699D8FB33786574EB735540243E66AED', X'699D8FB33786574EB735540243E66AED', 0, NULL, NULL, 2, NULL, 0, 'Rub the chicken pieces with the paprika.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68FFB183EC20104FA6B3FBD06D6D762C', X'699D8FB33786574EB735540243E66AED', X'699D8FB33786574EB735540243E66AED', 1, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a large skillet; add chicken and brown on all sides. Remove chicken from skillet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED3261762BFBF74099C6533CC96FE19E', X'699D8FB33786574EB735540243E66AED', X'699D8FB33786574EB735540243E66AED', 2, NULL, NULL, 2, NULL, 0, 'Add onion, green bell pepper, celery, and garlic. Saut over low heat until onion is tender, about 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4A3474621CCA947AEAB0BEA2AB276D1', X'699D8FB33786574EB735540243E66AED', X'699D8FB33786574EB735540243E66AED', 3, NULL, NULL, 2, NULL, 0, 'Stir in sausage, tomatoes, chicken broth, rice, thyme, salt, pepper, and hot sauce. Add chicken and turn to coat with sauce. Bring to a boil. Reduce heat, cover, and simmer for 30 minutes or until chicken is tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'608524FF37BAB544BBDF19439F9CA62F', X'699D8FB33786574EB735540243E66AED', X'699D8FB33786574EB735540243E66AED', 4, NULL, NULL, 2, NULL, 0, 'Stir in shrimp and cook about 3 to 5 minutes longer or just until shrimp turns pink.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A30EB97959CC7E428A2703846EB1BD32', X'043BB457BB04E2448B056F7E2C662486', X'043BB457BB04E2448B056F7E2C662486', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06A77C3D9FF846468AACB51F837A3C6C', X'043BB457BB04E2448B056F7E2C662486', X'043BB457BB04E2448B056F7E2C662486', 1, NULL, NULL, 2, NULL, 0, 'Pour beans into a 5- to 6-quart slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FB7BE0D2177904C91A2F348BB0E3666', X'043BB457BB04E2448B056F7E2C662486', X'043BB457BB04E2448B056F7E2C662486', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium heat, saut the beef and pork sausage, breaking up with a spatula, until no longer pink.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A704E4DFFEB2394D91D62C7AF7E54554', X'043BB457BB04E2448B056F7E2C662486', X'043BB457BB04E2448B056F7E2C662486', 3, NULL, NULL, 2, NULL, 0, 'Drain well and stir into beans.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12646B00C2F23A4DAD5D05E70C1578C8', X'043BB457BB04E2448B056F7E2C662486', X'043BB457BB04E2448B056F7E2C662486', 4, NULL, NULL, 2, NULL, 0, 'In the same skillet in a small amount of olive oil as needed, saut the onion over medium heat until lightly browned.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E008E4166F6E941A51833B25BA336C3', X'043BB457BB04E2448B056F7E2C662486', X'043BB457BB04E2448B056F7E2C662486', 5, NULL, NULL, 2, NULL, 0, 'Add red bell pepper and garlic; saute, stirring, for 1 minute longer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0FDB6A86F74734CA2911A2507FC2709', X'043BB457BB04E2448B056F7E2C662486', X'043BB457BB04E2448B056F7E2C662486', 6, NULL, NULL, 2, NULL, 0, 'Stir the vegetables into the beans.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24C3A1113253404FBB08B564A9661C3D', X'043BB457BB04E2448B056F7E2C662486', X'043BB457BB04E2448B056F7E2C662486', 7, NULL, NULL, 2, NULL, 0, 'Add chile pepper, jalapenos, salt, seasoning, and barbecue sauce. Stir to blend.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'585575A2D4826A4AB00EC29A15C7F54A', X'043BB457BB04E2448B056F7E2C662486', X'043BB457BB04E2448B056F7E2C662486', 8, NULL, NULL, 2, NULL, 0, 'Cover and cook on high for 3 to 4 hours or low for 6 to 8 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E378F85845499043B90B86CB1C6FAB68', X'043BB457BB04E2448B056F7E2C662486', X'043BB457BB04E2448B056F7E2C662486', 9, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6659729B8AD4034F84F0A01CD99B3A67', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A51A559D41C554FB2D6AEB0EEEF9D39', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 1, NULL, NULL, 2, NULL, 0, 'When the sausage meat is at room temperature, place it in a large mixing bowl with the chestnuts and mix gently.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF270F10752A2840872E6F8474C28002', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 2, NULL, NULL, 2, NULL, 0, 'Add the breadcrumbs, apricots, truffles, and foie gras (if using). Season with a large pinch of salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3547A9CB5370D540B57DBB4490E53DFA', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 3, NULL, NULL, 2, NULL, 0, 'Place the goose breast-side down and remove any excess fat and sinews. Place a roll of the stuffing down the center of the goose between the breast fillets.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A44A16E8D9F744EBB416B4E5759E2A3', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 4, NULL, NULL, 2, NULL, 0, 'Tuck in the legs and wings, then fold in the skin at the sides and edges over the stuffing. Roll tightly and tie with string.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADBBB937F25AA7449D292651D6D2ABA6', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 5, NULL, NULL, 2, NULL, 0, 'Lay the goose on a sheet of foil and pull the edges together and twist at the ends to form a large sausage.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC9BBA4130243B4896F07CB281371AB7', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 6, NULL, NULL, 2, NULL, 0, 'Place the goose into a large pan. Cover with water, then add the onion, carrots, and celery. Bring to the boil, simmer gently for 1 hour, turning occasionally.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19563C43AE2676458161E8573DA914F1', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 7, NULL, NULL, 2, NULL, 0, 'Remove the packet from the stock, strain the vegetables. Reserve the vegetables and the stock on one side.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DCD4B55C109EA4B84377C19D81A4840', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 8, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 F/190 C/Gas 5. Place the packet in a roasting tin and roast in a moderate oven for 1 hour 30 mins.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D476D6172FECD468379965224828F69', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 9, NULL, NULL, 2, NULL, 0, 'Remove the foil from the goose, return it to the roasting tin, place the giblets, vegetable mixture, garlic, and herbs around the goose and cook for a further 30 minutes. Remove from the oven.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC92E327D0B63848861EC5A9FE56851F', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 10, NULL, NULL, 2, NULL, 0, 'Wrap the cooked goose tightly in a fresh piece of foil and leave on one side to rest.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12872418EEE48C48A0CD5EA7699E1CE9', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 11, NULL, NULL, 2, NULL, 0, 'Pour away any fat in the roasting tin. Place the tin on a high heat on the stove, add the stock from the vegetables, and stir thoroughly, scraping up all the bits on the bottom of the dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CC6C8B69F863341B59742C5F68F391D', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 12, NULL, NULL, 2, NULL, 0, 'Simmer on a low heat for 20 mins then strain through a fine sieve. Adjust the seasoning and reduce further to create a slightly thick stock. Just before serving, whisk in a few small pieces of very cold butter.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04C9003CA86A2640964CCDD154953D63', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 13, NULL, NULL, 2, NULL, 0, 'Before serving, unwrap the goose, slice into thick slices, being careful not to break the stuffing in the center.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55489E50CE9F3848B19D7C64A06C2815', X'8942F807A0662A45916F9C582792B34F', X'8942F807A0662A45916F9C582792B34F', 14, NULL, NULL, 2, NULL, 0, 'Serve and enjoy with the gravy.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B844C46CFC89B941A6223F29EA0A1504', X'16894AC66FF9EB439E95C475F8CFD2DE', X'16894AC66FF9EB439E95C475F8CFD2DE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9623EDF3E85847498953ED21CB7E23EA', X'16894AC66FF9EB439E95C475F8CFD2DE', X'16894AC66FF9EB439E95C475F8CFD2DE', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet, melt the butter over medium heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B80AC18830E3124FAF58B0D48BF86B16', X'16894AC66FF9EB439E95C475F8CFD2DE', X'16894AC66FF9EB439E95C475F8CFD2DE', 2, NULL, NULL, 2, NULL, 0, 'Beat the eggs in a large bowl until combined and then add to the skillet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'889E8B6C0D56E84EAFF266B5A135BD33', X'16894AC66FF9EB439E95C475F8CFD2DE', X'16894AC66FF9EB439E95C475F8CFD2DE', 3, NULL, NULL, 2, NULL, 0, 'Cook the eggs, scraping the bottom of the skillet with a spatula and turning the eggs over occasionally until they are scrambled and set.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2733553CAE07DB41BD90E12931E9CB0B', X'16894AC66FF9EB439E95C475F8CFD2DE', X'16894AC66FF9EB439E95C475F8CFD2DE', 4, NULL, NULL, 2, NULL, 0, 'Add the cooked sausage and salsa to the scrambled egg mixture and mix gently.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2975B612897CA041A3787C9EF2A2D33D', X'16894AC66FF9EB439E95C475F8CFD2DE', X'16894AC66FF9EB439E95C475F8CFD2DE', 5, NULL, NULL, 2, NULL, 0, 'Warm the tortillas as directed on the product package.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3C39043D8C8CC48BD7CE7A806CF5F6D', X'16894AC66FF9EB439E95C475F8CFD2DE', X'16894AC66FF9EB439E95C475F8CFD2DE', 6, NULL, NULL, 2, NULL, 0, 'Place about 1/2 cup of the egg and sausage mixture onto each tortilla and sprinkle with some of the cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96E5E5D32BCE5C4893545F25D4C8F213', X'16894AC66FF9EB439E95C475F8CFD2DE', X'16894AC66FF9EB439E95C475F8CFD2DE', 7, NULL, NULL, 2, NULL, 0, 'Roll up each tortilla to make burritos, folding in the ends. Don''t overfill the tortillas or they will burst during reheating.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C84CF52B8F5B9F4DBF0D081D7014A0A8', X'16894AC66FF9EB439E95C475F8CFD2DE', X'16894AC66FF9EB439E95C475F8CFD2DE', 8, NULL, NULL, 2, NULL, 0, 'Place the burritos on parchment paper-lined cookie sheets and freeze them until they are completely frozen and firm. Then wrap each burrito individually in freezer wrap and package in zip-close freezer bags.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61108C8C92C3614589E012F3EA561034', X'16894AC66FF9EB439E95C475F8CFD2DE', X'16894AC66FF9EB439E95C475F8CFD2DE', 9, NULL, NULL, 2, NULL, 0, 'When you''re ready to eat, unwrap the burritos, wrap them loosely in microwave-safe paper towel and heat in the microwave on high power for 1 to 3 minutes until the filling is hot and the cheese is melted.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D73A91DA097FF498D1D69CBE152B686', X'CB83B1A60973F34AAD17B625D862CF5E', X'CB83B1A60973F34AAD17B625D862CF5E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F599339787B49A4DBFC5F9C2EADB6CA4', X'CB83B1A60973F34AAD17B625D862CF5E', X'CB83B1A60973F34AAD17B625D862CF5E', 1, NULL, NULL, 2, NULL, 0, 'Line a rimmed baking sheet with foil or parchment paper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39CD942A1CF31C49AEE7B36FD6E465C6', X'CB83B1A60973F34AAD17B625D862CF5E', X'CB83B1A60973F34AAD17B625D862CF5E', 2, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, combine the raw ground sausage and shredded cheese with the baking mix; blend with your hands until well mixed. If desired, add the grated onion and poultry seasoning. Alternatively, use a stand mixer with the paddle attachment.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9ADF549F417D054FAA7BEA8D3BE889EB', X'CB83B1A60973F34AAD17B625D862CF5E', X'CB83B1A60973F34AAD17B625D862CF5E', 3, NULL, NULL, 2, NULL, 0, 'Shape the mixture into 1 inch balls and arrange them on the prepared baking sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E37A83BB2014F458FB6EE7BAD3391D8', X'CB83B1A60973F34AAD17B625D862CF5E', X'CB83B1A60973F34AAD17B625D862CF5E', 4, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for about 12 to 15 minutes, or until golden brown.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A124002846C4E84CA65EAC49B1C3F588', X'CB83B1A60973F34AAD17B625D862CF5E', X'CB83B1A60973F34AAD17B625D862CF5E', 5, NULL, NULL, 2, NULL, 0, 'The sausage will be fully cooked, but if you are doubtful, you can check a few with an instant-read thermometer. They should register at least 160 F, the minimum safe temperature for ground pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2755A4CE3BB0E44B9FEADF2937263C9', X'CB83B1A60973F34AAD17B625D862CF5E', X'CB83B1A60973F34AAD17B625D862CF5E', 6, NULL, NULL, 2, NULL, 0, 'Remove the sausage balls to a paper towel-lined plate to drain.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4B37156C2CB144AA3795C8C69F279EE', X'CB83B1A60973F34AAD17B625D862CF5E', X'CB83B1A60973F34AAD17B625D862CF5E', 7, NULL, NULL, 2, NULL, 0, 'Serve with toothpicks and a mustard mayonnaise dip, aioli, or ranch-style dressing.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9E04D8E48E365439133C0B59878C94A', X'CB83B1A60973F34AAD17B625D862CF5E', X'CB83B1A60973F34AAD17B625D862CF5E', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'971D2967A3CF694F825FD3A2789FDE0E', X'60F0549F88D84349851823B3B8AC1EF7', X'60F0549F88D84349851823B3B8AC1EF7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C06E5AF5E50A14FBA7A2916EE4C9303', X'60F0549F88D84349851823B3B8AC1EF7', X'60F0549F88D84349851823B3B8AC1EF7', 1, NULL, NULL, 2, NULL, 0, 'Cut the bacon slices in half, crosswise, to make short strips.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADFB3557737C0649BB5BF0BD4904B70A', X'60F0549F88D84349851823B3B8AC1EF7', X'60F0549F88D84349851823B3B8AC1EF7', 2, NULL, NULL, 2, NULL, 0, 'Fry the bacon in a large pan over medium heat until some of the fat is rendered but the bacon is still pliable. Do not let the bacon get crispy or you won''t be able to wrap it around the sausages.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'716E502378935B4DA47B23B370E4FF41', X'60F0549F88D84349851823B3B8AC1EF7', X'60F0549F88D84349851823B3B8AC1EF7', 3, NULL, NULL, 2, NULL, 0, 'Place the bacon on paper towels and cool it down for five minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B3600E61B612B4284B6BDE45B8BD20A', X'60F0549F88D84349851823B3B8AC1EF7', X'60F0549F88D84349851823B3B8AC1EF7', 4, NULL, NULL, 2, NULL, 0, 'Wrap each sausage with a piece of the precooked bacon and secure with a toothpick.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A34AED438CFF846ADF91D503447E931', X'60F0549F88D84349851823B3B8AC1EF7', X'60F0549F88D84349851823B3B8AC1EF7', 5, NULL, NULL, 2, NULL, 0, 'Place the sugar in a bowl and roll each little sausage and bacon bundle in it. Place the sausages on a wire rack.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A799B1A1C9C9F42A21FAACE36CDB767', X'60F0549F88D84349851823B3B8AC1EF7', X'60F0549F88D84349851823B3B8AC1EF7', 6, NULL, NULL, 2, NULL, 0, 'Place the rack in a 13` x 9` baking pan lined with foil or parchment paper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E6CEAAEE264744AB0949D9BFCCC3E50', X'60F0549F88D84349851823B3B8AC1EF7', X'60F0549F88D84349851823B3B8AC1EF7', 7, NULL, NULL, 2, NULL, 0, 'Drizzle the maple syrup over the sausages and sprinkle with the pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'509178E1209BA64A8D0280EE593C2B31', X'60F0549F88D84349851823B3B8AC1EF7', X'60F0549F88D84349851823B3B8AC1EF7', 8, NULL, NULL, 2, NULL, 0, 'Bake the bacon-wrapped sausages for 25 to 35 minutes, or until the sausages are hot and the bacon is crispy.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C33D2FAB0E21834BA607E3824F0A6CD5', X'60F0549F88D84349851823B3B8AC1EF7', X'60F0549F88D84349851823B3B8AC1EF7', 9, NULL, NULL, 2, NULL, 0, 'Remove the rack from the baking pan and cool the wrapped sausage bundles for 4 to 5 minutes before serving. Always use the wire rack. The drippings that come from the bacon and sugar burn easily; keeping the sausages away from the drippings avoids them burning, too.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65A3810BA6CF62409D09E1206202C1BB', X'60F0549F88D84349851823B3B8AC1EF7', X'60F0549F88D84349851823B3B8AC1EF7', 10, NULL, NULL, 2, NULL, 0, 'Serve with sauces of your liking and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A232D5DA4ED682438542A04A423EEC38', X'89C2CDBF8ABD5349AC14D7D0530A29A9', X'89C2CDBF8ABD5349AC14D7D0530A29A9', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 325 F (165 C/Gas 3). Butter an 8-inch square baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CBCB2D0487EB0419F0CF514FAC05C5D', X'89C2CDBF8ABD5349AC14D7D0530A29A9', X'89C2CDBF8ABD5349AC14D7D0530A29A9', 1, NULL, NULL, 2, NULL, 0, 'In a heavy skillet, brown sausage, breaking up with a spatula. Drain off excess fat; set aside. If using bacon, fry until crispy, drain, and crumble.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACC0028B0A439E47B108614D1991868F', X'89C2CDBF8ABD5349AC14D7D0530A29A9', X'89C2CDBF8ABD5349AC14D7D0530A29A9', 2, NULL, NULL, 2, NULL, 0, 'In a saucepan, bring salted water to a boil and stir in grits. Cover, reduce heat to low and continue cooking for 5 minutes longer, stirring occasionally.?');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'583C8594951DF14A90328021ABFC1F81', X'89C2CDBF8ABD5349AC14D7D0530A29A9', X'89C2CDBF8ABD5349AC14D7D0530A29A9', 3, NULL, NULL, 2, NULL, 0, 'In a medium saucepan or saucier, melt butter; stir in flour and pepper. Cook, stirring, for 2 minutes. Gradually stir in the milk. Cook, stirring constantly until thickened. Add cheese, stirring until blended.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'159B97D24763144A883B2A5A4AE3947C', X'89C2CDBF8ABD5349AC14D7D0530A29A9', X'89C2CDBF8ABD5349AC14D7D0530A29A9', 4, NULL, NULL, 2, NULL, 0, 'Add the drained sausage or bacon and half of the cheese sauce to the cooked grits.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA46E2F5FAF0EC46898712016F546B75', X'89C2CDBF8ABD5349AC14D7D0530A29A9', X'89C2CDBF8ABD5349AC14D7D0530A29A9', 5, NULL, NULL, 2, NULL, 0, 'Spoon the mixture into the prepared baking dish. With the back of a large serving spoon, make four indentations in the grits. Break an egg into each indentation. Sprinkle lightly with salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0E2C0224917364EA909C7184537C200', X'89C2CDBF8ABD5349AC14D7D0530A29A9', X'89C2CDBF8ABD5349AC14D7D0530A29A9', 6, NULL, NULL, 2, NULL, 0, 'Bake for about 18 to 22 minutes, or until eggs are done as desired. Reheat remaining cheese sauce and drizzle over the eggs or serve on the side.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFB56A6BEB10454AA5E510A4B532ABEA', X'50BB91088B97C44AB61420E78CC61F04', X'50BB91088B97C44AB61420E78CC61F04', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40475AFF6ECA5A4DB245ACDF533E247D', X'50BB91088B97C44AB61420E78CC61F04', X'50BB91088B97C44AB61420E78CC61F04', 1, NULL, NULL, 2, NULL, 0, 'In a heavy skillet or saute pan over medium heat, cook the sausage, stirring, until browned. Remove the sausage to drain, leaving about 3 tablespoons of fat in the pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FEFB8722579364787481F7ED3500BB1', X'50BB91088B97C44AB61420E78CC61F04', X'50BB91088B97C44AB61420E78CC61F04', 2, NULL, NULL, 2, NULL, 0, 'Cook the celery, green pepper, and onion in the remaining sausage fat until onion is translucent. Stir in the garlic and cook for 1 minute longer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EDDA1A91CE95445AC0D8EC0ECA4FDE9', X'50BB91088B97C44AB61420E78CC61F04', X'50BB91088B97C44AB61420E78CC61F04', 3, NULL, NULL, 2, NULL, 0, 'Spoon the browned sausage, vegetables, and almonds into a 2 1/2 to 3-quart baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6B8088B677A93488E1241BB24427E3C', X'50BB91088B97C44AB61420E78CC61F04', X'50BB91088B97C44AB61420E78CC61F04', 4, NULL, NULL, 2, NULL, 0, 'Add rice and the chicken broth; mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25CCBB9FFA7DB447ADEC9C5FD3EF8817', X'50BB91088B97C44AB61420E78CC61F04', X'50BB91088B97C44AB61420E78CC61F04', 5, NULL, NULL, 2, NULL, 0, 'Cover and bake in the preheated oven for about 1 to 1 1/2 hours, or until liquid is completely absorbed and the rice is tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AC9F043CF995C4291EF07FFAD1E23BF', X'50BB91088B97C44AB61420E78CC61F04', X'50BB91088B97C44AB61420E78CC61F04', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C35AC5D0B666BD43A80846FFA950FFE3', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59A955D7E1AD5B4F839EA3A73C21BD1E', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 1, NULL, NULL, 2, NULL, 0, 'Place the beans in a large saucepan and cover them with cold water. Bring the beans to a gentle simmer over medium heat. Cover and cook them until they begin to turn tender, about 1 hour and 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEC3EB1FD5EB2343911DCB1BD3512F19', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 2, NULL, NULL, 2, NULL, 0, 'Add 1 teaspoon salt and 1/4 teaspoon ground black pepper to the beans and cook until the liquid has absorbed, about 25 additional minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBE75026EE7D4E4CB4C1F33314B47CBB', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 3, NULL, NULL, 2, NULL, 0, 'Remove the beans from the heat, drain and discard any other liquid, and set the beans aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F752ED9D5DC154EADA526CFE42ED537', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 4, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium-high heat, cook the lardons for 5 minutes, until they turn brown. Transfer them to a plate.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AAC11F3CB9C494E91E4A629D08E3284', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 5, NULL, NULL, 2, NULL, 0, 'Add the onions to the pan and saut them for 10 minutes, until they turn light golden brown. Transfer them to the plate with the lardons.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41299C1AFBCB1F4AACC888313DD42531', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 6, NULL, NULL, 2, NULL, 0, 'Brown the sausage and transfer it to a bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9087F6F7394ABE4DAC1A9AF56543EF38', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 7, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6437833EC5450A4F91EDCD79DD4E8D61', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 8, NULL, NULL, 2, NULL, 0, 'Drain all but 4 tablespoons of the fat from the skillet. Add the chicken breast pieces to the fat and brown them over medium-high heat. Transfer the chicken to the bowl with the sausage.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0A870CF2C8C9D45AA95694B75D607DF', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 9, NULL, NULL, 2, NULL, 0, 'Brown the chicken legs in the skillet. Then transfer them to the bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5665B869ECF4A44AF36C7829B67D019', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 10, NULL, NULL, 2, NULL, 0, 'Combine the lardons, onions, sausage, chicken pieces and legs, tomatoes, wine, chicken stock, bouquet garni, garlic, tomato paste, 3/4 teaspoon salt, and 1/4 teaspoon ground black pepper in a large casserole dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78F57C5C8E07314EA4B66E28D557B046', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 11, NULL, NULL, 2, NULL, 0, 'Cover with a lid or foil and bake the casserole for 25 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28AFC10AE9848C469F63CEBC3A8C9BBC', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 12, NULL, NULL, 2, NULL, 0, 'Add the carrots to the pan, cover, and bake for an additional 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54BE35F08367BE468B0AC0596D8F2EA6', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 13, NULL, NULL, 2, NULL, 0, 'Add the reserved white beans to the hot casserole and gently stir the mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BD8D59659FA9F40ADF5623E8C6A30F5', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 14, NULL, NULL, 2, NULL, 0, 'In a small bowl, toss the bread crumbs, parsley, and thyme with the melted butter. Sprinkle the seasoned bread crumbs over the casserole.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F44DCE4121B7434A87E80750A1EDD357', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 15, NULL, NULL, 2, NULL, 0, 'Bake it, uncovered, for 1 hour.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0EB51A8AA751141B4CFAD86F1293E19', X'2807D7C380DECF48B04CC6A9F312C9AF', X'2807D7C380DECF48B04CC6A9F312C9AF', 16, NULL, NULL, 2, NULL, 0, 'Discard the bouquet garni and serve the cassoulet hot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34ADA23A5675FD45BA52EC66D54AFEE5', X'7A154174FA89A54F8146623CD3B6C074', X'7A154174FA89A54F8146623CD3B6C074', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDBC4A770BE9EF4DA418E82C3AB0605A', X'7A154174FA89A54F8146623CD3B6C074', X'7A154174FA89A54F8146623CD3B6C074', 1, NULL, NULL, 2, NULL, 0, 'Clean lentils well by soaking them briefly and changing the water at least once.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'945294306DD4DB4F8F31603405057B56', X'7A154174FA89A54F8146623CD3B6C074', X'7A154174FA89A54F8146623CD3B6C074', 2, NULL, NULL, 2, NULL, 0, 'Put soaked lentils in a 2-quart saucepan, add cold water to cover, and bring to a boil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1851FCDA4BC7E43BF6984D443433A89', X'7A154174FA89A54F8146623CD3B6C074', X'7A154174FA89A54F8146623CD3B6C074', 3, NULL, NULL, 2, NULL, 0, 'Once boiling, lower heat to simmer and cook until not quite done, about 45 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F21051A5BF27D43A5FD99A48147C76D', X'7A154174FA89A54F8146623CD3B6C074', X'7A154174FA89A54F8146623CD3B6C074', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, pierce sausages in several places with knife or fork, and then put them in a small saucepan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D187B0D6F1F8DF44AA7B1DE97F07B098', X'7A154174FA89A54F8146623CD3B6C074', X'7A154174FA89A54F8146623CD3B6C074', 5, NULL, NULL, 2, NULL, 0, 'Add enough chicken broth to cover sausages, and place over medium heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E08BF5D53F212F4188C5A6B069C1BE1D', X'7A154174FA89A54F8146623CD3B6C074', X'7A154174FA89A54F8146623CD3B6C074', 6, NULL, NULL, 2, NULL, 0, 'Bring chicken broth to a gentle boil, and then simmer for about 40 minutes. From time to time, skim off and discard foam and fat that rise to the top.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F9ED210CC1F4641B735D42C86F7D77E', X'7A154174FA89A54F8146623CD3B6C074', X'7A154174FA89A54F8146623CD3B6C074', 7, NULL, NULL, 2, NULL, 0, 'When sausages are done, remove the pot from the heat and let them sit in the broth while you finish the lentils.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'507E5E0E5F33394AA1861E2D8A78B848', X'7A154174FA89A54F8146623CD3B6C074', X'7A154174FA89A54F8146623CD3B6C074', 8, NULL, NULL, 2, NULL, 0, 'Then, warm the oil in a medium skillet, and saut the onion, celery, carrot, garlic, and sage in the olive oil over medium heat until the onion is translucent and the vegetables are done.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A3FADE152307346A85CFF3163F98F9B', X'7A154174FA89A54F8146623CD3B6C074', X'7A154174FA89A54F8146623CD3B6C074', 9, NULL, NULL, 2, NULL, 0, 'Then, drain the sausages, saving their liquid. To the lentil pot, add the vegetables, season with salt and pepper, and add tomato paste. Mix gently using a wooden spoon.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B678C28B27A9C4D95396345F62203E2', X'7A154174FA89A54F8146623CD3B6C074', X'7A154174FA89A54F8146623CD3B6C074', 10, NULL, NULL, 2, NULL, 0, 'Add  cup of the broth in which you cooked the sausages. Taste and adjust the seasoning, if needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60778107F53E9C46BE221C28DFE747E9', X'1FB52A2CB5AE1C40B439D5D123A0F371', X'1FB52A2CB5AE1C40B439D5D123A0F371', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'109AC0A2CC17F14988F24BA2170580CD', X'1FB52A2CB5AE1C40B439D5D123A0F371', X'1FB52A2CB5AE1C40B439D5D123A0F371', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F. Lightly butter a shallow 2-quart baking dish or spray it with cooking oil spray.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'255BEB64BC9FC243A293491DF82B8341', X'1FB52A2CB5AE1C40B439D5D123A0F371', X'1FB52A2CB5AE1C40B439D5D123A0F371', 2, NULL, NULL, 2, NULL, 0, 'Cook the noodles in a saucepan of boiling salted water following the package directions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A71FEE4A5DE1A478680847BD70AA43A', X'1FB52A2CB5AE1C40B439D5D123A0F371', X'1FB52A2CB5AE1C40B439D5D123A0F371', 3, NULL, NULL, 2, NULL, 0, 'Drain in a colander and set aside.');
