INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5275A310A09200489738708017EBF9A5', X'1FB52A2CB5AE1C40B439D5D123A0F371', X'1FB52A2CB5AE1C40B439D5D123A0F371', 5, NULL, NULL, 2, NULL, 0, 'Place the skillet over medium heat and cook, stirring, until the sausage has browned and vegetables are tender. Drain off excess drippings.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7070003773F1A14CB55A477D62F01B3F', X'1FB52A2CB5AE1C40B439D5D123A0F371', X'1FB52A2CB5AE1C40B439D5D123A0F371', 6, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the sausage mixture with the cream of chicken soup, water, and cooked noodles. Taste and add salt and pepper, as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80805B83959EEE43A1B5FCC542FB1BAD', X'1FB52A2CB5AE1C40B439D5D123A0F371', X'1FB52A2CB5AE1C40B439D5D123A0F371', 7, NULL, NULL, 2, NULL, 0, 'Spoon the mixture into the prepared baking dish. Sprinkle with crumbled French fried onions.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0534DBD1061EAE4C867A56AC1AB7055D', X'1FB52A2CB5AE1C40B439D5D123A0F371', X'1FB52A2CB5AE1C40B439D5D123A0F371', 8, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for about 30 minutes, or until hot and bubbly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19E4C7A2F3E3FF44B05BFA710C3B124F', X'1FB52A2CB5AE1C40B439D5D123A0F371', X'1FB52A2CB5AE1C40B439D5D123A0F371', 9, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C0A4807CAD6A046B8CF654F5016B154', X'5DF3FC77BFB9004F9A6AE2AD6F8CF0C3', X'5DF3FC77BFB9004F9A6AE2AD6F8CF0C3', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6DE62A8ACA08F41AF362531D32C8EDC', X'5DF3FC77BFB9004F9A6AE2AD6F8CF0C3', X'5DF3FC77BFB9004F9A6AE2AD6F8CF0C3', 1, NULL, NULL, 2, NULL, 0, 'Cut a thin slice off the top off each roll and set aside. Hollow out each roll, leaving a shell about 1/4` thick. (You can save the crumbs to make bread crumbs; freeze them for another use.)');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'923313FF017D874395B050125579D315', X'5DF3FC77BFB9004F9A6AE2AD6F8CF0C3', X'5DF3FC77BFB9004F9A6AE2AD6F8CF0C3', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet, cook the sausage with the onion and garlic, stirring to break up the meat, until the sausage is browned and cooked. Drain well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'014AB67681C6184F8C805C3C3A1E045E', X'5DF3FC77BFB9004F9A6AE2AD6F8CF0C3', X'5DF3FC77BFB9004F9A6AE2AD6F8CF0C3', 3, NULL, NULL, 2, NULL, 0, 'Add the pasta sauce and Italian seasoning to the meat in the skillet; simmer for 5 to 10 minutes, stirring frequently.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41397FA70D3AB1459F29318EE88549F2', X'5DF3FC77BFB9004F9A6AE2AD6F8CF0C3', X'5DF3FC77BFB9004F9A6AE2AD6F8CF0C3', 4, NULL, NULL, 2, NULL, 0, 'In a medium bowl, beat together the ricotta cheese, cream cheese, and egg until blended. Stir in the Havarti cheese and mozzarella cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'711CA1747E9A0A4AB5DD712A4C9C2AAE', X'5DF3FC77BFB9004F9A6AE2AD6F8CF0C3', X'5DF3FC77BFB9004F9A6AE2AD6F8CF0C3', 5, NULL, NULL, 2, NULL, 0, 'Divide the meat mixture among the hollowed-out rolls. Top each with the ricotta mixture and sprinkle with the Parmesan cheese. Place all of the filled rolls on a cookie sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'301F9FDC928DFF4E90106981B8BFCF21', X'5DF3FC77BFB9004F9A6AE2AD6F8CF0C3', X'5DF3FC77BFB9004F9A6AE2AD6F8CF0C3', 6, NULL, NULL, 2, NULL, 0, 'Bake for 25 to 35 minutes or until the cheese on top starts to brown. Let the sandwiches cool for 10 minutes before serving.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5823A61D060F5D4A86DEF1F2EA0894DA', X'B448837CB4748D4F9060B83224BF59E5', X'B448837CB4748D4F9060B83224BF59E5', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F (180 C/Gas 4). Line a rimmed baking sheet with foil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D1763F99315114A9B2FD1BF55D1D679', X'B448837CB4748D4F9060B83224BF59E5', X'B448837CB4748D4F9060B83224BF59E5', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl combine the ground beef, sausage, egg, ketchup, 2 tablespoons of Worcestershire sauce, mustard, soy sauce, garlic, salt, and freshly ground black pepper. Mix gently until the mixture is well combined.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9209CBF0528F1C4F98E2C25F3667B6FB', X'B448837CB4748D4F9060B83224BF59E5', X'B448837CB4748D4F9060B83224BF59E5', 2, NULL, NULL, 2, NULL, 0, 'Shape the meat mixture into small balls (about 1 inch in diameter). Use a small cookie scoop or weigh the meatballs as you shape if you want to make sure they are uniform in size.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C69FD4DD5194F241BDF9C018F3D77285', X'B448837CB4748D4F9060B83224BF59E5', X'B448837CB4748D4F9060B83224BF59E5', 3, NULL, NULL, 2, NULL, 0, 'Arrange the meatballs on the prepared baking sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'077606C17C24704FB5DEB14C06DA5A8A', X'B448837CB4748D4F9060B83224BF59E5', X'B448837CB4748D4F9060B83224BF59E5', 4, NULL, NULL, 2, NULL, 0, 'Bake the meatballs for 25 to 30 minutes or until browned and thoroughly cooked. The minimum safe temperature for ground beef and ground pork is 160 F (71 C). If you use ground poultry in the meatballs, cook to 165 F (74 C).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'157C7EF79F282C4DBE86814C807A41A6', X'B448837CB4748D4F9060B83224BF59E5', X'B448837CB4748D4F9060B83224BF59E5', 5, NULL, NULL, 2, NULL, 0, 'Transfer the meatballs to the slow cooker or a large, deep skillet or saute pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE304125EB2EE44CAD92751F35964EDA', X'B448837CB4748D4F9060B83224BF59E5', X'B448837CB4748D4F9060B83224BF59E5', 6, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the condensed soup, sour cream, 1 teaspoon of Worcestershire sauce, Tabasco, and milk. Blend well. Pour over the meatballs. Cover and cook on low for 1 to 2 hours, or until hot, or heat over low heat in a saute pan on the stovetop for about 15 to 20 minutes, or until hot (do not boil).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E7A5EB2C4630E4697D0078E393FDC85', X'B448837CB4748D4F9060B83224BF59E5', X'B448837CB4748D4F9060B83224BF59E5', 7, NULL, NULL, 2, NULL, 0, 'If serving as an appetizer, serve them hot from the slow cooker or chafing dish, with toothpicks, small plates, and napkins for guests.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC44628566E64447B6575D43C652C5CE', X'B448837CB4748D4F9060B83224BF59E5', X'B448837CB4748D4F9060B83224BF59E5', 8, NULL, NULL, 2, NULL, 0, 'The meatballs also make a fabulous entree with rice or buttered noodles.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB560A4E39998F40ACAE4849BA7F1EA3', X'6E798F19CC817A488D5DBB87FCDD8CE6', X'6E798F19CC817A488D5DBB87FCDD8CE6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'228C24E770A11A4C91DCE82B64137A42', X'6E798F19CC817A488D5DBB87FCDD8CE6', X'6E798F19CC817A488D5DBB87FCDD8CE6', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB8A3B88ABC2214FB6368409463EE1A7', X'6E798F19CC817A488D5DBB87FCDD8CE6', X'6E798F19CC817A488D5DBB87FCDD8CE6', 2, NULL, NULL, 2, NULL, 0, 'Lightly grease a 1 1/2-quart casserole dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92FB320DF58AE34A99F07BC85D1BA529', X'6E798F19CC817A488D5DBB87FCDD8CE6', X'6E798F19CC817A488D5DBB87FCDD8CE6', 3, NULL, NULL, 2, NULL, 0, 'Cook the macaroni in boiling salted water following package directions; drain and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64DA02D91B98644B94F68194FC94C490', X'6E798F19CC817A488D5DBB87FCDD8CE6', X'6E798F19CC817A488D5DBB87FCDD8CE6', 4, NULL, NULL, 2, NULL, 0, 'In a skillet, saute the sausage, onion, and green bell pepper, stirring, until the sausage is no longer pink. Pour off excess fat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1ED742C40E74784E860F941B4984A0B7', X'6E798F19CC817A488D5DBB87FCDD8CE6', X'6E798F19CC817A488D5DBB87FCDD8CE6', 5, NULL, NULL, 2, NULL, 0, 'Add the flour and salt to the sausage mixture, blending well. Gradually add milk. Continue to cook over low heat, stirring constantly, until the sauce has thickened. Stir in the cooked and drained macaroni and 1 cup of the shredded Cheddar cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67D32D444712AC479FA834E4AEBFFC47', X'6E798F19CC817A488D5DBB87FCDD8CE6', X'6E798F19CC817A488D5DBB87FCDD8CE6', 6, NULL, NULL, 2, NULL, 0, 'Transfer macaroni and sausage casserole mixture to the prepared casserole.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B85040447DE15F4CB2FC545ACF24EBA9', X'6E798F19CC817A488D5DBB87FCDD8CE6', X'6E798F19CC817A488D5DBB87FCDD8CE6', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle remaining cheese over sausage casserole and bake in the preheated oven for 25 to 30 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'053455147BBB8F42BCFB43BD12D93328', X'6E798F19CC817A488D5DBB87FCDD8CE6', X'6E798F19CC817A488D5DBB87FCDD8CE6', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E57281BCFD72814F826271E58DCE31D5', X'F99AA2A97E76E04D8BA7786E22D9BABD', X'F99AA2A97E76E04D8BA7786E22D9BABD', 0, NULL, NULL, 2, NULL, 0, 'Butter a shallow 2-quart baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B13E79196F05DA4AAAE66DF9E6BC12AF', X'F99AA2A97E76E04D8BA7786E22D9BABD', X'F99AA2A97E76E04D8BA7786E22D9BABD', 1, NULL, NULL, 2, NULL, 0, 'Tear bread into small pieces and sprinkle in the bottom of the prepared baking dish. Arrange sausage patties over the bread; sprinkle with green onion, tomato, then the cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC33D41400546143A500CE853F4B0CA7', X'F99AA2A97E76E04D8BA7786E22D9BABD', X'F99AA2A97E76E04D8BA7786E22D9BABD', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk together the eggs, milk, salt, seasoned salt or Creole seasoning, black pepper, and paprika. Pour the egg mixture evenly over the cheese layer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF4E70808D2F154BA1E989AC72059D87', X'F99AA2A97E76E04D8BA7786E22D9BABD', X'F99AA2A97E76E04D8BA7786E22D9BABD', 3, NULL, NULL, 2, NULL, 0, 'Refrigerate overnight or bake immediately.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6693156975DF540AD1ACE0DB3C8525F', X'F99AA2A97E76E04D8BA7786E22D9BABD', X'F99AA2A97E76E04D8BA7786E22D9BABD', 4, NULL, NULL, 2, NULL, 0, 'If refrigerating overnight, take the casserole out of the refrigerator about 30 minutes before baking. Bake at in a preheated 350 F oven for 40 to 45 minutes, until set in the center.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA847CA3AE47F744A89DFD51F36C2328', X'F99AA2A97E76E04D8BA7786E22D9BABD', X'F99AA2A97E76E04D8BA7786E22D9BABD', 5, NULL, NULL, 2, NULL, 0, 'The minimum safe temperature for eggs is 160 F. If in doubt, check with an instant-read thermometer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F609EC4E4BB101489161B2588A40697D', X'A14DDCF7C4539149BBD98AB5AE4A0C5E', X'A14DDCF7C4539149BBD98AB5AE4A0C5E', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 375 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4162AA41AFF69245A55899B205EF2E83', X'A14DDCF7C4539149BBD98AB5AE4A0C5E', X'A14DDCF7C4539149BBD98AB5AE4A0C5E', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl combine the ground beef and sausage, egg, ketchup, 2 tablespoons of Worcestershire sauce, mustard, soy sauce, garlic, salt, and pepper. Blend thoroughly and then shape the mixture into small meatballs. Place on a rimmed foil-lined baking sheet or jelly roll pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FABC484A5C9C2B45A28C6954C404F7A1', X'A14DDCF7C4539149BBD98AB5AE4A0C5E', X'A14DDCF7C4539149BBD98AB5AE4A0C5E', 2, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 35 to 45 minutes, or until browned and cooked through.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A5A25B5275A5B4F9CE9B7F0B01CDD2E', X'A14DDCF7C4539149BBD98AB5AE4A0C5E', X'A14DDCF7C4539149BBD98AB5AE4A0C5E', 3, NULL, NULL, 2, NULL, 0, 'In a medium bowl mix the sauce ingredients together. Place meatballs and sauce in a crockpot. Cook on HIGH for 30 to 45 minutes, or until the sauce is hot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'345B498D7151CA48B78B6C2B30F635C4', X'A14DDCF7C4539149BBD98AB5AE4A0C5E', X'A14DDCF7C4539149BBD98AB5AE4A0C5E', 4, NULL, NULL, 2, NULL, 0, 'The mixture may also be cooked on the stovetop. Bring to a simmer over low heat and cook for about 10 minutes. Do not boil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02145C8838EAC841AA567E3C928105C8', X'A14DDCF7C4539149BBD98AB5AE4A0C5E', X'A14DDCF7C4539149BBD98AB5AE4A0C5E', 5, NULL, NULL, 2, NULL, 0, 'Use as a main dish served over rice or noodles or as an appetizer served with toothpicks.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55C4905119D47048823512E1804F2DF8', X'05038AB3D3B90949856CC34183F83C56', X'05038AB3D3B90949856CC34183F83C56', 0, NULL, NULL, 2, NULL, 0, 'Fill a large stockpot with about 8 quarts of water. Add the crab boil seasoning and bring to a full boil. Continue boiling for 10 to 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB063A8EF731AE4FA8BE74BB3FFD0484', X'05038AB3D3B90949856CC34183F83C56', X'05038AB3D3B90949856CC34183F83C56', 1, NULL, NULL, 2, NULL, 0, 'Cut sausage into 2-inch lengths; add to water.');
