INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9C8FD70E0D32641B4A0ADB2535DE7E5', X'40938708B04DEB488E45FBD770AE4C4A', X'40938708B04DEB488E45FBD770AE4C4A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E74171F073F9D14C83657E6102FE7593', X'40938708B04DEB488E45FBD770AE4C4A', X'40938708B04DEB488E45FBD770AE4C4A', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 F and lightly coat a baking sheet with non-stick spray or a homemade oil-based spray.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8929DA3ED622040AD78BDD49EE4DE3E', X'40938708B04DEB488E45FBD770AE4C4A', X'40938708B04DEB488E45FBD770AE4C4A', 2, NULL, NULL, 2, NULL, 0, 'Arrange the mushroom caps, top down, on the baking sheet. Spray with a light layer of cooking spray, then bake in the oven until just slightly softened; about 15 minutes. Remove from the oven, and set the mushrooms aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02AEDE1FA5847444B3025EEF0AD04DCD', X'40938708B04DEB488E45FBD770AE4C4A', X'40938708B04DEB488E45FBD770AE4C4A', 3, NULL, NULL, 2, NULL, 0, 'Heat 1 tablespoon of the oil in a skillet over medium-high heat. Saute vegetarian sausage until it is separated and resembles cooked loose sausage, about 4 minutes. Use a wooden spoon to break it up as needed. Transfer the cooked vegetarian sausage to a bowl, and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D7791270CDC0F4187479AE44F4F8EF7', X'40938708B04DEB488E45FBD770AE4C4A', X'40938708B04DEB488E45FBD770AE4C4A', 4, NULL, NULL, 2, NULL, 0, 'Heat the remaining oil in the skillet and saute the onion, peppers, and garlic until they are softabout 6 minutes. Remove the pan from the heat, then mix in the cooked sausage, bread crumbs, and parsley. Season the filling to taste with a bit of salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0B5D6C3C631294093683886DA41BCDF', X'40938708B04DEB488E45FBD770AE4C4A', X'40938708B04DEB488E45FBD770AE4C4A', 5, NULL, NULL, 2, NULL, 0, 'Form 1 tablespoon of the sausage and onion filling into a ball. Place it into a mushroom cap, pressing it firmly into the mushroom. (It should be generously mounded.) Repeat, filling all the mushrooms. Arrange the stuffed mushrooms on the baking sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07A2450F7262414D8A15F2BB160BE377', X'40938708B04DEB488E45FBD770AE4C4A', X'40938708B04DEB488E45FBD770AE4C4A', 6, NULL, NULL, 2, NULL, 0, 'Bake the stuffed mushrooms until the filling is lightly browned on top, about 12 minutes. Just before serving, squeeze a bit of lemon juice on the mushrooms, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82C7DF4153D17A428A3ED647A253255C', X'40938708B04DEB488E45FBD770AE4C4A', X'40938708B04DEB488E45FBD770AE4C4A', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DA0CF0DDE75944EA801EA681493680E', X'34241408A19CA546A577EC67B0072E30', X'34241408A19CA546A577EC67B0072E30', 0, NULL, NULL, 2, NULL, 0, 'Cut pork tenderloin into 1 inch rounds. Heat oil in large skillet over medium heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92DE9F496B20DD49BC93E5BEE790346A', X'34241408A19CA546A577EC67B0072E30', X'34241408A19CA546A577EC67B0072E30', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat oven to 375 F, and spray a baking sheet with nonstick cooking spray.?');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A6C3157A664F34D8696D74CED9EF7CC', X'34241408A19CA546A577EC67B0072E30', X'34241408A19CA546A577EC67B0072E30', 2, NULL, NULL, 2, NULL, 0, 'Saut medallions one minute per side, then transfer to baking sheet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5AC792C4C9331428746EE22A6017598', X'34241408A19CA546A577EC67B0072E30', X'34241408A19CA546A577EC67B0072E30', 3, NULL, NULL, 2, NULL, 0, 'Whisk vinegar, honey, and mustard together in a small bowl. Generously brush medallions with glaze.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8AEC6753EEE25498BE7C7675A01E89C', X'34241408A19CA546A577EC67B0072E30', X'34241408A19CA546A577EC67B0072E30', 4, NULL, NULL, 2, NULL, 0, 'Cook in the oven for 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44119B60732A14409F734CCE4782FCF8', X'0786163060BFC043A8FC4832D734D686', X'0786163060BFC043A8FC4832D734D686', 0, NULL, NULL, 2, NULL, 0, 'Trim the pork of excess fat and remove the silver skin. Cut into 3/4-inch slices and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B138CAA2E57814CA7FE33F416E5CAAF', X'0786163060BFC043A8FC4832D734D686', X'0786163060BFC043A8FC4832D734D686', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet, melt 2 tablespoons of butter over medium heat. Add the shallot and mushrooms and cook, stirring, until mushrooms are tender. Remove the mushrooms to a plate and set aside. Add the remaining tablespoon of butter to the skillet along with the olive oil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2372F83C86FE294B80ED6DFE287E3858', X'0786163060BFC043A8FC4832D734D686', X'0786163060BFC043A8FC4832D734D686', 2, NULL, NULL, 2, NULL, 0, 'Cook the pork medallions for about 10 minutes, turning to brown both sides, or until cooked through. Sprinkle with salt and pepper and remove to a warm plate; keep warm.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28F83FB6DD5F4B41A6286C52CBF949DB', X'0786163060BFC043A8FC4832D734D686', X'0786163060BFC043A8FC4832D734D686', 3, NULL, NULL, 2, NULL, 0, 'Add wine or apple juice to the skillet. Stir in the cream and mustard; bring to a boil. Boil, stirring, for about 2 minutes. Add the mushrooms to the cream mixture and cook for 1 minute longer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B56B153F1A13F14DBCBD0A9DFD373EFE', X'0786163060BFC043A8FC4832D734D686', X'0786163060BFC043A8FC4832D734D686', 4, NULL, NULL, 2, NULL, 0, 'Arrange pork medallions on a serving platter and spoon mushroom sauce over all. Sprinkle with rosemary leaves, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C797F583172F54BA0289987A2BEA14F', X'0786163060BFC043A8FC4832D734D686', X'0786163060BFC043A8FC4832D734D686', 5, NULL, NULL, 2, NULL, 0, 'Serve with noodles, rice, or potatoes along with a tossed salad or steamed vegetables.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6B3B222EF199E45B721F4EC2E20261A', X'DE7AFFAF69BA0A40B382002B0155BA33', X'DE7AFFAF69BA0A40B382002B0155BA33', 0, NULL, NULL, 2, NULL, 0, 'Pierce the tenderloin all over with a fork and place in baking dish.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4777C24AE876314F911EB1E5DB30663F', X'DE7AFFAF69BA0A40B382002B0155BA33', X'DE7AFFAF69BA0A40B382002B0155BA33', 1, NULL, NULL, 2, NULL, 0, 'Mix remaining ingredients and pour over pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'189C45A899E59141ABE2E90607F5734C', X'DE7AFFAF69BA0A40B382002B0155BA33', X'DE7AFFAF69BA0A40B382002B0155BA33', 2, NULL, NULL, 2, NULL, 0, 'Cover and marinate in the refrigerator for 2 to 24 hours, turning pork once or twice. Or just let the pork marinate at room temperature for 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89E645FB416BF142B64882EFA3E9D642', X'DE7AFFAF69BA0A40B382002B0155BA33', X'DE7AFFAF69BA0A40B382002B0155BA33', 3, NULL, NULL, 2, NULL, 0, 'When ready to cook, preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C182E54E0FA694DBB2D24B66A281F1E', X'DE7AFFAF69BA0A40B382002B0155BA33', X'DE7AFFAF69BA0A40B382002B0155BA33', 4, NULL, NULL, 2, NULL, 0, 'Place the two tenderloins on a shallow baking pan, at least 2 inches apart.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B17496546FF54B4B8DCF48D228D209A5', X'DE7AFFAF69BA0A40B382002B0155BA33', X'DE7AFFAF69BA0A40B382002B0155BA33', 5, NULL, NULL, 2, NULL, 0, 'Roast for 35 to 40 minutes until pork registers 145 F on a meat thermometer. Let stand 5 minutes, then slice to serve. Pork will be light pink in the center.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7ED3A67407E25840A7AC47CB2626AF13', X'0E5E25225B12A5469E6207E2507D6488', X'0E5E25225B12A5469E6207E2507D6488', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03FE5B19A9291248A48369DFB3D5FFE8', X'0E5E25225B12A5469E6207E2507D6488', X'0E5E25225B12A5469E6207E2507D6488', 1, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large skillet over medium heat. Trim excess fat and silver skin from the pork. Season with the salt and pepper or grill seasoning. Sear on all sides, about 6 to 8 minutes total. Transfer to a baking pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE276D2C0984304A9F63BD39E5074650', X'0E5E25225B12A5469E6207E2507D6488', X'0E5E25225B12A5469E6207E2507D6488', 2, NULL, NULL, 2, NULL, 0, 'Combine the remaining ingredients in a saucepan and bring to a simmer. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F2C3DB5A12B8245BDCBA2AB11A3CC81', X'0E5E25225B12A5469E6207E2507D6488', X'0E5E25225B12A5469E6207E2507D6488', 3, NULL, NULL, 2, NULL, 0, 'Roast the pork tenderloin in the preheated oven for about 12 minutes. Brush generously with the glaze mixture and then continue roasting for 5 to 10 minutes longer, or until the pork registers at least 145 F on an instant-read thermometer inserted into the thickest part of a tenderloin.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1878FBF5E8FBC149B8B8CFBDC399A63C', X'0E5E25225B12A5469E6207E2507D6488', X'0E5E25225B12A5469E6207E2507D6488', 4, NULL, NULL, 2, NULL, 0, 'Reheat the remaining glaze and serve with the pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D9A6CA4A2DAD4479F77E2F8692D93F3', X'0E38B9133DEA1E46B53D2DFE124E4AA6', X'0E38B9133DEA1E46B53D2DFE124E4AA6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5207C99D1891FC41AEDB7D907A43D0AE', X'0E38B9133DEA1E46B53D2DFE124E4AA6', X'0E38B9133DEA1E46B53D2DFE124E4AA6', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFAD8E46C80DE6448B801E9DE061E53F', X'0E38B9133DEA1E46B53D2DFE124E4AA6', X'0E38B9133DEA1E46B53D2DFE124E4AA6', 2, NULL, NULL, 2, NULL, 0, 'Wash tenderloins and pat dry. Remove excess fat and silver skin.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63C26CEC62ED444F8A0931CB71CCD597', X'0E38B9133DEA1E46B53D2DFE124E4AA6', X'0E38B9133DEA1E46B53D2DFE124E4AA6', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle the tenderloins all over with seasoning and pepper. Heat olive oil in a large skillet over medium-high heat. Sear the tenderloins on all sides, about 6 to 8 minutes total.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4A37056117D354A81F4AC6A0F2D476D', X'0E38B9133DEA1E46B53D2DFE124E4AA6', X'0E38B9133DEA1E46B53D2DFE124E4AA6', 4, NULL, NULL, 2, NULL, 0, 'Transfer the tenderloins to the oven and roast for about 15 to 20 minutes (could be longer, depending on the thickness of tenderloins), or until an instant-read thermometer or meat thermometer registers 145 F to 150 F when inserted in the thickest part of tenderloin.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10E4631759D4D34AA11BBA7A9538BC5A', X'0E38B9133DEA1E46B53D2DFE124E4AA6', X'0E38B9133DEA1E46B53D2DFE124E4AA6', 5, NULL, NULL, 2, NULL, 0, 'While the pork tenderloins are roasting, prepare the cherry sauce. In a saucepan, combine the jam, vinegar, brown sugar, cinnamon, allspice, and a dash of salt. Bring to a simmer and simmer, stirring occasionally, for 6 to 8 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFF32C12C293FC4D8F497130F3A8D62A', X'0E38B9133DEA1E46B53D2DFE124E4AA6', X'0E38B9133DEA1E46B53D2DFE124E4AA6', 6, NULL, NULL, 2, NULL, 0, 'Remove pork tenderloins from the oven and let rest for 5 minutes. Slice and serve with warm cherry sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7E5569D29E3C24CB50B73E3C4CCDEA6', X'0E38B9133DEA1E46B53D2DFE124E4AA6', X'0E38B9133DEA1E46B53D2DFE124E4AA6', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
