INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'387A894EB711844AB4215A9C8A8240B0', X'279AC181FC5D1344B9A84B5D01D73860', X'279AC181FC5D1344B9A84B5D01D73860', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B64AC0D8F0AE0142AB046DFE7E0CCD97', X'279AC181FC5D1344B9A84B5D01D73860', X'279AC181FC5D1344B9A84B5D01D73860', 1, NULL, NULL, 2, NULL, 0, 'Line a baking pan with foil, spray with cooking spray and place in oven.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'533256103568E14BA9C9DDC4E037DBB7', X'279AC181FC5D1344B9A84B5D01D73860', X'279AC181FC5D1344B9A84B5D01D73860', 2, NULL, NULL, 2, NULL, 0, 'Trim fat from pork tenderloins and butterfly the meat, cutting them nearly in half lengthwise. Open the pork tenderloins and layout, pounding to flatten with the palm of the hand or the bottom of a heavy skillet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE0CBC8E33ADAA46AF5F0BF4F63A9CD3', X'279AC181FC5D1344B9A84B5D01D73860', X'279AC181FC5D1344B9A84B5D01D73860', 3, NULL, NULL, 2, NULL, 0, 'Chop rosemary if using fresh. Rub pork tenderloins all over with cut sides of garlic halves then olive oil, then sprinkle rosemary, salt, and pepper on both sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0D6F0EB63AFC1429C27AEF7394B98A5', X'279AC181FC5D1344B9A84B5D01D73860', X'279AC181FC5D1344B9A84B5D01D73860', 4, NULL, NULL, 2, NULL, 0, 'Remove baking pan from the oven and place pork on the hot tray. Return to oven and roast for about 20 minutes (at least 145 F internal temperature). Remove and let stand 5 minutes, then slice.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46DB907295E14646BEF6D1FC3DF022EE', X'D45D58200BD09540B47E4F307379163F', X'D45D58200BD09540B47E4F307379163F', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F/180 C/Gas 4.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15EE22A80F09624ABD06A67F11773E07', X'D45D58200BD09540B47E4F307379163F', X'D45D58200BD09540B47E4F307379163F', 1, NULL, NULL, 2, NULL, 0, 'In a large ovenproof skillet, heat 2 tablespoons of vegetable oil over medium-high heat. Trim excess fat from pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A80F0A6A1FD5A40A55FBF6EECC61B43', X'D45D58200BD09540B47E4F307379163F', X'D45D58200BD09540B47E4F307379163F', 2, NULL, NULL, 2, NULL, 0, 'In a bowl combine the 2 tablespoons of mustard with the salt and pepper. Rub the mixture over the pork tenderloins. Add the pork to the hot skillet and brown on all sides. This will take 4 to 6 minutes. Transfer to the oven and roast, uncovered, for about 30 minutes, or until the tenderloins reach or exceed the minimum safe temperature for pork, 145 F/63 C.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE478ADEFE08F446B93295A1B33FA4FD', X'D45D58200BD09540B47E4F307379163F', X'D45D58200BD09540B47E4F307379163F', 3, NULL, NULL, 2, NULL, 0, 'Remove pork to a serving plate and tent loosely with a sheet of foil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E2308AE136C8A44BC932155E0DC8097', X'D45D58200BD09540B47E4F307379163F', X'D45D58200BD09540B47E4F307379163F', 4, NULL, NULL, 2, NULL, 0, 'Add the sauce ingredients to the skillet and bring to a boil over medium heat, stirring and scraping up browned bits. Simmer for 1 minute.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DEB74B9048A2642AF4EA723821A9764', X'D45D58200BD09540B47E4F307379163F', X'D45D58200BD09540B47E4F307379163F', 5, NULL, NULL, 2, NULL, 0, 'To serve, drizzle the sauce over the sliced pork tenderloins.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7B2830BA3B35B4B80D1A9C4BD46764D', X'C15D23F31F716444A7B290CDDAE94907', X'C15D23F31F716444A7B290CDDAE94907', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A43D2312E94CAD45A69D2417EC29A3E8', X'C15D23F31F716444A7B290CDDAE94907', X'C15D23F31F716444A7B290CDDAE94907', 1, NULL, NULL, 2, NULL, 0, 'Combine yogurt with onion, lemon juice, garlic, ginger, salt, cumin, coriander, and black pepper in medium bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20B5C1AC17874540A6B5ED40270A4223', X'C15D23F31F716444A7B290CDDAE94907', X'C15D23F31F716444A7B290CDDAE94907', 2, NULL, NULL, 2, NULL, 0, 'Place pork pieces in a resealable plastic bag and pour marinade over pork. Make sure all pieces of pork are coated well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EF4E3E48450CB4EA556B4B71DDCF376', X'C15D23F31F716444A7B290CDDAE94907', X'C15D23F31F716444A7B290CDDAE94907', 3, NULL, NULL, 2, NULL, 0, 'Release air from the bag and seal well. Place pork in the refrigerator and let marinate for 4 to 12 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB4DF2CE9C7B13459E101051AABEB887', X'C15D23F31F716444A7B290CDDAE94907', X'C15D23F31F716444A7B290CDDAE94907', 4, NULL, NULL, 2, NULL, 0, 'Preheat grill to medium heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5B5ED70F620C34AA370037D6FBD9271', X'C15D23F31F716444A7B290CDDAE94907', X'C15D23F31F716444A7B290CDDAE94907', 5, NULL, NULL, 2, NULL, 0, 'Thread marinated pork pieces onto skewers, about 4 to 5 pieces on each depending on the length of skewer. Do not overcrowd.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3ACC6DD4A125E6499AD9432D91258B10', X'C15D23F31F716444A7B290CDDAE94907', X'C15D23F31F716444A7B290CDDAE94907', 6, NULL, NULL, 2, NULL, 0, 'Grill over a medium fire for about 25 to 30 minutes, turning often, or until pork has cooked through (reaching an internal temperature of 140 F/60 C).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13151504039D154697E0ABFF7F0161F2', X'C15D23F31F716444A7B290CDDAE94907', X'C15D23F31F716444A7B290CDDAE94907', 7, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from the heat and allow to rest for 10 minutes, covered, before serving.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDE0677F39CE954592B9E6D6204C423F', X'C15D23F31F716444A7B290CDDAE94907', X'C15D23F31F716444A7B290CDDAE94907', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CD2552DB81DBF4287FF04F6E68FCF1F', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60C111FBDAA56B4F84E3320ED01DC1AA', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', 1, NULL, NULL, 2, NULL, 0, 'Prepare the vegetables. Peel the onion and chop. Slice the bell pepper lengthwise; remove the seeds and ribs and chop. Peel the sweet potatoes and cut into 1-inch pieces.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B69B9FDBF124B45936C006891CC362E', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', 2, NULL, NULL, 2, NULL, 0, 'Lightly oil the slow cooker or spray it with nonstick cooking spray. Pat the pork roast dry with paper towels; trim and discard excess fat. Place the pork in the slow cooker. Arrange the sweet potatoes, bell pepper, and onions around the pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2566C93942DEB4882E78AB212440F65', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', 3, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the tomatoes, brown sugar, cinnamon, and black pepper. Stir until well mixed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C1CC381F99E1B4589852596270AE4E7', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', 4, NULL, NULL, 2, NULL, 0, 'Spoon the tomato mixture over the pork roast and vegetables. If the tomatoes aren''t very juicy, add about 1/2 cup of chicken stock.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'040FADA5C02E7F4B8465B9800B2AC905', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', 5, NULL, NULL, 2, NULL, 0, 'Cover the pot and set the slow cooker to 3 hours on high or 5 hours on low. If you want the pork to be falling apart, add an hour to the low setting or about 1/2 hour to 45 minutes to the high setting.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B1F08DACA98184EB997BE319ED77E9C', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', 6, NULL, NULL, 2, NULL, 0, 'When the dish is ready, arrange the pork and sweet potatoes on a platter and keep warm. Drizzle juices over the pork and vegetables just before serving.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99D41233B9FC0B41BAA1239DE5A86C7C', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', X'B340B102D9A37B4EBB1F1FEE17AAC7DF', 7, NULL, NULL, 2, NULL, 0, 'Enjoy your meal!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC9876DA115D78458062B66C6582EF58', X'99D78E846665E444BA692A0472BB0D2B', X'99D78E846665E444BA692A0472BB0D2B', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 200 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21CADE02DBDBD745984C6E434D593949', X'99D78E846665E444BA692A0472BB0D2B', X'99D78E846665E444BA692A0472BB0D2B', 1, NULL, NULL, 2, NULL, 0, 'Season pork medallions generously with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA72859E5BDDB14E874C41A207C45B5D', X'99D78E846665E444BA692A0472BB0D2B', X'99D78E846665E444BA692A0472BB0D2B', 2, NULL, NULL, 2, NULL, 0, 'Heat oil in a medium skillet (not non-stick) over medium heat. Add pork and cook on each side until browned, about three minutes per side. Place medallions on a plate in the oven.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FF142CA4D10DE4CAF2ED48B7DFA3D70', X'99D78E846665E444BA692A0472BB0D2B', X'99D78E846665E444BA692A0472BB0D2B', 3, NULL, NULL, 2, NULL, 0, 'Melt butter in skillet. Add shallots, apples, sage, and rosemary and saute until browned. Add Calvados and chicken stock and deglaze the pan. Reduce liquid by 1/3.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2797BDCAA46EFA42BDF8DEFC90870BBF', X'99D78E846665E444BA692A0472BB0D2B', X'99D78E846665E444BA692A0472BB0D2B', 4, NULL, NULL, 2, NULL, 0, 'Add cream and reduce to desired consistency. Divide pork between plates and spoon apples and sauce over the top.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4242D78E7312747B2B75227B96D10B6', X'62E77C02ED1DFF4A8A61D515B33C54DB', X'62E77C02ED1DFF4A8A61D515B33C54DB', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F (180 C/Gas 4).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A8B5745DE512D40BFB941B657BCEB4E', X'62E77C02ED1DFF4A8A61D515B33C54DB', X'62E77C02ED1DFF4A8A61D515B33C54DB', 1, NULL, NULL, 2, NULL, 0, 'Place flour in the cooking bag and shake to coat the interior. Cut the pork tenderloin lengthwise, stopping about 1/2-inch from cutting through. Open the split and flatten the meat out with the smooth side of a meat tenderizer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E123C8041F401143960DC0250FDA7907', X'62E77C02ED1DFF4A8A61D515B33C54DB', X'62E77C02ED1DFF4A8A61D515B33C54DB', 2, NULL, NULL, 2, NULL, 0, 'In a skillet over medium-low heat, saute the onions, celery, carrot, garlic, and thyme in the butter until the vegetables are tender. Add 1 1/2 cups cooked wild rice; stir to combine. Taste and add kosher salt and freshly ground black pepper, as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEA0EF7F117DAB48A10E808A0067C9E7', X'62E77C02ED1DFF4A8A61D515B33C54DB', X'62E77C02ED1DFF4A8A61D515B33C54DB', 3, NULL, NULL, 2, NULL, 0, 'Spread the stuffing mixture over the split flattened pork tenderloin. Fold the meat back over itself and tie with kitchen string to secure. Add the tied tenderloin and chicken broth to the prepared cooking bag; close bag with the provided tie.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'722F273658E54D4788E6CBA1C3925ED3', X'62E77C02ED1DFF4A8A61D515B33C54DB', X'62E77C02ED1DFF4A8A61D515B33C54DB', 4, NULL, NULL, 2, NULL, 0, 'Place the bag in a shallow baking dish or pan. Bake in the preheated oven for about 1 hour, or until the pork reaches the safe minimum temperature of 145 F (63 C).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15ECEC33C8B073439C23D3A71C502098', X'62E77C02ED1DFF4A8A61D515B33C54DB', X'62E77C02ED1DFF4A8A61D515B33C54DB', 5, NULL, NULL, 2, NULL, 0, 'Pour the chicken broth into a saucepan and skim off excess fat. Combine cornstarch with a few teaspoons of water and stir to dissolve. Stir the cornstarch mixture into the broth and cook, stirring, until thickened. Taste and adjust seasonings with salt and pepper, if necessary.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61249F91F56D5B49BBF1526CC8A790B8', X'62E77C02ED1DFF4A8A61D515B33C54DB', X'62E77C02ED1DFF4A8A61D515B33C54DB', 6, NULL, NULL, 2, NULL, 0, 'Slice the pork and serve with the sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA419A0FE2EF0B4EB4EB47D60AC776CB', X'B66714FB9BAD3148A8A028CBEC7FC500', X'B66714FB9BAD3148A8A028CBEC7FC500', 0, NULL, NULL, 2, NULL, 0, 'Combine the thyme, marjoram, salt, pepper, minced garlic, maple syrup, chicken broth, lemon juice, and melted butter in a large ziplock plastic bag and mix gently.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E340A0EB2C31594985344F79182B2DBB', X'B66714FB9BAD3148A8A028CBEC7FC500', X'B66714FB9BAD3148A8A028CBEC7FC500', 1, NULL, NULL, 2, NULL, 0, 'Add the pork slices, close the bag, turn to coat the meat, and refrigerate for 1 to 2 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'464BA2E26B3EC44A93DFBE5D7A9F7FA3', X'B66714FB9BAD3148A8A028CBEC7FC500', X'B66714FB9BAD3148A8A028CBEC7FC500', 2, NULL, NULL, 2, NULL, 0, 'Remove the pork from the marinade; discard marinade.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C9E94E8A74AAC4296870084F4B2A7CC', X'B66714FB9BAD3148A8A028CBEC7FC500', X'B66714FB9BAD3148A8A028CBEC7FC500', 3, NULL, NULL, 2, NULL, 0, 'Grill the pork slices on a dual contact two-sided grill for 3 to 5 minutes until cooked to 145F, or cook for 6 to 9 minutes, turning once, on a single grill over medium coals. You can also grill this outside on gas or charcoal; cook for 6 to 9 minutes, making sure you check for doneness at the earliest time.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEF39BBDCE641D459A9FCCB19753DC7C', X'B66714FB9BAD3148A8A028CBEC7FC500', X'B66714FB9BAD3148A8A028CBEC7FC500', 4, NULL, NULL, 2, NULL, 0, 'Brush the pork with more maple syrup, if desired, and let stand for 2 minutes, and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F57117E748738E4EB4950196714E9102', X'612699E54E8E1344B0CAF25BF6B61157', X'612699E54E8E1344B0CAF25BF6B61157', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3FB8CA05AED1540874B650557D73E3B', X'612699E54E8E1344B0CAF25BF6B61157', X'612699E54E8E1344B0CAF25BF6B61157', 1, NULL, NULL, 2, NULL, 0, 'Pat the pork loin with a paper towel to remove excess moisture. If desired, cut away any excess fat. You can also leave it on and then pour the liquids in a gravy separator to remove excess fat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D69F23A39397E84DB5F89D3C8C84FF16', X'612699E54E8E1344B0CAF25BF6B61157', X'612699E54E8E1344B0CAF25BF6B61157', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl combine the garlic powder, thyme, paprika, freshly ground black pepper, and kosher salt.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95EC3BF2B855144C99222DD639BA2AE0', X'612699E54E8E1344B0CAF25BF6B61157', X'612699E54E8E1344B0CAF25BF6B61157', 3, NULL, NULL, 2, NULL, 0, 'Rub the pork all over with the mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46B6B11D02F2D446BFD533DEA711D37D', X'612699E54E8E1344B0CAF25BF6B61157', X'612699E54E8E1344B0CAF25BF6B61157', 4, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large skillet and sear the pork on all sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F08F6E1EB3EE694F813E39EDCA3328D9', X'612699E54E8E1344B0CAF25BF6B61157', X'612699E54E8E1344B0CAF25BF6B61157', 5, NULL, NULL, 2, NULL, 0, 'Place the pork in a slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C69B206679CD3C428934CA3F1280760F', X'612699E54E8E1344B0CAF25BF6B61157', X'612699E54E8E1344B0CAF25BF6B61157', 6, NULL, NULL, 2, NULL, 0, 'In a 2-cup measuring cup combine the chicken stock, lemon juice, soy sauce, and Worcestershire sauce; blend well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'149244D52558BB43A13EAB6268EC734C', X'612699E54E8E1344B0CAF25BF6B61157', X'612699E54E8E1344B0CAF25BF6B61157', 7, NULL, NULL, 2, NULL, 0, 'Pour the liquids into the crock pot with the roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'599708A6BE546E469C28A203C365E74F', X'612699E54E8E1344B0CAF25BF6B61157', X'612699E54E8E1344B0CAF25BF6B61157', 8, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 6 to 8 hours or on high for about 3 hours. Check the roast at the lower time for doneness to avoid overcooking. An instant-read thermometer should register at least 145 F (63 C).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'959C1BC702C9B446A7F3830BF3CB50B5', X'612699E54E8E1344B0CAF25BF6B61157', X'612699E54E8E1344B0CAF25BF6B61157', 9, NULL, NULL, 2, NULL, 0, 'Remove the roast to a platter, tent with foil, and keep warm.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA8393C1E3DD0841BD56ADE1C6010742', X'612699E54E8E1344B0CAF25BF6B61157', X'612699E54E8E1344B0CAF25BF6B61157', 10, NULL, NULL, 2, NULL, 0, 'Pour the liquids into a gravy separator, then into a medium saucepan. Bring to a boil. Taste and adjust the seasonings. If the liquids are very watery from the long cooking time, boil for a few minutes to reduce and concentrate the flavors.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A48A59C95B4FFE49BB150F0E0280AC2B', X'612699E54E8E1344B0CAF25BF6B61157', X'612699E54E8E1344B0CAF25BF6B61157', 11, NULL, NULL, 2, NULL, 0, 'Put the cornstarch in a small cup or bowl and whisk with the cold water. Whisk the cornstarch mixture into the simmering liquids and continue cooking, stirring, until the sauce is thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B436732ECA72B3449F35C3435479598D', X'612699E54E8E1344B0CAF25BF6B61157', X'612699E54E8E1344B0CAF25BF6B61157', 12, NULL, NULL, 2, NULL, 0, 'Slice the pork and serve it with the hot thickened sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC951305220F87498F6B35BFC3AE01C7', X'612699E54E8E1344B0CAF25BF6B61157', X'612699E54E8E1344B0CAF25BF6B61157', 13, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'779185412C0E44448BBE54F75B9C54CC', X'94F8A31DE480104CBA6542F19753432C', X'94F8A31DE480104CBA6542F19753432C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B83B3D22B2B51449AC3151C0939178EC', X'94F8A31DE480104CBA6542F19753432C', X'94F8A31DE480104CBA6542F19753432C', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325 F/165 C/Gas 3.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50D4E532FF55134BBF15E9622711EE4A', X'94F8A31DE480104CBA6542F19753432C', X'94F8A31DE480104CBA6542F19753432C', 2, NULL, NULL, 2, NULL, 0, 'Rub pork loin with 1 teaspoon garlic powder, 1 teaspoon onion powder, 1 1/2 teaspoons thyme, then sprinkle with salt and pepper; place pork loin in a shallow roasting pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5406A47BB2048E43B732B566E8A93115', X'94F8A31DE480104CBA6542F19753432C', X'94F8A31DE480104CBA6542F19753432C', 3, NULL, NULL, 2, NULL, 0, 'Roast in the preheated oven for 50 to 55 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98940966D9880B4FA07F8E58C33B611E', X'94F8A31DE480104CBA6542F19753432C', X'94F8A31DE480104CBA6542F19753432C', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, peel and quarter potatoes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1002F2D1E1916043BCB550D54BE2E017', X'94F8A31DE480104CBA6542F19753432C', X'94F8A31DE480104CBA6542F19753432C', 5, NULL, NULL, 2, NULL, 0, 'Place potatoes in a large bowl; toss with olive oil, 1 teaspoon dried thyme, chives, 1/2 teaspoon garlic powder, and salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3760D1BF71D01468748F636A4358EB6', X'94F8A31DE480104CBA6542F19753432C', X'94F8A31DE480104CBA6542F19753432C', 6, NULL, NULL, 2, NULL, 0, 'Place potatoes around pork loin; roast an additional 45 to 60 minutes, or until pork registers at least 145 F on a thermometer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'812DA5BECB4B884691A910D7374B1BA4', X'94F8A31DE480104CBA6542F19753432C', X'94F8A31DE480104CBA6542F19753432C', 7, NULL, NULL, 2, NULL, 0, 'Cover roast pork loin with foil and let stand for about 15 minutes before slicing.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'088F92E5DB7AA04184EC3A5F9CE74C01', X'94F8A31DE480104CBA6542F19753432C', X'94F8A31DE480104CBA6542F19753432C', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC184D399D149240977EBBCDDF081860', X'EF36C01B8166EE4C940D1E307586FFB5', X'EF36C01B8166EE4C940D1E307586FFB5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2787388E8BD464408985E350F46F19B1', X'EF36C01B8166EE4C940D1E307586FFB5', X'EF36C01B8166EE4C940D1E307586FFB5', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 400 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35F64D7A2F674B439CC167BDC1DE3185', X'EF36C01B8166EE4C940D1E307586FFB5', X'EF36C01B8166EE4C940D1E307586FFB5', 2, NULL, NULL, 2, NULL, 0, 'Butterfly the pork by cutting horizontally down the center, but not all the way through. Open the meat up and lay flat. Place a sheet of plastic wrap over the meat and pound to thin it to a uniform thickness.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C304F7AE2020DC49A4F0631C20F097E0', X'EF36C01B8166EE4C940D1E307586FFB5', X'EF36C01B8166EE4C940D1E307586FFB5', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle the meat lightly with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3CB024BC750D44284AB36B193C61A93', X'EF36C01B8166EE4C940D1E307586FFB5', X'EF36C01B8166EE4C940D1E307586FFB5', 4, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the stuffing mix, chicken broth, butter, and cranberries. Mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83663A3FA6580C4795A5CACA66DC7D01', X'EF36C01B8166EE4C940D1E307586FFB5', X'EF36C01B8166EE4C940D1E307586FFB5', 5, NULL, NULL, 2, NULL, 0, 'Spoon onto the pork and pat evenly over the surface. Roll the pork up and fasten with a few pieces of kitchen twine.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B98CCB81CA68DB4D8179119DC2A336D5', X'EF36C01B8166EE4C940D1E307586FFB5', X'EF36C01B8166EE4C940D1E307586FFB5', 6, NULL, NULL, 2, NULL, 0, 'Place, seam side down, in the baking dish. Rub the rolled-up pork all over with the cut side of the garlic. Sprinkle lightly with seasoned salt or more salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18C6A349F3D2D441AC62625EE625F3A6', X'EF36C01B8166EE4C940D1E307586FFB5', X'EF36C01B8166EE4C940D1E307586FFB5', 7, NULL, NULL, 2, NULL, 0, 'Roast for 10 minutes; reduce heat to 350 F and continue roasting for 45 minutes longer, or until pork registers 155 F on an instant-read thermometer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6853B91E6519BD4EABE7550F04D58A8C', X'EF36C01B8166EE4C940D1E307586FFB5', X'EF36C01B8166EE4C940D1E307586FFB5', 8, NULL, NULL, 2, NULL, 0, 'Let the pork stand for 5 minutes before slicing into 1/2-inch slices.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'732E1A248E71AF4C8FC18B70EFE06096', X'C9921E2E61FE3845B35E2CD35592D309', X'C9921E2E61FE3845B35E2CD35592D309', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D66A209B4EB2884A887164A7F7E4370C', X'C9921E2E61FE3845B35E2CD35592D309', X'C9921E2E61FE3845B35E2CD35592D309', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325 F. Line a roasting pan with foil and place a rack in the pan. Add the pork roast to the rack and roast for about 25 to 30 minutes per pound, or 2 hours to 2 1/2 hours for a 5-pound roast. The roast should register at least 145 F on a food thermometer, the minimum safe temperature for pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0DFADE92565B047BEA20265C0614A8D', X'C9921E2E61FE3845B35E2CD35592D309', X'C9921E2E61FE3845B35E2CD35592D309', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the barbecue sauce. Heat the butter or vegetable oil in a medium saucepan over medium-low heat. Add the onion and cook until softened and translucent.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DCD79E21CE2D443B9D473BBE1472FB0', X'C9921E2E61FE3845B35E2CD35592D309', X'C9921E2E61FE3845B35E2CD35592D309', 3, NULL, NULL, 2, NULL, 0, 'Add the water, ketchup, cider vinegar, brown sugar, Worcestershire sauce, oregano, paprika, salt, garlic powder, dry mustard, black pepper, and ground cloves and mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6585D9628A778144915E04474EA77F6F', X'C9921E2E61FE3845B35E2CD35592D309', X'C9921E2E61FE3845B35E2CD35592D309', 4, NULL, NULL, 2, NULL, 0, 'Bring the sauce mixture to a simmer. Reduce the heat to low and simmer for about 15 to 20 minutes, or until the sauce has reduced and thickened somewhat and the flavors are concentrated.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9E6994D0BCBB14C904A1BD1295C60D1', X'C9921E2E61FE3845B35E2CD35592D309', X'C9921E2E61FE3845B35E2CD35592D309', 5, NULL, NULL, 2, NULL, 0, 'Baste the pork roast with the barbecue sauce several times during the last 30 to 45 minutes of roasting time.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F14417B56F5AC44DBC5A0A35840D0626', X'C9921E2E61FE3845B35E2CD35592D309', X'C9921E2E61FE3845B35E2CD35592D309', 6, NULL, NULL, 2, NULL, 0, 'Let the roast rest for 10 to 15 minutes before slicing.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8506CBA44CC21449A7120F95EC53D961', X'A0463DA618FBCB40819E6DF7359F5B9F', X'A0463DA618FBCB40819E6DF7359F5B9F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43F35D97535BB842BC41BCD7861F4E0C', X'A0463DA618FBCB40819E6DF7359F5B9F', X'A0463DA618FBCB40819E6DF7359F5B9F', 1, NULL, NULL, 2, NULL, 0, 'With a small, sharp knife, make 16 small cutsabout 1/4- to 1/2-inch deepin the pork roast. Insert the garlic quarters.');
