INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'588ECDE7D5F1334298915E4770B7E218', X'A0463DA618FBCB40819E6DF7359F5B9F', X'A0463DA618FBCB40819E6DF7359F5B9F', 3, NULL, NULL, 2, NULL, 0, 'Pour 1/2 cup water into slow cooker; add the roast. Cover and cook on LOW for 7 to 9 hours. Pork roast should read at least 145 F on an instant-read thermometer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1C9D80A7EBE6F4987D06E60C78815E4', X'A0463DA618FBCB40819E6DF7359F5B9F', X'A0463DA618FBCB40819E6DF7359F5B9F', 4, NULL, NULL, 2, NULL, 0, 'If desired, thicken juices. Remove roast from juices. Combine cornstarch with the 3 tablespoons water; stir until smooth then stir into the crockpot juices. Cook on HIGH until thickened. Serve with the pork roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39953CB779FD444B9EB7A81B07E44C60', X'A0463DA618FBCB40819E6DF7359F5B9F', X'A0463DA618FBCB40819E6DF7359F5B9F', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60122C5F00022D44A69738C2B14DFF91', X'0768993E1C562C43ACF0CDDC4E7238D0', X'0768993E1C562C43ACF0CDDC4E7238D0', 0, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the brown sugar with the curry powder, cumin, salt, and pepper, and mix well. Rub the spice mixture thoroughly into the pork roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'576F6A20788A9A48AE0FE85627E01DF0', X'0768993E1C562C43ACF0CDDC4E7238D0', X'0768993E1C562C43ACF0CDDC4E7238D0', 1, NULL, NULL, 2, NULL, 0, 'Place the chopped onions and garlic in the bottom of a 4 to 5-quart crockpot. Place the roast on top of the onions. Pour the apple juice overall.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5822F40FAF314498EBE83FE94D2F025', X'0768993E1C562C43ACF0CDDC4E7238D0', X'0768993E1C562C43ACF0CDDC4E7238D0', 2, NULL, NULL, 2, NULL, 0, 'Cover the crockpot and cook on low for 7 to 8 hours or until a reliable meat thermometer reads 140 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45B03D9DB121F948BCF6D44835CBF3D4', X'0768993E1C562C43ACF0CDDC4E7238D0', X'0768993E1C562C43ACF0CDDC4E7238D0', 3, NULL, NULL, 2, NULL, 0, 'Remove the pork from the crockpot and cover it snugly with foil. Let the pork stand for 15 minutes before slicing.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A02AFBA8D938D2478AD90D479FF0213B', X'0768993E1C562C43ACF0CDDC4E7238D0', X'0768993E1C562C43ACF0CDDC4E7238D0', 4, NULL, NULL, 2, NULL, 0, 'You can thicken the oniony juices with some cornstarch dissolved in water and serve alongside if you like. Pour the juices from the slow cooker into a saucepan. Add 1 to 2 tablespoons of cornstarch mixed with more apple juice. Bring the mixture to a boil until it thickens to the consistency you want.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18576042ED25E54D95857363BC1A2824', X'5394796CBB434447817521F2B3CD6017', X'5394796CBB434447817521F2B3CD6017', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5D5EC85ED7F2C4CB46585FCA1F52526', X'5394796CBB434447817521F2B3CD6017', X'5394796CBB434447817521F2B3CD6017', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC27831E08E2204BB4C01D9E5251F791', X'5394796CBB434447817521F2B3CD6017', X'5394796CBB434447817521F2B3CD6017', 2, NULL, NULL, 2, NULL, 0, 'Combine the steak seasoning and 1 tablespoon brown sugar; rub the mixture all over the roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A01FA4ADC3D144198EAC97B2B03B066', X'5394796CBB434447817521F2B3CD6017', X'5394796CBB434447817521F2B3CD6017', 3, NULL, NULL, 2, NULL, 0, 'Put the roast in a foil-lined baking dish. Arrange bacon over the pork, tucking the ends under the roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'916F2B092DEAF44B8D96FB7151440708', X'5394796CBB434447817521F2B3CD6017', X'5394796CBB434447817521F2B3CD6017', 4, NULL, NULL, 2, NULL, 0, 'Roast to about 140 F on an instant-read thermometer, or about 55 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B56DD744D040794F9A18173661448D63', X'5394796CBB434447817521F2B3CD6017', X'5394796CBB434447817521F2B3CD6017', 5, NULL, NULL, 2, NULL, 0, 'Combine glaze ingredients. Generously brush the glaze mixture over the roast and continue roasting for 15 minutes, or until the roast registers about 155 F on a meat thermometer or instant-read thermometer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C85CFCD0F961314597481B8FBED60E06', X'5394796CBB434447817521F2B3CD6017', X'5394796CBB434447817521F2B3CD6017', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAE7AB59FB56E6438685D569599F82AD', X'99292FDDE22B0F439E42BE66E9DD4DD7', X'99292FDDE22B0F439E42BE66E9DD4DD7', 0, NULL, NULL, 2, NULL, 0, 'Place potatoes and bell peppers in the bottom of 4 to 5-quart slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC865C94E385CA4092BD9F867606BF57', X'99292FDDE22B0F439E42BE66E9DD4DD7', X'99292FDDE22B0F439E42BE66E9DD4DD7', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix mustard, thyme, salt, and pepper and spread evenly over pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DBB2683C18B42449FBD7EEB46FDFABA', X'99292FDDE22B0F439E42BE66E9DD4DD7', X'99292FDDE22B0F439E42BE66E9DD4DD7', 2, NULL, NULL, 2, NULL, 0, 'Place the pork on top of the vegetables in the slow cooker and pour beef broth over all.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF99971FAEAC0C45A302AE6EB676F0D8', X'99292FDDE22B0F439E42BE66E9DD4DD7', X'99292FDDE22B0F439E42BE66E9DD4DD7', 3, NULL, NULL, 2, NULL, 0, 'Cover the slow cooker and cook on low for 8 to 9 hours. Slice pork and serve with vegetables and juices. If you have a new, hotter cooking crockpot, cook on low for 6 to 7 hours until pork registers 145 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C87591F765ABA747A9AB7AF695303FB0', X'3BC4AE80380A064B9F136499DE8601DF', X'3BC4AE80380A064B9F136499DE8601DF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0631D2860DB8C2409C84F699DCDEBE34', X'3BC4AE80380A064B9F136499DE8601DF', X'3BC4AE80380A064B9F136499DE8601DF', 1, NULL, NULL, 2, NULL, 0, 'Lightly grease a 5 to 6-quart slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6BA2C5A90AC2541BC2A932692F519C2', X'3BC4AE80380A064B9F136499DE8601DF', X'3BC4AE80380A064B9F136499DE8601DF', 2, NULL, NULL, 2, NULL, 0, 'Put stuffing mix in a large bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADDDB0D9BAF48D48A9CB126A28DE0E97', X'3BC4AE80380A064B9F136499DE8601DF', X'3BC4AE80380A064B9F136499DE8601DF', 3, NULL, NULL, 2, NULL, 0, 'In a skillet or saut pan, cook the onion, celery, and carrots in the butter over medium-low heat until softened. Combine the onion mixture with the stuffing mix.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D2A5E117E373B47BB8B61C386496CF0', X'3BC4AE80380A064B9F136499DE8601DF', X'3BC4AE80380A064B9F136499DE8601DF', 4, NULL, NULL, 2, NULL, 0, 'Add the parsley, chicken broth, 1/2 teaspoon salt, and dried cranberries; mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29D8F612A3577E4D9B2D118B3D91C8B8', X'3BC4AE80380A064B9F136499DE8601DF', X'3BC4AE80380A064B9F136499DE8601DF', 5, NULL, NULL, 2, NULL, 0, 'Spoon stuffing mixture into the slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8538BC2056FE3D469F14BA71B56F25C1', X'3BC4AE80380A064B9F136499DE8601DF', X'3BC4AE80380A064B9F136499DE8601DF', 6, NULL, NULL, 2, NULL, 0, 'Combine the rub ingredients and rub over the pork roast. Place pork on the stuffing mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6944F5381FFCC949AA77034ED1848B3D', X'3BC4AE80380A064B9F136499DE8601DF', X'3BC4AE80380A064B9F136499DE8601DF', 7, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW for 7 to 9 hours, or until pork is cooked through.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94A6AE5C08B4F64DAF7C64A195442189', X'2AD8478D4453584698EB5163E6ECFA1B', X'2AD8478D4453584698EB5163E6ECFA1B', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 400 F. Line a baking pan with heavy duty foil and place the roast on the foil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F98AF3C50B6BBC47B663DD041B9F58BE', X'2AD8478D4453584698EB5163E6ECFA1B', X'2AD8478D4453584698EB5163E6ECFA1B', 1, NULL, NULL, 2, NULL, 0, 'Rub the pork loin all over with the Cajun seasoning blend.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E268F204E8E0349BC4F77E4F73BE6CF', X'2AD8478D4453584698EB5163E6ECFA1B', X'2AD8478D4453584698EB5163E6ECFA1B', 2, NULL, NULL, 2, NULL, 0, 'Roast for 20 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'579DB9635B017C4AAC27FECEAACED217', X'2AD8478D4453584698EB5163E6ECFA1B', X'2AD8478D4453584698EB5163E6ECFA1B', 3, NULL, NULL, 2, NULL, 0, 'Combine the apple juice, honey, and brown sugar; mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5346927D7D4B1045B10F5CB5766A430C', X'2AD8478D4453584698EB5163E6ECFA1B', X'2AD8478D4453584698EB5163E6ECFA1B', 4, NULL, NULL, 2, NULL, 0, 'Reduce the oven heat to 325 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BB5EAB6D3D19747BE86AA818255E971', X'2AD8478D4453584698EB5163E6ECFA1B', X'2AD8478D4453584698EB5163E6ECFA1B', 5, NULL, NULL, 2, NULL, 0, 'Roast for about 1 hour to 1 1/2 hours longer, frequently basting with the honey mixture, or to about 145 F on a meat thermometer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD844648F1D9984EA81DFABC95BF513E', X'2AD8478D4453584698EB5163E6ECFA1B', X'2AD8478D4453584698EB5163E6ECFA1B', 6, NULL, NULL, 2, NULL, 0, 'Let the roast rest for 15 to 20 minutes before slicing.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DFC39E8706F7C4ABB09CE6C3B03674E', X'9C34A3DFBAC90442A56CA6848BC307D9', X'9C34A3DFBAC90442A56CA6848BC307D9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D5147ACFB54B446BC5E7EBA950183CD', X'9C34A3DFBAC90442A56CA6848BC307D9', X'9C34A3DFBAC90442A56CA6848BC307D9', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425 F. Place a rack in a shallow, foil-lined roasting pan or 13x9x2-inch baking pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A7DE4501F6A724AB7A64BA903479749', X'9C34A3DFBAC90442A56CA6848BC307D9', X'9C34A3DFBAC90442A56CA6848BC307D9', 2, NULL, NULL, 2, NULL, 0, 'Place the pork loin roast on the rack and rub all sides of the pork with the cut sides of the pieces of garlic. Sprinkle all sides of roast evenly with the seasoned salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'102ACB1252536C4A8B322F3DCA8E9CFB', X'9C34A3DFBAC90442A56CA6848BC307D9', X'9C34A3DFBAC90442A56CA6848BC307D9', 3, NULL, NULL, 2, NULL, 0, 'Roast the pork uncovered for about 20 to 25 minutes per pound, or until the internal temperature of the roast when using an instant-read thermometer is at least 145 F to 160 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F346703025C0B543A7F48A032C506069', X'9C34A3DFBAC90442A56CA6848BC307D9', X'9C34A3DFBAC90442A56CA6848BC307D9', 4, NULL, NULL, 2, NULL, 0, 'Tent the roast loosely with foil and let it rest for 10 to 15 minutes before slicing.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B3D71498FC5FB4B92642A8FED4EFB63', X'9C34A3DFBAC90442A56CA6848BC307D9', X'9C34A3DFBAC90442A56CA6848BC307D9', 5, NULL, NULL, 2, NULL, 0, 'Slice and serve with your choice of sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C717DEE15BD674428C6A70A1E153F22F', X'AB13060597A18847966CA2FAC4B5945D', X'AB13060597A18847966CA2FAC4B5945D', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 325 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A34CD58BAF1874191D5DF239F12758B', X'AB13060597A18847966CA2FAC4B5945D', X'AB13060597A18847966CA2FAC4B5945D', 1, NULL, NULL, 2, NULL, 0, 'Rub the roast all over with the garlic halves; discard the garlic. Place the roast, fat side up, on a rack in a roasting pan; sprinkle with salt and pepper. If there is little fat on top of the roast, lay 2 slices of bacon across the top (optional).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C9E24238DA12644860F6EDC63AACDCF', X'AB13060597A18847966CA2FAC4B5945D', X'AB13060597A18847966CA2FAC4B5945D', 2, NULL, NULL, 2, NULL, 0, 'Roast the pork, uncovered, for about 2 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DCD05239C922842833E51CE5D092890', X'AB13060597A18847966CA2FAC4B5945D', X'AB13060597A18847966CA2FAC4B5945D', 3, NULL, NULL, 2, NULL, 0, 'Combine the remaining ingredients in a bowl. Brush the glaze over the roast and continue baking for 30 to 45 minutes, or until the roast registers about 155 F on a meat thermometer inserted into the center of the meatiest part of the roast (not touching bone or fat). Let the roast rest for 10 minutes before slicing.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47ACB983086445458843D85F424FAD30', X'D4F9E1B7085E9149A9C2C8E14B4E412F', X'D4F9E1B7085E9149A9C2C8E14B4E412F', 0, NULL, NULL, 2, NULL, 0, 'Whisk together apple cider, brown sugar, and salt. If you don''t have a whisk, substitute with a fork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD5F317AB1A7A547BDD0C7D707EC66B7', X'D4F9E1B7085E9149A9C2C8E14B4E412F', X'D4F9E1B7085E9149A9C2C8E14B4E412F', 1, NULL, NULL, 2, NULL, 0, 'Then, place pork loin roast and apple cider mixture in a heavy zip-top bag. Squeeze out the air from the bag and seal. Refrigerate for eight hours or overnight.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C4973E5564C5E469F5CC26532493126', X'D4F9E1B7085E9149A9C2C8E14B4E412F', X'D4F9E1B7085E9149A9C2C8E14B4E412F', 2, NULL, NULL, 2, NULL, 0, 'Drain pork loin roast, discarding the liquid. Rub all sides with Worcestershire sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEE2684671B340428B6B3A294D979270', X'D4F9E1B7085E9149A9C2C8E14B4E412F', X'D4F9E1B7085E9149A9C2C8E14B4E412F', 3, NULL, NULL, 2, NULL, 0, 'Next, whisk together brown sugar, kosher salt, paprika, onion powder, sage, and chipotle chile pepper. Coat the roast evenly with the spice mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9335EEDC7D9F444687A5993A2BBB21FD', X'D4F9E1B7085E9149A9C2C8E14B4E412F', X'D4F9E1B7085E9149A9C2C8E14B4E412F', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 F. Line a 9-inch by 13-inch baking pan with foil and insert a roasting rack. Place the pork loin roast on the baking rack, fat side up.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'892508672F405546AC17E59E31E5A24F', X'D4F9E1B7085E9149A9C2C8E14B4E412F', X'D4F9E1B7085E9149A9C2C8E14B4E412F', 5, NULL, NULL, 2, NULL, 0, 'Roast for 20 minutes. Reduce heat to 350 F, and roast an additional hour (165 F internal temperature recommended). Remove from oven, tent with foil and let rest for 15 minutes before slicing to serve. Garnish with apple slices, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'798C85C797E68943A5756C95864F34FD', X'212C3B43C3159C4E803A8CDEFD414B8A', X'212C3B43C3159C4E803A8CDEFD414B8A', 0, NULL, NULL, 2, NULL, 0, 'Place the pork loin roast in the slow cooker and coat all sides with the minced garlic.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'188EFA988BCFF146A292D29C745A543E', X'212C3B43C3159C4E803A8CDEFD414B8A', X'212C3B43C3159C4E803A8CDEFD414B8A', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the cranberry sauce, brown sugar, apple juice, and chopped apples. Pour sauce over pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDFAE996D190564FA7338052EE74C48B', X'212C3B43C3159C4E803A8CDEFD414B8A', X'212C3B43C3159C4E803A8CDEFD414B8A', 2, NULL, NULL, 2, NULL, 0, 'Cover the crockpot and cook on LOW for 7 to 9 hours. Taste the sauce and add salt and pepper as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2F9139E44E15040BBCBE7A6EEF630A1', X'212C3B43C3159C4E803A8CDEFD414B8A', X'212C3B43C3159C4E803A8CDEFD414B8A', 3, NULL, NULL, 2, NULL, 0, 'If you think the sauce needs thickening, you can add a cornstarch slurry. In a small bowl, stir the cornstarch with the cold water until smooth. Add the slurry to the liquids and stir to blend. Increase the setting to HIGH and continue cooking for 5 to 10 minutes, until thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC6F072F5FFC81499BFE57590002E34E', X'212C3B43C3159C4E803A8CDEFD414B8A', X'212C3B43C3159C4E803A8CDEFD414B8A', 4, NULL, NULL, 2, NULL, 0, 'Remove the pork from the crockpot onto a cutting board. Slice the pork and arrange on a platter. Spoon over sauce and serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6327F110C14EEB49ABE18FE3F5FF6F3B', X'11CFDED0BE385D43A66B3E30BC771DB1', X'11CFDED0BE385D43A66B3E30BC771DB1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C4CE881F7DCCE47B4E46CF5FCCB0EC9', X'11CFDED0BE385D43A66B3E30BC771DB1', X'11CFDED0BE385D43A66B3E30BC771DB1', 1, NULL, NULL, 2, NULL, 0, 'In a large Dutch oven or oven-safe roasting pan, coat the bottom with vegetable oil and bring the temperature up to high. Brown the pork loin roast in the hot oil on all sides. Remove the pork roast from the pan. Drain all but 3 tablespoons of fat from pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB1202D482BA9840BE222F3121F5EFE4', X'11CFDED0BE385D43A66B3E30BC771DB1', X'11CFDED0BE385D43A66B3E30BC771DB1', 2, NULL, NULL, 2, NULL, 0, 'In the pan, saut onions and carrots in the pan drippings until softened. Stir in beer, salt, pepper, bay leaf, and whole cloves.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA070F7F3F267F42A6D5731BE534E906', X'11CFDED0BE385D43A66B3E30BC771DB1', X'11CFDED0BE385D43A66B3E30BC771DB1', 3, NULL, NULL, 2, NULL, 0, 'Return the pork to the pan or Dutch oven and cover tightly with aluminum foil. Bake at 350 F for 2 hours or until the pork is tender. Check with a meat thermometer in the thickest part of the roast. It should be at least 145 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E09FA1E7126CB344BF4F566ABC4EA119', X'11CFDED0BE385D43A66B3E30BC771DB1', X'11CFDED0BE385D43A66B3E30BC771DB1', 4, NULL, NULL, 2, NULL, 0, 'Place the pork loin on a serving platter and keep it warm.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C42772132891EA43A4AE37E4D178B43F', X'11CFDED0BE385D43A66B3E30BC771DB1', X'11CFDED0BE385D43A66B3E30BC771DB1', 5, NULL, NULL, 2, NULL, 0, 'Pour the cooking liquid from pan into a large bowl; skim off fat and remove the bay leaf.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8903FB8FC941AD4083EDF4641E3C59C0', X'11CFDED0BE385D43A66B3E30BC771DB1', X'11CFDED0BE385D43A66B3E30BC771DB1', 6, NULL, NULL, 2, NULL, 0, 'Place the liquid with the vegetables into a blender; cover and process at low speed until smooth. Or press through sieve or process in a food processor in batches.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83970B538F7EBB4FAE39F2CF2F38DE7A', X'11CFDED0BE385D43A66B3E30BC771DB1', X'11CFDED0BE385D43A66B3E30BC771DB1', 7, NULL, NULL, 2, NULL, 0, 'Pour the processed beer sauce into a saucepan. Bring to a boil, stirring often.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1974C6E1667D6C499014A30CD266EDB1', X'11CFDED0BE385D43A66B3E30BC771DB1', X'11CFDED0BE385D43A66B3E30BC771DB1', 8, NULL, NULL, 2, NULL, 0, 'Slice the pork roast. Spoon the sauce over the sliced pork to serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE1EAE56210CFC48BA5F5C89DB843BE0', X'D0F1C870391BC543A161F2507C4320EF', X'D0F1C870391BC543A161F2507C4320EF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59600D3706466545A9FBCCD4F2415BC2', X'D0F1C870391BC543A161F2507C4320EF', X'D0F1C870391BC543A161F2507C4320EF', 1, NULL, NULL, 2, NULL, 0, 'Brown pork roast on all sides in a large skillet.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F464AC8E6AC1A4B888AA8FE903251F5', X'D0F1C870391BC543A161F2507C4320EF', X'D0F1C870391BC543A161F2507C4320EF', 2, NULL, NULL, 2, NULL, 0, 'Make cuts in roast with a small knife and insert garlic slices.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F3CEFF3BEFE8D47B1D74DECC448EB20', X'D0F1C870391BC543A161F2507C4320EF', X'D0F1C870391BC543A161F2507C4320EF', 3, NULL, NULL, 2, NULL, 0, 'Place in slow cooker and season with salt, pepper, and sage and thyme or poultry seasoning.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B10CFA7CB34E29459059EC784E48583E', X'D0F1C870391BC543A161F2507C4320EF', X'D0F1C870391BC543A161F2507C4320EF', 4, NULL, NULL, 2, NULL, 0, 'Add broth and wine, if used.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81C3722B830E0848AA66AE6F82420B9E', X'D0F1C870391BC543A161F2507C4320EF', X'D0F1C870391BC543A161F2507C4320EF', 5, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW for 8 to 10 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10030EA1B3D2404280AF19ADCEC237F6', X'D0F1C870391BC543A161F2507C4320EF', X'D0F1C870391BC543A161F2507C4320EF', 6, NULL, NULL, 2, NULL, 0, 'Remove roast and skim excess fat from juices.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBF330596A34624F958962680229DC0E', X'D0F1C870391BC543A161F2507C4320EF', X'D0F1C870391BC543A161F2507C4320EF', 7, NULL, NULL, 2, NULL, 0, 'Combine flour with about three tablespoons cold water and whisk until smooth.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F48915BD2E87B4C9C60153B3505CA49', X'D0F1C870391BC543A161F2507C4320EF', X'D0F1C870391BC543A161F2507C4320EF', 8, NULL, NULL, 2, NULL, 0, 'Turn the slow cooker to high and stir in the flour mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6E56687665024489889C4C0EC009F83', X'D0F1C870391BC543A161F2507C4320EF', X'D0F1C870391BC543A161F2507C4320EF', 9, NULL, NULL, 2, NULL, 0, 'Cook and stir until thickened (this can be done more quickly on the stovetop).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55BC6B76030DCF458126754FF4AF12B9', X'D0F1C870391BC543A161F2507C4320EF', X'D0F1C870391BC543A161F2507C4320EF', 10, NULL, NULL, 2, NULL, 0, 'Serve sauce over the pork with rice or potatoes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A7065858F50F14F83AE0A7951E1C1FB', X'D0F1C870391BC543A161F2507C4320EF', X'D0F1C870391BC543A161F2507C4320EF', 11, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'739BC23F1D9822469557069410D7DBC2', X'E667B1D45004A1419877BB131FB5F6AE', X'E667B1D45004A1419877BB131FB5F6AE', 0, NULL, NULL, 2, NULL, 0, 'Pour the water into a plastic container with a lid, large enough to fit the pork. Add the salt, maple syrup, garlic, black pepper, and rosemary. Whisk together until the salt is dissolved. Add the pork loin roast. The pork should be fully submerged, so place a small plate on top to weigh it down if necessary. Cover tightly and refrigerate for between 8 and 12 hours, or overnight.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'372B7B5925DF484E808449B18A242BE2', X'E667B1D45004A1419877BB131FB5F6AE', X'E667B1D45004A1419877BB131FB5F6AE', 1, NULL, NULL, 2, NULL, 0, 'Remove the pork from the brine, and pat dry with paper towels.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FED919159C2ED42ABB3FE8A03F81E58', X'E667B1D45004A1419877BB131FB5F6AE', X'E667B1D45004A1419877BB131FB5F6AE', 2, NULL, NULL, 2, NULL, 0, 'Rub with the oil, season with salt and fresh ground black pepper to taste, and place, fattier side up, in a small roasting pan. Roast in a preheated 325 F. oven for about 25 minutes per pound. For a 3-pound pork loin roast, the time will be about 1 hour 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C90FDA197730F41A8C20A6AA126F5A5', X'E667B1D45004A1419877BB131FB5F6AE', X'E667B1D45004A1419877BB131FB5F6AE', 3, NULL, NULL, 2, NULL, 0, 'While the pork loin roasts, mix the Dijon Mustard and the maple syrup together in a small bowl.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6ED3CFF99196FE45BBF8C682267569C6', X'E667B1D45004A1419877BB131FB5F6AE', X'E667B1D45004A1419877BB131FB5F6AE', 4, NULL, NULL, 2, NULL, 0, 'Approximately 30 minutes ahead of being done, remove the pork loin from the oven and spread the glaze over the top.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B83E4B9BCFD3EA4098940397A011DA26', X'E667B1D45004A1419877BB131FB5F6AE', X'E667B1D45004A1419877BB131FB5F6AE', 5, NULL, NULL, 2, NULL, 0, 'Remove when the loin''s internal temp reaches 155-160 F. Let rest for at least 10 minutes before slicing and serving.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BF826738BCA1244BEED93F94950B098', X'3FEBE9337578DF44989644A1868C17D7', X'3FEBE9337578DF44989644A1868C17D7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68484BA60744084D874103DE9FB89F11', X'3FEBE9337578DF44989644A1868C17D7', X'3FEBE9337578DF44989644A1868C17D7', 1, NULL, NULL, 2, NULL, 0, 'In a saucepan, combine broth, cornstarch, soy sauce, brown sugar, garlic, and ginger. Bring to a boil, stirring constantly.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'258D814BEA8D8B469DD19308052B6013', X'3FEBE9337578DF44989644A1868C17D7', X'3FEBE9337578DF44989644A1868C17D7', 2, NULL, NULL, 2, NULL, 0, 'Reduce heat and continue to cook, stirring, until thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A52F9AA3075394F9F21671245B37D6E', X'3FEBE9337578DF44989644A1868C17D7', X'3FEBE9337578DF44989644A1868C17D7', 3, NULL, NULL, 2, NULL, 0, 'Arrange pork cubes on skewers, alternating with mushrooms, peppers, and onion wedges.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD27D905282D2E4E9D75C55CFE3DD5F6', X'3FEBE9337578DF44989644A1868C17D7', X'3FEBE9337578DF44989644A1868C17D7', 4, NULL, NULL, 2, NULL, 0, 'Grill or broil for 20 to 30 minutes, or until done, turning and brushing with the sauce mixture frequently.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AEAAAFD9BAF7646800F2B37027234C8', X'3FEBE9337578DF44989644A1868C17D7', X'3FEBE9337578DF44989644A1868C17D7', 5, NULL, NULL, 2, NULL, 0, 'Serve with tomato wedges and hot cooked rice, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE4F9F2F8690C144BF044DC3F11889EF', X'E6D20702B7C076459085119390FBB622', X'E6D20702B7C076459085119390FBB622', 0, NULL, NULL, 2, NULL, 0, 'Pat the pork roast with paper towels to dry.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A79D335ED73724B99FF3F307DFEF85C', X'E6D20702B7C076459085119390FBB622', X'E6D20702B7C076459085119390FBB622', 1, NULL, NULL, 2, NULL, 0, 'Combine the orange juice, minced garlic, mustard, Worcestershire sauce, and honey or brown sugar in a large resealable food storage bag or container. Add the pork roast and seal. Marinate the pork for at least 2 hours or overnight, turning occasionally to keep the roast coated with marinade.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DFAAD0C11B36549999BC87BFDB9F5AB', X'E6D20702B7C076459085119390FBB622', X'E6D20702B7C076459085119390FBB622', 2, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4E6B9FF09358D4892E59A1F38E894DE', X'E6D20702B7C076459085119390FBB622', X'E6D20702B7C076459085119390FBB622', 3, NULL, NULL, 2, NULL, 0, 'Line a roasting pan with foil and place a rack in the pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E3F9270479DEC4987CB7409570F5620', X'E6D20702B7C076459085119390FBB622', X'E6D20702B7C076459085119390FBB622', 4, NULL, NULL, 2, NULL, 0, 'Remove the roast from the marinade and place it on the rack in the prepared roasting pan. Wrap the tops of the bones with foil to keep them from becoming charred, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19B8CB3C5624DB47B38DE8327F917017', X'E6D20702B7C076459085119390FBB622', X'E6D20702B7C076459085119390FBB622', 5, NULL, NULL, 2, NULL, 0, 'Roast the pork for about 20 minutes per pound. The minimum safe temperature for porkaccording to the USDAis 145 F. If unsure, check with an instant-read food thermometer inserted into the thickest part of the roast, not touching bone.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9A10223DDFBB440AEFE5BFD74B92FB9', X'E6D20702B7C076459085119390FBB622', X'E6D20702B7C076459085119390FBB622', 6, NULL, NULL, 2, NULL, 0, 'Bring the remaining marinade to a full boil. Combine the cornstarch and water and stir until smooth. Add the cornstarch mixture to the marinade and cook, stirring, until it is thickened.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'001B3B43154EB642A9CB45C50CB0C332', X'E6D20702B7C076459085119390FBB622', X'E6D20702B7C076459085119390FBB622', 7, NULL, NULL, 2, NULL, 0, 'Tent the pork loosely with foil and let it rest for about 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB3ADB496F03744EBA54F70D7DD859EF', X'E6D20702B7C076459085119390FBB622', X'E6D20702B7C076459085119390FBB622', 8, NULL, NULL, 2, NULL, 0, 'To serve, slice the roast between the ribs and drizzle each slice with some of the sauce mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E83ECA47907E8D4E98E16BCC18EC81B9', X'50C7B87D30933C4EBCF82F60F130C2CB', X'50C7B87D30933C4EBCF82F60F130C2CB', 0, NULL, NULL, 2, NULL, 0, 'Trim excess fat from the roast and sprinkle lightly with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AF96BC936D7F34A8368D212D9B70062', X'50C7B87D30933C4EBCF82F60F130C2CB', X'50C7B87D30933C4EBCF82F60F130C2CB', 1, NULL, NULL, 2, NULL, 0, 'Place the pork loin in the slow cooker or crock pot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA98969243F08740A1E2B1DAFF4F7AFF', X'50C7B87D30933C4EBCF82F60F130C2CB', X'50C7B87D30933C4EBCF82F60F130C2CB', 2, NULL, NULL, 2, NULL, 0, 'In a bowl combine cranberries, honey, orange zest, and spices and blend well. Spoon the cranberry mixture over roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'830780D9B292FA48BECFEE0536E6F18A', X'50C7B87D30933C4EBCF82F60F130C2CB', X'50C7B87D30933C4EBCF82F60F130C2CB', 3, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 7 to 9 hours or until the pork is cooked and tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F3DD4C0DEE51D458C91F21AF337AE00', X'50C7B87D30933C4EBCF82F60F130C2CB', X'50C7B87D30933C4EBCF82F60F130C2CB', 4, NULL, NULL, 2, NULL, 0, 'Serve the roast with mashed or baked potatoes and steamed broccoli or green beans. Seasoned cornbread dressing or bread dressing would be an excellent side dish as well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE359ED3C6AFC44D81B8EC7DB13B1DAD', X'7A4897CC49C619409EBD69FF62CD9E80', X'7A4897CC49C619409EBD69FF62CD9E80', 0, NULL, NULL, 2, NULL, 0, 'Place pork loin in a large glass baking dish. Combine marinade in and pour over meat. Make sure all sides are well coated with mixture. Cover with plastic wrap and place in refrigerator for 4 to 6 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A00465F41A5124DB310A5657A38A272', X'7A4897CC49C619409EBD69FF62CD9E80', X'7A4897CC49C619409EBD69FF62CD9E80', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat. Place onto grill and sear for 2 minutes on each side. Reduce heat to medium or medium-low and grill pork roast for 45 minutes or until internal temperature reaches 165 F, in thickest part of the meat. Once cooked through, remove heat and let meat rest before carving.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD20C0D5C0260C4BB1CDDF5ADE9A751A', X'699E38560C0F2D43B1C20604A8CE7AC1', X'699E38560C0F2D43B1C20604A8CE7AC1', 0, NULL, NULL, 2, NULL, 0, 'Rub the pork roast all over with the barbecue seasonings or seasoned salt.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52F1553848422C4F8AC18C357F261410', X'699E38560C0F2D43B1C20604A8CE7AC1', X'699E38560C0F2D43B1C20604A8CE7AC1', 1, NULL, NULL, 2, NULL, 0, 'Place the roast in the crock pot and then add the water or broth and onion.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F644A7C8D9D7441A1AB2F31453C1A06', X'699E38560C0F2D43B1C20604A8CE7AC1', X'699E38560C0F2D43B1C20604A8CE7AC1', 2, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW for 4 to 6 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68610B81A65E1445ADDA76452C7B32C0', X'699E38560C0F2D43B1C20604A8CE7AC1', X'699E38560C0F2D43B1C20604A8CE7AC1', 3, NULL, NULL, 2, NULL, 0, 'Drain off the excess liquids and add the canned beans, barbecue sauce, and pepper; cook for 2 to 3 hours longer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'266E295F931F36498DA2BAB38749F54A', X'699E38560C0F2D43B1C20604A8CE7AC1', X'699E38560C0F2D43B1C20604A8CE7AC1', 4, NULL, NULL, 2, NULL, 0, 'Taste and add salt, as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D1243449732EF4CB15CC6746A1AAF8A', X'699E38560C0F2D43B1C20604A8CE7AC1', X'699E38560C0F2D43B1C20604A8CE7AC1', 5, NULL, NULL, 2, NULL, 0, 'Serve the pork on a platter with the beans, along with coleslaw, cornbread or muffins, and potato salad.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91968610199B0147BD20AB062C995E69', X'067E5B8BFE4BA842AA1281F64D24FC23', X'067E5B8BFE4BA842AA1281F64D24FC23', 0, NULL, NULL, 2, NULL, 0, 'Pat the pork roast with paper towels to dry. Season all over with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F9C6072D4519D4787E976123EA70D92', X'067E5B8BFE4BA842AA1281F64D24FC23', X'067E5B8BFE4BA842AA1281F64D24FC23', 1, NULL, NULL, 2, NULL, 0, 'Peel the onion, cut it in half, and chop. Peel and mince the garlic.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAB2DC2519E31A4C8C71126F1CD40A89', X'067E5B8BFE4BA842AA1281F64D24FC23', X'067E5B8BFE4BA842AA1281F64D24FC23', 2, NULL, NULL, 2, NULL, 0, 'Melt 2 tablespoons of the butter in a large braising pan or Dutch oven over medium heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'532F8A728AE1A846A4A670730E3EA400', X'067E5B8BFE4BA842AA1281F64D24FC23', X'067E5B8BFE4BA842AA1281F64D24FC23', 3, NULL, NULL, 2, NULL, 0, 'When the butter is hot, add the onions; cook until translucent. Remove the onions to a plate and set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'243E4C8ED8614249B0BE5EB4F8FB8E51', X'067E5B8BFE4BA842AA1281F64D24FC23', X'067E5B8BFE4BA842AA1281F64D24FC23', 4, NULL, NULL, 2, NULL, 0, 'Add the remaining 2 tablespoons of butter. Add the pork roast and brown on all sides. Add the minced garlic.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F846DEBA73FB74285D36A33A44EF494', X'067E5B8BFE4BA842AA1281F64D24FC23', X'067E5B8BFE4BA842AA1281F64D24FC23', 5, NULL, NULL, 2, NULL, 0, 'Remove outer leaves from the cabbage and then cut into 6 to 8 wedges or chop coarsely. Arrange the cabbage around the pork roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'725035094C6CE34293FEB7B5736B893C', X'067E5B8BFE4BA842AA1281F64D24FC23', X'067E5B8BFE4BA842AA1281F64D24FC23', 6, NULL, NULL, 2, NULL, 0, 'Heat oven to 325 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1437FC23901554684765FD41DDD70E9', X'067E5B8BFE4BA842AA1281F64D24FC23', X'067E5B8BFE4BA842AA1281F64D24FC23', 7, NULL, NULL, 2, NULL, 0, 'In a cup, combine the apple juice, honey, and vinegar; stir in the dried leaf thyme. Pour over the pork and cabbage.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFC1CD233CD4664F8D8B4E7EC2F9FDDD', X'067E5B8BFE4BA842AA1281F64D24FC23', X'067E5B8BFE4BA842AA1281F64D24FC23', 8, NULL, NULL, 2, NULL, 0, 'Cover the pan and transfer to the oven. Bake for 2 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0F01B962B0B4841A0AD9538BBFCF652', X'067E5B8BFE4BA842AA1281F64D24FC23', X'067E5B8BFE4BA842AA1281F64D24FC23', 9, NULL, NULL, 2, NULL, 0, 'Scrub the fingerling potatoes and add them to the pork and cabbage. Cover and bake for 45 minutes to 1 hour longer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD16CC09C3572B40AFA801880D01AEE7', X'067E5B8BFE4BA842AA1281F64D24FC23', X'067E5B8BFE4BA842AA1281F64D24FC23', 10, NULL, NULL, 2, NULL, 0, 'Pork should register at least 145 on an instant-read thermometer inserted into the center of the roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89029BA8B5DD8B4A8A6B181F6FC40F89', X'F8EF923A1CFE6541ACAE659B516F4B54', X'F8EF923A1CFE6541ACAE659B516F4B54', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'662C685D7DCC7D489BA76C1B7F17490B', X'F8EF923A1CFE6541ACAE659B516F4B54', X'F8EF923A1CFE6541ACAE659B516F4B54', 1, NULL, NULL, 2, NULL, 0, 'If the pork has an excessive layer of fat, trim it a bit. A little fat will help keep the roast juicy over the long period of cooking.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1EAD933DDD15B4F812604525847966E', X'F8EF923A1CFE6541ACAE659B516F4B54', X'F8EF923A1CFE6541ACAE659B516F4B54', 2, NULL, NULL, 2, NULL, 0, 'Rub the roast all over with the garlic halves, then sprinkle with salt and pepper, then prick the roast all over with a fork or skewer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84F3B3C5180A82438D409ACBB9AA7A76', X'F8EF923A1CFE6541ACAE659B516F4B54', X'F8EF923A1CFE6541ACAE659B516F4B54', 3, NULL, NULL, 2, NULL, 0, 'In a cup or bowl, combine 1 cup of the brown sugar, the mustard, and vinegar. Rub all over the roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E65768476DE6140A90FDD54FBEE93A2', X'F8EF923A1CFE6541ACAE659B516F4B54', X'F8EF923A1CFE6541ACAE659B516F4B54', 4, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW for 7 to 9 hours, or until tender but not falling apart. Pour off the excess juices.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CC64E1141387044B3B9D45D3066C24B', X'F8EF923A1CFE6541ACAE659B516F4B54', X'F8EF923A1CFE6541ACAE659B516F4B54', 5, NULL, NULL, 2, NULL, 0, 'Combine the remaining 1/3 cup brown sugar with cinnamon; Spread the mixture over the top of the roast. Cover and continue cooking on LOW for 1 hour longer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'653168E9100CF0409A797527FF5D6B61', X'24417AEFE6AA7C49931AB1769437D7C9', X'24417AEFE6AA7C49931AB1769437D7C9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1969678B705A54B8DDBB925D6ADF832', X'24417AEFE6AA7C49931AB1769437D7C9', X'24417AEFE6AA7C49931AB1769437D7C9', 1, NULL, NULL, 2, NULL, 0, 'In a large oiled skillet or wok, saut pork and potatoes for 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA0216096C792845B04D42C744E67DBC', X'24417AEFE6AA7C49931AB1769437D7C9', X'24417AEFE6AA7C49931AB1769437D7C9', 2, NULL, NULL, 2, NULL, 0, 'Add onion and zucchini and continue to saut for 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB8248E5A8E81148A8FB2FE9D0DA9CFE', X'24417AEFE6AA7C49931AB1769437D7C9', X'24417AEFE6AA7C49931AB1769437D7C9', 3, NULL, NULL, 2, NULL, 0, 'Add bean paste, sesame oil, sugar, and garlic to the pan, stirring to combine. Saut for 3 to 4 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81FF40605422BF4CA07CD5CEEAF6FDBF', X'24417AEFE6AA7C49931AB1769437D7C9', X'24417AEFE6AA7C49931AB1769437D7C9', 4, NULL, NULL, 2, NULL, 0, 'Add 6 cups of water and the carrots and bring to a boil, then reduce to a simmer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1995CFDE2ACCBC419E6EA803697B34C9', X'24417AEFE6AA7C49931AB1769437D7C9', X'24417AEFE6AA7C49931AB1769437D7C9', 5, NULL, NULL, 2, NULL, 0, 'Mix cornstarch with 1/2 cup cold water.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A6737A608F66E45A5E6DDC5418955CA', X'24417AEFE6AA7C49931AB1769437D7C9', X'24417AEFE6AA7C49931AB1769437D7C9', 6, NULL, NULL, 2, NULL, 0, 'Pour into the sauce to thicken and cook for 15 minutes, or until vegetables are tender.');
