INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2593D85BA9132C49ABA31552DD87F0B8', X'24417AEFE6AA7C49931AB1769437D7C9', X'24417AEFE6AA7C49931AB1769437D7C9', 8, NULL, NULL, 2, NULL, 0, 'Place a large helping of noodles in a big soup bowl. Ladle the jajang sauce over the noodles (myun).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2887BF2C0C6B3044A2AD57CDEC314E3C', X'24417AEFE6AA7C49931AB1769437D7C9', X'24417AEFE6AA7C49931AB1769437D7C9', 9, NULL, NULL, 2, NULL, 0, 'Serve with sliced raw onions and white vinegar on the side (to splash onto the noodles).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A5D7FAA7090D243B9919AC3EE643FDB', X'24417AEFE6AA7C49931AB1769437D7C9', X'24417AEFE6AA7C49931AB1769437D7C9', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'450CC347EB937042BB289D81BC438C42', X'8ED136D34CAB2144B493464B1932FED6', X'8ED136D34CAB2144B493464B1932FED6', 0, NULL, NULL, 2, NULL, 0, 'Place the olive oil in a large skillet over medium-high Brown the roast on all sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01D998BCD6DD24498E5F1A626C659CB4', X'8ED136D34CAB2144B493464B1932FED6', X'8ED136D34CAB2144B493464B1932FED6', 1, NULL, NULL, 2, NULL, 0, 'Transfer the roast into a large cooking pot and cover with the chicken stock (adding water if necessary to cover the meat completely).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C5AEA275EF2DF428686D00DF271521E', X'8ED136D34CAB2144B493464B1932FED6', X'8ED136D34CAB2144B493464B1932FED6', 2, NULL, NULL, 2, NULL, 0, 'Cut the onion into quarters and add to the pot, along with the bay leaf.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAFC4909D57FBF459342B1580697BD30', X'8ED136D34CAB2144B493464B1932FED6', X'8ED136D34CAB2144B493464B1932FED6', 3, NULL, NULL, 2, NULL, 0, 'Bring the liquid to a boil and simmer over low heat, covered, until the meat is cooked through and tender, about 1 1/2 hours depending on the size. When you cut into the meat, the juices should run clear.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'599A3C6F8A58BB4DAF75FC517CE13E46', X'8ED136D34CAB2144B493464B1932FED6', X'8ED136D34CAB2144B493464B1932FED6', 4, NULL, NULL, 2, NULL, 0, 'Remove the meat from the pot and place it in a roasting pan. Reserve 2 cups of the cooking liquid. Preheat the oven to 350 degrees.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41D7FEFF86046C45ABDCF92EA1ABBF39', X'8ED136D34CAB2144B493464B1932FED6', X'8ED136D34CAB2144B493464B1932FED6', 5, NULL, NULL, 2, NULL, 0, 'Mix the aji panca paste, salt, vinegar, minced garlic, cumin, turmeric, and Sazn Goya together in a small bowl. (If you can''t find jarred aji panca paste, you can also use dried aji panca peppers to make your own: soak the dried peppers briefly in very hot water, then process them in a blender or food processor with some vegetable oil to make a paste).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C83A70BE5E63ED45A31556FA5C903AAF', X'8ED136D34CAB2144B493464B1932FED6', X'8ED136D34CAB2144B493464B1932FED6', 6, NULL, NULL, 2, NULL, 0, 'Spread the aji pepper and garlic mixture onto the pork, turning it to cover all sides. Make several deep slits in the meat to help introduce the seasoning. Add the reserved cooking liquid to the roasting pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EFB7277262B2449804975D126177D31', X'8ED136D34CAB2144B493464B1932FED6', X'8ED136D34CAB2144B493464B1932FED6', 7, NULL, NULL, 2, NULL, 0, 'Roast the meat in the oven until heated through, turning the meat over once, for a total time of about 30-45 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF2E655DFEE8324DBFCD95562828C808', X'8ED136D34CAB2144B493464B1932FED6', X'8ED136D34CAB2144B493464B1932FED6', 8, NULL, NULL, 2, NULL, 0, 'Let cool, slice thinly, and serve with salsa criolla.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A6FC34BADD4E14EB5FF791D78526113', X'900A8FCE5DD5AD4FA98042320EE98F3A', X'900A8FCE5DD5AD4FA98042320EE98F3A', 0, NULL, NULL, 2, NULL, 0, 'Add the fresh herbs, stems and all, into a 9 x 12 baking dish or pan, along with the olive oil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1ACDE5C3F4E7DA4393DE64F074F0DA90', X'900A8FCE5DD5AD4FA98042320EE98F3A', X'900A8FCE5DD5AD4FA98042320EE98F3A', 1, NULL, NULL, 2, NULL, 0, 'Toss to coat, and lay the pork loin over the bed of herbs.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1F76AEC7F03574B9618D25BEABA9117', X'900A8FCE5DD5AD4FA98042320EE98F3A', X'900A8FCE5DD5AD4FA98042320EE98F3A', 2, NULL, NULL, 2, NULL, 0, 'Season the pork loin generously with salt, fresh ground black pepper, and paprika. Scatter the garlic cloves around the meat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4A2447D491D074BB08263772BF0272D', X'900A8FCE5DD5AD4FA98042320EE98F3A', X'900A8FCE5DD5AD4FA98042320EE98F3A', 3, NULL, NULL, 2, NULL, 0, 'Roast for 35 minutes, then remove and turn the pork loin over. Using a pair of tongs, cover the top of the pork with the wilted herbs.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6687A835B1318942A562370135893250', X'900A8FCE5DD5AD4FA98042320EE98F3A', X'900A8FCE5DD5AD4FA98042320EE98F3A', 4, NULL, NULL, 2, NULL, 0, 'Place back in the oven, and roast for 30 minutes more, or until it reaches an internal temperature of 155 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C44136A0A83B7940A27538CF6B446F82', X'900A8FCE5DD5AD4FA98042320EE98F3A', X'900A8FCE5DD5AD4FA98042320EE98F3A', 5, NULL, NULL, 2, NULL, 0, 'Allow to rest loosely covered with foil for 15 minutes before slicing and serving.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85F42438E9FE114EB9CA688F0B6423E3', X'900A8FCE5DD5AD4FA98042320EE98F3A', X'900A8FCE5DD5AD4FA98042320EE98F3A', 6, NULL, NULL, 2, NULL, 0, 'Garnish with the roasted garlic cloves and any of the natural meat juices from the pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C9ABD8907795D45B458CDBA202D7002', X'9B07B79F2DA41445AD3D012554161FFC', X'9B07B79F2DA41445AD3D012554161FFC', 0, NULL, NULL, 2, NULL, 0, 'Pat the pork roast dry with paper towels. Trim and discard excess fat. Sprinkle with kosher salt and black pepper and place the roast in the crock pot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'727944CF12E1FD4192FBCEDAA8D40624', X'9B07B79F2DA41445AD3D012554161FFC', X'9B07B79F2DA41445AD3D012554161FFC', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the chicken stock, bouillon, green onions, red wine vinegar, and rosemary. Stir the mixture well and pour over the pork roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'212628E0F6BFEC4ABA15BE61F8F7BEB1', X'9B07B79F2DA41445AD3D012554161FFC', X'9B07B79F2DA41445AD3D012554161FFC', 2, NULL, NULL, 2, NULL, 0, 'Cover the crock pot and cook on low for about 4 to 6 hours. The roast can be cooked longer, but for neat slices, check for doneness after 4 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'950089EFCFBD4F438C476A47A3480C50', X'9B07B79F2DA41445AD3D012554161FFC', X'9B07B79F2DA41445AD3D012554161FFC', 3, NULL, NULL, 2, NULL, 0, 'Remove roast to warm serving platter and keep warm.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEE34E3EEBE31B439D21E9A4AD5917C6', X'9B07B79F2DA41445AD3D012554161FFC', X'9B07B79F2DA41445AD3D012554161FFC', 4, NULL, NULL, 2, NULL, 0, 'Strain the juices and skim off any excess fat. Pour 2/3 cup of the juices into a saucepan and add the cherry preserves. Add food coloring if desired. Bring to a boil and continue to cook for 1 to 2 minutes, or until slightly reduced. Taste and add salt and pepper, as needed.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'610A53F23CDA7F4F99633C90BA1B2E33', X'9B07B79F2DA41445AD3D012554161FFC', X'9B07B79F2DA41445AD3D012554161FFC', 5, NULL, NULL, 2, NULL, 0, 'Slice the pork roast and drizzle with some of the sauce. Serve the rest of the sauce at the table.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09AE651757C36A43985E560BC41B65B1', X'9B07B79F2DA41445AD3D012554161FFC', X'9B07B79F2DA41445AD3D012554161FFC', 6, NULL, NULL, 2, NULL, 0, 'If desired, garnish servings with chopped chives or parsley.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BA3E8261A65E5449CD56B61CC1B220B', X'AE0C47F38079EB4AA08D223AC9806C20', X'AE0C47F38079EB4AA08D223AC9806C20', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C2AA9AA46053E45ADC6276AD7D8C664', X'AE0C47F38079EB4AA08D223AC9806C20', X'AE0C47F38079EB4AA08D223AC9806C20', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 325 F. Lightly oil a roasting pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39F87A631A9B5945A0FAF9AEB5ED732A', X'AE0C47F38079EB4AA08D223AC9806C20', X'AE0C47F38079EB4AA08D223AC9806C20', 2, NULL, NULL, 2, NULL, 0, 'If the pork has been butterflied and tied, open it up and separate into halves.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C98E209610B6442A9031D66651E820F', X'AE0C47F38079EB4AA08D223AC9806C20', X'AE0C47F38079EB4AA08D223AC9806C20', 3, NULL, NULL, 2, NULL, 0, 'If it is all in one piece, slice the pork roast almost through; open and lay flat, cut side up. Again, cut almost through each side of the pork. Place a sheet of plastic wrap over the butterflied pork and pound to flatten.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0390DA0984D3E342A56DD4B1F5D9DCA9', X'AE0C47F38079EB4AA08D223AC9806C20', X'AE0C47F38079EB4AA08D223AC9806C20', 4, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine stuffing and melted butter.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86C789C09438DB4EB486B21B061C55C9', X'AE0C47F38079EB4AA08D223AC9806C20', X'AE0C47F38079EB4AA08D223AC9806C20', 5, NULL, NULL, 2, NULL, 0, 'Stir in remaining stuffing ingredients; mix well.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20ECEDAF5B88F44485BC5B0AB838428B', X'AE0C47F38079EB4AA08D223AC9806C20', X'AE0C47F38079EB4AA08D223AC9806C20', 6, NULL, NULL, 2, NULL, 0, 'Spread stuffing on the pork and then roll or, if the pork is in two pieces, top it with the other half of the roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35AFD4815B059B41A643363AA141A084', X'AE0C47F38079EB4AA08D223AC9806C20', X'AE0C47F38079EB4AA08D223AC9806C20', 7, NULL, NULL, 2, NULL, 0, 'Secure roast halves well with skewers or toothpicks or tie it with kitchen twine and place in the prepared roasting pan; sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DD05353369624419439CE32CCB70EFD', X'AE0C47F38079EB4AA08D223AC9806C20', X'AE0C47F38079EB4AA08D223AC9806C20', 8, NULL, NULL, 2, NULL, 0, 'Roast in the preheated oven for 1 1/2 to 2 hours or until a meat thermometer registers at least 160 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94BBE021EC59DD40B3D8A2D75B5A1466', X'AE0C47F38079EB4AA08D223AC9806C20', X'AE0C47F38079EB4AA08D223AC9806C20', 9, NULL, NULL, 2, NULL, 0, 'Let the roast stand for 10 minutes before slicing.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E666F8662F7EDA468A9628EC7BD813DF', X'AE0C47F38079EB4AA08D223AC9806C20', X'AE0C47F38079EB4AA08D223AC9806C20', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB201CE05D41B445960D5B0B9FA62FE2', X'87D18AB80BBF834C9AB94DDFC10BA8E2', X'87D18AB80BBF834C9AB94DDFC10BA8E2', 0, NULL, NULL, 2, NULL, 0, 'Place the pork loin in the slow cooker. Cut the smoked sausage into 2- to 3-inch pieces and add to the slow cooker, along with the knockwurst or bratwurst.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D7ABEB638BC3341BF2E7E0173841D41', X'87D18AB80BBF834C9AB94DDFC10BA8E2', X'87D18AB80BBF834C9AB94DDFC10BA8E2', 1, NULL, NULL, 2, NULL, 0, 'In a bowl combine the rinsed sauerkraut, brown sugar, mustard, and seeds. Stir until well blended. Spoon over the pork and sausage and tuck a bay leaf into the mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CA65396B6026146A7FEB13F4CD97AD0', X'87D18AB80BBF834C9AB94DDFC10BA8E2', X'87D18AB80BBF834C9AB94DDFC10BA8E2', 2, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 7 to 9 hours or on high for about 4 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D588F9CC7C931546AF95822F37DF640B', X'D8C2223C1B4D8842A0FDACB9D2562ED5', X'D8C2223C1B4D8842A0FDACB9D2562ED5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32CB246B35D4FB4FA9F0E420A8B88B4D', X'D8C2223C1B4D8842A0FDACB9D2562ED5', X'D8C2223C1B4D8842A0FDACB9D2562ED5', 1, NULL, NULL, 2, NULL, 0, 'Combine the paprika, coriander, cumin, cinnamon, ginger, salt, pepper, and garlic powder.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2DEC6F66298CA42A3CE606C7F924124', X'D8C2223C1B4D8842A0FDACB9D2562ED5', X'D8C2223C1B4D8842A0FDACB9D2562ED5', 2, NULL, NULL, 2, NULL, 0, 'Heat oven to 425 F (220 C/Gas 7). Rinse the roast and remove any netting. Rub the roast all over with the spice mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F0A760355A2E24FA2F2B79C8DB1BAD5', X'D8C2223C1B4D8842A0FDACB9D2562ED5', X'D8C2223C1B4D8842A0FDACB9D2562ED5', 3, NULL, NULL, 2, NULL, 0, 'Place the pork roast on a rack in a baking dish or roasting pan and roast it in the preheated oven for 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB52B6D7FB8BE84D94A115F0C5AAA73F', X'D8C2223C1B4D8842A0FDACB9D2562ED5', X'D8C2223C1B4D8842A0FDACB9D2562ED5', 4, NULL, NULL, 2, NULL, 0, 'Reduce heat to 350 F and continue roasting for about 1 to 1 1/4 hours, or until an instant-read thermometer or oven probe in the center of the thickest part of the roast reaches at least 145 F, the minimum safe temperature for pork, according to the USDA.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0630EC067CA39E448CDF480D8A514B58', X'D8C2223C1B4D8842A0FDACB9D2562ED5', X'D8C2223C1B4D8842A0FDACB9D2562ED5', 5, NULL, NULL, 2, NULL, 0, 'Remove the roast to a warm platter, tent with foil, and let it rest for 5 minutes before slicing.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3B895894CFFA740A7C60CDED2FC9A4A', X'D8C2223C1B4D8842A0FDACB9D2562ED5', X'D8C2223C1B4D8842A0FDACB9D2562ED5', 6, NULL, NULL, 2, NULL, 0, 'Serve the roast with the sour cream gravy, below, along with mashed or baked potatoes and corn, steamed broccoli, or green beans. Seasoned bread or cornbread dressing also goes well with pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEFF1CE535313744A90158BDA4229807', X'A6D534ABECE4C64096BC47A6F16F61D1', X'A6D534ABECE4C64096BC47A6F16F61D1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A27DE8C31EF7274B85C1F69607010CD8', X'A6D534ABECE4C64096BC47A6F16F61D1', X'A6D534ABECE4C64096BC47A6F16F61D1', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD08881B1FE661469A9188C0B7E079A2', X'A6D534ABECE4C64096BC47A6F16F61D1', X'A6D534ABECE4C64096BC47A6F16F61D1', 2, NULL, NULL, 2, NULL, 0, 'Trim excess fat from the pork roast. Sprinkle pork roast with salt and pepper or a seasoning blend, then rub on all sides with garlic.');
