INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92A1886C836CC749B143D912CFEB466F', X'D313156ACE5ED9438A910B36E9677B6C', X'D313156ACE5ED9438A910B36E9677B6C', 3, NULL, NULL, 2, NULL, 0, 'Drain and reserve marinade. Arrange pork cubes onto skewers, leaving about 1/4 inch space in between each cube.');
