INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'426C34CD0376994D8758168C9FB489AA', X'D313156ACE5ED9438A910B36E9677B6C', X'D313156ACE5ED9438A910B36E9677B6C', 5, NULL, NULL, 2, NULL, 0, 'Cook for about 5 minutes over a medium fire then turn.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D3755F19B1A844DA08C8FB3A3E34E99', X'D313156ACE5ED9438A910B36E9677B6C', X'D313156ACE5ED9438A910B36E9677B6C', 6, NULL, NULL, 2, NULL, 0, 'Baste with more marinade and continue cooking for another 5 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3977240C5A2A34D8524D38DCC2C5A75', X'D313156ACE5ED9438A910B36E9677B6C', X'D313156ACE5ED9438A910B36E9677B6C', 7, NULL, NULL, 2, NULL, 0, 'Discard remaining marinade.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6E9F5CB1BE2C445B2B0D39B2321F1CE', X'D313156ACE5ED9438A910B36E9677B6C', X'D313156ACE5ED9438A910B36E9677B6C', 8, NULL, NULL, 2, NULL, 0, 'Continue cooking and turning until done, or once the meat reaches an internal temperature of 165 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43374881699B6846969DEBE7A6F7A3AB', X'D313156ACE5ED9438A910B36E9677B6C', X'D313156ACE5ED9438A910B36E9677B6C', 9, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from heat and serve with side sauces of choice.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0536633838507746BA14708D34040B36', X'C2D8498C9D31844CA212D8E8BD2CB4CD', X'C2D8498C9D31844CA212D8E8BD2CB4CD', 0, NULL, NULL, 2, NULL, 0, 'Place the pork roast in a large zip-top plastic bag.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFD9278D6E1B9943A0D4C983B09AAF94', X'C2D8498C9D31844CA212D8E8BD2CB4CD', X'C2D8498C9D31844CA212D8E8BD2CB4CD', 1, NULL, NULL, 2, NULL, 0, 'Combine the remaining ingredients (beer, honey, Dijon mustard, chopped onion, olive oil, minced garlic, rosemary, salt, black pepper) and pour this marinade over the pork. Seal the bag and allow it to marinate in the refrigerator for 1 to 2 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9CE48B99AB33146B317EA3B067C9910', X'C2D8498C9D31844CA212D8E8BD2CB4CD', X'C2D8498C9D31844CA212D8E8BD2CB4CD', 2, NULL, NULL, 2, NULL, 0, 'Preheat grill to medium heat. Remove the pork roast from the bag and place it on a rotisserie over a drip pan. Reserve the marinade.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1485EC4A8E173649B30D41270C726806', X'C2D8498C9D31844CA212D8E8BD2CB4CD', X'C2D8498C9D31844CA212D8E8BD2CB4CD', 3, NULL, NULL, 2, NULL, 0, 'In a saucepan, bring the marinade to a boil and then simmer 5 to 7 minutes. Remove the sauce from the heat. Baste the roast with the sauce every 20 minutes. The roast should cook for 1 1/2 hours or so and will reach an internal temperature of 160 F when it is cooked through.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CF8AB5A9FEBC240B6231CE6AD1931E9', X'C2D8498C9D31844CA212D8E8BD2CB4CD', X'C2D8498C9D31844CA212D8E8BD2CB4CD', 4, NULL, NULL, 2, NULL, 0, 'Once cooked, remove the roast from the heat and let sit for at least 5 minutes before carving.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0ACD9CFEF1337649AE2E7D7731727D70', X'C2D8498C9D31844CA212D8E8BD2CB4CD', X'C2D8498C9D31844CA212D8E8BD2CB4CD', 5, NULL, NULL, 2, NULL, 0, 'Carve your pork roast crosswise into 1/2-inch slices to serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F27C633593802847BB92D5FACA3257FA', X'60DE38248917F54E8F6B1FD2A3968BCE', X'60DE38248917F54E8F6B1FD2A3968BCE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BB85B0458E7074FA8CB15B0AE5C9292', X'60DE38248917F54E8F6B1FD2A3968BCE', X'60DE38248917F54E8F6B1FD2A3968BCE', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet, heat the vegetable oil over medium heat. Add the onion and cook for 3 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5ADCF46D1C847546A59C4773B2CA31D8', X'60DE38248917F54E8F6B1FD2A3968BCE', X'60DE38248917F54E8F6B1FD2A3968BCE', 2, NULL, NULL, 2, NULL, 0, 'If the roast is wrapped in netting, remove it. Sprinkle the roast all over with salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5A3378CE1FE1A439E5992AC0C9C16EB', X'60DE38248917F54E8F6B1FD2A3968BCE', X'60DE38248917F54E8F6B1FD2A3968BCE', 3, NULL, NULL, 2, NULL, 0, 'Add to the onions and brown on all sides, about 8 to 10 minutes total.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC6A791565F86849B0704E8F95A515CD', X'60DE38248917F54E8F6B1FD2A3968BCE', X'60DE38248917F54E8F6B1FD2A3968BCE', 4, NULL, NULL, 2, NULL, 0, 'Transfer the roast and onions to the slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B48F50C32232C942993AF316A5AC4BD3', X'60DE38248917F54E8F6B1FD2A3968BCE', X'60DE38248917F54E8F6B1FD2A3968BCE', 5, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the sauce ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EC26206E5553A45BB76F70BB93E09B8', X'60DE38248917F54E8F6B1FD2A3968BCE', X'60DE38248917F54E8F6B1FD2A3968BCE', 6, NULL, NULL, 2, NULL, 0, 'Pour over the roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80F2CB66F93B844BB8B619D6F5523FF8', X'60DE38248917F54E8F6B1FD2A3968BCE', X'60DE38248917F54E8F6B1FD2A3968BCE', 7, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW for 5 to 7 hours, or until the roast is tender and thoroughly cooked. The minimum safe temperature for pork (according to foodsafety.gov) is 145 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2CF24DC30E5CF41B53EBBEA28C1A78C', X'60DE38248917F54E8F6B1FD2A3968BCE', X'60DE38248917F54E8F6B1FD2A3968BCE', 8, NULL, NULL, 2, NULL, 0, 'Carefully remove the pork loin roast to a pan or platter.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'791C4786BEC12B4C9669D8EB0B78E691', X'60DE38248917F54E8F6B1FD2A3968BCE', X'60DE38248917F54E8F6B1FD2A3968BCE', 9, NULL, NULL, 2, NULL, 0, 'Skim excess fat from the sauce and pour into a saucepan. Bring to a boil on the stovetop and cook, stirring occasionally, until the sauce is reduced and thickened, about 4 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC6A0A6A50FF8941AB669832B38407AB', X'60DE38248917F54E8F6B1FD2A3968BCE', X'60DE38248917F54E8F6B1FD2A3968BCE', 10, NULL, NULL, 2, NULL, 0, 'Put the pork loin roast and sauce back in the slow cooker and keep warm until serving time.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A41D99CB8050B04D947903275472A548', X'60DE38248917F54E8F6B1FD2A3968BCE', X'60DE38248917F54E8F6B1FD2A3968BCE', 11, NULL, NULL, 2, NULL, 0, 'Slice and serve the pork roast with potatoes and a side vegetable or serve in sandwiches with coleslaw.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A452E291EE4D04889B3F16821558CD8', X'60DE38248917F54E8F6B1FD2A3968BCE', X'60DE38248917F54E8F6B1FD2A3968BCE', 12, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA14CB26EC97064CAD4560F87728AC07', X'224A9721FC62144ABAEE0822A16116D4', X'224A9721FC62144ABAEE0822A16116D4', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44B8C34712EE6C4BA3DE7462F834F317', X'224A9721FC62144ABAEE0822A16116D4', X'224A9721FC62144ABAEE0822A16116D4', 1, NULL, NULL, 2, NULL, 0, 'Place the roast in a shallow roasting pan. In a small bowl, combine the olive oil, thyme, marjoram, garlic, dry mustard, salt, and pepper, and mix to make a paste. Rub this mixture over the roast. Let the pork stand at room temperature for 30 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45328910FAA8A44EB224581A6DE7B80C', X'224A9721FC62144ABAEE0822A16116D4', X'224A9721FC62144ABAEE0822A16116D4', 2, NULL, NULL, 2, NULL, 0, 'Roast the pork for 20 minutes, then reduce the heat to 300 F without opening the oven door.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED4F3BF134D6514984646DF9A2FA72EE', X'224A9721FC62144ABAEE0822A16116D4', X'224A9721FC62144ABAEE0822A16116D4', 3, NULL, NULL, 2, NULL, 0, 'Roast the pork until the internal temperature registers 145 F, about 18 to 23 minutes per pound. Let roast stand, covered, 5 to 10 minutes before carving.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCEEE53C49C275438D59BD21FA052EBC', X'224A9721FC62144ABAEE0822A16116D4', X'224A9721FC62144ABAEE0822A16116D4', 4, NULL, NULL, 2, NULL, 0, 'You can also roast the pork at 350 F for 20 to 25 minutes per pound until the internal temperature registers 145 F. Cover the roast with aluminum foil and let stand for 10 minutes, then carve to serve.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2075C8E18A33A44BAD80DCA2A536DAE', X'C06455C8EB66F64F8EA5C021D2E26215', X'C06455C8EB66F64F8EA5C021D2E26215', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5E1B2174EFC8F4D89A7D1FEB4DA8A62', X'C06455C8EB66F64F8EA5C021D2E26215', X'C06455C8EB66F64F8EA5C021D2E26215', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40E67BCFE34EAB45B39E58E8EE74814D', X'C06455C8EB66F64F8EA5C021D2E26215', X'C06455C8EB66F64F8EA5C021D2E26215', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the olive oil, thyme, chicken broth, salt, and pepper. Roll pork in this mixture, then roll in both types of the Parmesan cheese to coat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED340F0802104A41A6A92503B5587178', X'C06455C8EB66F64F8EA5C021D2E26215', X'C06455C8EB66F64F8EA5C021D2E26215', 3, NULL, NULL, 2, NULL, 0, 'Place the pork on a roasting pan and sprinkle with any remaining cheese.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7FA1B128B46034DB8C0E9CF5FE38C88', X'C06455C8EB66F64F8EA5C021D2E26215', X'C06455C8EB66F64F8EA5C021D2E26215', 4, NULL, NULL, 2, NULL, 0, 'Roast, uncovered, for 40 to 50 minutes until an internal meat thermometer registers at least 145 F.');
