INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D39BA030532414BA42466D084838242', X'0309D32E45B7DC4E9B3A3A19A7C27C1A', X'0309D32E45B7DC4E9B3A3A19A7C27C1A', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 325 F (165 C/Gas 3).');
