INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D81BE431A5AE634CB2771D18D83D12F9', X'845C221EDCD2EB4CA0EA12415F48BC18', X'845C221EDCD2EB4CA0EA12415F48BC18', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 325 F.');
