INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E9EDECDF57FC848A39DE8D27AC6A722', X'845C221EDCD2EB4CA0EA12415F48BC18', X'845C221EDCD2EB4CA0EA12415F48BC18', 3, NULL, NULL, 2, NULL, 0, 'Bring the mixture to a boil.');
