INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'363E8458B4CF354D9D7A540D9206316E', X'845C221EDCD2EB4CA0EA12415F48BC18', X'845C221EDCD2EB4CA0EA12415F48BC18', 5, NULL, NULL, 2, NULL, 0, 'Roast the pork in the preheated oven for about 2 to 2 1/2 hours or to an internal temperature of at least 145 F on a meat thermometer.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67B927ED38DF8748B7C913C0B10C6C1A', X'845C221EDCD2EB4CA0EA12415F48BC18', X'845C221EDCD2EB4CA0EA12415F48BC18', 6, NULL, NULL, 2, NULL, 0, 'Baste the roast pork frequently with the sauce throughout the cooking time.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57C26370E83D1343B556ACB3AD501E30', X'E08800D2C3444F4DBD1694681A71C47F', X'E08800D2C3444F4DBD1694681A71C47F', 0, NULL, NULL, 2, NULL, 0, 'Place the potatoes and baby carrots around the bottom edge of a 4 to 6-quart crock pot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7404BC37376794299E75A00BD62C9B1', X'E08800D2C3444F4DBD1694681A71C47F', X'E08800D2C3444F4DBD1694681A71C47F', 1, NULL, NULL, 2, NULL, 0, 'Place the onion and garlic in the bottom of the crockpot.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D95679E8D999634E8C8F1B0A3935BAAC', X'E08800D2C3444F4DBD1694681A71C47F', X'E08800D2C3444F4DBD1694681A71C47F', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the mustard, tarragon, thyme, salt, and pepper, and spread over the pork roast.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0745B32853B0224BAFE873C28CB0ABBA', X'E08800D2C3444F4DBD1694681A71C47F', X'E08800D2C3444F4DBD1694681A71C47F', 3, NULL, NULL, 2, NULL, 0, 'Place the roast in the crockpot on top of all of the vegetables and pour the beef broth over all.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF42671CF4E90741BFD7240FFB8AB70D', X'E08800D2C3444F4DBD1694681A71C47F', X'E08800D2C3444F4DBD1694681A71C47F', 4, NULL, NULL, 2, NULL, 0, 'Cover the crockpot and cook on low for 7 to 9 hours or until the pork is 150 F on a meat thermometer and the vegetables are tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2CD9951E5B57E488815D26757EEC291', X'E08800D2C3444F4DBD1694681A71C47F', X'E08800D2C3444F4DBD1694681A71C47F', 5, NULL, NULL, 2, NULL, 0, 'Remove the pork and vegetables to a serving platter; cover tightly with foil to keep warm and let stand for 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAD2C4F4C953E742AE7527764DF08E84', X'E08800D2C3444F4DBD1694681A71C47F', X'E08800D2C3444F4DBD1694681A71C47F', 6, NULL, NULL, 2, NULL, 0, 'During this time, thicken the juices in the slow cooker for a gravy by mixing the cornstarch with water using a wire whisk. Add this slurry to the juices in the crockpot and cook on high for 10 to 15 minutes until thickened. Serve the gravy with the meat and vegetables.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B063F0B6E10E594986931DBA3C99252B', X'C9AC33AD2638684AB05FAA5D4B02F70F', X'C9AC33AD2638684AB05FAA5D4B02F70F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0976725B3961A44A79C3D61403DA346', X'C9AC33AD2638684AB05FAA5D4B02F70F', X'C9AC33AD2638684AB05FAA5D4B02F70F', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 325 F (165 C/Gas 3).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A7914317BF9D54ABFA2D63CB026E239', X'C9AC33AD2638684AB05FAA5D4B02F70F', X'C9AC33AD2638684AB05FAA5D4B02F70F', 2, NULL, NULL, 2, NULL, 0, 'Score the fat on the pork roast in a diamond design. Rub salt and pepper over surface of the meat. Place the roast, fat side up, on a rack in a shallow roasting pan. Insert meat thermometer into thickest part of the meat, making sure it does not touch fat or bone. Roast for 1 hour and 45 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BAF04C478044A4A8F069B2A5847502E', X'C9AC33AD2638684AB05FAA5D4B02F70F', X'C9AC33AD2638684AB05FAA5D4B02F70F', 3, NULL, NULL, 2, NULL, 0, 'In a saucepan combine the apricot nectar, brown sugar, cornstarch, mustard, and vinegar; cook over medium heat, stirring constantly, until mixture is thickened and bubbly. Reserve 1/2 cup apricot mixture for the sauce. Baste roast with the remaining apricot glaze mixture.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36D449714CE1C34EB2F6297A845C153C', X'C9AC33AD2638684AB05FAA5D4B02F70F', X'C9AC33AD2638684AB05FAA5D4B02F70F', 4, NULL, NULL, 2, NULL, 0, 'Roast the pork for an additional 30 minutes or until meat thermometer registers 160. Remove the pork to a serving platter, reserving 3 tablespoons drippings. Keep the roast warm while you prepare the sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADAD7DD2CDC9D54797546C379507E771', X'C9AC33AD2638684AB05FAA5D4B02F70F', X'C9AC33AD2638684AB05FAA5D4B02F70F', 5, NULL, NULL, 2, NULL, 0, 'Combine reserved apricot mixture and drippings in a medium saucepan; stir well. Cook over medium heat, stirring constantly until sauce comes to a full boil. Serve the roast with sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45E575F5DAC0E54C91D3468861AAC2F2', X'EC80A5A58BCEA048AEFC5812492E85F8', X'EC80A5A58BCEA048AEFC5812492E85F8', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F/180 C/Gas 4.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A807F7732EB1F04AA3CD9AAD22E95976', X'EC80A5A58BCEA048AEFC5812492E85F8', X'EC80A5A58BCEA048AEFC5812492E85F8', 1, NULL, NULL, 2, NULL, 0, 'Line a roasting pan (large enough to hold the roast) with foil.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86CB8FB30A3EF648BDEE11A642B398BA', X'EC80A5A58BCEA048AEFC5812492E85F8', X'EC80A5A58BCEA048AEFC5812492E85F8', 2, NULL, NULL, 2, NULL, 0, 'In a bowl blend the orange juice concentrate with the melted butter and steak sauce.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D15A16056452241B41D9CF49D63CBA3', X'EC80A5A58BCEA048AEFC5812492E85F8', X'EC80A5A58BCEA048AEFC5812492E85F8', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle the pork roast lightly with salt and pepper and place it in the roasting pan.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BD58E4E081D9D4F8D5B78080B882C83', X'EC80A5A58BCEA048AEFC5812492E85F8', X'EC80A5A58BCEA048AEFC5812492E85F8', 4, NULL, NULL, 2, NULL, 0, 'Brush the orange mixture over the pork');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90BBB75AC2B7C84AA5C75371AD2BD575', X'EC80A5A58BCEA048AEFC5812492E85F8', X'EC80A5A58BCEA048AEFC5812492E85F8', 5, NULL, NULL, 2, NULL, 0, 'Roast for about 20 to 30 minutes per pound, or until the pork is fully cooked.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9505BB477CBAF42BD14E33137166750', X'EC80A5A58BCEA048AEFC5812492E85F8', X'EC80A5A58BCEA048AEFC5812492E85F8', 6, NULL, NULL, 2, NULL, 0, 'Baste the pork again with the orange juice mixture about 5 to 10 minutes before it''s done.');
