INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC5BE08D0D4130479F20656E6C858F74', X'83118172B956FE4D961F8167570C7986', X'83118172B956FE4D961F8167570C7986', 8, NULL, NULL, 2, NULL, 0, 'Place the apple quarters around the pork.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'680B5624A51C5B4CA8E490005D57C088', X'83118172B956FE4D961F8167570C7986', X'83118172B956FE4D961F8167570C7986', 9, NULL, NULL, 2, NULL, 0, 'Bake, uncovered, basting frequently, for 20 minutes, or until the apples are just tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2D3A8F21E6C2343A074A8248E1A0B9A', X'83118172B956FE4D961F8167570C7986', X'83118172B956FE4D961F8167570C7986', 10, NULL, NULL, 2, NULL, 0, 'Let the pork stand for 10 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E29C5B7333401A4C8C9D4368C3805C7A', X'83118172B956FE4D961F8167570C7986', X'83118172B956FE4D961F8167570C7986', 11, NULL, NULL, 2, NULL, 0, 'Slice the pork roast and serve with the sweet potatoes and apples.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35FFA6549209C14789AC15D31D3408AB', X'CC13531FA49A3C4B9BB27F961FC38A96', X'CC13531FA49A3C4B9BB27F961FC38A96', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9AF82ABB7BE38459355D2EB5C6992E7', X'CC13531FA49A3C4B9BB27F961FC38A96', X'CC13531FA49A3C4B9BB27F961FC38A96', 1, NULL, NULL, 2, NULL, 0, 'Peel the red onion and slice off the ends. Cut the onion in half lengthwise and slice each half thinly. In a small bowl or jar, combine the sliced onion with 1/3 cup of lime juice, 3/4 teaspoon of kosher salt, and 1/2 teaspoon of black pepper. If desired, add 1 teaspoon of finely chopped cilantro. Set aside.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6EAC3F7C333424D91B4DFDE570758BE', X'CC13531FA49A3C4B9BB27F961FC38A96', X'CC13531FA49A3C4B9BB27F961FC38A96', 2, NULL, NULL, 2, NULL, 0, 'Pat the pork shoulder dry with paper towels, and cut it into large chunks.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D2A78BE16CD504DAE2320095F906B76', X'CC13531FA49A3C4B9BB27F961FC38A96', X'CC13531FA49A3C4B9BB27F961FC38A96', 3, NULL, NULL, 2, NULL, 0, 'Choose the high saut setting on the Instant Pot and add 2 tablespoons of olive oil. Working in batches, brown the pork in the hot oil (about 8 minutes total).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8FFB2F566E81F47B077F2F74E27D717', X'CC13531FA49A3C4B9BB27F961FC38A96', X'CC13531FA49A3C4B9BB27F961FC38A96', 4, NULL, NULL, 2, NULL, 0, 'Cancel the saut function, and put all the browned pork in the pot. Add the oregano, cumin, and 2 teaspoons of kosher salt. Toss with the pork, then add the jalapeo rings, quartered onion, and bay leaves. Cut the 2 oranges and 2 limes in half and juice them; add the juice to the pot along with the chicken stock.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1559932C1897384D9095F5A59BE28001', X'CC13531FA49A3C4B9BB27F961FC38A96', X'CC13531FA49A3C4B9BB27F961FC38A96', 5, NULL, NULL, 2, NULL, 0, 'Lock the lid in place, and make sure the steam release valve is in the sealing position. Select manual (or pressure cook button), high pressure, and set the time to 40 minutes. When the time is up, let the pressure release naturally (this will take about 20 to 30 minutes).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'784E322D25C7B842A79596862F31721A', X'CC13531FA49A3C4B9BB27F961FC38A96', X'CC13531FA49A3C4B9BB27F961FC38A96', 6, NULL, NULL, 2, NULL, 0, 'Line a large baking sheet with foil. Preheat the broiler.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8E73311F33BE342915886F4715677C5', X'CC13531FA49A3C4B9BB27F961FC38A96', X'CC13531FA49A3C4B9BB27F961FC38A96', 7, NULL, NULL, 2, NULL, 0, 'Remove the pork pieces to a cutting board using a slotted spoon. Shred or chop the pork, and arrange the pieces in a single layer on the foil-lined baking sheet. Broil the pork for about 2 to 3 minutes, or until the edges are crisp.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4171C283B2CD04D9A59DC37435D3269', X'CC13531FA49A3C4B9BB27F961FC38A96', X'CC13531FA49A3C4B9BB27F961FC38A96', 8, NULL, NULL, 2, NULL, 0, 'Arrange the pork in tortillas and garnish with the pickled onion, cilantro, salsa or diced tomato, and radishes, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D4E7D500476D949942748769A66C850', X'CC13531FA49A3C4B9BB27F961FC38A96', X'CC13531FA49A3C4B9BB27F961FC38A96', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6B3D7F96F0E2F46AAA898A8D2A08876', X'BEDAE5F66BBA1544ADC0F7824DDCF974', X'BEDAE5F66BBA1544ADC0F7824DDCF974', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA6B0408509BBD4DB6D63E078CFBD2EB', X'BEDAE5F66BBA1544ADC0F7824DDCF974', X'BEDAE5F66BBA1544ADC0F7824DDCF974', 1, NULL, NULL, 2, NULL, 0, 'Place one sliced onion at the bottom of the slow cooker.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC88D3440DC95F4AADEC35990AA8B9F3', X'BEDAE5F66BBA1544ADC0F7824DDCF974', X'BEDAE5F66BBA1544ADC0F7824DDCF974', 2, NULL, NULL, 2, NULL, 0, 'Stud the pork roast with cloves (or put them in a small cheesecloth bag or tea strainer), if using. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'317F990B3216664AB830CCF90BED4941', X'BEDAE5F66BBA1544ADC0F7824DDCF974', X'BEDAE5F66BBA1544ADC0F7824DDCF974', 3, NULL, NULL, 2, NULL, 0, 'Place the pork in the slow cooker on top of the sliced onion. Cover with the second sliced onion and add 2 cups of water.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBD7A3DEB8E2DF47B53700AFA5C0ECF1', X'BEDAE5F66BBA1544ADC0F7824DDCF974', X'BEDAE5F66BBA1544ADC0F7824DDCF974', 4, NULL, NULL, 2, NULL, 0, 'Cover and cook on low 9 to 12 hours, or until the pork is very tender and falling apart.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C080A847A9DE241B7D66853229E841A', X'BEDAE5F66BBA1544ADC0F7824DDCF974', X'BEDAE5F66BBA1544ADC0F7824DDCF974', 5, NULL, NULL, 2, NULL, 0, 'Remove the pork roast from the pot. Remove and discard the cloves and excess fat and liquids along with bones if the roast was not boneless. You may also discard the sliced onions, if desired.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CF1423DA7699B4D9FF39A130AC18506', X'BEDAE5F66BBA1544ADC0F7824DDCF974', X'BEDAE5F66BBA1544ADC0F7824DDCF974', 6, NULL, NULL, 2, NULL, 0, 'When the pork roast is cool enough to handle, use a fork or your fingers to pull it apart until the entire roast is shredded. If you make pulled pork often, you might want to buy meat shredding claws.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA03DAAB40477B468A7ABA90709F0233', X'BEDAE5F66BBA1544ADC0F7824DDCF974', X'BEDAE5F66BBA1544ADC0F7824DDCF974', 7, NULL, NULL, 2, NULL, 0, 'Return the pulled pork to the slow cooker. Add the finely chopped onion, barbecue sauce, and optional red pepper flakes, and optional liquid smoke. Cover the pot and continue to cook on the high setting for 1 to 2 hours, or until the onions are tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1BB6716F0992E468B43EE29A5DAF535', X'BEDAE5F66BBA1544ADC0F7824DDCF974', X'BEDAE5F66BBA1544ADC0F7824DDCF974', 8, NULL, NULL, 2, NULL, 0, 'Serve the pulled pork on large, crusty buns with your favorite barbecue sauce. Garnish with dill pickles, thinly sliced raw onion, and pickled banana peppers. Serve with crisp, homemade slaw, either piled on the pork sandwiches or on the side.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66922F248CC8E84D933CF81A1582FA78', X'C5C5028564580044AD5249C8F10274D2', X'C5C5028564580044AD5249C8F10274D2', 0, NULL, NULL, 2, NULL, 0, 'Cut the pork into several large pieces and place the meat in the slow cooker with 1 cup salsa, the cumin, salt, and pepper to taste.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE1CCC5465B4CB4DAA6BA19436AF2ADC', X'C5C5028564580044AD5249C8F10274D2', X'C5C5028564580044AD5249C8F10274D2', 1, NULL, NULL, 2, NULL, 0, 'Cook on low for 6 to 8 hours (or 4 hours on high) or until pork is fork tender.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC03056C52A0094CA15B2BA97FEC6D64', X'C5C5028564580044AD5249C8F10274D2', X'C5C5028564580044AD5249C8F10274D2', 2, NULL, NULL, 2, NULL, 0, 'Remove pork from slow cooker, reserving liquid, and shred finely with a fork or using a food processor (fitted with the plastic blade).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CA76DC72B559C4A9ACD2B5DE761FBA5', X'C5C5028564580044AD5249C8F10274D2', X'C5C5028564580044AD5249C8F10274D2', 3, NULL, NULL, 2, NULL, 0, 'Place shredded pork and reserved liquid in a large skillet and saute until liquid evaporates and pork starts to brown slightly. Stir in remaining salsa until desired consistency.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAFFD8FFBC15024F8E7325644C49BF0B', X'C5C5028564580044AD5249C8F10274D2', X'C5C5028564580044AD5249C8F10274D2', 4, NULL, NULL, 2, NULL, 0, 'For making pupusas, add pork and salsa back to a ?food processor, fitted with metal blade, and process with short pulses until finely ground.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EEF6B2106978049B8ED33E2B30D7EF1', X'54E30CF65522FD488C13CF9AAFFE7E1E', X'54E30CF65522FD488C13CF9AAFFE7E1E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABB956C7F005BD46A6D0383FFB2C9E92', X'54E30CF65522FD488C13CF9AAFFE7E1E', X'54E30CF65522FD488C13CF9AAFFE7E1E', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 250 F.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E04AC7ADBEA4143B58035E32879B31E', X'54E30CF65522FD488C13CF9AAFFE7E1E', X'54E30CF65522FD488C13CF9AAFFE7E1E', 2, NULL, NULL, 2, NULL, 0, 'Using a small sharp, pointed knife, cut 10 to 12 deep slits/pockets into the top of the roast. The slits should be randomly spaced.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C397A2CBF350C42A2EA93DCF06B2DE6', X'54E30CF65522FD488C13CF9AAFFE7E1E', X'54E30CF65522FD488C13CF9AAFFE7E1E', 3, NULL, NULL, 2, NULL, 0, 'Stuff each slit with a sliver of garlic and a leaf or two of rosemary.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DF41B97695E204B81DFB5B8FAEC6656', X'54E30CF65522FD488C13CF9AAFFE7E1E', X'54E30CF65522FD488C13CF9AAFFE7E1E', 4, NULL, NULL, 2, NULL, 0, 'Season all sides generously with salt and pepper.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DACE4E24C6368E49A835DA835EE428BC', X'54E30CF65522FD488C13CF9AAFFE7E1E', X'54E30CF65522FD488C13CF9AAFFE7E1E', 5, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EB98387B0CCCA41AEF6E4B3906BCC5A', X'54E30CF65522FD488C13CF9AAFFE7E1E', X'54E30CF65522FD488C13CF9AAFFE7E1E', 6, NULL, NULL, 2, NULL, 0, 'Once the oil is hot (you will see wisps of steam rising) add roast and brown well on all sides.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BF8A72CC2889F47A4C64100C89C9326', X'54E30CF65522FD488C13CF9AAFFE7E1E', X'54E30CF65522FD488C13CF9AAFFE7E1E', 7, NULL, NULL, 2, NULL, 0, 'Transfer roast to a rack in a small roasting pan, place in the center of the oven and cook until the internal temperature is 145 F for 60 to 70 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BFA85F6395CBE40A902999A89246E6A', X'54E30CF65522FD488C13CF9AAFFE7E1E', X'54E30CF65522FD488C13CF9AAFFE7E1E', 8, NULL, NULL, 2, NULL, 0, 'Turn roast over at 30-minute mark.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC1B6CD40C051145AE08B11501386907', X'54E30CF65522FD488C13CF9AAFFE7E1E', X'54E30CF65522FD488C13CF9AAFFE7E1E', 9, NULL, NULL, 2, NULL, 0, 'Place roast on a cutting board, tent with foil, and allow to rest for 15 minutes.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA71383802E95A4CB488F82A0AFDA1BB', X'54E30CF65522FD488C13CF9AAFFE7E1E', X'54E30CF65522FD488C13CF9AAFFE7E1E', 10, NULL, NULL, 2, NULL, 0, 'Slice and serve. (The meat should be a very pale pink color.)');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07CDE484C3315B44A53AFB6E8B3B7B71', X'54E30CF65522FD488C13CF9AAFFE7E1E', X'54E30CF65522FD488C13CF9AAFFE7E1E', 11, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D98F0099727501438BED5616ABE2EF45', X'98C4ACE14B05C942816793DA603D0404', X'98C4ACE14B05C942816793DA603D0404', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A1467780810FE448BFCC6C090C90F0A', X'98C4ACE14B05C942816793DA603D0404', X'98C4ACE14B05C942816793DA603D0404', 1, NULL, NULL, 2, NULL, 0, 'Put the pork shoulder in the slow cooker, cutting the roast to fit if necessary.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E152C4E5E8F582438A54884C44AFA433', X'98C4ACE14B05C942816793DA603D0404', X'98C4ACE14B05C942816793DA603D0404', 2, NULL, NULL, 2, NULL, 0, 'Add the water or other liquid and sprinkle the pork lightly with kosher salt, freshly ground black pepper, and cayenne pepper, if using.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18D25CBBF0553F4CA16125EF6C146976', X'98C4ACE14B05C942816793DA603D0404', X'98C4ACE14B05C942816793DA603D0404', 3, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW for 7 to 9 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E5CC07E0ACA3348B3AA1C5662FF91FD', X'98C4ACE14B05C942816793DA603D0404', X'98C4ACE14B05C942816793DA603D0404', 4, NULL, NULL, 2, NULL, 0, 'Drain off the excess liquids, shred the meat with two forks, and discard any excess fat and bone.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAE1E153E116724DBF3219AD3C7030ED', X'98C4ACE14B05C942816793DA603D0404', X'98C4ACE14B05C942816793DA603D0404', 5, NULL, NULL, 2, NULL, 0, 'Return the meat to the crockpot and pour about 1 cup of barbecue sauce over. Cover and cook another 1 to 2 hours.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3107759DB9B5F4E8FB3BC97AABD542A', X'98C4ACE14B05C942816793DA603D0404', X'98C4ACE14B05C942816793DA603D0404', 6, NULL, NULL, 2, NULL, 0, 'Serve the shredded barbecued pork with extra barbecue sauce on the side.');
