INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AEC7F183C5CF14890743336C2DF5866', X'18A93CA0A6B6E94F92775ABD943E50D9', X'18A93CA0A6B6E94F92775ABD943E50D9', 3, NULL, NULL, 2, NULL, 0, 'Place the seasoned pork shoulder in a roasting pan with a rack and transfer it to the oven.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB88C32ECF8C544CAB916BD635C7D2A5', X'18A93CA0A6B6E94F92775ABD943E50D9', X'18A93CA0A6B6E94F92775ABD943E50D9', 4, NULL, NULL, 2, NULL, 0, 'Roast for 20 minutes, then lower the heat to 250 F and cook until your probe thermometer reads 140 F, which will take another 1 hour or so. The exterior of the roast will be a beautiful brown color.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'401DA8577AE36843AC4C9B7A8C0504BB', X'18A93CA0A6B6E94F92775ABD943E50D9', X'18A93CA0A6B6E94F92775ABD943E50D9', 5, NULL, NULL, 2, NULL, 0, 'Remove the roast from the oven, cover it loosely with foil, and let it rest for 15 minutes (during which time the temperature will continue to rise until it reaches the target temperature of 145 F).');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D0F95BC93714540B4444BC2E2A66336', X'18A93CA0A6B6E94F92775ABD943E50D9', X'18A93CA0A6B6E94F92775ABD943E50D9', 6, NULL, NULL, 2, NULL, 0, 'Slice, serve, and enjoy.');
INSERT INTO `VWR INTERNAL MeatRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B04F5DD6AC3C8747B33CD966DDC6918A', X'6E2EEAC4B1C89E4FA0803F7B17142098', X'6E2EEAC4B1C89E4FA0803F7B17142098', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
